INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (14, '713418', '互助北山国家森林公园', NULL, '青海 > 互助 > 互助景点 > 互助北山国家森林公园', '海东', '互助', 'https://img1.qunarzz.com/travel/poi/201303/14/78553b8b6d31d929ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/201303/14/69f5a21ad9adea16ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/201303/14/16882bfef241ec04ddb12cfb.jpg;https://img1.qunarzz.com/travel/d6/1609/2c/efb34479aba2e9b5.jpg;https://img1.qunarzz.com/travel/poi/1803/52/c796f63d39b6537.jpg;https://img1.qunarzz.com/travel/d1/1803/b5/1ad2e9f0890672b5.jpg', '4.8', '互助景点排名第4', '4小时 - 6天', '14', '36.822154', '102.532213', '1', '北山国家森林公园位于互助土族自治县东北部，公园内群峰巍峨，山清水秀，天高云淡，空气洁净，高原特色突出，是旅游避署、疗养度假、科普考察的胜地，是青海省唯一的以森林自然景观为主体的生态公园。\n公园由元甫达坂、浪士当、卡索峡、扎龙沟、下河五大景区组成，兼有雄、奇、险、秀四大特色，主要景点有“达坂红叶、龙尾观云、擎天一柱、圣母天池、石龙烟雨、高山牧场、药水瀑布”等80余处。', '青海互助土族自治县东北部', NULL, 'http://www.hzbsjq.com/', '9:00-17:00', 4008, '夏秋季节，夏季的森林公园内气候清凉，秋季则是色彩斑斓的世界。', '北山国家森林公园位于互助土族自治县东北部，距省会西宁110公里。建议从西宁包车前往或到互助县后包车前往。西宁包车费用约500元/天。', NULL, NULL, 'http://travel.qunar.com/p-oi713418-huzhubeishanguojiasen', '', '2', '', '2', '1', '2', 6, 31561, 137, NULL, 49337, NULL, 44822, 15045, 17455, 12970, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (15, '9721425', '艾比湖', NULL, '新疆 > 精河 > 精河景点 > 艾比湖', '博尔塔拉', '精河', 'https://img1.qunarzz.com/travel/poi/1707/fc/354b58de93df7e37.jpg', '--', '精河景点排名第1', NULL, '0', '44.8774230388', '82.6667399348', '0', '位于精河县城以北35公里处，西与北疆铁路精河到阿拉山口段相邻，向北行35公里，即到阿拉山口，东为甘家湖梭梭林自然保护区。艾比湖是准噶尔盆地最大的湖泊，也是新疆最大咸水湖。博尔塔拉河、精河、奎屯河，分别从西、南、东三个方向注入艾比湖，成为湖水的主要来源。湖面呈椭圆状，面积650平方公里，水深平均2——3米，湖面海拔189米。\n中华人民共和国成立初期湖面面积1200平方公里，如今湖面已经萎缩至500平方公里左右，湖滨地区荒漠化程度加剧，成为中国西部沙尘暴主要策源地之一，直接威胁到天山北坡经济带的可持续发展和新亚欧大陆桥的安全运行。', '新疆 博州 精河县', NULL, NULL, '全天', 5786, '春季 秋季 夏季', '博乐市区有到达艾比湖自然保护区的旅游专线，或者可以选择自驾。', NULL, NULL, 'http://travel.qunar.com/p-oi9721425-aibihu', '4', '3', '1', '4', '5', '1', 4, 25804, 151, 159989, 52147, 22272, 124691, 150206, 18493, 6538, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (16, '9201141', '板梁古村', NULL, '湖南 > 永兴 > 永兴景点 > 板梁古村', '郴州', '永兴', 'https://img1.qunarzz.com/travel/d2/1803/33/adcf200d4f552ab5.jpg;https://img1.qunarzz.com/travel/d2/1803/ce/7c9d43c0b85c6cb5.jpg;https://img1.qunarzz.com/travel/d9/1803/6f/10e4ab8bf3282cb5.jpg;https://img1.qunarzz.com/travel/d7/1803/45/a93eca66eb544bb5.jpg;https://img1.qunarzz.com/travel/d7/1803/44/0b61f10acd11f8b5.jpg;https://img1.qunarzz.com/travel/d7/1803/2c/900bbabbc74d24b5.jpg', '4.7', '永兴景点排名第1', NULL, '3', '26.084327', '112.873869', '0', '板梁古村位于湖南省郴州市永兴县高亭乡境内，现有人口1886人，耕地面积1260亩，一个自然村布局，辖19个村民小组，村民以种粮、种烤烟为业，生活传统古朴。 　　板梁村历史久远，初建于宋末元初，强盛于明清时代，距今有600多年历史，是原金陵县的重要集镇，也是桂阳、耒阳、常宁往返的商埠之地。 　　板梁人杰地灵，是当地刘姓的主要开源地之一，据族谱记载，从板梁迁徙开发的刘姓村庄有400多个，约八万多人。在历朝为官者数百人，历史底蕴厚重，传奇故事多多，是典型的湘南宗族聚落。开国大将黄克诚从板梁暴动走向革命道路。目前为国家AAA级景区、湖南省历史文化名村。 　整个古村占地3平方公里，背靠象岭平展延伸，依山就势，规模非常宏大，村前视野开阔，小河绕村而下，三大古祠村前排列，古驿道穿村而过，石板路连通大街小巷。村前有七层古塔，进村有石板古桥，村内建有庙祠亭阁，旧私塾，还有古商街、古钱庄。古村小桥流水，曲径通幽，宝塔、古井、石板路布局机巧，奇石异村令人叫绝，乡村古风别有洞天。 　　古村内至今仍保存了360多栋完好无损的明清历史建筑，这些历千劫而不倒的古民居，栋栋雕梁画栋，飞檐翘角，无论是它的水磨青砖，还是门当户对，或者是它的砖雕、石雕、木雕，其工艺都十分精湛，让人叹为观止。总而言之，板梁古村蕴藏着中国古老的宗法仪式、儒学传统、风水观念、哲学意识、建筑技巧、生态原理等。被誉为规模最大，保存最全，风水最好，文化底蕴最厚重的“湘南第一村”。', '湖南省郴州市永兴县高亭乡', '0735-5913522', NULL, '8:30-17:00', 16409, NULL, '郴州乘坐到马田、耒阳或衡阳的车，在板梁古村路口下车（上车前记得叫司机到地方提醒下车），再打摩的或面包车到景区。自驾\n建议自行驾车前往。', NULL, '3-4小时', 'http://travel.qunar.com/p-oi9201141-banlianggucun', '', '4', '4', '5', '', '2', 2, 44335, 133, NULL, 177843, 84726, 116714, NULL, 42545, 12609, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (17, '9127097', '金卉庄园', NULL, '湖北 > 孝感 > 孝感景点 > 金卉庄园', '孝感', '孝感', 'https://img1.qunarzz.com/travel/poi/1511/85/f1916d522ce9bbf7.jpg;https://img1.qunarzz.com/travel/poi/1606/c7/4492baee18d0759a.jpg', '4.9', '孝感景点排名第11', '4小时 - 1天', '10', '31.017517', '113.952764', '2', '“金卉庄园”是一记散落于荆楚大地的美丽之梦，梦中的乡村、梦中的田园、梦中的泥土、梦中的芳香、梦中的春夏秋冬。 湖北金卉庄园是一个集“农业科普观光、四季花海展示、果蔬采摘体验、乡村特色美食、田园会议住宿、户外培训拓展、运动娱乐健身、水上欢乐世界、艺术文化交流、婚纱摄影及婚庆文化活动举办”为一体的生态休闲旅游度假庄园。 金卉庄园将力争成为国内一流、华中地区最大的花卉艺术观赏中心、婚庆（婚纱摄影）活动基地、及科普农业观光教育基地、户外拓展运动休闲中心，企业文化活动中心，填补孝感市无著名旅游景点的空白，使游客真正享受旅游观光、休闲、购物、度假的“一站式”体验，促使孝感跨入“立体旅游”新时代。', '湖北省孝感市朋兴店107国道旁', '(0712)2999666', 'http://www.hbjhzy.com', '8:30-17:30', 4185, '金卉庄园属于北亚热带湿润季风气候区。气候温和，季风气候显著，冬寒夏热，四季分明。一年四季鲜花盛开。', '乘车\n孝感市乘坐孝感到肖港（孝昌）的汽车或火车站到太子的汽车，到【金卉庄园】门口下车\n\n自驾\n孝感市区驾车沿北京路（107国道）往孝昌方向10公里到金卉庄园\n\n', '请遵守参观地点旅游规定，禁止吸烟、随地吐痰、乱扔垃圾和随意进入非参观游览区内拍照等不良行为；与游客和当地居民交际时，注意文明礼貌，尊重当地习俗；攀爬高处，既要防止跌落受伤，同时也要预防脚被尖锐物扎伤或被山区蛇虫咬伤；经过高处或钢索栈道时，必需扶好栏杆或钢索；不要拥挤追逐，小心踏空；经过台阶和狭窄、路滑地段，谨防跌倒；如经过有正在施工地段，需保持安全距离，走安全通道，不要随意进入施工现场，防止跌落、扎伤、触电、坍塌等事故。', NULL, 'http://travel.qunar.com/p-oi9127097-jinhuizhuangyuan', '', '5', '1', '1', '', '1', 8, 34265, 153, NULL, 222750, 11782, 13629, NULL, 8507, 5723, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (18, '704301', '香溪洞', NULL, '陕西 > 安康 > 安康景点 > 香溪洞', '安康', '安康', 'https://img1.qunarzz.com/travel/poi/1803/9a/b327b32791e01537.jpg;https://img1.qunarzz.com/travel/d6/1606/e2/8c2b8ac426c434f7.jpg', '4', '安康景点排名第1', '2小时', '9', '32.6666326172', '109.0419055177', '4', '香溪洞风景区分香溪洞、三天门、蜈蚣山、牛蹄岭五个景区，有50多个景点。稀有珍贵树木12种，被谓之“活化石”，为国家一级保护植物。1989年12月，香溪洞风景区被列为第一批省级风景名胜区。香溪洞风景区，四面群山环抱，翠屏相列，草木葳蕤，亭宇林立，一派绮丽明媚的风光！小溪、流水清澈，悬帘挂布。溪水两岸，绿树馥郁，清馨气爽，碧草香花，平铺如茵。溪旁长有奇花“香团刺”（俗称七里香），每逢春季盛开之时，花随水转，水播花香，香味远扬，令人陶醉，故名“香溪洞”。', '陕西省安康市汉滨区城南约5公里处', '0915-3287906', 'http://www.akxxd.com/', '夏季7:00-19:00；冬季等待景区具体通知', 11198, NULL, '乘车：\n从安康市内乘车可达，车程约30分钟。在香溪路口可乘中巴；旅游旺季市内有专车前往；瀛湖码头也有中巴去香溪洞。', NULL, NULL, 'http://travel.qunar.com/p-oi704301-xiangxidong', '2', '', '', '', '5', '4', 7, 48319, 155, 31345, NULL, NULL, NULL, 156830, 80485, 40392, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (19, '9630113', '红坎瀑布', NULL, '海南 > 白沙 > 白沙景点 > 红坎瀑布', '白沙', '白沙', 'https://img1.qunarzz.com/travel/poi/1704/a3/92a6b06c487a3037.png;https://img1.qunarzz.com/travel/poi/1704/ef/0cd601811996be37.png', '--', '白沙景点排名第3', NULL, '0', '19.079775', '109.50896', '0', '红坎瀑布总落差145米。瀑布源于海拔1101米的红坎岭，两岸峰峦矗立，熔岩密布。凌空倾泻，宛如银色巨龙从天而降。透过青枝藤蔓临近仰望，瀑流顺陡山而生，沿断崖而长，犹如淑女头上轻柔的俊发披散落地，几分潇洒，几分飘逸。瀑布又好似银蛇狂舞，冲击叠岩景象万千。激起成水珠，珍珠般蹦弹；落下成水花，晶莹般璀璨；散开成水雾，云霞般柔曼。时出时没，忽明忽暗，为寂静的红坎地质遗迹增添了灵气。一到雨季，山洪暴发，瀑布声若雷鸣，震撼群山，气势雄伟壮观。', '海南省白沙黎族自治县元门乡东南部', NULL, NULL, '8:00-18:00', 2937, NULL, '乘坐前往白沙县城的巴士，然后在县城乘坐摩的前往。', NULL, NULL, 'http://travel.qunar.com/p-oi9630113-hongkanpubu', '1', '3', '4', '3', '1', '1', 1, 28804, 106, 14498, 28762, 125165, 26984, 10669, 22058, 6000, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (20, '8456070', '索南达杰自然保护站', NULL, '青海 > 玉树 > 玉树景点 > 索南达杰自然保护站', '可可西里', '玉树', 'https://img1.qunarzz.com/travel/d1/1705/ad/be3455c7c99033b5.jpg;https://img1.qunarzz.com/travel/d6/1705/d2/3890dfcb26c64fb5.jpg;https://img1.qunarzz.com/travel/d2/1705/bc/19736e7b5db2cb5.jpg;https://img1.qunarzz.com/travel/d8/1705/ef/e1ca975d06f7dbb5.jpg;https://img1.qunarzz.com/travel/d7/1705/96/954d1e01952f93b5.jpg;https://img1.qunarzz.com/travel/d4/1705/ca/af1a8c6ebb4916b5.jpg', '4.5', '玉树景点排名第57', '2小时 - 3小时', '8', '35.4379882812', '93.6081736507', '5', '可可西里自然保护区的索南达杰保护站，海拔4479米，是五个保护站中建设最早、设施最全的一个。它的名字来自于一个真实的故事：1994年1月18日，治多县西部工委书记索南达杰为保护藏羚羊，一人同18名偷猎者枪战，英勇牺牲。死后，他的身体还保持站立射击的姿势。可可西里被称为“世界第三极”，自然环境严酷，气候恶劣，人类无法长期居住、生产和生活，因而保留了其原始的生态环境和独特的自然景观。这里作为可可西里反偷猎工作的最前沿基地。促进了可可西里藏羚羊保护的进程。', NULL, '0976-8416301', NULL, '可可西里自然保护区并未对外开放，欲前往保护区腹地需经保护站驻格尔木办事处工作人员同意。', 591, NULL, '从格尔木市区包车前往需300元左右，可同玉珠峰和昆仑山口一起参观。', '因海拔较高，建议携带厚衣物前往。', NULL, 'http://travel.qunar.com/p-oi8456070-suonandajieziranbaohu', '', '', '3', '3', '3', '5', 4, 39060, 159, NULL, NULL, 92085, 85318, 34756, 357303, 111609, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (21, '3728700', '悬壁长城', NULL, '甘肃 > 嘉峪关 > 嘉峪关景点 > 悬壁长城', '嘉峪关', '嘉峪关', 'https://img1.qunarzz.com/travel/d4/1611/f9/26f7727fb1310bb5.jpg;https://img1.qunarzz.com/travel/d9/1509/65/d23b7f16d99a8d.jpg;https://img1.qunarzz.com/travel/d3/1610/ba/8af6ce125cf58cb5.jpg;https://img1.qunarzz.com/travel/d9/1611/36/5bdeee88604f1bb5.jpg;https://img1.qunarzz.com/travel/d3/1708/f3/eed8d4ef0c5b2eb5.jpg;https://img1.qunarzz.com/travel/d5/1408/16/4bc603db3f9a9bb6ffffffffc8d65eac.jpg', '4.2', '嘉峪关景点排名第6', '1小时 - 2小时', '85', '39.7799094542', '98.2950488649', '91', '悬壁长城似长城倒挂，铁壁悬空，故有“悬壁”之称。它建筑于明嘉靖十八年，是嘉峪关军事防御体系的重要组成部分，因地势险峻，气势雄伟，故有“西部八达岭”之称。 悬壁长城原墙现只余一截，，现存750米长城经1987年重修，其中有231米城墙悬挂于高150米，倾斜度为45’的山脊上，高达6米，片石、土层厚度如旧。在墙头增筑垛墙和宇墙，首尾各添筑一墩台，在首墩和山坡上筑台阶式馒道。游人拾级而上，平坦处如履平地，险峻处如攀绝壁，颇似北京八达岭长城。有诗赞云：“万里长城万里关，迭障黑山暗壁悬”。', '嘉峪关关城北8公里处石关峡口北侧黑山北坡', '0937-6395099', NULL, '旺季（5月1日-10月31日）8:00-20:00，淡季（11月1日-次年4月30日）8:30-18:00。', 13643, '嘉峪关市属温带干旱气候，年温差较大，年平均气温6℃左右，年降水量80多毫米。冬季1月最冷，绝对最低气温－28．6℃，夏季7月最热，绝对最高气温38℃。每年5月至10月，是嘉峪关市最佳旅游时节。', '从关城大门或市内乘出租车前往，往返40元；游览嘉峪关以后从正门（售票的大门）出来，很晚都可以找得到出租车回市区，10元/车。', NULL, '1小时30分钟', 'http://travel.qunar.com/p-oi3728700-xuanbizhangcheng', '', '3', '', '1', '', '3', 9, 28889, 115, NULL, 47369, NULL, 12490, NULL, 72723, 27989, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (22, '9149846', '永乐群岛', NULL, '海南 > 西沙群岛 > 西沙群岛景点 > 永乐群岛', '三沙', '西沙群岛', 'https://img1.qunarzz.com/travel/d3/1606/60/e95a65a02873369a.jpg;https://img1.qunarzz.com/travel/d9/1512/ad/647744e00b86b5f7.jpg;https://img1.qunarzz.com/travel/d0/1512/33/4410a57725d772f7.jpg;https://img1.qunarzz.com/travel/d9/1606/87/fd6e3a465262c59a.jpg;https://img1.qunarzz.com/travel/d8/1606/76/e0d271242b1df39a.jpg;https://img1.qunarzz.com/travel/d7/1512/9e/79bbd9ee3e107af7.jpg', '5', '西沙群岛景点排名第7', NULL, '5', '16.597104', '111.700669', '1', '为建成中国版马尔代夫，自2014年春西沙旅游航线开通一年多来，永乐群岛已接待了1万多名游客，昔日沉寂的永乐群岛一天天热闹起来，建设项目增多、岛礁生活改善、旅游活动越来越旺。为配合旅游产业发展，永乐群岛将在5个岛礁建设5个特色：晋卿岛有住宿休闲、鸭公岛提供餐饮服务、羚羊礁主推珍珠养殖文化、甘泉岛建唐宋文化遗址文博馆、银屿则主打潜水游泳。', '海南省直辖县级行政单位西沙群岛', NULL, NULL, NULL, 5943, '海南旅游旺季：从9月份到农历春节，因为海南的秋冬季温暖怡人，而且冬季更是到海滨旅游的最好时候。在冬天，即使地球其他角落寒流来袭，仍可以穿着夏装、沙滩鞋在阳光充沛的海滩悠闲漫步，浸润在暖暖的海水中，吃着热带水果，享受着天涯海角的浪漫。\n旅游淡季：海南地处热带，终年气温高，寒暑变化不大，年平均气温25.5度。海南最热的月份是6月，平均气温为28.5℃，极端最高气温为35.7℃，所以海南基本是全年避寒、消暑、度假、旅游的好地方。即使选择淡季的3月到8月，相信也不会让你失望。', '从文昌的清澜港有西沙群岛的补给船琼沙2号，还有不定期从三亚榆林军港出发的补给舰，到永兴岛，15小时左右可以到达岛上，坐琼沙2号需提前一周向西沙工委申请办上岛证，没有上岛证是不能乘坐补给船的。', NULL, NULL, 'http://travel.qunar.com/p-oi9149846-yonglequndao', '', '2', '', '', '5', '3', 1, 37266, 190, NULL, 42475, NULL, NULL, 253531, 66820, 24837, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (23, '7432357', '门源油菜花', NULL, '青海 > 门源 > 门源景点 > 门源油菜花', '海北', '门源', 'https://img1.qunarzz.com/travel/d1/1703/c5/b936e8c3e2c4fb5.jpg;https://img1.qunarzz.com/travel/d7/1709/88/499ad04723b600b5.jpg;https://img1.qunarzz.com/travel/d5/1708/5e/8977e34ce3e865b5.jpg;https://img1.qunarzz.com/travel/d8/1606/cf/8cb6b658bc3727f7.jpg;https://img1.qunarzz.com/travel/d5/1609/62/2b405cedccb2fbb5.jpg;https://img1.qunarzz.com/travel/d4/1610/fe/b9e8bd2f08abccb5.jpg', '4.6', '门源景点排名第2', NULL, '262', '37.48204', '101.447439', '22', '门源油菜花位于青海省海北藏族自治州的门源县，是青海省及西北地区的主要油料产区，这里的油菜花景色甚为壮观，具有十足的西部风情，成为青海旅游的一大旅游亮点，每年七月初开始这里就进入了油菜花盛开的季节，青石嘴镇的元山观花台和县水泥厂对面的南山上和东部仙米林区两个地点是最佳的拍摄地点，这里可以拍到非常漂亮的照片。', '青海省海北藏族自治州的门源县', '0970-8610333', NULL, '6月—9月 全天开放', 8287, '开花时间：7月5日—7月25日\n最佳花期：7月10日—7月20日。', '从西宁可以包车去门源，公车比较少。包车往返的价格在500左右。', '1、观景台要门票的，对于摄影爱好者比较值得，可以拍到漂亮的照片；\n2、每年的7月18日—25日，举办门源油菜花节。\n3、进入花田拍照要注意，应该是不允许的，随意进入会罚款。', NULL, 'http://travel.qunar.com/p-oi7432357-menyuanyoucaihua', '4', '2', '', '3', '3', '3', 8, 49661, 94, 145277, 14364, NULL, 85639, 42561, 95933, 20218, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (24, '707608', '西山风景区', NULL, '江苏 > 苏州 > 苏州景点 > 西山风景区', '苏州', '苏州', 'https://img1.qunarzz.com/travel/poi/1803/6c/1fa3f48a8119c237.jpg;https://img1.qunarzz.com/travel/d6/1608/d4/2372ea72c1da63b5.jpg;https://img1.qunarzz.com/travel/d5/1608/b0/16528423f738deb5.jpg;https://img1.qunarzz.com/travel/d2/1608/50/40decc0daa8b98b5.jpg;https://img1.qunarzz.com/travel/d7/1608/1c/326221c97246e9b5.jpg;https://img1.qunarzz.com/travel/d7/1608/91/5cf1985cf765f4b5.jpg', '2.5', '苏州景点排名第28', '3天 - 6天', '5', '31.129671', '120.307859', '17', '西山是洞庭山的简称，南北宽11公里，东西长15公里，面积79.8平方公里，系太湖第一大岛。西山景区是太湖风景名胜区的精华。它是以群岛风光、花果丛林、吴越以来的古迹见长，以浏览、度假为主的湖岛区。它拥有湖中群岛、湖湾山水、山中坞谷、山顶峰峦四个风景层次。全区规划建设缥缈云场、水月问茶、林屋晚烟、消夏渔歌、里犁云、玄阳稻浪、肖山遗踪、毛公积雪、西湖夕照、石 公秋月，风凰烟雨、金铎松篁、文化巽峰、大沙观帆、罗汉古刹、鸡笼梅雪、明湾古村、角风涛、天王鱼国、横山旭日等二十个景点。', '苏州市吴中区金庭镇', '0512-66310058、0512-66310038、4000688798', 'http://www.xsly.org/', '西山全天开放，内部各小景点开放时间不一，大致为：夏季8:00-17:00，冬季8:00-16:30。', 3106, NULL, '当地交通\n镇内旅游旺季时有小中巴往返于各景点，每人每站2元，平时可租乘机动三轮车或微型面包车前往，起步费5元。\n\n地铁\n乘坐地铁1号线在【木渎站】下，换乘69路，69路走西山岛的东路。\n\n公交\n金庭换乘枢纽站可坐694路、695路、697路，线路基本涵盖了岛上各个景点。', NULL, NULL, 'http://travel.qunar.com/p-oi707608-xishanfengjingqu', '4', '', '1', '', '4', '2', 0, 26511, 140, 46420, NULL, 7093, NULL, 67936, 30359, 10136, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (25, '704957', '三山岛', NULL, '江苏 > 三山岛 > 三山岛景点 > 三山岛', '太湖', '三山岛', 'https://img1.qunarzz.com/travel/d9/1712/82/269585bbdc3ea9b5.jpg;https://img1.qunarzz.com/travel/d6/1712/a7/74fb2007d2571bb5.jpg;https://img1.qunarzz.com/travel/d0/1712/23/148ce9a8f7508bb5.jpg;https://img1.qunarzz.com/travel/poi/201303/14/2b082442571e578addb12cfb.jpg;https://img1.qunarzz.com/travel/poi/201303/14/678e9fec19918be1ddb12cfb.jpg;https://img1.qunarzz.com/travel/d9/1712/51/e3ee39184e31e2b5.jpg', '4.7', '三山岛景点排名第1', '3天 - 6天', '3', '31.0357374258', '120.3015282846', '1', '三山岛位于苏州西山和东山之间，是太湖上真正的岛屿，由一个大岛三山岛，两个小岛泽山岛、嶡山岛组成，游客游览的主要是三山岛。三山岛因岛上的北山、行山、小姑山三座山得名，主峰海拔81.2米。岛上的田园风光，奇巧的太湖石，烟波飘渺的太湖湖景是三山岛最吸引人的地方。\n岛的西南角沿着太湖边有十二生肖石，是一排天然的动物造型太湖石，从湖上看过去角度最好。可以让农家主人替你联系快艇，在第一天的黄昏坐上快艇，既能欣赏奇石，还能看到太湖上的美景和落日，鸭子自在地游泳，渔民开着小船回家，夕阳在开阔的湖面拉出一条长长的余晖，一派世外桃源的景象。', '中国江苏省苏州吴县', '0512-66373358、0512-66373907', 'http://www.ssdly.com.cn/', '三山岛上全天开放。班船时间：入岛9:00-17:00，出岛8:30-16:30。周五至周日的班船要比周一至周四多，具体班次可参考\nhttp://www.ssdly.com.cn/bcskb/。\n以上为冬令时，仅供参考，以景区实际时间为准，夏令时将做适当调整。', 10171, NULL, '东山镇长圻码头和沙滩山码头均可抵达三山岛，其中长圻码头有班船和快艇，沙滩山码头仅有快艇。班船行程约25分钟，票价15元／人；包快艇180元/船，散客快艇25元/人，满7人就可开船。班船20分钟，快艇5-10分钟。\n东山长圻码头——三山岛先奇码头班船时刻表\n■周一至周四：\n上岛：09:00、11:00、14:00、17:00\n出岛：08:00、11:00、14:00、17:00\n■ 周五至周日及节假日：\n上岛：09:00、10:30、12:00、14:00、16:00、17:30\n出岛：08:00、09:30、11:00、13:00、15:00、17:00', NULL, NULL, 'http://travel.qunar.com/p-oi704957-sanshandao', '1', '', '3', '2', '1', '1', 7, 23573, 208, 13426, NULL, 73881, 17117, 23210, 19768, 5832, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (26, '708452', '日月湾', NULL, '海南 > 万宁 > 万宁景点 > 日月湾', '万宁', '万宁', 'https://img1.qunarzz.com/travel/poi/1708/e5/f3e32bcd8b8f0e37.jpg;https://img1.qunarzz.com/travel/poi/1708/92/417ed817d8c7a237.jpg;https://img1.qunarzz.com/travel/poi/1708/40/3e7ccee97c30c937.jpg;https://img1.qunarzz.com/travel/poi/1708/76/877a597b0f3c4e37.jpg;https://img1.qunarzz.com/travel/d0/1506/a0/772afed273eb76.jpg;https://img1.qunarzz.com/travel/d3/1507/8f/0b128253f22973.jpg', '4.6', '万宁景点排名第6', '2小时 - 4小时', '30', '18.6342116293', '110.223172487', '26', '日月湾海门游览区分为海门公园和台海民俗文化游览区（台湾村）两部分。海门公园主要景观和配套服务项目有：海门奇观、椰林休闲广场、龙王鼎、沙滩娱乐、海边骑马等。台海民俗文化游览区内，在一条湾涎小河的主线的牵引下，将“天长地久”大拱门、清水造雾、外婆家、台海兰苑、盘古掌、人造瀑布、快活椰林、风情演艺、中华龙等景物有机地联动起来，具有静态景观、观有动态景观。槟榔园里还有高山族对歌亭、椰叶吹奏亭、海誓山盟、高山族民居、九族文化馆、椰风海韵、高山歌舞表演场等。', '海南省万宁市日月湾海边', '0898-62585235', NULL, '7:30—17:30', 1038, '全年皆宜。海南长夏无冬，终年湿润，无明显季节之分。', '风采车\n万宁当地特色的交通工具，又叫“蹦蹦车”，票价按距离1—10元不等。\n汽车\n万宁当地私营的中巴车，招手即停，票价按距离收费。\n公交\n乘兴隆旅游公交5路车往日月湾旅游区。\n\n', NULL, '2-4小时', 'http://travel.qunar.com/p-oi708452-riyuewan', '', '2', '', '3', '1', '4', 2, 48330, 112, NULL, 14395, NULL, 72243, 19046, 46467, 48043, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (27, '9600398', '董子园风景区', NULL, '山东 > 德州 > 德州景点 > 董子园风景区', '德州', '德州', 'https://img1.qunarzz.com/travel/poi/1704/49/e5c556ddf1fa5b37.png', '4.7', '德州景点排名第24', NULL, '3', '37.449044992', '116.379729268', '0', '景区内以沙王河水系为脉络，建有多座灰白色调的仿古建筑，景区内有董子读书台、中国德州儒家文化展馆、“三策固本”水上牌坊、董子文化街、春晖园等景点。董子读书台是景区内的主景，董仲舒青年时期曾在德州研读儒学13年，历史上建有董子读书台，后毁于战火。复建后的董子读书台高24.8米，由董子读书大殿、董子祠及相关配套工程组成。读书台前耸立着汉代大儒董仲舒的青铜像。整个景区以儒家文化为主题，大力弘扬儒家思想文化，同时，通过宣传董子读经“三年不窥园”的精神，鼓励青年刻苦努力，成为“贤良方正”的栋梁之材。景区内还建有石舫、湖心亭、五曲桥、赵州桥、擎天柱喷泉、阴阳鱼广场等景观。', '山东省德州市德城区董子园风景区', '0534-2561901', NULL, '8:30-17:00', 14178, NULL, '附近公交站：开发区商贸城', NULL, NULL, 'http://travel.qunar.com/p-oi9600398-dongziyuanfengjingqu', '3', '5', '4', '', '3', '4', 3, 30933, 176, 28312, 163065, 171647, NULL, 88540, 110880, 47039, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (28, '704385', '盖斯墓', NULL, '新疆 > 哈密 > 哈密景点 > 盖斯墓', '哈密', '哈密', 'https://img1.qunarzz.com/travel/poi/1409/16/4c4e42dfc5045739ffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/poi/1803/7d/f1ceff7aa963c837.jpg', '4.4', '哈密景点排名第2', '1小时', '20', '42.824007', '93.502387', '0', '盖斯墓，位于哈密市西郊，是一座融阿拉伯与汉族建筑风格于一体的建筑物，伊斯兰信徒朝的重要圣地之一。相传，唐贞观年间，应唐太宗李世民邀请，伊斯兰教先知穆罕默德派弟子盖斯、乌外斯，万嘎斯三人来中国传教。万嘎斯病逝广州，盖斯和乌外斯到达长安，受到唐太宗的欢迎。回国途中，乌外斯病逝于河西之回回堡，盖斯则于唐贞观九年（公元635年）殁于星星峡，被草草掩埋。后来，哈密回王派人在星星峡为其修建一座拱北陵墓。\n墓占地8亩，整个墓地为长方形（南北狭长）后部为盖斯墓主体建筑。盖斯墓东西长22米，南北宽12米，通高15米，基座方形，圆形拱顶，分上下两部分，上部为拱式圆顶，用绿色琉璃砖贴面，下部为方形；外部建有回廊，南北各有7根，东西各有6根木柱支撑，四周有50公分高的木栏围起，结构精致，形体威严。', '哈密市城郊乡大营门村', '0902-2357505', NULL, '9:30-19:00', 7274, '最佳季节是春秋季节，此时的哈密气候较为舒适，秋季则是哈密瓜果上市的季节。', '盖斯墓靠近哈密王陵，可参观完哈密王陵后，往东走，在第一个路口往北拐，直走就可以到盖斯墓，步行15分钟可到。', NULL, NULL, 'http://travel.qunar.com/p-oi704385-gaisimu', '', '3', '5', '', '5', '5', 7, 49677, 185, NULL, 50560, 281362, NULL, 397166, 339432, 95517, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (29, '9474771', '闸坡', NULL, '广东 > 阳江 > 阳江景点 > 闸坡', '阳江', '阳江', 'https://img1.qunarzz.com/travel/d6/1705/4c/06289fe6dd0118b5.jpg;https://img1.qunarzz.com/travel/d9/1705/3d/a95ff3e93dff8ab5.jpg;https://img1.qunarzz.com/travel/d6/1609/90/c8d427ebf3cc44b5.jpg;https://img1.qunarzz.com/travel/d1/1609/92/69d6ecebe73c9b5.jpg;https://img1.qunarzz.com/travel/d1/1609/35/2dd452566a2cdcb5.jpg;https://img1.qunarzz.com/travel/d5/1608/1d/d4099e24be8872b5.jpg', '4.7', '阳江景点排名第6', NULL, '4', '21.574987', '111.848297', '18', '闸坡是海陵岛重镇，辖区17平方公里，常住人口三万。前清时期，西南沿海港口成为船舶避风补给之处，闸坡镇开始建制。随着商务和渔业生产的发展，镇上渐渐有了店铺作坊，有了酒肆茶楼，有了集市贸易。港口内船密人稠，镇外开辟了稻田菜地，如今作为全国十大渔港的闸坡港，海市热闹非凡。 闸坡紧靠大角湾，是一个以渔业为主的古镇，位于海陵岛，海陵岛位于广东省阳江市西南端。大角湾位于闸坡镇东南，是岛上最吸引人的景观。滩长2.45公里，宽50至60米，以阳光、沙滩、海浪、海鲜驰名于世。 在闸坡能得到完全的放松，充分的刺激，游乐项目包括冲浪、橡皮艇、摩托艇、沙滩赛马、骑骆驼、沙滩排球、逐浪溜冰、激光射击、钓鱼、烧烤等。闸坡镇每到入夜异常热闹，夜市通宵营业。', '广东省阳江市海陵岛', '0662-3888240', NULL, '大角湾 7:00-18:00', 13336, NULL, '概况\n海陵岛交通网络四通八达，广州、深圳、珠海等地均有直达旅游车至阳江或闸坡，阳江车站直达快车不停往返于闸坡与阳江之间，旅游区内有中巴、电瓶车、黄包车穿梭往返、交通十分便利。\n闸坡至各地班车时刻：\n闸坡——阳江（48公里）班车：每天6:15—21:30 每15分钟一班（阳江市汽车总站及第二汽运站均有发车）\n闸坡——广州班车：每天9:10、14:30 各一班\n闸坡——珠海班车：每天8:40、14:30、16:10各一班\nTips：车票实行全市联网管理，可从海陵岛试验区车站直接购买从阳江市直达各地车票。', NULL, NULL, 'http://travel.qunar.com/p-oi9474771-zhapo', '2', '4', '3', '', '3', '5', 6, 45584, 188, 10616, 182766, 73153, NULL, 55015, 225252, 94758, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (30, '7469130', '泉城欧乐堡梦幻世界', NULL, '山东 > 齐河 > 齐河景点 > 泉城欧乐堡梦幻世界', '德州', '齐河', 'https://img1.qunarzz.com/travel/d4/1701/22/43318afb86ee89b5.jpg;https://img1.qunarzz.com/travel/d0/1701/20/b2351d10315cecb5.jpg;https://img1.qunarzz.com/travel/d8/1701/dc/18c381ca9099feb5.jpg;https://img1.qunarzz.com/travel/d2/1701/97/434328ff376e4ab5.jpg;https://img1.qunarzz.com/travel/d7/1701/65/0bc6280f91f80ab5.jpg;https://img1.qunarzz.com/travel/d7/1701/dd/27a7c59015bde6b5.jpg', '4.7', '齐河景点排名第1', '3天', '7', '36.749665', '116.851086', '0', '泉城欧乐堡梦幻世界由山东省坤河旅游开发有限公司投资30多亿元打造，荟萃欧美经典建筑风格与顶尖科技元素，由欢乐派梦幻小镇、龙之心、魔境仙踪、狂野非洲、童话镇、天空之城、秘境之湖七大主题区组成，引进世界巅峰游乐项目多余种。亚洲唯一的蓝火过山车总造价1亿多元，由世界一流的设备供应商德国马克公司所提供，全长1098米，制高点38米，最高时速可达100公里，采用火箭发射式原理，让游客体验瞬间离地的超刺激、惊险快感。\n观光火车让您一站式体验到心仪已久的游乐设备，沿途可以欣赏到火山、冰川、奇石林景观，还可观赏到多种国家珍稀保护动物。\n5D球幕飞行影院是国际最先进的大型室内科幻娱乐项目之一。巨幅半球形银幕配合场面宏大的电影画面和尖端三维技术，由提升悬挂式座椅把你带到15米高空，就像坐上滑翔机一样，带你身临其境地凌空飞行、俯瞰世界各地的美丽风光！\n4D动感轨道车，乘坐超炫时空战车，配合尖端的三维技术及环境特效，带您零距离体验地球面临毁灭所带来的感官震撼。同时，唤醒大家爱护自然，保护自然的意识，实现人与自然的和谐共存。该项目不仅具备观影性、刺激性，还具有深刻的教育性特点。', '山东省德州市齐河县黄河国际生态城旅游路07号', '0531-85507866，0531-85507818', 'http://www.baxian.com.cn/Holiday/quancheng/index.shtml', '9:00-17:00', 4358, NULL, '概况\n泉城欧乐堡梦幻世界位于山东省齐河县黄河国际生态城旅游路07号，济南建邦黄河大桥北首西行12公里（泉城海洋极地世界东邻）。景区距济南市区仅有20分钟的车程，各位游客可以到济南客运中心（位于天桥区堤口路75号，天桥区政府西邻，堤口路大润发东斜对面）乘做K906路公交车可直达景区。\n\n\n', NULL, NULL, 'http://travel.qunar.com/p-oi7469130-quanchengoulebaomenghuan', '4', '1', '', '', '', '5', 1, 28892, 176, 76268, 12594, NULL, NULL, NULL, 347964, 107238, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (31, '714007', '扎尕那景区', NULL, '甘肃 > 迭部 > 迭部景点 > 扎尕那景区', '甘南', '迭部', 'https://img1.qunarzz.com/travel/d3/1511/71/9115d39ac9b53df7.jpg;https://img1.qunarzz.com/travel/d5/1708/43/9d27fa7c19d2e7b5.jpg;https://img1.qunarzz.com/travel/d4/1509/36/c5095fc8d69a9d.jpg;https://img1.qunarzz.com/travel/d2/1511/82/bbb2405fc53847f7.jpg;https://img1.qunarzz.com/travel/d8/1610/1f/1517a66aef6aafb5.jpg;https://img1.qunarzz.com/travel/d4/1610/7d/89e4fd2a7c0838b5.jpg', '4.6', '迭部景点排名第1', '1小时 - 2小时', '151', '34.244449', '103.199339', '21', '扎尕那景区集石林、峭峰、森林、田园及村寨为一体。正北是璀璨生辉、巍峨恢宏、雄伟壮观的迭山主脊光盖山石峰，另外三面皆是秀峰环拱，苍松翠柏郁郁葱葱，犹如高峻雄浑、坚不可推的城墙，把扎尕那四村一寺围在城中，其中东哇村和桑拉寺院正巧座落于石城中央。城内左上角还有一道出口，是由石山断裂形成的石质狭道，南北走向，长百余米，宽仅数米，石峡两面是垂直挺拔的岩壁，一条小溪悬泻而下，声响如雷。此道亦是洮叠古道中的险关。此为石城“后门”。而在石城正南方，有一道石山对峙而成的“正门”，门外是一条南北走向的十里峡谷，恰似城外长廊，南端矗立两遭数百来高的对称岩壁，犹如两堵巨型门墩组成宏伟的前门，是俗称“鬼门关”的纳加石门。', '甘南迭部县益哇乡', NULL, NULL, '全天', 1285, '每年6月-8月，这时甘南的气温一般保持在15～30℃之间，凉爽宜人，草原上到处开满了格桑花。', '公共交通\n兰州到迭部，兰州汽车南站发车，至迭部每天三趟班车，发车时间分别为8:40、9:40、10:20。从迭部县城包车到扎尕那约30km，半小时左右，单程60-100元，柏油公路已通到各个村子里。\n\n包车\n郎木寺拼人包车到扎尕那十分方便，当日往返每人约50元，两日往返每人约100-150元。\n\n', NULL, NULL, 'http://travel.qunar.com/p-oi714007-zhaganeijingqu', '', '', '3', '2', '4', '5', 6, 47709, 107, NULL, NULL, 44121, 21119, 150156, 344065, 107919, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (32, '9554267', '昌坊度假村', NULL, '江西 > 新余 > 新余景点 > 昌坊度假村', '新余', '新余', 'https://img1.qunarzz.com/travel/poi/1701/d6/07e2773897a86637.jpg;https://img1.qunarzz.com/travel/poi/1701/b2/bd560aa5ee470637.jpg;https://img1.qunarzz.com/travel/poi/1701/8e/b909ba86c59e7f37.jpg', '--', '新余景点排名第14', NULL, '0', '27.912788', '114.852251', '0', '新余昌坊度假村村位于江西省新余市北部山区，离国家重点风景名胜区仙女湖仅一山之隔，景区内有山林生态，湖泊生态和半人工生态的耕地，是天然氧吧。其村山环水绕，柳暗花明，泉水叮咚，冬暖夏凉，空气清新，环境优美，历史文化深厚，旅游资源丰富。昌坊村曾有“江西小南京”之称，也有“上有天堂，下有苏杭，还有昌坊”之誉，更有“宝山荟萃地，山泉聚集地，夏布发祥地”之说。村民从南宋时期就开始了破麻织夏布渊源文化，是本地区夏布发祥地，加上古树、古洞、古庙（遗迹）、古窑（遗迹）、古戏、古水井，形成了独具特色的山区文化。', '新余市渝水区欧里镇', '0790-6796071、0790-2181682、138****7466', 'http://www.xychangfang.com/', '全天', 12852, NULL, '公共交通\n市区乘坐客运班车到达景区，车程半小时。', NULL, NULL, 'http://travel.qunar.com/p-oi9554267-changfangdujiacun', '', '3', '', '4', '4', '5', 8, 41869, 96, NULL, 90693, NULL, 59212, 186973, 276431, 105879, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (33, '9486441', '龙虎山花语世界景区', NULL, '江西 > 鹰潭 > 鹰潭景点 > 龙虎山花语世界景区', '鹰潭', '鹰潭', 'https://img1.qunarzz.com/travel/d2/1704/32/42f4c232d74d3db5.jpg;https://img1.qunarzz.com/travel/d9/1704/98/9818a4d400f3a5b5.jpg;https://img1.qunarzz.com/travel/d1/1704/ec/0d278ff3b36fbbb5.jpg;https://img1.qunarzz.com/travel/d8/1704/f0/d8850706abc8eeb5.jpg;https://img1.qunarzz.com/travel/d6/1704/8f/02b2a36627af0bb5.jpg;https://img1.qunarzz.com/travel/d6/1704/8e/144c7994cd0f53b5.jpg', '--', '鹰潭景点排名第2', NULL, '1', '28.116393', '116.987962', '3', '江西龙虎山花语世界景区是一个多层次、全覆盖的生态旅游度假养生综合体。 花语世界景区占地1000亩，位于世界自然遗产的龙虎山排衙峰脚下，是龙虎山五A景区的一个主要组成部分。2016年正式开园的花语世界园是景区的核心，与正在建设的三大区域组成整个旅游集群。景区以花为媒，集观光、度假、休闲、餐饮、购物、民宿、微农庄、蔬菜花园、高端养生于一体，创意生态园艺生活内容，引领园艺生活成为时尚的高品质生活方式。 花语世界园是整个景区的一期项目，占地280亩，集3000余种优新花卉苗木品种，以欧式园林风格为主，强调彩色、空间的巧妙组合，体现最新的园艺景观创意潮流，景观设计唯美新颖，现建有：幻影花廊、瑶池黛影、林下花境、玫瑰花园、杜鹃花园、荷塘观鱼、香草花溪、台地花海、阳光草坪等主题景点，特色鲜明。充分展示了“公园是舞台，花卉是演员，游客是观众”的园艺体验之旅。2015年春建成开园游人如织，花在丛中笑，人在花海行，人景合一，完美诠释了人与自然相和谐的道教理念，已成为中国道教发源地、国家5A级龙虎山景区的新景点，江南最具园艺特色的旅游目的地。 由花语天地街、花语休闲庄、花语养生谷组成的花语世界景区二期占地720亩，紧靠排衙峰下。依山势缓坡开发运营的三大区域，是园艺生活内容的不同表达。 花语天地街区主要由现代园艺生活的销售与互动为主，包含了花语休闲广场、花雨快速生态餐厅、花语咖啡馆、烘焙房、甜品屋、生态茶馆、生活超市、花影艺术影像中心、花语大舞台、花语停车场等生态商业配套设施组成。 花语休闲庄是由集合美宿、特色民宿、星空睡眠、花卉庄园、香草能量园、采摘农庄与亲子娱乐区的组合呈现，集合现代生态理念的居住、体验互动活动精华，将最前端、最现代、最具亲和力的园艺生活方式融于自然的怀抱。', '江西省鹰潭市龙虎山景区排衙石大道', NULL, NULL, '8:30-17:30', 19907, NULL, '公共交通\n鹰潭火车站广场（出站方向的左边）有旅游专线大巴直达龙虎山景区游客中心前进500米到达花语世界景区。', NULL, NULL, 'http://travel.qunar.com/p-oi9486441-longhushanhuayushijie', '2', '4', '1', '5', '', '3', 4, 46217, 100, 29043, 180302, 15120, 86172, NULL, 99205, 22580, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (34, '7528470', '七仙岭温泉', NULL, '海南 > 保亭 > 保亭景点 > 七仙岭温泉', '保亭', '保亭', 'https://img1.qunarzz.com/travel/d8/1608/86/f6f66a4aba353ab5.jpg', '5', '保亭景点排名第3', '6小时', '3', '18.707732', '109.703208', '2', '温泉温度最高可达95℃，现有自喷泉眼7只，日出水量7000多吨，含有多种微量元素，温泉水属硅酸重碳酸钠型水，日出水量3800多L，是海南岛探明的出水量最大、温度最高的温泉，具有清净肌肤、美容、医疗保健等功效。现已建成数家温泉度假村，可供游客度假休闲，活动内容有矿泉疗养康复、观赏民族风情、参加登山观景等。', '海南省保亭黎族苗族自治县七仙岭温泉旅游区1号七仙岭温泉国家森林公园', NULL, NULL, '08:00-22:30', 20476, '四季皆宜。', '公交\n1）三亚汔车总站坐中巴16元到保亭车站（运营时间：每天8：00－5：30，每30分钟一班，行程约两小时）；\n在海口可在西站乘保亭的省直快车59元到保亭车站（运营时间：每天09:00－17:30发车，每2小时一班，行程约三小时）；\n另可乘空调车和普通车，票价分别是51元/人，40元/人。\n2）到了保亭车站，乘坐到七仙岭温泉度假区的专车，车票2元。也可转乘风采车或摩的到达，单程5元。', NULL, NULL, 'http://travel.qunar.com/p-oi7528470-qixianlingwenquan', '', '3', '4', '4', '3', '3', 4, 25481, 153, NULL, 41787, 122743, 41987, 69455, 28226, 24247, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (35, '7528428', '金水台温泉', NULL, '广东 > 云浮 > 云浮景点 > 金水台温泉', '云浮', '云浮', 'https://img1.qunarzz.com/travel/poi/1412/51/51bf5799762d04c2cdb.jpg;https://img1.qunarzz.com/travel/poi/1411/cb/cb1289b93dafdb48213a9cdb.jpg;https://img1.qunarzz.com/travel/d4/1707/ee/7c62647a3c72e3b5.jpg', '4', '云浮景点排名第1', '1天', '1', '22.615002', '112.479546', '1', '金水台温泉坐落于中国禅宗六祖慧能的故乡，是一所集旅游度假、商务会议、餐饮住宿、健身休闲、温泉疗养、中外贵宾接待为一体的特色大型温泉度假村。金水台温泉环境优美，娱乐配套设施十分完善，还设有高级歌舞厅、健康中心、足球场、篮球场、网球场、户外拓展训练基地等。到金水台温泉休闲、度假、养生、会议，不但可以饱览这里优美的湖光山色，还可以享受天然负离子清洗心肺，聆听百鸟欢唱，其中滋味，须身临其境而得之。', '云浮市新兴县水台镇金水台旅游度假村内', '0766-2513333', 'http://www.jstwq.com/index.html', '08:00-00:30', 2159, '四季皆宜。秋冬更佳。', '公交\n1、广州到金水台温泉乘车路线：广州天河客运站—新兴汔车总站（约3小时）—稔东水专线车—水台镇（约40分钟）\n2、深圳到金水台温泉乘车路线：深圳龙岗（罗湖）—金水台温泉门口（约4小时）\n3、佛山到金水台温泉乘车路线：佛山鸿运汔车站（桂城汔车站）—新兴汔车站—稔东水专线车— 水台镇（约40分钟）\n4、肇庆到金水台温泉乘车路线：肇庆汔车总站—新兴汔车站—稔东水专线车—水台镇（约40分钟）\n\n自驾车\n1、肇庆—S273—西江大桥—高要市—白土—更楼—双和公路—金水台温泉\n2、深圳/东莞—虎门大桥—中山—中江高速—江门—江鹤高速—共和出口—双和公路—金水台温泉\n3、广州/佛山—佛开高速—九江—鹤山—共和出口—双和公路—金水台温泉\n4、珠海—江珠高速—江门—江鹤高速—共和出口—双和公路—金水台温泉', '1.做文明游客，请穿泳装游乐，谢绝穿透明泳装。\n2.儿童需在有监护能力的成人监护下游乐，游玩项目时，请听从现场工作人员的指引。\n3.游玩前请将佩带的手表，首饰等饰物除下并自行保管好，禁止佩带或携带眼镜游乐。\n4.避免空腹、饭后、酒后泡温泉，泡温泉与吃饭时间至少应间隔一小时。\n5.选择适应自身的高、中、低温度的温泉池，一般从低温到高温，每次不宜超过15分钟，及时补充水分。\n6.泡温泉时，记住合上双眼，以冥想的心情，缓缓地深呼吸数次，才能真正地释放身心压力。\n1、一定要记得把身上的金属饰品摘下来，不然你会很难过地发现自己心爱的首饰已经被硫化成黑色的了。\n2、避免空腹、饭后、酒后泡温泉，泡温泉与吃饭时间至少应间隔一小时。\n3、选择适应自身的高、中、低温度的温泉池，一从低温到高温，每次15-20分钟即可。\n4、温泉不宜长时间浸泡，否则会有胸闷、口渴、头晕等现象。在泉水中感觉口干、胸闷时，就得上池边歇歇，或喝点饮料补充水分。\n5、皮肤干燥者浸泡温泉之后最好立刻抹上滋润乳液，以免肌肤水分大量流失引起不适。\n6、过烫过酸的温泉不要泡，温度在30～45℃比较适宜。\n7、最好不要独自一人泡，以免发生意外。\n8、泡完温泉后不必用清水冲洗，但是强酸性温泉和硫化氢温泉刺激性较大最好还是再冲洗一下，以防有副作用，皮肤容易过敏的人更要注意。\n9、根据水质划分，温泉一般分为中性碳酸泉、碱性碳酸氢钠泉、盐泉和硫磺泉四种。温泉的水质不同，则对不同的病症具有不同的疗效。如各种成分都有的单纯泉，对于神经痛、风湿、皮肤病等有疗养作用;含有二氧化碳的碳酸泉，则对治疗高血压、心脏病有好处。所以泡温泉以前，最好先了解温泉的种类，并根据自身条件进行选择，才能真正达到泡温泉的预期目的，并可避免给身体带来伤害。', NULL, 'http://travel.qunar.com/p-oi7528428-jinshuitaiwenquan', '', '', '1', '3', '', '4', 8, 28753, 105, NULL, NULL, 9323, 84296, NULL, 55001, 51066, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (36, '705550', '木渎', 'Mudu Ancient Town', '江苏 > 苏州 > 苏州景点 > 木渎', '苏州', '苏州', 'https://img1.qunarzz.com/travel/d8/1604/a1/364cb595ee9013f7.jpg;https://img1.qunarzz.com/travel/d6/1604/dc/f75e3614292db0f7.jpg;https://img1.qunarzz.com/travel/d7/1506/e7/334f111d3f0368.jpg;https://img1.qunarzz.com/travel/d5/1610/7a/49c68690d173fb5.jpg;https://img1.qunarzz.com/travel/d7/1604/9a/12a9e1d4ef02e3f7.jpg;https://img1.qunarzz.com/travel/d5/1604/7f/887df82871d01ff7.jpg', '4.1', '苏州景点排名第9', '1天', '80', '31.257168', '120.51991', '88', '木渎是江南唯一的中国园林古镇。明清时有私家园林30多处，现已修复严家花园、虹饮山房、灵岩山馆、古松园、榜眼府第、盘隐草堂等。其中严家花园为江南名园，为台湾政要严家淦先生故居；虹饮山房是乾隆民间行宫，内有二十道清代圣旨，弥足珍贵。\n木渎四周群山拱峙，又毗邻太湖，既得真山真水之趣，又具小桥流水之幽，更有私家园林、名人故居等众多的人文古迹，因而，木渎景区名列太湖风景区十三个景区之首。作为中国唯一的园林古镇，木渎在明清时有私家园林30多处，迄今仍保留了10余处。其中，严家花园乃乾隆的老师、中国最长寿的诗人沈德潜故居，后归木渎首富严国馨，园中布局疏密曲折，高下得宜，局部处理精巧雅致，幽深婉约，显示了营造者独具匠心的造园艺术，现代建筑学家刘敦桢、梁思成、童俊等人数次考察此园，倍加推崇，称其为苏州当地园林之“翘楚”。', '江苏省苏州市吴中区木渎镇山塘街188号', '0512-66564042', 'http://www.mudu.com.cn', '春、夏、秋季：8:00-17:00，冬季：8:00-16:30', 8869, '四季皆宜，每年的4-10月去景色更好一些。', '公交\n1、在苏州乐园大门对面站台乘坐38路到【木渎古镇（翠坊桥）】下车即可。\n2、乘坐69路到【木渎人民医院】下车。\n3、苏州长途汽车客运南站下车可以在出站口对面乘坐43路、63路均可到【木渎古镇】。\n\n地铁\n苏州地铁1号线可直达，在地铁【木渎站】下车。', NULL, NULL, 'http://travel.qunar.com/p-oi705550-mudu', '5', '', '4', '', '2', '5', 9, 47323, 158, 221163, NULL, 58771, NULL, 45436, 269962, 93639, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
