INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (40, '703036', '北寺塔', NULL, '江苏 > 苏州 > 苏州景点 > 北寺塔', '苏州', '苏州', 'https://img1.qunarzz.com/travel/d7/1410/04/b44f5c555f302c65ffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d4/201403/09/9dba0b880b2eac96ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/201303/13/9c2c5f4d67eb277dddb12cfb.jpg;https://img1.qunarzz.com/travel/d6/1708/81/fe711852f1bc0bb5.jpg;https://img1.qunarzz.com/travel/d9/1702/2d/168cca6c3c1d3bb5.jpg;https://img1.qunarzz.com/travel/d7/1602/14/6d9a952704b5cdf7.jpg', '3.5', '苏州景点排名第16', '2小时', '40', '31.329243', '120.626192', '43', '“北寺塔”俗称报恩寺，是苏州最古老的佛寺，距今已1700多年。始建于三国吴赤乌年间，相传是孙权母亲吴太夫人舍宅而建，古称“通玄寺”。唐开元年间改为“开元寺”。五代北周显德年间重建，易名为“报恩寺”。\n北寺塔高76米，八角九层，雄冠江南。登高可俯瞰全城风光。塔的四周尚存部分明清时期重建的报恩寺殿堂建筑。位于塔东的不染尘观音殿，俗呼楠木观音殿，始建于南宋绍兴二十三年，现存殿宇为明万历时重建，是苏州保存最完整的明代建筑。殿为重檐歇山造，面阔五楹，进深五间，内四架，前置檐廊，檐高7米，四周檐柱为抹角石柱，内柱用楠木。', '江苏省苏州市人民路1918号', NULL, NULL, '7:45-17:30', 10636, '四季皆宜。', '公交\n乘坐1路、5路、 8路、54路、101路、102路、313路、602路、夜1路、夜3路、游4路、游4路长线到达【北寺塔东】。\n自驾\n沿人民路行驶1.2公里，转向桃花坞大街行驶20米，稍向右转进入西北街即可。', NULL, NULL, 'http://travel.qunar.com/p-oi703036-beisita', '4', '3', '3', '4', '1', '2', 8, 30392, 128, 42082, 76856, 61146, 152300, 20864, 49725, 13865, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
