INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (43, '7475130', '广富林遗址文化公园', NULL, '上海 > 上海景点 > 广富林遗址文化公园', '上海', '上海', 'https://img1.qunarzz.com/travel/d7/1803/3a/dc065a4aa4003bb5.jpg;https://img1.qunarzz.com/travel/d3/1803/35/ca7d4e2f3bf83b5.jpg;https://img1.qunarzz.com/travel/d9/1803/42/1f14f3306b323b5.jpg;https://img1.qunarzz.com/travel/d2/1803/e5/2b7f3fc80486d6b5.jpg;https://img1.qunarzz.com/travel/d9/1803/8e/1d96267497673eb5.jpg;https://img1.qunarzz.com/travel/d6/1803/c9/cb09f9f3aaff65b5.jpg', '4.6', '上海景点排名第156', '2小时', '18', '31.068189', '121.204525', '9', '20世纪60年代末，广富林小市镇仍保存了原有的布局，街坊和住宅屋宇依旧还是古色古香的传统样式。广富林的传统住宅也很有特色。20世纪70年代以前，住宅一进或两进，以一进深的为大部分，常见有一进三间者；两进深建筑，为前后二幢平屋与东西两厢房组成。20世纪80年代，广富林高氏宅是一座乡村大型住宅，始建于清代后期，厨房灶头别有一番特色，灶头的绘画图案乃是民间艺术的典型产物。居室内的传统家具摆设，如桌、椅、床、案、几、凳、米桶、鞋桶、箱柜等，另有极少见的藤编广漆元宝形摇篮。', '上海市松江区方松街道广富林村村北', NULL, NULL, '全天', 15961, '四季皆宜.', '公交\n乘坐9号线松江大学城下来，再坐24路到【华东政法大学站】下车即可。\n自驾\n从嘉松南路出发，沿嘉松南路行驶1.7公里，左转进入辰花路，在银泽路高梁泾桥左转行驶300米到达终点。', NULL, NULL, 'http://travel.qunar.com/p-oi7475130-guangfulinyizhiwenhua', '3', '', '2', '2', '2', '4', 6, 31829, 163, 94438, NULL, 21482, 44244, 43595, 146968, 50735, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (44, '9202787', '熊岳植物园', NULL, '辽宁 > 营口 > 营口景点 > 熊岳植物园', '营口', '营口', 'https://img1.qunarzz.com/travel/d9/1612/2a/8f3d0f6f939ea4b5.jpg;https://img1.qunarzz.com/travel/d0/1612/dd/de6c31ab8e933eb5.jpg;https://img1.qunarzz.com/travel/d8/1612/8d/8200ac8833b4c6b5.jpg;https://img1.qunarzz.com/travel/d8/1612/ee/4838dc6364bb59b5.jpg;https://img1.qunarzz.com/travel/d5/1612/33/ccd7f3966fa3afb5.jpg;https://img1.qunarzz.com/travel/d6/1612/95/f1f4dff79166dbb5.jpg', '3', '营口景点排名第42', NULL, '1', '40.180924', '122.161655', '0', '熊岳植物园是我国植物园林中建园最早、引种北方树种最全，在国内外具有一定影响力的树木标本园，素有“亚洲袖珍标本园”之称。熊岳树木园，已成为我国北方保存燕北地区木本植物种质资源较全的树木园之一。目前已免费对外开放。熊岳植物园由初始园和新建植物园两部分组成，总占地面积270167平方米，其中初始园58461平方米，新园211706平方米。规划为水系景观区、植物观赏区、水果采摘区、生态餐饮区四部分。熊岳植物园保存了来自亚洲、欧洲、美洲的珍稀树木62科152属共500余种。如中外闻名素有活化石之称的古老孑遗树种银杏和水杉，珍稀树种天女木兰、玉铃花、刺楸、三亚钓樟、 刺五加，经济价值较高的特产树种杜仲和漆树，以及从国外引入的萌芽松、北美乔松、西黄松、短叶松、脂松和欧洲云杉、火炬树、美国鹅掌楸、日本厚朴、银槭、红槲栎、栓栎、沼生栎及欧洲白桦等成为该园独具风格的树木。园中奇株异树，千姿百态，古木参天，蔽空绿荫，鸟鸣婉转，木香馥郁，令人顿生回归大自然之感。', '辽宁省营口市鲅鱼圈区熊岳镇古城东南近铁东小学', NULL, NULL, NULL, 19371, NULL, '公交\n乘坐k1路、熊岳1路至【熊岳植物园】下，步行前往。', NULL, NULL, 'http://travel.qunar.com/p-oi9202787-xiongyuezhiwuyuan', '', '', '2', '1', '2', '5', 7, 21508, 157, NULL, NULL, 46202, 16564, 31668, 361941, 83682, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (45, '706005', '苏州摩天轮乐园', NULL, '江苏 > 苏州 > 苏州景点 > 苏州摩天轮乐园', '苏州', '苏州', 'https://img1.qunarzz.com/travel/d1/201404/03/328d2e7c93d11b96ddb12cfb.jpg;https://img1.qunarzz.com/travel/d2/1603/f2/03a45e1a41b6d9f7.jpg;https://img1.qunarzz.com/travel/poi/201303/14/9bb7a5873c472092ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/201303/14/4a94ece3cd354eb9ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/1803/b9/646f3117a9865e37.jpg;https://img1.qunarzz.com/travel/d5/1704/f6/f557721eba897ab5.jpg', '4.4', '苏州景点排名第10', '4小时 - 6小时', '42', '31.3223560555', '120.7226686581', '36', '苏州摩天轮乐园位于金鸡湖东岸，园中最耀眼的是水上摩天轮，高120米，坐在上面可以俯看乐园全景和金鸡湖，加上园中建筑设计成欧式城堡的模样，浪漫的感觉很受情侣的追捧，也吸引了不少拍婚纱照的新人。\n进入乐园，大门对面就是摩天轮，普通型座舱6人乘坐，座舱内装有空调，环境舒适，转一圈约25分钟。如果想要包舱，需要另付200元/舱。作为乐园最热门的项目，节假日或是双休日，在摩天轮下排上1、2小时队并不稀奇。\n大门右手边，在4D影院中可以看电影，9:30-16:00，每小时一场。摩天轮边上的过山车也是园中的热门项目，呈蝶型，游客可以体验到鸟一样飞翔的感受，非常刺激。还有在月牙形轨道上旋转的迪斯科、儿童迷宫、儿童游乐区等游乐项目。\n大门左手边，旋转木马有上下两层，骑着白马，好像进入公主与王子的童话世界。这里还有星空秋千等游乐项目。\n乐园内有吃饭的地方，大门两侧就有商场，摩天轮下有欧式风格的城堡餐厅，大门左手边有外形为海盗船的餐厅，不过吃的东西都挺贵的，还是建议自备干粮。', '苏州市现代休闲广场南侧、金鸡湖边', '0512-62373737', 'http://www.szmtl.net/index.html', '9:30-16:00（日场15:30截止入园）。', 8472, '全年', '公交\n乘坐公交120路到【摩天轮公园】站下车，步行3分钟。\n地铁\n地铁1号线【时代广场】站2号出口，步行13分钟。\n\n', NULL, NULL, 'http://travel.qunar.com/p-oi706005-suzhoumotianlunleyuan', '1', '3', '4', '2', '5', '3', 0, 22054, 87, 18637, 78920, 171828, 27866, 294683, 63515, 25497, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (46, '8666363', '黄河宫', NULL, '宁夏 > 中卫 > 中卫景点 > 黄河宫', '中卫', '中卫', 'https://img1.qunarzz.com/travel/poi/1707/80/f358aae71c42537.jpg;https://img1.qunarzz.com/travel/poi/1707/b1/a61ac5a95f58237.jpg;https://img1.qunarzz.com/travel/poi/1707/a2/39aafea24e5a9237.jpg;https://img1.qunarzz.com/travel/poi/1707/f2/4ad53a04b4eeff37.jpg;https://img1.qunarzz.com/travel/poi/1707/32/a150947d02129637.jpg;https://img1.qunarzz.com/travel/d7/1612/1a/063e229b4693eab5.jpg', '5', '中卫景点排名第56', '2小时', '4', '37.491961', '105.220937', '6', '“大河之舞”主题公园是中卫市城市运动休闲旅游的主要景点之一。黄河宫位于中卫“大河之舞”文化主题园内，是大河之舞的核心建筑。黄河宫及水滴设计创意来自“黄河之水天上来”诗词寓意，以水滴形态对整个建筑形式进行凝炼，分为地上水滴建筑主体和地下黄河展示宫黄河宫陈列布展的理念是：将黄河宫打造成黄河形成展示型、人类文明弘扬型、黄河科普教育型、生态保护宣传型、矿产奇石鉴赏型的综合性主题展馆。\n黄河宫陈列布展内容分为三个部分：第一部分是黄河流域地理地貌篇，其主要内容有黄河的形成、黄河源头、黄河河段、黄河主要支流、黄河湖泊、黄河湿地、黄河流域全貌沙盘、黄河水利等方面的介绍与展示。第二部分是黄河流域人文历史篇，主要内容是黄河沿线九省区（青海、四川、甘肃、宁夏、内蒙古、山西、陕西、河南、山东）代表性的历史文化遗存介绍与文物展示，侧重展示宁夏境内的重要历史文物及回族民俗文物，并安排了大麦地岩画专题展示。第三部分是黄河生态生物篇，主要展示了黄河流域生态概貌、黄河流域各种动物、植物标本模型、黄河奇石及中卫矿产。', '宁夏中卫市沙坡头区黄河大桥北，向西1公里', NULL, NULL, '周一至周五：8:00-18:00\n周六至周日：8:00-18:30', 3749, '全年皆宜', '公交\n乘坐公交中卫-小河沿路公交车在【黄河桥北】站下车，步行前往\nTips\n本公交采用单一票制，票价5元', NULL, NULL, 'http://travel.qunar.com/p-oi8666363-huanghegong', '', '', '', '3', '3', '4', 9, 25748, 205, NULL, NULL, NULL, 90875, 82231, 54407, 48050, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (47, '708090', '沧浪亭', NULL, '江苏 > 苏州 > 苏州景点 > 沧浪亭', '苏州', '苏州', 'https://img1.qunarzz.com/travel/d7/1701/f7/50055bd891eed5b5.jpg;https://img1.qunarzz.com/travel/d0/1701/eb/30326a7b35714ab5.jpg;https://img1.qunarzz.com/travel/d9/1701/da/97c5737998899fb5.jpg;https://img1.qunarzz.com/travel/d3/1503/11/220620c443a40e.jpg;https://img1.qunarzz.com/travel/d0/1701/96/521775f049e235b5.jpg;https://img1.qunarzz.com/travel/d3/1502/62/a9b30b89e02002.jpg', '4.5', '苏州景点排名第14', '2小时', '65', '31.3013110746', '120.6312745734', '60', '沧浪亭，在苏州现存诸园中历史最为悠久。始建于北宋，为文人苏舜钦的私人花园，称“沧浪亭”。沧浪亭占地面积1.08公顷。园内有一泓清水贯穿，波光倒影，景象万千。沧浪亭主要景区以山林为核心，四周环列建筑，亭及依山起伏的长廊又利用园外的水画，通过复廊上的漏窗渗透作用，沟通园内、外的山、水，使水面、池岸、假山、亭榭融成一体。园中山上石径盘旋，古树葱茏，箬竹被覆，藤萝蔓挂，野卉丛生，朴素自然，景色苍润如真山野林。', '江苏省苏州市三元坊沧浪亭街3号', NULL, NULL, '7:30-17:30', 12830, '每年的4-10月去景色更好一些。细雨天赏雨是个不错的选择，而蚊虫多时慎去。二月中旬至二月底有兰花展，九月底至十月上旬有竹展。', '公交\n乘坐游2、游4、游5 、1路、5路、27路、30路、39路、101路、102路、103路、218路、261路、308路、309路公交均可到达。', '沧浪亭里的蚊虫很厉害，特别是夏天去时一定要准备好蚊不叮、风油精等。', '30分钟', 'http://travel.qunar.com/p-oi708090-canglangting', '2', '3', '3', '1', '3', '2', 3, 42577, 105, 19715, 64030, 73367, 20350, 22792, 36145, 10763, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (48, '9195322', '盆景园', NULL, '上海 > 上海景点 > 盆景园', '上海', '上海', 'https://img1.qunarzz.com/sight/p11/201211/07/8be217237665932a93835fbb.jpg', '--', '上海景点排名第2695', NULL, '0', '31.007257', '121.238528', '0', '盆景园位于本区西北部，占地4.1万平方米。园由序景、树桩盆景、山石盆景和服务等四个区组成。园内有竹亭、竹廊、树皮亭、柴门、藤架和青瓦粉墙的展室，构成江南庭院式园林。序景区里的盆景博物馆第一馆为砖木结构，面积115平方米；第二馆为混合结构，面积374平方米。两馆均系江南民居式建筑，馆中陈列中国盆景发展的史料，重点介绍海派盆景资料。区内展出“迎客松”、“松鹤延年”、“枯木逢春”等巨型盆景。树桩盆景区中有一条曲曲折折的混合结构游廊，总面积为563平方米。游廊将全区分割成多个小院，院中陈列各式树桩盆景千余盆，虬枝横空，古朴入画。游廊两面空敞，下置围栏，各院落有相对的两门可出入。雨天或烈日当空时，游人在廊中就可以观赏到两旁院落中的盆景。区内劲松院的松柏类盆景有五针松、罗汉松、黑松、真柏、桧柏、金钱松、矮紫杉等树种，苍松翠柏、古朴素雅。花果院的花果类盆景有梅、榆、紫藤、海棠、石榴、红枫、火棘、胡颓子、腊梅等树种，铁干银枝，繁花硕果。小型、微型盆景展览原来是在一座名四景轩的仿明清建筑内。轩为混合结构，面积322平方米。轩中陈列的都是在数寸方圆的小盆中栽种的各种小树桩，远望犹如掌上之物，近观却有旷野林木之姿。1995年移小、微型盆景于展览温室内。温室为八角形，混合结构，面积443平方米。山石盆景区的水石盆景馆系仿明清建筑，混合结构，面积449平方米。入口处有长达7米的以斧劈石制成的桂林山水盆景，气势雄伟。馆内陈列以斧劈石、英石、石笋、钟乳石、太湖石、浮石、砂积石、海母石、芦管石等各种石料制成的水石盆景共28盆，把各种山川奇景尽收于咫尺之间。', '上海市徐汇区龙吴路1111号上海植物园内', NULL, 'http://www.shbg.org/', '08:30-16:30', 12404, NULL, '公交\n从市内乘坐720路、824路至【百色路平福路】站下车。', NULL, NULL, 'http://travel.qunar.com/p-oi9195322-penjingyuan', '4', '', '3', '3', '', '1', 2, 25642, 151, 101607, NULL, 83398, 40119, NULL, 7669, 6162, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (49, '9202889', '中国白银第一坊', NULL, '湖南 > 永兴 > 永兴景点 > 中国白银第一坊', '郴州', '永兴', 'https://img1.qunarzz.com/travel/poi/1605/8c/c84c4d79e80381f7.jpg', '3.8', '永兴景点排名第8', NULL, '0', '26.118873', '113.116228', '0', '中国白银第一坊占地面积40亩，由中国银都——湖南永兴龙头企业之一的湖南鑫达银业公司投资，工程总投资3000万元。景区按冶炼观光区、形象展示区、旅游购物区、道教活动区等四区进行规划，整个景区为中国传统的中轴对称式园林建筑，包括银楼、信仪堂、祖师殿、原始冶炼作坊、银文化博物馆、原始作坊展区、草堂寒竹等经典景点。其中白银博物馆设古代馆和现代馆，古代馆收集整理了中华民族几千年的冶炼史，陈设了从战国时期到明清时代的民间银器，挖掘了永兴三百多年的冶炼文化；现代馆通过沙盘展示、图文并茂的形式，将永兴19家规模冶炼企业进行集中展示，是各界人士领略“没有银矿的银都奇迹”的主要窗口。主要有白银博物馆、银楼、信义堂和金银工艺品展览馆四个景点，传统和现代的白银制造品，体现了永兴这一工业的独特魅力。银文化博物馆—集中华民族几千年的白银冶炼史，银器古董，冶炼文化于一馆，向世人展示“千秋典藏，百年传奇”的历史长卷；原始白银冶炼作坊—展示勤劳、智慧的先辈们使用过的原始冶炼工具及工艺。让游客领略古代银的冶炼过程；祖师殿—供奉火神祝融的地方，让游客祈求事业、家庭红红火火；信义堂—供奉情深义重的关公元帅，提醒世人“想要成事业，信义记心间”；银楼—用银打造的楼阁，墙上是银，摆件是银，让人置身于银的世界，展示银都的实力和风采；银制品体验坊—游客与工匠互动的作坊，让游客在参与中获得知识与快乐；草堂寒竹—游客休憩地。', '湖南省郴州市永兴县永兴大道中国白银第一坊', NULL, NULL, '8:00-18:00', 3030, NULL, '公交\n从长沙出发，长沙汽车南站—永兴，发车时间：早上八点或下午三点。', NULL, '2-3小时', 'http://travel.qunar.com/p-oi9202889-zhongguobaiyindiyifang', '', '', '', '', '2', '5', 8, 40478, 81, NULL, NULL, NULL, NULL, 17557, 351611, 106801, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
