INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (53, '709084', '瑶里古镇', NULL, '江西 > 瑶里 > 瑶里景点 > 瑶里古镇', '景德镇', '瑶里', 'https://img1.qunarzz.com/travel/d8/1505/29/9b412120474685.jpg;https://img1.qunarzz.com/travel/d7/1611/25/baa104302fedf0b5.jpg;https://img1.qunarzz.com/travel/d4/1510/3c/14d6f794a36443f7.jpg;https://img1.qunarzz.com/travel/d7/1505/f8/b9d925e85cdde.jpg;https://img1.qunarzz.com/travel/d2/1505/67/3afe4990b5e52f.jpg;https://img1.qunarzz.com/travel/d4/1505/7a/e490e10a048ec3.jpg', '4.4', '瑶里景点排名第1', '2小时 - 6天', '76', '29.5473560647', '117.5867637411', '0', '瑶里原名窑里，正由窑而得名。近代之后，由于地处山区，又远离任何一条现代交通线――公路、铁路，瑶里进一步衰落，她曾经有过的那一段辉煌历史逐渐被凝固和尘封，被世人所遗忘。但正因为她被遗忘了，她的自然环境和聚落风貌都保存得较为完整，非常具有观赏性。奇岩飞瀑、原始森林、江南古祠、明清建筑、古窑遗址、革命旧居无不让你流连忘返。', '景德镇市浮梁县瑶里镇曹家坦', '0798-2601909、2601393', NULL, '7:00—17:00', 2517, '瑶里旅游主要是以自然生态游、红色游和陶瓷文化游为主，一年四季来都合适。', '公交\n旅游专线：人民广场—瑶里\n\n班车\n客车到景德镇后，去里村短途汽车站乘坐从“景德镇-瑶里古镇”的班车，每天两班。票价13元。\n上午7:30从景德镇出发，9:00左右到景德镇瑶里古镇；\n下午14:00从景德镇出发，16:00左右到瑶里古镇。下午从瑶里返市里的直达班车有两班：早上9：00左右出发，下午16：00左右出发。\n\n自驾\n在景德镇下高速后，走景德镇东一路或者东二路，路上有瑶里风景区标示牌，到黄泥头村，走加油站对面那条路（有标示牌）直接到达瑶里。沿途经过“王港乡”-“藏湾乡”-“鹅湖镇”-“瑶里”。', NULL, '1天', 'http://travel.qunar.com/p-oi709084-yaoliguzhen', '3', '', '', '', '3', '5', 0, 37704, 132, 92956, NULL, NULL, NULL, 87267, 226329, 102379, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (54, '5765060', '呀诺达雨林景区', NULL, '海南 > 保亭 > 保亭景点 > 呀诺达雨林景区', '保亭', '保亭', 'https://img1.qunarzz.com/travel/d2/1607/85/9d28d5fde104e453.jpg;https://img1.qunarzz.com/travel/d2/1706/6f/f76fb114f9c8a0b5.jpg;https://img1.qunarzz.com/travel/d8/1510/39/45ac313993acec.jpg;https://img1.qunarzz.com/travel/d8/1512/66/b586c45e1f4faff7.jpg;https://img1.qunarzz.com/travel/d4/1409/15/222ba94d40bcb358ffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/poi/201407/28/6fc2ba24861a6321c8d65eac.jpg', '4.6', '保亭景点排名第1', '4小时 - 6天', '541', '18.456698', '109.673664', '58', '呀诺达雨林景区北与五指山、七仙岭比肩相连；东眺南海万倾波涛，美丽的海棠湾近在咫尺；南与中国第一温泉南田温泉仅一水之隔。在三亚市最新修编的旅游总体规划中，本景区位于大三亚旅游规划中的生态景观轴上，是“三亚旅游圈”的“金三角”地区。景区面积45平方公里，周边是123平方公里的生态恢复保护区，充分以天然形胜和热带雨林景观为主体基础景观，融汇“热带雨林文化、黎峒文化、南药文化、生肖文化”等优秀文化理念于一体，构建一个以“原始绿色生态”为主格调的大型生态文化旅游主题旅游景区，现已建成雨林谷和梦幻谷两个景观区域，及以美国电瓶车、生态栈道、飞瀑索道、高品质旅游巴士环绕成18公里长的园区通道。“呀诺达”是形声词，在海南本土方言中表示一、二、三。景区赋予它新的内涵，“呀”表示创新，“诺”表示承诺，“达”表示践行，同时“呀诺达”又被意为欢迎、你好，表示友好和祝福。', '海南省保亭黎族苗族自治县三道镇', '0898-83883333', 'http://www.yanoda.com/', '周一至周五：8:00-18:00\n节假日含周末：7:30-18:00', 6605, '3月-5月和9月-10月最佳。此时适合去呀诺达雨林文化旅游区，可以品尝地道的养生大餐。', '公交\n三亚——呀诺达旅游专线班车时间表： A站8:00三亚明珠广场发车，15:30从景区返回；B站8:30大东海银泰酒店发车，15:30从景区返回；C站8:00-8:30三亚湾区域发车，15:30从景区返回；D站8:30-9:00亚龙湾区域发车，15:30从景区返回。\n备注：三亚湾班车线路为君澜酒店——海月广场（金凤凰酒店）；亚龙湾区域班车行驶线路为：仙人掌度假酒店至瑞吉度假酒店沿线。至少提前1天预订以上旅游专线班车，车票36元/人往返。', '踏瀑戏水活动设在呀诺达雨林深处，落差200米的梦幻谷中；体验者可以穿上草鞋，带上头盔，体验穿越激流险滩的惊险与刺激；在体验飞瀑戏水的同时，还可以观赏梦幻谷的瑰丽景观（为安全起见，景区配备了专业教练和防护设备）。', NULL, 'http://travel.qunar.com/p-oi5765060-yanuodayulinjingqu', '', '', '5', '', '', '5', 1, 39286, 142, NULL, NULL, 186287, NULL, NULL, 234951, 111025, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (55, '722389', '戚城文物景区', NULL, '河南 > 濮阳 > 濮阳景点 > 戚城文物景区', '濮阳', '濮阳', 'https://img1.qunarzz.com/travel/d4/1705/ff/c402356210affeb5.jpg;https://img1.qunarzz.com/travel/d0/1705/3b/5ee8be4d383fa8b5.jpg;https://img1.qunarzz.com/travel/d1/1705/9e/7369b5c71069a0b5.jpg;https://img1.qunarzz.com/travel/d9/1705/13/1722bb42b38109b5.jpg;https://img1.qunarzz.com/travel/d7/1705/37/401c3aa67ae2c4b5.jpg;https://img1.qunarzz.com/travel/d7/1705/c4/cd7d948630a188b5.jpg', '4.5', '濮阳景点排名第3', '1小时 - 2小时', '11', '35.757638545', '115.0375169311', '1', '戚城文物景区，是濮阳市区一个免费开放的市民公园，是当地人休闲娱乐的一个场所。这里曾经是春秋时期卫国一座重要的城池，公元前626年至公元前531年期间，各诸侯国在卫国会盟十五次，其中七次在这里会盟。\n整个景区包含了仿秦汉风格的阙门，仿远古风格的颛顼玄宫，仿唐代建筑风格的龙宫，仿汉代建筑风格的历史陈列馆，仿明清建筑的子路墓祠，反映春秋战国时卫国文化氛围的“桑间濮上”苑、表现濮阳古战场的“历史名战微缩景观”，伏羲之母华胥的卧雕及伏羲亭、夏后启在昆吾所铸之九鼎及铸鼎轩等等多个仿古建筑，这些建筑都是近年来重新修建的。\n几千年的风风雨雨过去了，这里已经看不见什么当年遗留下来的地面建筑了，只有在景区的东面，有一座夯土台，是当时诸侯会盟的盟坛基址。如今这样更像是城市中的一片公用绿地，市民们喜欢带着孩子来这里散散步，嬉闹玩耍。', '濮阳市京开大道71号', '0393-8119207', 'http://www.pysqicheng.com/', '8:00-18:00', 4978, NULL, '公交\n市内乘公交6路、11路、17路到【戚城公园】站下即可。', NULL, NULL, 'http://travel.qunar.com/p-oi722389-qichengwenwujingqu', '2', '', '', '2', '', '4', 5, 33320, 105, 18471, NULL, NULL, 36596, NULL, 192447, 40065, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
