INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (57, '9281562', '自贡嘻哈乐园', NULL, '四川 > 自贡 > 自贡景点 > 自贡嘻哈乐园', '自贡', '自贡', 'https://img1.qunarzz.com/travel/poi/1606/1e/0b50ded0cd268f9a.jpg;https://img1.qunarzz.com/sight/p0/1603/39/3914af8eca3ea74590.img.jpg', '4.1', '自贡景点排名第52', NULL, '0', '29.327162', '104.807649', '0', '自贡嘻哈乐园位于沿滩新区时代大道4号创兴城27栋。是自贡地区最大、游乐设备最齐全的一家集运动、探索、挑战、体验于一体的室内室外综合游乐园，自正式营业以来，就广受大众青睐，每逢节假日慕名而来的家长小朋友可谓是熙来攘往、络绎不绝。 嘻哈乐园室内一万多平米设有旋转飞车、蹦极、自控海螺、海洋转马、迷你穿梭、挖掘机、丛林狙击手、旋转小蜜蜂、百花乐园、拯救小鸭、海上游乐园、皇家马车、亲子打青蛙、儿童海盗船、小魔盘、托马斯火车、大象火车、飞车竞赛、碰碰车、卡丁车、儿童城堡、太空沙、儿童拓展、钓鱼、乐吧车、画画等几十种深受儿童喜爱的游乐项目；室外两万多平米设有过山车、高空飞翔、大摆锤、海盗船、迪斯科转盘、惊呼狂叫、神州飞碟、激战鲨鱼岛、自控飞机、花果山漂流、探空梭等十余种成人也可以参与的更加新颖火爆、惊险刺激的娱乐设备。 所以在这里，无论是童心未泯的成年人，还是懵懵懂懂的小朋友都可以自由自在尽情游玩，穿梭在种类繁多的各种特色游乐项目之间，体验无限欢乐的亲子时光，同时嘻哈乐园也是所有自贡市民周末休闲娱乐的好去处。', '自贡市沿滩新区时代大道4号创兴城27栋', '0813-8283366', NULL, '9:00-21:00', 7431, NULL, '公交\n市内乘坐61路B线路到【君豪御园】站下车即到。', NULL, NULL, 'http://travel.qunar.com/p-oi9281562-zigongxihaleyuan', '3', '2', '3', '', '', '1', 3, 23760, 125, 63020, 19545, 96891, NULL, NULL, 14039, 6312, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (58, '9487271', '淮北南湖风景区', NULL, '安徽 > 濉溪 > 濉溪景点 > 淮北南湖风景区', '淮北', '濉溪', 'https://img1.qunarzz.com/travel/d9/1608/b1/5c9c3763df5b7bb5.jpg;https://img1.qunarzz.com/travel/d9/1608/9c/a74057c0f86cf5b5.jpg;https://img1.qunarzz.com/travel/d6/1608/86/84f58518d7ba7cb5.jpg;https://img1.qunarzz.com/travel/poi/1607/48/b671d1d87c48fddb.jpg;https://img1.qunarzz.com/sight/p0/201403/10/954b005480a8b34f30d55beb6a76d09f.jpg;https://img1.qunarzz.com/travel/d8/1608/89/221735b1a4079bb5.jpg', '0', '濉溪景点排名第1', NULL, '1', '33.9073147852', '116.8171769735', '0', '南湖湿地公园是皖北地区规划面积最大的水上娱乐、旅游、休闲度假景区，已经成为淮北城市规划建设的重要组成部分，是我市的城中湖。该公园占地面积5.2平方公里，其中水面5000亩，该公园距市中心仅2.5公里，交通十分便利。自1995年建园以来，共投资5千多万元，建起烈山宾馆、天缘山庄大酒店、逍遥津酒楼和投资中心等住宿、餐饮、休闲服务设施以及三亚沙滩湖滨浴场、龙亭公园、造波池、激流勇进等娱乐设施。实施了9公里环湖大道与湖滨南岸建设，安装葡萄灯100柱，共植树10万余棵，湿地绿化栽植水草、芦苇、莲藕、菱角等品种，草坪20多万平方米，绿地面积已达3780亩。目前，园内野生湿地植物资源丰富，已有各种植物100余科，仅湿地植物就达36余科74余种，各类侯鸟20余种，水生鱼类18种以及各种鼠兔、草兔、黄鼠等湿地兽类。每年都有大量侯鸟，如大雁、白鹤在湿地公园迁徙栖息，还有数千只其它鸟类在此过冬。该公园自保护建设以来，完善了排污设施，杜绝污染源进入湿地，同时每天有2万立方米净水进入湖区，使之流动循环，再加上植物的自然净化，常年水质保持二类标准以上。南湖湿地公园的整体框架已经搭建，依据总体规划，将建成综合服务区、主题水文化区、教研科普区、湿地核心保护区、滨水活动区、水上活动区、北部广场区。我们将以生态保护为目的，全面整合旅游资源，把南湖风景区打造成皖北地区设施一流、功能一流、环境一流、人文一流的水文化主题旅游度假区。', '安徽省淮北烈山区长山中路南湖湿地公园', NULL, NULL, '全天开放', 2936, NULL, '公交\n市内乘坐公交117、118路至【淮北国家矿山公园】站下。', NULL, NULL, 'http://travel.qunar.com/p-oi9487271-huaibeinanhufengjingqu', '3', '3', '', '', '', '2', 4, 42570, 146, 96232, 24873, NULL, NULL, NULL, 21550, 10219, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (59, '713560', '自贡恐龙博物馆', NULL, '四川 > 自贡 > 自贡景点 > 自贡恐龙博物馆', '自贡', '自贡', 'https://img1.qunarzz.com/travel/d5/1702/17/d56587e08bb227b5.jpg;https://img1.qunarzz.com/travel/d7/1702/40/d4a1fcea91ce48b5.jpg;https://img1.qunarzz.com/travel/d3/1702/e1/373017e95d7c9eb5.jpg;https://img1.qunarzz.com/travel/d2/1702/d6/cc165a78e0c5fdb5.jpg;https://img1.qunarzz.com/travel/d8/1702/c6/a13940c3cc5d1db5.jpg;https://img1.qunarzz.com/travel/d1/1606/6e/12611b0acb647f9a.jpg', '4.3', '自贡景点排名第2', '2小时 - 3小时', '26', '29.4011572473', '104.8338724919', '9', '主体馆舍内设有化石埋藏馆、中央大厅、化石装架陈列馆、报告厅和恐龙生态环境厅。恐龙埋藏遗址有1900平方米，可见到多种恐龙的骨骼错落交织，分布在近水平状延伸的砂岩层中，是巨大恐龙墓地的缩影。在装架馆内，有近10米高的“天府峨眉龙”和多具恐龙的复原骨架组合，以及古脊椎动物化石标本。馆舍外形用天然砂岩石块堆垒而成，使整个建筑与恐龙发掘现场相协调，立意新颖，造型独特，引人入胜。大山铺发掘的各种恐龙及伴生物化石数以百计，组成了一个完整的“恐龙动物群”。最珍贵的是目前世界上发掘最早的中侏罗纪剑龙和首次发现的翼龙，填补了恐龙演化史上的一段空白。', '自贡市大安区燊海井路四段268号', '0813-5802095', 'http://www.zdm.cn/', '营业时间：8:30-17:30。\n讲解服务时间：9:20-16:30。', 11888, NULL, '公交\n市内可乘3路、15路、35路公交车在终点站下车即到。', NULL, NULL, 'http://travel.qunar.com/p-oi713560-zigongkonglongbowuguan', '5', '5', '1', '2', '4', '4', 9, 31570, 146, 227350, 86190, 7769, 47125, 192131, 108833, 48196, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (60, '8686354', '鸠摩罗什寺', NULL, '甘肃 > 武威 > 武威景点 > 鸠摩罗什寺', '武威', '武威', 'https://img1.qunarzz.com/travel/d7/1804/56/147e6c423fe2ffb5.jpg;https://img1.qunarzz.com/travel/d1/1804/e4/5ba66adf257e89b5.jpg;https://img1.qunarzz.com/travel/d9/1804/fa/9143f82a8679e8b5.jpg;https://img1.qunarzz.com/travel/d1/1804/81/2ff1d96dcf2afb5.jpg;https://img1.qunarzz.com/travel/d8/1804/6e/a01fa1edc5f491b5.jpg;https://img1.qunarzz.com/travel/d1/1804/ae/6c31dd6b05ce5eb5.jpg', '3.3', '武威景点排名第38', NULL, '10', '37.938252', '102.642939', '12', '罗什寺历经沧桑，多劫多难。殿宇僧舍无一幸存，现仅存的罗什塔，即罗什祖师圆寂荼毗后“薪灭形碎，唯舌不坏”之舌舍利塔。虽风尘满面，仍挺立如初。据史料记载，武威鸠摩罗什寺，距今已有1600年的历史，是我国古代著名的西域高僧、佛经翻译家鸠摩罗什初入内地弘法演教之处。寺内雄立的宝塔是为纪念鸠摩罗什而修建的，塔内供奉鸠摩罗什的舌舍利。', '甘肃省武威市北大街', NULL, NULL, '8:00-18:00', 10131, NULL, '公交\n市内可乘坐1路、3路、6路、8路内环、8路外环、9路、101路公交车，在【北关什字】公交站下车，步行172米即可到达。', NULL, NULL, 'http://travel.qunar.com/p-oi8686354-jiumoluoshensi', '', '5', '4', '', '', '3', 4, 40138, 83, NULL, 149721, 42257, NULL, NULL, 91056, 21892, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (61, '714795', '葡萄沟', NULL, '新疆 > 吐鲁番 > 吐鲁番景点 > 葡萄沟', '吐鲁番', '吐鲁番', 'https://img1.qunarzz.com/travel/poi/201211/15/0e1acf7f4785f3b3ddb12cfb.jpg;https://img1.qunarzz.com/travel/d4/1804/64/1b1fdc983cbc6ab5.jpg;https://img1.qunarzz.com/travel/d5/1804/ec/811391ef35e26fb5.jpg;https://img1.qunarzz.com/travel/d4/1804/47/bb48dae9bad70cb5.jpg;https://img1.qunarzz.com/travel/d3/1804/91/321d78856d9f1db5.jpg;https://img1.qunarzz.com/travel/d9/1804/e8/5c2c10d6e42842b5.jpg', '4.1', '吐鲁番景点排名第1', '2小时 - 3小时', '103', '43.026076', '89.2535', '104', '迷人的葡萄沟，位于吐鲁番市东北10公里的火焰山中，这是一条南北长约7公里、东西宽约2公里的峡谷。人工引来的天山雪水沿着第一人民渠穿沟而下，潺潺流水声给葡萄沟增添了青春的活力。两面山坡上，梯田层层叠叠，葡萄园连成一片，到处郁郁葱葱，犹如绿色的海洋。在这绿色的海洋中，点缀着桃、杏、梨、桑、苹果、石榴、无花果等各种果树，一幢幢粉墙朗窗的农舍掩映在浓郁的林荫之中，一座座晾制葡萄干的“荫房”排列在山坡下、农家庭院上，别具特色。夏天，沟里风景优美，凉风习习，是火洲避暑的天堂，旅游季节，中外宾客，川流不息，络绎不绝。', '新疆吐鲁番市东北10公里的火焰山中', '0995-8614688', NULL, '旺季（4月21日-10月20日）8:00-21:00 ；淡季（10月21日-次年4月20日）10:00-18:30。', 14493, '对于游客来说，七、八、九月份是旅游吐鲁番的最佳季节，因为此时正是各种瓜果成熟的时候。', '公交\n市内可搭乘4路至【葡萄沟接待站】下车步行前往即可', '小贴士：由于吐鲁番处于盆地之中，所以不必太担心会被晒黑，但仍需要准备好遮阳帽、太阳镜和足够的润肤霜。', '3小时', 'http://travel.qunar.com/p-oi714795-putaogou', '4', '3', '', '3', '4', '1', 2, 25982, 67, 181789, 74189, NULL, 67970, 87423, 22188, 6077, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
