INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (65, '708614', '白浪滩', NULL, '广西 > 防城港 > 防城港景点 > 白浪滩', '防城港', '防城港', 'https://img1.qunarzz.com/travel/d9/1601/77/2e72acc306fe64f7.jpg;https://img1.qunarzz.com/travel/d8/1601/13/c12bf973576584f7.jpg;https://img1.qunarzz.com/travel/d9/1611/14/dbd17bc6db2dd7b5.jpg;https://img1.qunarzz.com/travel/d5/1611/d1/f846b2473a36db5.jpg;https://img1.qunarzz.com/travel/d6/1611/46/ea441d1bb063cbb5.jpg;https://img1.qunarzz.com/travel/d6/1611/9b/cd2360c5534eacb5.jpg', '3.5', '防城港景点排名第2', '2小时 - 3小时', '21', '21.5415440796', '108.284210653', '20', '白浪滩景区（原称大平坡）沙子中富含钛矿而白中泛黑，是世界上较罕见的“黑金沙滩”之一。钛经酸解、水解与氧反应生成的二氧化钛，是世界上最白的物质，粘附力、遮盖力强，广泛应用于美白化妆品，因此很多游客来白浪滩的目的是挖一个坑把自己埋起来，让人体中分泌的汗液等酸性物质催化钛与氧的反应，从而最简单、最直接地天然美白。', '江山半岛月亮湾西南侧6公里处', '0770-3398069', NULL, '全天开放', 18944, '夏季为佳。\n\n', '公交\n在防城车站（三运站）或港口车站有防城至白浪滩专线，在柜台买票上车即可。港口车站售票处可直接购买港口至白浪滩专线班车票，下车即可看见白浪滩游客服务\n中心。', NULL, '半天或1天', 'http://travel.qunar.com/p-oi708614-bailangtan', '3', '4', '5', '1', '2', '2', 5, 27269, 117, 32101, 106123, 190023, 9237, 11226, 29315, 12806, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (66, '9631519', '江山半岛旅游度假区', NULL, '广西 > 防城港 > 防城港景点 > 江山半岛旅游度假区', '防城港', '防城港', 'https://img1.qunarzz.com/travel/poi/1704/2c/06c5138f49380237.jpg', '--', '防城港景点排名第23', NULL, '1', '21.54349', '108.300824', '6', '江山半岛位于中国大陆海岸线最南端的防城港市港口区江山乡北部湾畔，面积208平方公里，是广西最大的半岛，因其形状似龙，在当地又被称为白龙半岛。江山半岛属于丘陵地带，植被茂密，生态环境非常优美。度假区位于半岛东南岸，总面积为63平方公里，海岸线总长为32公里，度假区内的自然、人文旅游资源非常丰富，有一马平川的沙滩大平坡、千姿百态的怪石滩、中国华南沿海第一次发现的古代恐龙化石等景点。', '广西壮族自治区防城港市防城区江山半岛', NULL, NULL, '全天', 11553, NULL, '公交\n在防城港汽车站乘防城-大坪坡的巴士可到半岛海滩，返程时可乘大坪坡巴士返回防城港市区。\n自驾\n可以过跨海大桥，走防东公路，到江山乡往左拐即可，路边有路标指示。', NULL, NULL, 'http://travel.qunar.com/p-oi9631519-jiangshanbandaolu:you', '', '', '5', '', '2', '2', 8, 28829, 207, NULL, NULL, 311254, NULL, 17794, 31924, 11374, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (67, '9524572', '黑鱼湖', NULL, '黑龙江 > 大庆 > 大庆景点 > 黑鱼湖', '大庆', '大庆', 'https://img1.qunarzz.com/sight/p0/201403/10/3793d1167a3d20f03e8418459000583c.jpg', '--', '大庆景点排名第15', NULL, '0', '46.7775114588', '125.1549582809', '0', '黑鱼湖生态园隶属于大庆油田供水公司，原名黑鱼泡，以盛产黑鱼而得名，位于大庆市的东北部。七十年代，为解决油田开发地下注水和部分农业灌溉用水，兴建了北部引嫩工程，将黑鱼湖修建成水库，成为大庆市重要水源地。黑鱼湖面积相当于10个杭州的西湖。周围湿地景观旖人，蒲草丛生，鸥鸟成群，大小泡泽错落其间，环境自然，生态原始，有包括丹顶鹤、东方白鹳、灰鹤、野鸭、白鹳、大雁、啄木鸟、猫头鹰、红嘴江鸥等数十种珍禽栖息于此；湖内水域生长着黑鱼、草鱼、青鱼、花鲢、白鲢、鲤鱼、鲫鱼、嘎牙鱼等四十余种野生鱼类。', '大庆市林甸县', NULL, NULL, '全天', 20437, NULL, '公交\n在萨尔图公交总站乘坐24路公交车，终点站下车即到。', NULL, NULL, 'http://travel.qunar.com/p-oi9524572-heiyuhu', '2', '', '', '', '3', '4', 7, 42337, 172, 23210, NULL, NULL, NULL, 27961, 197676, 46359, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (68, '7469601', '张掖国家湿地公园', NULL, '甘肃 > 张掖 > 张掖景点 > 张掖国家湿地公园', '张掖', '张掖', 'https://img1.qunarzz.com/travel/d7/1710/d5/7f0b43dac24c5bb5.jpg;https://img1.qunarzz.com/travel/d7/1710/f0/b38eec7184f04cb5.jpg;https://img1.qunarzz.com/travel/d4/1704/1c/d506a9bc267e3eb5.jpg;https://img1.qunarzz.com/travel/poi/1803/41/f79613f4017c4e37.jpg;https://img1.qunarzz.com/travel/d8/1803/32/dc64eac119ecb7b5.jpg;https://img1.qunarzz.com/travel/d5/1803/8b/1148a19e3c9487b5.jpg', '4.3', '张掖景点排名第18', '2小时 - 4小时', '21', '38.981879', '100.452531', '21', '以黑河水和广袤湿地为代表性的生态资源，是张掖生存发展的生态屏障,也成就了旧时张掖 “塞上江南”的美誉。旧地方志称：“一城山光、半城塔影、连片苇溪、遍地古刹。”民间流传着 “甘州不干水池塘”的谚语。据志书记载，旧时张掖城内外举步见塘，抬头见苇，家家泉水，户户垂柳，特别是城区北郊的沼泽湖滩,旧称“北湖”，芦苇蒲草成片相连，山泉湖水碧波荡漾，水鸟云集，鱼翔浅底，草木繁茂，荡舟水草之中，尽赏四时美景。“不望祁连山顶雪，错将张掖认江南”，便是古城张掖美丽景色的真实写照。同时，可以把张掖湿地、祁连山风光、峡谷、草原、森林、冰川以及周边自然景观，以及大佛寺、马蹄寺及丝绸之路古道、古城、古村、古堡、古人类活动遗迹等人文景观，进行整体组团营销，也可以与青海湖、内蒙阿右旗联合开发荒漠绿洲高原环线旅游线路，这一旅游产品具有旅游的差异性，在世界范围内相当独特。', '张掖市甘州区三一二国道北段', '0936-8589010', 'http://www.zyxqsd.com', '全天开放', 7024, '5月、6月、7月、8月、9月较为适宜。', '公交\n在市内乘坐4路, 在【收割机厂】站下车，再往北步行大约1.9公里到。', NULL, NULL, 'http://travel.qunar.com/p-oi7469601-zhangyeguojiashidigong', '', '4', '5', '', '3', '5', 2, 45198, 183, NULL, 95161, 266201, NULL, 98001, 333977, 106884, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (69, '5740443', '伊斯兰风情街', NULL, '内蒙古 > 呼和浩特 > 呼和浩特景点 > 伊斯兰风情街', '呼和浩特', '呼和浩特', 'https://img1.qunarzz.com/travel/d2/1703/30/557e9a0f9f7997b5.jpg;https://img1.qunarzz.com/travel/poi/201408/12/41089a1f5a18b484c8d65eac.jpg;https://img1.qunarzz.com/travel/d1/1512/bf/a8fd2c1c2fb880f7.jpg;https://img1.qunarzz.com/travel/d7/1504/ca/d46dd281e760f8.jpg;https://img1.qunarzz.com/travel/d2/201407/21/722298838d6e8225c8d65eac.jpg;https://img1.qunarzz.com/travel/d6/1511/62/545dab1c6b4239f7.jpg', '4.2', '呼和浩特景点排名第1', '1小时 - 2小时', '40', '40.814499', '111.661306', '34', '这里回族群众聚居、伊斯兰风格建筑集中、穆斯林商业繁荣，特别是因有着千百年浓厚的伊斯兰文化积淀，使这里形成了浓郁的伊斯兰氛围。\n在雨后的下午走上洗刷一新的伊斯兰建筑特色景观街，街道两侧以叠涩拱券、穹隆、彩色琉璃砖装饰出来的高楼气势宏伟，浑厚饱满的绿色或黄色的球形殿顶，高耸的柱式塔楼，以沙漠黄为主的色调，让人领略到浓郁的伊斯兰风情。', '内蒙古自治区呼和浩特市回民区', NULL, NULL, '全天开放', 13309, '风情街四季前往皆可，每年4至10月，是呼和浩特最佳旅游季节。要是去草原，最好是在7月中旬至9月初前往，这时的草原景致迷人，而且正好有一年一度的“那达慕”大会举行，届时，逛古召，登古塔，观赛马，骑马驰骋草原，其乐无比。', '公交\n在市内搭乘公交至【旧城北门】站，步行可到达。', NULL, '2小时', 'http://travel.qunar.com/p-oi5740443-yisilanfengqingjie', '', '', '5', '', '2', '3', 9, 48978, 186, NULL, NULL, 317243, NULL, 45294, 33429, 25790, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (70, '3728703', '嘉峪关关城', NULL, '甘肃 > 嘉峪关 > 嘉峪关景点 > 嘉峪关关城', '嘉峪关', '嘉峪关', 'https://img1.qunarzz.com/travel/d6/1610/64/b15e1163ee4abdb5.jpg;https://img1.qunarzz.com/travel/d8/1707/3e/d567b878b69a25b5.jpg;https://img1.qunarzz.com/travel/d9/1707/7f/a4089492d40930b5.png;https://img1.qunarzz.com/travel/d7/1601/f2/fbb35894cad67df7.jpg;https://img1.qunarzz.com/travel/d8/1606/1d/a77adbd2886679f7.jpg;https://img1.qunarzz.com/travel/d1/1606/89/9451c0d63985c5f7.jpg', '4.3', '嘉峪关景点排名第1', '1小时 - 2小时', '271', '39.8085868661', '98.2879939761', '168', '嘉峪关规模宏大，气势雄伟，整个建筑由内城、外城、城墙等部分组成“城内有城”，它作为内地与西域、中原与大漠之间纷争与融合的见证，悲壮而辉煌。整个关城建造坚固雄伟，气势磅礴，关城呈梯形，周长733米，面积33500平方米，东西城垣开门，城楼对称，三层五间式，周围有廊，城四隅有角楼，南北墙中段有敌楼。两门内北侧有马道达城顶。关城正中有一官井。西面城垣凸出，中间开门，门额刻“嘉峪关”三字。原有城楼，与东西两楼形制相同，三楼东西成一线。', '嘉峪关市区西南6公里处', '0937-6396110', NULL, '旺季:08:30—18:00，淡季:9:00—17:00', 4974, '5—10月最佳。每年5～10月是关城的最佳旅游时间。嘉峪关属温带干旱气候，年温差较大，年平均气温6℃左右，年降水量80多毫米。冬季1月最冷，最低气温-28.6℃，夏季7月最热，最高气温38℃。7月中旬将举办嘉峪关国际滑翔节，届时会有精彩的演出和赛事，一定不要错过。10月是攀登七一冰川的好时节，带上墨镜、涂好防晒霜、踏上登山鞋，好好体验一番征服冰川的乐趣。建议游玩', '公交\n在市区乘坐4路公交车到关城风景区站下车即可。\n在市区乘坐6路公交车到关城旅游景区站下车即可。\n\n', NULL, NULL, 'http://travel.qunar.com/p-oi3728703-jiayuguanguancheng', '1', '4', '', '2', '5', '5', 1, 29298, 175, 18617, 117439, NULL, 37479, 197708, 342590, 110446, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (71, '9171264', '中华黄河楼', NULL, '宁夏 > 青铜峡 > 青铜峡景点 > 中华黄河楼', '吴忠', '青铜峡', 'https://img1.qunarzz.com/travel/d0/1601/11/9f1f16f3d132e9f7.jpg;https://img1.qunarzz.com/travel/d2/1601/41/10f746889a0558f7.jpg;https://img1.qunarzz.com/travel/d0/1601/59/2f15969bd0c76cf7.jpg;https://img1.qunarzz.com/travel/d9/1601/95/1854afb351be1ef7.jpg;https://img1.qunarzz.com/travel/d9/1601/b8/291c9335ad85a6f7.jpg;https://img1.qunarzz.com/travel/d8/1601/ec/a9c329c4cafc8df7.jpg', '5', '青铜峡景点排名第1', '1小时 - 2小时', '2', '38.006734', '106.148208', '1', '中华黄河楼位于黄河西岸、青铜峡滨河大道东侧，是宁夏回族自治区打造沿黄城市带标志性建筑物。内设黄河中国历史文化展览馆、黄河宁夏历史文化展览馆、黄河印象展览馆、黄河文化演艺厅等。是全方位展示黄河五千年灿烂文明的博物馆、黄河文化汇宁夏的重要载体和西部乃至全国的知名旅游景点。是宁夏黄河金岸的点睛之笔和标志性的景观工程之一，将打造出全国展示黄河文化第一楼，与黄鹤楼、滕王阁和鹳雀楼并称中国四大名楼。', '吴忠青铜峡市黄河路与滨河大道交叉处', NULL, NULL, '淡季（11月-次年4月）：9:30-16:30；\n旺季（5月-10月）：9:30-17:30。', 4445, NULL, '公交\n在吴忠市内乘坐4路公交车至【饮料厂】，下车步行2.5公里即可到达。\n\n自驾\n自驾线路：吴忠市区-盛元西街-开元大道-万上公路-蒋朝公路-滨河大道-终点。', NULL, NULL, 'http://travel.qunar.com/p-oi9171264-zhonghuahuanghelou', '', '5', '4', '', '', '2', 8, 39558, 106, NULL, 241010, 135801, NULL, NULL, 21679, 12451, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (72, '5419369', '张掖丹霞国家地质公园', 'Zhangye Danxia National Geological Park', '甘肃 > 张掖 > 张掖景点 > 张掖丹霞国家地质公园', '张掖', '张掖', 'https://img1.qunarzz.com/travel/d3/1706/c1/2cb1e3d30b8020b5.jpg;https://img1.qunarzz.com/travel/d1/1703/38/f788d35dbba74ab5.jpg;https://img1.qunarzz.com/travel/d5/1706/27/f9f78b1e7abc79b5.jpg;https://img1.qunarzz.com/travel/d1/1509/b8/703291debbce1f.jpg;https://img1.qunarzz.com/travel/d7/1509/f5/c3414e34261c7d.jpg;https://img1.qunarzz.com/travel/d5/1608/c0/f0c2086c825c36b5.jpg', '4.7', '张掖景点排名第1', '2小时 - 6小时', '823', '38.972563', '100.05029', '602', '张掖丹霞地貌是国内唯一的丹霞地貌与彩色丘陵景观复合区。地处祁连山北麓，临泽、肃南县境内，分布面积约510平方公里，省级地质公张掖丹霞地质公园、省级风景名胜区，东距张掖市区30公里，北距临泽县城20公里。中国丹霞地貌总数达790处，集中分布在东南部、西南部以及西北部干旱区的26个省区。其中，张掖丹霞地貌面积达536平方公里以上，是我国干旱地区最典型和面积最大的丹霞地貌景观，是中国丹霞地貌发育最大最好、地貌造型最丰富的地区之一。', '张掖市肃南县南台村', '0936-5953862', 'http://www.zydanxia.com/index.html', '根据全年日出、日落时间的变化，景区全年具体运营时间如下：\n　　1月1日——2月28日开园时间为上午7:30分，闭园时间为下午17:00分;\n　　3月1日——4月10日开园时间为为上午6:30分，闭园时间为下午18:00分;\n　　4月11日——5月4日开园时间为上午6:00分，闭园时间为下午18:00分;\n　　5月5日——8月31日、10月1日——10月7日开园时间为上午5:30分，闭园时间为下午19:00分;\n　　9月1日——9月31日、10月8日——10月31日开园时间为上午6:30分，闭园时间为下午18:00分;\n　　11月1日——12月31日开园时间为上午7:30分，闭园时间为下午17:00分。', 6801, '观赏丹霞地貌的最佳时间就是雨后，这样经过雨水的湿润，色彩更加绚丽明亮。', '公交\n在张掖汽车西站乘坐张掖到肃南县或者临泽县的班车。从张掖汽车西站有班车直达丹霞公园，到景区门口下车即可，票价10元/人。运营班次为9:00-12:00每1小时一趟，15:30-16:30各一趟；\n临泽丹霞由西入口进，肃南丹霞在东入口。国庆期间包车前往，上午冰沟下午七彩丹霞，七座面包车600元。', NULL, NULL, 'http://travel.qunar.com/p-oi5419369-zhangyedanxiaguojiadi', '3', '5', '4', '3', '3', '3', 7, 29897, 93, 91810, 214947, 192233, 53550, 66126, 33977, 27272, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (73, '701803', '姜女祠', NULL, '陕西 > 铜川 > 铜川景点 > 姜女祠', '铜川', '铜川', 'https://img1.qunarzz.com/travel/poi/1803/94/ddf2c356f2e0cb37.jpg;https://img1.qunarzz.com/travel/poi/1410/22/81edcbccd1467eabffffffffc8d65eac.jpg', '2', '铜川景点排名第11', '2小时', '1', '35.151584', '109.051652', '0', '据《陕西通志》、《大名一统志》、《郡国志》以及山海关孟姜女庙刻记载“孟姜女是陕西同官（今铜川郊区）”人。相传孟姜女千里送寒衣，却没见到丈夫的面，她悲痛不已，放声大哭，一下子哭倒了长城800里，在倒塌的废墟里她找到了丈夫的骸骨。于是带着骸骨往回走，快到金锁关时，秦兵追至，情急中孟姜女抓住身边的山崖一转，挡住了追兵，人们把那座山转为搬转山。', '陕西省铜川市郊区金山山麓', NULL, NULL, '8：00-18：00', 20172, '春、秋。', '公交：\n从铜川市内乘坐1路或7路公交车，到油库下车可达。', NULL, NULL, 'http://travel.qunar.com/p-oi701803-jiangnu:ci', '', '1', '', '3', '2', '1', 0, 40808, 91, NULL, 23077, NULL, 49274, 43085, 23824, 6447, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (74, '704345', '玉华宫风景名胜区', NULL, '陕西 > 铜川 > 铜川景点 > 玉华宫风景名胜区', '铜川', '铜川', 'https://img1.qunarzz.com/travel/d1/1705/d6/ba17664c5cb81db5.jpg;https://img1.qunarzz.com/travel/d1/1705/dd/76f1396eef74c3b5.jpg;https://img1.qunarzz.com/travel/d9/1705/7e/18e4b38ce67544b5.jpg;https://img1.qunarzz.com/travel/d4/1705/cf/d9d3e343e46152b5.jpg;https://img1.qunarzz.com/travel/d7/1705/a6/37b833301cce97b5.jpg;https://img1.qunarzz.com/travel/d3/1705/71/0f7404c88374e7b5.jpg', '4', '铜川景点排名第2', '2小时', '8', '35.3344630165', '108.9207898536', '2', '玉华景区千顷松涛天然植被保存完好生物种类繁多具有较高的观赏和科研价值。深秋凉爽宜人、红叶似火；隆冬玉树梨花。景区平均气温比西安低摄氏10—12度。\n玉华宫是盛唐帝王的行宫，唐高祖李渊建，唐太宗李世民扩建为五门九殿，完成了20万公顷的大般若经巨著，于公元664年二月五日圆寂于此。现有速成院玄奘手制石雕佛足印、石雕金刚佛座等珍归文无收藏于中国历史博物馆。珊瑚谷（西宫）、郭玉沟（东宫）佛翕石窟、佛雕石刻遗址保存完好，具有极其重要的科研价值。', '陕西省铜川市印台区金锁关镇', '0919-7586011', NULL, '8:00-18:00', 19958, NULL, '公交：\n从铜川市区乘坐公交旅游专线3路公交车可直达玉华宫景区。3路公交车始发站为北关郊区政府，终始站为玉华宫博物馆广场，途经金锁关、玉华宫；运营时间7：30－16：30。在西安火车站或城北客运站乘发往铜川的长途车，下车后在铜川北关汽车站改乘发往玉华宫的旅游专线3路公交车。\n自驾：\n从西安出发上西铜高速，在铜黄高速金锁关（焦坪）出口下高速，沿305省道行驶20公里即到玉华宫景区。从金锁关到景区沿途均有路标导示。', NULL, NULL, 'http://travel.qunar.com/p-oi704345-yuhuagongfengjingming', '', '', '4', '4', '3', '5', 9, 34352, 115, NULL, NULL, 193765, 80802, 96624, 311017, 87576, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
