INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (76, '9488089', '世博会湖北馆', NULL, '湖北 > 潜江 > 潜江景点 > 世博会湖北馆', '潜江', '潜江', 'https://img1.qunarzz.com/travel/d9/1606/70/844228becd5ac99a.jpg', '4.5', '潜江景点排名第6', NULL, '2', '30.443416', '112.902714', '0', '湖北馆，中国上海世博会内地馆之一。展示面积600平方米，外形以古篆书\"水\"字为原型，以水流动的形状轨迹作为造型主线，外观设计元素选择湖北特有的符号--楚风云翔。湖北馆主题是\"江湖连通、城市公园\"。', '潜江市曹禺公园内', NULL, NULL, '全天', 13501, NULL, '公交17路等', NULL, NULL, 'http://travel.qunar.com/p-oi9488089-shibohuihubeiguan', '3', '', '', '', '4', '1', 4, 23630, 64, 22745, NULL, NULL, NULL, 140724, 7558, 5006, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (77, '713319', '交河故城', NULL, '新疆 > 吐鲁番 > 吐鲁番景点 > 交河故城', '吐鲁番', '吐鲁番', 'https://img1.qunarzz.com/travel/d4/1508/94/2196a374c6601a.jpg;https://img1.qunarzz.com/travel/d8/1508/e7/e4057946927c94.jpg;https://img1.qunarzz.com/travel/d6/1707/75/48d61b7286ee52b5.jpg;https://img1.qunarzz.com/travel/d4/1508/32/dee3954b5fab85.jpg;https://img1.qunarzz.com/travel/d6/1508/fd/7a744020b96c9.jpg;https://img1.qunarzz.com/travel/d3/1706/11/117e0495f93b9db5.jpg', '4.3', '吐鲁番景点排名第4', '2小时 - 3小时', '88', '42.9594849311', '89.0721904719', '91', '交河故城，维吾尔语称雅尔果勒阔拉，位于吐鲁番市以西10公里的雅儿乃孜沟30米的悬崖平台上，南瞰盐山、北控交河，地势十分险要。四面环水，故城状如柳叶、为一河心洲，南北长约1650米，东西最宽处约300 米，曾是车师前部王国的国都。交河城系车师人所建，建筑年代早于秦汉，距今约2000-2300年。车师又称作姑师，是生活在这里最早的原始居民。交河故城为唐代遗存。建筑布局别开生面，独具一格，是目前世界上最大最古老也是保存的最好的土建筑城市。古城中央有一连接南门和佛教大寺院南北向的子午大道，把古城一分为二，分为东区、西区。', '新疆吐鲁番市以西10公里的雅儿乃孜沟30米的悬崖平台上', '0995-8667154', NULL, '10:00-19:00', 10680, '秋季适合来这里游玩。', '公交车\n市内可搭乘3路至【沙疗所】，但此路线步行较远，建议打车前往', NULL, '2-3小时', 'http://travel.qunar.com/p-oi713319-jiaohegucheng', '', '', '1', '5', '2', '1', 4, 40137, 131, NULL, NULL, 16312, 163206, 29545, 21318, 6691, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (78, '7565541', '文笔峰道家文化苑', NULL, '海南 > 定安 > 定安景点 > 文笔峰道家文化苑', '定安', '定安', 'https://img1.qunarzz.com/travel/poi/1412/52/52395f3d225c9866cdb.jpg', '3', '定安景点排名第2', '2小时 - 4小时', '2', '19.558051', '110.373981', '0', '海南文笔峰道家文化苑，坐落在距海口46公里处的定安县文笔峰境内。文笔峰与定安南丽湖山水相依、行意相生，自古被视为“南溟奇甸”人文之大成的象征，也是海南岛人文景观的一个缩影。千百年来，这里沉淀了丰富的文化内涵，诞育了许多闻名遐迩的贤德雅士，众多美丽神话传说、民间故事广为流传。\n地处于此的文笔峰道教文化苑是集旅游、观光、休闲、娱乐、文化研究为一体的大型文化主题景区。文笔峰道教文化苑景区占地面积近千亩，以南宋建筑风格为基调，环文笔峰而建。众多仿古建筑阁殿错落有致地分布于山体周围，使景区气势恢宏、壮观古朴、气韵万千。\n文笔峰，面积仅3平方公里，海拔180多米，实际高度仅百米，是海南岛东北部一座其貌不扬的山峰。 就是这样一座普通的山峰，却是定安文化的象征。这里是道家的合法建筑所在地，也是海南文明开发的发源地。', '海口46公里处定安文笔峰境内', '0898-66288075', NULL, '8:00-18:00', 12396, '四季皆宜。', '海南文笔峰乘车路线：\n\n在海口东站乘坐海口至居丁班车(半小时一班)居丁高速路口下车乘坐摩托车内进3公里。\n定安车站发往文笔峰景区直达车：早8;00 午12:00\n晚5:20\n海口东站 或南站东站在海府路\n\n南站在南海大道40-50分钟从海口东站或南站坐省直快车到定安县，然后再从定安坐中巴车到文笔峰即可\n\n海口东站、南站、中国城从海口坐的车直接到文笔峰即可\n\n\n海南文笔峰自驾车路线：\n\n东线高速居丁/南丽湖出口内进3公里\n1、从海口市出发，沿东线高速行驶45公里，在龙湖镇口下高速，然后向西行驶3公里即可到达目的地。\n\n2、海南文笔峰距市区15分钟路程，距机场45分钟路程，距海边30分钟的路程', NULL, NULL, 'http://travel.qunar.com/p-oi7565541-wenbifengdaojiawenhua', '4', '2', '3', '5', '5', '3', 8, 49796, 101, 84124, 31397, 40633, 140815, 89332, 49601, 22993, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (79, '9599478', '雄县', NULL, '河北 > 雄安新区 > 雄安新区景点 > 雄县', '雄安新区', '雄安新区', 'https://img1.qunarzz.com/travel/poi/1704/43/ab257eec9bdff637.jpg;https://img1.qunarzz.com/travel/poi/1704/b5/e92ab9aeee153d37.jpg;https://img1.qunarzz.com/travel/poi/1704/eb/190ea3058b953437.jpg;https://img1.qunarzz.com/travel/poi/1704/46/78c70b5e80b78637.jpg;https://img1.qunarzz.com/travel/poi/1704/1f/7f908ffa621ca337.jpg', '--', '雄安新区景点排名第2', NULL, '0', '38.999249', '116.115389', '0', '雄县隶属于河北省保定市，地处冀中平原，北距首都108公里，东距天津100公里，西距保定70公里，东依霸州市，西南隔白洋淀与安新县相连，西部与容城县相接，并与安新县和容城县一起设立为河北雄安新区。雄县以丘陵、山地为主，地热田面积广大，地热水储备量大，温泉旅游业发达；同时历史悠久，有着古地道文化、古玩文化以及石雕文化。雄县与外界交通便利，目前已融入了京津城市大交通框架，到北京只需45分钟行程，特别是津保铁路通车后，雄县到天津红桥区只需15分钟行程。雄县拥有宋辽古地道、雄县南界碑、白洋淀温泉城等众多景点。', '河北省保定市雄县', NULL, NULL, NULL, 9444, NULL, '火车：\n可乘坐火车到达【白沟火车站】，再搭乘公交或机动三轮车前往雄县其他地方。\n汽车：\n可乘坐汽车到达【雄县汽车站】，再搭乘公交或机动三轮车前往雄县其他地方。', NULL, NULL, 'http://travel.qunar.com/p-oi9599478-xiongxian', '5', '1', '', '', '3', '5', 7, 48568, 205, 154249, 12605, NULL, NULL, 24745, 127315, 90545, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (80, '703372', '沙扒湾', 'ShaPaWan', '广东 > 阳西 > 阳西景点 > 沙扒湾', '阳江', '阳西', 'https://img1.qunarzz.com/travel/d4/201405/30/f845b2c00bd877e8ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/1507/52/c0e6bb6bc4b683.jpg;https://img1.qunarzz.com/travel/d2/201405/30/739a815b58851683ddb12cfb.jpg;https://img1.qunarzz.com/travel/d9/1501/bd/11cc127645fd05.jpg;https://img1.qunarzz.com/travel/d2/201405/30/81f12bb3c799c1abddb12cfb.jpg;https://img1.qunarzz.com/travel/d3/1502/4b/725c26a3ad6180.jpg', '4.2', '阳西景点排名第3', '1小时 - 4小时', '29', '21.5217542268', '111.474484638', '5', '沙扒湾位于阳江地区阳西县沙扒镇，有中国的“马尔代夫”之称。它头枕风光秀丽的北仔岭，面向浩瀚的南海。包括沙扒海滨、月亮湾、青洲岛和白额岭等景点。这里，湾似虹，沙如玉，岸边绿林含翠，海面碧波粼粼，是一个理想的天然浴场。沙扒湾海岸线长2公里，由于住宿接待酒店少，规模较小，外来旅游人数有限，因此这里没有其他海滩的喧哗，2公里长的海岸线上可能只留下你一个人长长的脚印。岸边马尾松林带7万多平方米，绿树成荫。沙扒湾的海滩是自由出入的，离镇中心步行也只需十五分钟。青洲岛在沙扒湾的对面，须乘豪华游艇前往。青洲岛周围生态保存得非常好，海水清澈、海产丰富。在这里可以畅游清澈的海水，抓海胆、海参，还可以捡五彩的鹅卵石。傍晚坐在沙滩边上的餐厅，吹着海风，看着日落，品着真正的海鲜，写意非常。晚饭后漫步往古朴的渔村，看着坐在家门织网的渔家妇女，感受粤西渔村的祥和生活。散步后返回沙滩上，又看到另一番热闹：当地村民三五知己地坐在沙滩上喝着啤酒，听着涛声，数着星星，欣赏着远处阳西新八景之一的“青洲渔火”，还有偶尔天空上璀璨的烟花，好不惬意。', '广东省阳江地区阳西县沙扒镇', '138****1247', 'http://www.shapawan.cn/', '7:00-19:30', 9530, '10月—次年3月，夏长冬暖，夏季平均气温为28摄氏度，冬季平均气温12摄氏度。夏秋季多台风，每年的四月至九月为雨季。', '驾车\n建议自驾前往。', NULL, '4小时', 'http://travel.qunar.com/p-oi703372-shabawan', '', '5', '', '5', '2', '1', 3, 43453, 62, NULL, 271708, NULL, 134458, 48473, 18916, 5436, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
