INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (82, '9168708', '岭·格萨尔主题公园', NULL, '青海 > 玛多 > 玛多景点 > 岭·格萨尔主题公园', '果洛', '玛多', 'https://img1.qunarzz.com/travel/poi/1609/75/e6e460d4fbb9da37.jpg;https://img1.qunarzz.com/travel/poi/1602/fd/c32469c02a6d98f7.jpg;https://img1.qunarzz.com/travel/poi/1602/c5/53aa4b48d870df7.jpg', '--', '玛多景点排名第12', '1小时', '0', '34.91486', '98.217323', '0', '博览园以英雄史诗《格萨尔王传》为主题，整体再现了岭国八十员大将的音容尊貌，充分挖掘了史诗中诸多脍炙人口的故事情节，既具观赏性，又不乏史料价值，是海内外游客认识格萨尔文化的乐园，也是学者探究格萨尔精神的领地，更是弘扬民族精神和核心价值观的主阵地，是玛多县竭力打造的一处景点品牌文化和远古复始的宝典。', '果洛藏族自治州玛多县玛查理镇政府南', NULL, NULL, '全天开放', 4998, '夏季', '建议驾车前往', NULL, NULL, 'http://travel.qunar.com/p-oi9168708-linggesaerzhutigongyuan', '', '4', '', '1', '4', '3', 5, 47453, 171, NULL, 116242, NULL, 7354, 81352, 61556, 24766, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
