INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (94, '9209635', '白沙起义纪念碑', NULL, '海南 > 琼中 > 琼中景点 > 白沙起义纪念碑', '琼中', '琼中', 'https://img1.qunarzz.com/travel/d6/1611/59/396587470aeb66b5.jpg;https://img1.qunarzz.com/travel/d7/1611/63/90b77878cc75f5b5.jpg;https://img1.qunarzz.com/travel/d5/1611/75/45ef08f2c59a03b5.jpg;https://img1.qunarzz.com/travel/d7/1611/81/a96e2ee47d61e3b5.jpg;https://img1.qunarzz.com/travel/d8/1611/de/19e3cf1cc7aba1b5.jpg;https://img1.qunarzz.com/travel/d9/1611/12/8ac01f25fb89e0b5.jpg', '3', '琼中景点排名第4', '1小时 - 2小时', '1', '19.02649', '109.689274', '1', '白沙起义纪念碑位于海南琼中县红毛镇境内，是为纪念黎族领袖王国兴带领农民起义的历史事迹而建的。纪念碑坐南向北，后依德伦山，前向黎母岭尖。1988年11月，海南省人民政府为纪念1943年8月黎族首领王国兴、王玉锦领导的白沙黎、苗族人民武装起义而建。1937年7月，全国抗日战争爆发。1939年2月，日本侵占海南岛。当时琼崖国民党军队和地主武装抵抗无力，节节败退，逃入五指山区。对黎、苗族人民进行残酷的政治压迫和经济剥削，从1940年至1943年春，共杀害无辜黎族群众九千多人，苗族群众三千多人，使黎族、苗族人民陷入了绝境，生活在水深火热之中。为此以黎族人民领袖王国兴为首发动了“白沙起义”，为琼崖抗日战争至解放战争的胜利立下了不可磨灭的伟大历史功勋。', NULL, NULL, NULL, '全天', 19277, NULL, '建议自驾前往', NULL, NULL, 'http://travel.qunar.com/p-oi9209635-baishaqiyijinianbei', '', '', '', '4', '1', '5', 2, 28846, 128, NULL, NULL, NULL, 159042, 12310, 312113, 89745, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
