INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (103, '9207334', '汉江三峡风景区', NULL, '陕西 > 安康 > 安康景点 > 汉江三峡风景区', '安康', '安康', 'https://img1.qunarzz.com/travel/poi/1605/12/ac1e02821dc6d9f7.jpg;https://img1.qunarzz.com/travel/poi/1708/d8/2e3df73f7ed98337.jpg', '5', '安康景点排名第3', NULL, '1', '32.982183', '108.283651', '0', '凤凰峡起于凤凰山之凤首处，为汉江最窄处，尤以莲花古渡口最为著名。此间，在峡谷两岸，一块块白玉似的石头宛若一朵朵白莲，千姿百态，气象万千，绵延几公里，蔚为奇观，这就是古今闻名的莲花石。至后柳镇，汉江水域变得宽阔而敞亮，这便是柳溪峡。柳溪峡两岸连山，气势磅礴，水鸟蹁跹，帆影点点，汽笛嘹亮，渔歌悠扬。后柳古镇历历在目，其情美不胜收，其境如诗如画。第三峡香柏峡因著名景点香柏岩而得名。香柏树是这一带常见的树木，四季长青，并散发出淡淡的幽香，弥漫在整个峡谷之中。香柏峡比凤凰峡略为宽阔，但两岸雄山耸峙，如刀劈斧削，壁立千仞，给人一种大气阳刚之美。', '陕西省安康市石泉县', NULL, NULL, '10:00-17:00', 20389, NULL, '建议自驾前往。', NULL, NULL, 'http://travel.qunar.com/p-oi9207334-hanjiangsanxiafengjing', '5', '5', '4', '4', '4', '4', 4, 28960, 64, 199723, 353357, 179341, 100949, 103925, 148963, 52864, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (104, '714953', '陈毅故居', NULL, '四川 > 乐至 > 乐至景点 > 陈毅故居', '资阳', '乐至', 'https://img1.qunarzz.com/travel/poi/1605/87/8af2654fff128af7.jpg;https://img1.qunarzz.com/travel/d3/1509/92/0a127865ff7a3.jpg;https://img1.qunarzz.com/travel/d9/1509/b2/201c699b0b9cca.jpg;https://img1.qunarzz.com/travel/d4/1509/42/9753e2aa9f056a.jpg;https://img1.qunarzz.com/travel/d5/1509/80/863cf29a270ad8.jpg;https://img1.qunarzz.com/travel/d4/1509/46/20cd27592a6806.jpg', '4.3', '乐至景点排名第1', NULL, '3', '30.3920526964', '104.956247879', '0', '陈毅故居陈毅故居位于乐至县劳动乡正沟湾，距乐至县城29.5公里，是革命先辈、开国元勋陈毅元帅的出生地。故居始建于清乾隆初年，1980年8月重修原貌，是一座具有浓厚川中民居特色的三重堂四合院式的木质结构瓦房。馆前塑陈毅铜质站像，高2.6米。馆连同绿化区共占地2.5万平方米。陈列馆内陈列反映陈毅生平事迹的图片、实物4000多件，并收藏了国内著名学者、艺术家怀念陈毅的诗词、书画等作品。1981年，四川省人民政府颁布陈毅故居为四川省重点文物保护单位，邓小平为陈毅旧居亲笔题写了“陈毅故居”名匾。1986年9月陈毅纪念馆依故居而建，展有陈毅生平事迹的图片、实物和国内外著名学者、艺术家怀念他的诗词、书画等。陈毅故居和陈毅纪念馆相得益彰、浑然一体，整个区内松柏挺秀、环境幽雅，是人们瞻仰先辈、领略川中民居特色的好所在。', '四川-资阳乐至县劳动乡正沟湾', '028-23336012', NULL, '8:00-18:00', 5663, NULL, '建议自驾前往。', NULL, NULL, 'http://travel.qunar.com/p-oi714953-chenyiguju', '', '', '', '2', '5', '2', 8, 20387, 67, NULL, NULL, NULL, 17668, 150178, 18277, 13108, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
