INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (107, '9508239', '乌海湖', NULL, '内蒙古 > 乌海 > 乌海景点 > 乌海湖', '乌海', '乌海', 'https://img1.qunarzz.com/travel/d1/1608/70/9054db419194fbb5.jpg;https://img1.qunarzz.com/travel/d2/1710/62/07c4ed9602c53bb5.jpg;https://img1.qunarzz.com/travel/d8/1710/96/1e0799f47b6da5b5.jpg;https://img1.qunarzz.com/travel/d1/1608/5c/9361e8edc4054bb5.jpg;https://img1.qunarzz.com/travel/d1/1608/9c/a5635cfa5333c3b5.jpg;https://img1.qunarzz.com/travel/d1/1608/b8/9df19d75610dd3b5.jpg', '5', '乌海景点排名第15', NULL, '10', '39.666135', '106.785472', '11', '一个城市的美，在于有灵气，而灵气来自于滋润万物的水。水犹如流动在城市躯体中的血液，让城市充满朝气和活力。许多游客对乌海的美丽惊叹不已，湖光水色、城市的神韵给他们留下了深刻的印象。乌海湖的形成不仅加深了游客对乌海的印象，而且展示了乌海人对外开放、发展经济的热情和信心。', NULL, NULL, NULL, '全天', 18307, NULL, '建议自驾前往。', NULL, NULL, 'http://travel.qunar.com/p-oi9508239-wuhaihu', '2', '3', '', '4', '3', '5', 6, 35406, 207, 25895, 46293, NULL, 126647, 95032, 253906, 81675, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (108, '702678', '太姥山', NULL, '福建 > 福鼎 > 福鼎景点 > 太姥山', '宁德', '福鼎', 'https://img1.qunarzz.com/travel/d6/1505/be/daf0bb3534891f.jpg;https://img1.qunarzz.com/travel/d8/1612/4e/a6fdff9a83f5f3b5.jpg;https://img1.qunarzz.com/travel/d6/1612/a4/2b5a473994974b5.jpg;https://img1.qunarzz.com/travel/d0/1612/41/8d6ed7aaef5db0b5.jpg;https://img1.qunarzz.com/travel/d3/1705/a3/fa3e40f2a168bab5.jpg;https://img1.qunarzz.com/travel/d9/1505/34/c719d044f6996a.jpg', '4.7', '福鼎景点排名第1', '3天 - 6天', '58', '27.121213', '120.201018', '18', '太姥山位于闽浙边界的福建省福鼎市境内，北邻浙江温州118公里，南距福建福州200公里，雄峙于东海之滨，山海相依、傲岸秀拔，以 “山海大观”称奇。传说东海诸仙常年聚会于此，故有“海上仙都”的美誉。它北望浙江雁荡、西眺福建武夷，三者成鼎足之势，闽人称武夷、太姥为双绝，浙人视雁荡、太姥为昆仲， 构成闽越三大名山。\n太姥山是一处以花岗岩峰林岩洞为特色，融山、海、川、岛和人文景观于一体的风景旅游胜地，观赏面积92.02平方公里，保护面积200平方公里，拥有太姥山岳、九鲤溪瀑、福瑶列岛、晴川海滨四大景区和瑞云畲寨、冷城古堡、翠郊古民居三处独立景点。旅游资源有武夷、雁荡山水之秀丽；有黄山、泰山云雾之奇幻；有崂山、三亚海景沙情；有普陀、武当佛踪道迹。游人就近便可登山、探洞、戏水、游岛、漂流……寻觅古刹幽远，领略畲寨风情，一举多得。这里年平均气温13.6度，是旅游度假避暑的好去处。', '宁德市福鼎市秦屿镇九七五县道', '0593-7277848', 'http://www.517time.com/', '8:00-20:00', 3786, NULL, '交通指南：\n\n自驾：G15高速公路（又称沈海线、同三线）在太姥山设互通口，南距福州220公里，北距温州100公里。由北面来，经温州后一路向南，途径瑞安、苍南、福鼎等地，太姥山下高速；由南面来，经福州后一路向北，途经连江、宁德、霞浦等地，在太姥山下高速；由西面来，途经武夷山、政和、周宁、福安、霞浦等地，太姥山下高速。出太姥山互通口，按指示牌行走（两个环岛均向右），进入秦太旅游公路，前行12公里即到太姥山景区大门。\n\n动车：太姥山处在国家“四纵四横”铁路干线之一的“沪深客运专线”上，乘坐动车来太姥山旅游，最优选择是在“太姥山”站下车，太姥山站距离景区仅16公里，乘坐公交车20分钟左右即到。其次可选择在“福鼎”站下车，福鼎站距离太姥山景区约40公里，乘坐直达公交车50分钟左右即到。\n\n飞机：太姥山景区紧邻温州龙湾国际机场和福州长乐国际机场，您可先到温州或者福州之后转乘动车或者大巴车抵达。', '这里曾是电影《隐婚男女》的拍摄地，电影中刘若英告诉陈奕迅，太姥山娘娘是保佑人获得爱情与幸福的女神，建议他在这里许愿。来此拜太姥山娘娘求姻缘。', '建议1-2天', 'http://travel.qunar.com/p-oi702678-tailaoshan', '', '', '2', '', '', '5', 4, 24687, 180, NULL, NULL, 24355, NULL, NULL, 342458, 104364, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (109, '7469233', '十里银滩', NULL, '广东 > 阳江 > 阳江景点 > 十里银滩', '阳江', '阳江', 'https://img1.qunarzz.com/travel/d2/1704/16/1d244b1c2a1bc9b5.jpg;https://img1.qunarzz.com/travel/d9/1612/a8/96187ef81eb74db5.jpg;https://img1.qunarzz.com/travel/d4/1706/f4/5b924bc150b783b5.jpg;https://img1.qunarzz.com/travel/d0/1706/fc/af3995132d6b7cb5.jpg;https://img1.qunarzz.com/travel/d5/1703/c0/6ad1943c67257cb5.jpg;https://img1.qunarzz.com/travel/d3/1410/10/87984af5caf6a3c8ffffffffc8d65eac.jpg', '4.8', '阳江景点排名第2', '4小时', '45', '21.574101', '111.877273', '46', '十里银滩位于海陵岛中南部，它是海陵岛的众多海滩之一。十里银滩的特点在于海岸线很长，它的海岸线长达16.5公里，金色长滩，一望无际。往海上丝绸之路博物馆的路上，就可见到碧海向金沙涌去的景象。这里沙质洁净均匀，水质清澈透明，是一处海边戏水的好地方。\n十里银滩另外一个优势是在于干净及人少，这里距离旅游大道比大角湾远，所以游人要比大角湾少。大角湾相对人较多，所以旺季的时候就像下饺子般，而这里的人群就要少很多了，可以图个清静。\n十里银滩没有大角湾的大浪，在这里主要玩沙滩车和飞艇，不建议游泳。坐沙滩车可以轻松横跨十里银滩，领略十里银滩作为上海大世界基尼斯纪录最长海滩的魅力与风光。除外，和一般的海滩一样，这里也设有帆船、游艇、水上垂钓、冲浪等水上项目以及沙滩排球、足球等体育活动项目，可以选择赶兴趣的游玩，据说冲浪很不错。', '广东省阳江市海陵镇', '0662-3853333', NULL, '海滩本身全天开放，服务及娱乐设施等的营业时间为8:00-18:00。', 3511, '每年3月到11月，此间阳光充足，海水温暖，适宜下水游泳。', '景点位于海陵岛上\n如何到达海陵岛？\n广州到闸坡有班车直达，在省汽车站乘坐班车。\n如何到达十里银滩？\n海陵岛没有公交车，以摩的和电三轮为主。岛上景点比较集中，从一个景区到另一个景区乘车大约5-10分钟。', '1. 海陵岛上有很多摩的司机，说是可以免费带路去酒店或者饭店，实际上他们会借此收取佣金，已经形成了产业链。出行前不妨提前在网上订好房间。\n2. 岛上有些黑店专坑游客，有的会通过拉客司机判断你是否是外地人，然后给你比本地人价格高的餐牌。另外，称海鲜的称可能会缺斤少量。如果想要比较保险，可以就在入住的酒店用餐，比较有名的是碧珠楼1楼的海鲜餐厅。早餐建议吃吃闸坡的特色猪肠碌和海鲜粥，非常美味。\n3. 渔船出海涉及安全问题，最好查看该船的许可证、保险、执业资格证等证件再上船，有部分是无证和无保险船只，所以请务必小心。', NULL, 'http://travel.qunar.com/p-oi7469233-shiliyintan', '', '2', '', '3', '2', '5', 1, 27217, 72, NULL, 38346, NULL, 31329, 19664, 230571, 100799, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (110, '9500059', '金沙湾', NULL, '青海 > 海晏 > 海晏景点 > 金沙湾', '海北', '海晏', 'https://img1.qunarzz.com/travel/d2/1805/d6/286a730c781127b5.jpg;https://img1.qunarzz.com/travel/d6/1805/12/6eca23c459ef9db5.jpg;https://img1.qunarzz.com/travel/d9/1805/4a/e190f880776620b5.jpg;https://img1.qunarzz.com/travel/d6/1805/f1/90b4dd9b53e9dfb5.jpg;https://img1.qunarzz.com/travel/d3/1804/4c/bc77f81a6c3686b5.jpg;https://img1.qunarzz.com/travel/d4/1804/d8/915b94d4762ea7b5.jpg', '4.3', '海晏景点排名第11', '1小时 - 2小时', '21', '36.766964', '100.776386', '0', '在青海湖环湖东路约二十几公里处，有一片金黄色的沙丘群横亘在路的两侧，这一片小小的沙漠地带便是青海湖金沙湾。青海湖金沙湾，以观看金色的沙漠风光为主要特色，沙丘背后的蔚蓝湖水，景色奇幻优美。此外，还可可尽情游玩多种沙丘娱乐项目，如滑沙、骑骆驼、体验沙地摩托等，价格实惠。', '青海省海北藏族自治州海晏县', NULL, NULL, '9:00-19:00', 5696, NULL, '景点位于青海湖环湖东路约二十几公里处，建议自驾或包车前往。', NULL, NULL, 'http://travel.qunar.com/p-oi9500059-jinshawan', '', '3', '3', '2', '2', '2', 5, 42025, 61, NULL, 74309, 38929, 10279, 43693, 23889, 10890, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (111, '716389', '西双版纳原始森林公园', NULL, '云南 > 西双版纳 > 西双版纳景点 > 西双版纳原始森林公园', '西双版纳', '西双版纳', 'https://img1.qunarzz.com/travel/d1/1708/93/862ba7bf537627b5.jpg;https://img1.qunarzz.com/travel/d8/1607/43/0819ed839268b753.jpg;https://img1.qunarzz.com/travel/d2/1512/46/00a9ee8d3baf1df7.jpg;https://img1.qunarzz.com/travel/d8/1607/61/c027b17534cf0f53.jpg;https://img1.qunarzz.com/travel/d6/1610/59/113d8032dafc58b5.jpg;https://img1.qunarzz.com/travel/d2/1611/dd/3129c59063a8f4b5.jpg', '4.3', '西双版纳景点排名第2', '12小时', '146', '22.0342159918', '100.8805962906', '116', '西双版纳原始森林公园地处昆洛国道旁，品种繁多的热带植物遮天蔽日，龙树板根、独木成林、老茎生花、植物绞杀等植物奇观异景随处可见，峡谷幽深、乌鸣山涧、林木葱茂、湖水清澈，让您真切感受到大自然的神秘。爱伲寨的抢亲、泼水节的欢畅、各民族的歌舞表演，任游客亲身参与，使游客置身于浓郁的民族风情中留连忘返。孔雀开屏迎宾，猴子与人嘻戏，黑熊、蟒蛇、蜥蜴、穿山甲等珍稀动物，让您见识真正的动物王国，让游客充分感受人与自然、人与动物的和谐相融。\n西双版纳原始森林公园里还西息着上千种动物。在密林深处，你可以看到悠然亭立的鹤，自然觅食的鹿，在古藤上皮荡悠的猴，偶尔还能看见国家一类保护动物野牛和犀牛的身影。除了优美的自然景观，这里还有独具特色的风情节目。 傣族头领如片领登基仪式的宏大场面，辉煌的傣族宫殿建筑，精湛的民族歌舞，多姿多彩的民族服饰，定能使您大开眼界，从中领略到傣族文化的博大精深。', '云南西双版纳景洪市区北郊8公里', '0691-2759888', NULL, '7:30-18:00', 17448, '西双版纳地区最佳的旅游时间在10月至次年6月。西双版纳的气候只有干湿之分，每年11月至次年4月为干季，雨季在每年5-10月间。', '景洪城外八公里外的昆洛公路旁,有班车过去,3元/人,景区内也有电瓶车可以坐。', NULL, NULL, 'http://travel.qunar.com/p-oi716389-xishuangbannayuanshisen', '3', '5', '4', '2', '5', '2', 0, 48476, 181, 37051, 105698, 183591, 28388, 134683, 14613, 13128, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (112, '9530086', '龙江三峡国家森林公园', NULL, '黑龙江 > 萝北 > 萝北景点 > 龙江三峡国家森林公园', '鹤岗', '萝北', 'https://img1.qunarzz.com/travel/poi/1610/f9/fcaf474c74601837.jpg;https://img1.qunarzz.com/travel/poi/1610/72/fccf98bba65b0e37.jpg;https://img1.qunarzz.com/travel/poi/1610/c9/4cbcf661fd05f737.jpg;https://img1.qunarzz.com/travel/d6/1610/8d/31c3562ceb48b7b5.jpg;https://img1.qunarzz.com/travel/d3/1610/ad/1f7576103321c5b5.jpg;https://img1.qunarzz.com/travel/d2/1610/43/cd2a1eab041c3db5.jpg', '3', '萝北景点排名第4', '2小时 - 3小时', '0', '47.917239', '130.889678', '0', '龙江三峡国家森林公园由龙门峡、金龙峡和金满峡而得名。公园依山傍水，风光旖旎，尽显北国雄伟壮丽风光。与长江三峡形成南北呼应。其中龙门峡波涛汹涌，奇峰多姿，金龙峡幽深秀丽、蜿蜒欲腾，金满峡峰峦叠嶂、山雄水幽。夏日乘船顺流而下，两岸山势嶙峋、峰回路转，蓝天碧水之间，鹰击长空、鱼翔浅底、花开丛中，令人心旷神怡。并且公园内有全国第二大红松母树林，有风景秀丽的名山岛，还有七宝树、胭脂沟、慈禧行宫、八仙阁、太平沟城门、程公德政碑、鱼王庙、兴东兵备道等清朝时期的一些历史遣址、建筑和树木，景色极为秀美。', '鹤岗市于萝北县三江平原的西北部', NULL, NULL, '全天', 9651, NULL, '距离鹤岗市区较远，建议自驾出行。', NULL, NULL, 'http://travel.qunar.com/p-oi9530086-longjiangsanxiaguojia', '5', '', '', '2', '2', '2', 7, 36305, 209, 359391, NULL, NULL, 21106, 29472, 31401, 10972, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
