INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (114, '8595570', '龙景峡谷', NULL, '湖南 > 资兴 > 资兴景点 > 龙景峡谷', '郴州', '资兴', 'https://img1.qunarzz.com/travel/d1/1610/ef/f7afdbfbe8db57b5.jpg;https://img1.qunarzz.com/travel/d2/1610/5a/01c2ffd78c1a01b5.jpg;https://img1.qunarzz.com/travel/d7/1610/51/77250019b9ad16b5.jpg;https://img1.qunarzz.com/travel/d2/1610/ed/5defc542a09e9b5.jpg;https://img1.qunarzz.com/travel/d1/1610/d0/0d4cc775d1e576b5.jpg;https://img1.qunarzz.com/travel/d1/1610/cf/bc56bbc809c928b5.jpg', '4.3', '资兴景点排名第16', NULL, '8', '25.870374', '113.317881', '5', '龙景峡谷位于资兴市境内，是一处集旅游观光、休闲健康、水上娱乐为一体的综合性旅游景区，峡谷内有堪称南国最大的瀑布群——龙景十八瀑；有喻为中国野外水上第一滑的龙景水上滑道；有国内最大的天然氧吧——龙景峡谷负氧离子集聚区。她是继东江漂流后，东江湖黄金旅游线上绽开的又一朵奇葩。景区山水相映，各具特色，峡谷全线游程为2.5公里。沿途山势陡峭，青山滴翠，古木参天，清潭、瀑布成群，是我国目前瀑布密集最高的景区，有大小瀑布26处，水潭18个。在景区中能呼吸到高达9.4万个/立方厘米的空气负氧离子和多种有益人体健康的植物精气，是国内罕见的高负离子区。', '郴州市资兴市东江湖风景区内', '0735-3356623', 'http://www.dongjianghu.com', '9:00-18:00', 7614, '夏季最佳。', '可乘坐景区大巴到达景区，而后步行到达。', NULL, '1小时', 'http://travel.qunar.com/p-oi8595570-longjingxiagu', '3', '3', '3', '3', '5', '4', 8, 41567, 187, 21173, 20408, 54597, 88252, 322284, 175610, 46855, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (115, '712142', '格根塔拉草原', NULL, '内蒙古 > 乌兰察布 > 乌兰察布景点 > 格根塔拉草原', '乌兰察布', '乌兰察布', 'https://img1.qunarzz.com/travel/poi/1803/ca/a745b75c8a6c9637.jpg;https://img1.qunarzz.com/travel/poi/1707/d4/241f6b484f4b537.jpg;https://img1.qunarzz.com/travel/poi/1707/75/ec5f551f31618837.jpg;https://img1.qunarzz.com/travel/poi/1609/32/7c17a16e7e1fc437.jpg', '5', '乌兰察布景点排名第9', '2小时 - 4小时', '3', '41.7840364975', '111.8529736839', '2', '规模最大的蒙古族民族风情旅游景点，位于乌兰察布大草原的腹地，距自治区首府呼和浩特128公里，有柏油马路相通。格根塔拉草原蒙语意为“夏营盘”“避暑胜地”，乘车从呼市出发2小时，这里牧草茂盛、牲畜遍地，草原特色浓厚迷人，自古以来就是优良的天然牧场。每年七月二十五日――三十一日举办的那达慕草原旅游节集蒙古民族文艺、体育、生产生活习俗之大全，赛马、摔跤、射箭，风情独特，使游客流连忘返。格根塔拉草原景区内有千人宴会大厅一处，大小餐厅近30处。格根塔拉旅游区除设有200多顶传统蒙古包外，还建有现代豪华蒙古包和战车式蒙古包150顶，其整体建筑造型均体现了民族特色。', '内蒙古四子王旗查干补力格苏木格日勒图雅嘎查', '0474-5208888', NULL, '8:00-18:00', 11331, NULL, '可从呼和浩特市乘班车到乌兰察布市后，再转车到四子王旗，也可直接从呼和浩特市或乌兰察布市包车前往。', NULL, NULL, 'http://travel.qunar.com/p-oi712142-gegentalacaoyuan', '', '3', '', '3', '2', '4', 9, 49545, 142, NULL, 84041, NULL, 91917, 46284, 77315, 44359, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (116, '715060', '魏晋墓群', NULL, '甘肃 > 嘉峪关 > 嘉峪关景点 > 魏晋墓群', '嘉峪关', '嘉峪关', 'https://img1.qunarzz.com/travel/poi/201408/12/776471ecdd1e0499c8d65eac.jpg;https://img1.qunarzz.com/travel/poi/201211/15/634768a7e09923daddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/201408/12/366e0b079b554d66c8d65eac.jpg;https://img1.qunarzz.com/travel/poi/201408/12/b128cd04c2af8d19c8d65eac.jpg', '4', '嘉峪关景点排名第12', '1小时 - 2小时', '4', '39.8599534916', '98.4401833418', '6', '嘉峪关魏晋砖壁画墓是“果园――新城魏晋墓群”的一部分，位于嘉峪关市区东约18公里的新城镇，在近13平方公里的地域内分布着魏晋时期的古墓葬一千余座，素有“地下画廊”之称。1972年――1979年期间，先后发掘了18座墓葬，其中9座为画像砖墓，9座为素砖墓。其中五号墓于1973年5月整体搬迁到甘肃省博物馆。目前正式对外开放2座，新城墓葬建筑形制独特，墓葬由墓道、墓门、门楼、前室、中室、后室、甬道、耳室、壁龛等组成。建筑用砖分为印纹方形砖、长方形砖、雕刻砖、画像砖等。墓门用条形砖叠砌为拱券形，券顶以上采用条形砖叠造门楼，门楼墙面嵌砌有斗拱、人物、兽首等造型的雕刻砖，墓顶多为？顶式和拱券式。\n墓葬共出土760余副砖画，形式多为一砖一画、半砖一画或几块砖组成的连环画。这些砖壁画色彩鲜艳，风格独特，画技巧妙，砖壁画内容取材于当时的现实生活，有反映农耕、养殖、采桑、宴居、出行、军事操练、狩猎等内容，是研究魏晋时期西北地区的政治、经济、文化、军事、民族、民俗及气候等的实物资料。其中绘画方面的内容，在美术领域填补了我国魏晋时期绘画史上的空白。', '甘肃嘉峪关市新城乡', NULL, NULL, '8:30-20:00', 13311, '嘉峪关气候：嘉峪关气候属于温带干旱气候，年平均气温6摄氏度左右，昼夜温差较大，年降水量80多毫米，1月份最 冷，7月份最热 最佳旅游时间：每年5月－10月是最佳旅游时间', '可从嘉峪关市区包车前往，50元往返，单程30分钟。', NULL, NULL, 'http://travel.qunar.com/p-oi715060-weijinmuqun', '', '', '1', '5', '3', '1', 4, 43028, 90, NULL, NULL, 15021, 187000, 42906, 6259, 5575, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (117, '7588071', '勐焕大金塔', NULL, '云南 > 芒市 > 芒市景点 > 勐焕大金塔', '德宏', '芒市', 'https://img1.qunarzz.com/travel/d5/1701/e5/21f16557a9ef2db5.jpg;https://img1.qunarzz.com/travel/poi/1803/b4/a015f89e2cfddb37.jpg;https://img1.qunarzz.com/travel/d3/1610/26/744d56d2874717b5.jpg;https://img1.qunarzz.com/travel/d6/1610/d7/244d7da378b19fb5.jpg;https://img1.qunarzz.com/travel/d5/1712/92/c0cf4f1bccfb68b5.jpg;https://img1.qunarzz.com/travel/d7/1802/d0/9655442c8f4a1eb5.jpg', '4.5', '芒市景点排名第14', '1小时 - 4小时', '18', '24.42744', '98.602946', '10', '勐焕大金塔座落在芒市东南部海拔一千零七十九点六米的雷崖让山顶。传说“释加牟尼”生前转世为金鸡时曾生活于此地，佛涅槃后数百年，佛教弟子“召罕大”为传播佛祖教义亲临此山修炼，为“召罕大”有个修练的好环境，野草和荆棘全部让开，故称为雷牙让山，意为野草让开的地方，尔后在此山建一佛塔，自古以来是人们公认的佛教圣地之一。', '德宏傣族景颇族自治州芒市东南部的雷崖让山顶', '0692-2116199', NULL, '8:30-21:00', 12064, NULL, '可打的到雷崖让山山脚，然后沿着登山步道往上走到山顶即可。', NULL, '1小时', 'http://travel.qunar.com/p-oi7588071-menghuandajinta', '', '', '2', '', '2', '1', 1, 46502, 114, NULL, NULL, 37566, NULL, 13004, 16029, 6305, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (118, '706673', '巴松措', NULL, '西藏 > 工布江达 > 工布江达景点 > 巴松措', '林芝', '工布江达', 'https://img1.qunarzz.com/travel/d5/1601/fa/aade657c4976b8f7.jpg;https://img1.qunarzz.com/travel/d5/1601/f1/b087a1248d7cef7.jpg;https://img1.qunarzz.com/travel/d8/1512/dd/2d6acf1f5ea957f7.jpg;https://img1.qunarzz.com/travel/d2/1601/db/350cb0306aa4a6f7.jpg;https://img1.qunarzz.com/travel/d8/1512/4d/315b48996b8f0df7.jpg;https://img1.qunarzz.com/travel/d1/1511/3a/97f4483f46dffcf7.jpg', '4.5', '工布江达景点排名第3', '2小时', '136', '29.9884389307', '93.8686708886', '29', '巴松错又名错高湖，藏语中是“绿色的水”的意思，湖面海拔3700多米，湖面面积达6千多亩，位于距林芝地区工布江达县50多千米的巴河上游的高峡深谷里，是红教的一处著名神湖和圣地。虽然巴松错深藏在交通闭塞、远离城镇的山沟里，但它却以其林木繁茂和群山耸立中的那一池碧水而广为外界所知，成为林芝地区最早为人所知的风景之一。巴松错湖水清澈见底，四周环绕的雪山倒映其中。黄鸭、沙鸥、白鹤等飞禽悄然浮游在水面，湖中则是游鱼如织往来穿梭，更显得一派宁静。距岸边大约一百米处有一座小岛名为扎西岛，传说该岛是一“空心岛”，即岛与湖底是不相连而漂浮在湖水上的。虽然只是个传说，却让人觉得蔚为神奇，你到岛上也不妨试试在岛的某些地方跺跺脚，看是否有空心的感觉。', '西藏林芝地区工布江达县', '0894-6333453', 'http://www.basongcuo.com.cn/index.php/Home/Index/index.html', '9:00-16:00', 19889, NULL, '可以自驾或者在八一镇或拉萨包车，包小车一般在600元左右一天（淡旺季浮动较大）。', '1、到湖心扎西岛需要乘坐摆渡木筏。木筏并不以桨摆渡，而是通过手拉一根贯通小岛和湖岸的钢缆摆渡；\n2、巴松错的最佳季节是秋天，层林尽染，云淡风清，是摄影爱好者最为喜爱的季节。巴松措交通(怎么去巴松措)搭车去巴松措巴松错位于318国道旁，距工布江达县城70公里，来往车辆较多，交通便利。如搭车前往，在公路边会看到“巴松错风景区”指示牌的岔路口下车即可，然后过巴河桥北行约40公里处便可抵达巴松错湖边。包车、自驾去巴松措包车前往巴松错在八一镇包车最为合适。', NULL, 'http://travel.qunar.com/p-oi706673-basongcuo', '5', '3', '1', '5', '3', '2', 3, 23430, 91, 157784, 98980, 20729, 296939, 33729, 23166, 13475, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (119, '722215', '槟榔谷', NULL, '海南 > 保亭 > 保亭景点 > 槟榔谷', '保亭', '保亭', 'https://img1.qunarzz.com/travel/d4/1507/b5/15ebbdd1a10f05.jpg;https://img1.qunarzz.com/travel/d4/1507/30/b2aa50b86af74b.jpg;https://img1.qunarzz.com/travel/d0/1607/ae/d02c95ae5b2de753.jpg;https://img1.qunarzz.com/travel/d1/1611/4f/59a759e2135f47b5.jpg;https://img1.qunarzz.com/travel/d7/1611/79/d45a791189fa97b5.jpg;https://img1.qunarzz.com/travel/poi/1803/8d/92bf80dc701d737.jpg', '4.4', '保亭景点排名第13', '4小时', '225', '18.375693', '109.677349', '16', '甘什岭槟榔谷原生态黎苗文化旅游区，地处三亚市与保亭县交界处甘什岭自然保护区境内，因景区两边森林峻峭，中间是一条连绵数公里的槟榔谷地，故称槟榔谷。槟榔谷可以说是海南民族文化活化石，现在从海南旅游的角度来介绍下槟榔谷。对于想真正了解海南最原始民族文化的游客来说，甘什岭海南原住民文化游览区无疑是您不二的选择。景区由原住民黎村、原蚩尤苗寨和原始雨林谷三大版块组成，是海南省游客满意十佳景区和十大最佳特色魅力旅游风景区之一，是海南省最丰富、最权威、最灵动、最纯正的民族文化“活体”博物馆。', '海南省保亭黎族苗族自治县甘什岭自然保护区', '0898-38661116', 'http://www.binglanggu.com', '8:00-17:30', 2752, '12月至来年4月。这时的三亚气候舒适，是个避寒的好去处，没有了夏季的炎热，也避开了台风季节。', '客车\n可从三亚汽车总站乘坐开往保亭、五指山方向的客车，直接在槟榔谷景区下车，票价在11元左右，全程历时约45分钟。三亚槟榔谷游客集散中心散客专线车：从天通酒店、亚龙湾中心酒店到槟榔谷，但是要坐专线车需要报第三方公司的一日游项目，价格根据项目而定。\n\n出租车\n可在市内乘坐出租车直接到槟榔谷景区，景区距离三亚市内约28公里。\n\n自驾\n三亚出发，驱车经田独镇，行至东线高速与中线224国道交汇处，左拐上中线224国道往五指山、保亭方向行驶10公里左右，即可到达。海口出发，沿东线高速向三亚方向行使，下东线高速后至与中线224国道交汇处，右拐上中线224国道往五指山、保亭方向行驶10公里左右，即可到达。', NULL, '2-3小时', 'http://travel.qunar.com/p-oi722215-binlanggu', '4', '', '', '4', '1', '2', 1, 47642, 204, 153714, NULL, NULL, 151539, 18784, 11625, 12461, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (120, '707359', '仙岛湖', NULL, '湖北 > 阳新 > 阳新景点 > 仙岛湖', '黄石', '阳新', 'https://img1.qunarzz.com/travel/d5/1706/ff/bc5543e1a5c308b5.jpg;https://img1.qunarzz.com/travel/d6/1706/2c/fb5cb9f06f07f5b5.jpg;https://img1.qunarzz.com/travel/d3/1706/94/4f438d9c8de82db5.jpg;https://img1.qunarzz.com/travel/d7/1706/ab/4bf71143d0d903b5.jpg;https://img1.qunarzz.com/travel/d0/1706/f9/116708d4389e7b5.jpg;https://img1.qunarzz.com/travel/d7/1706/d4/d6d71513b7b041b5.jpg', '3.3', '阳新景点排名第3', NULL, '6', '29.8017592995', '114.8548668024', '0', '阳新仙岛湖1002个岛镶嵌在4.6万亩的水面上，恰似银河星座，不是仙境胜似仙境，享有荆楚第一奇湖之美誉，是省级生态旅游风景区。仙岛湖因湖畔山崖上悬有一块\"灵通仙岛\"的古匾而得名。这里自然风光旖旎，人文古迹众多，生态野趣横生，是鄂赣边境的一处度假胜地。\n仙岛湖承雨区内全是深山峡谷，植皮繁茂，地广人稀。自然风貌在这里仿佛凝固在远古蛮时代。独特的生态环境，使仙岛湖的千娇百媚之中，更多了一分清鲜淳朴；万顷湖水晶莹剔透，常年透明度7米以上，部分水域可达10米，属国家一级水质；空气的负离子浓度每立方米1010个；日平均气温：盛夏时约25℃；降冬时约8℃。茂密的植皮，纯洁的湖水，洁净的空气，宜人的空气，宜人的气温，没有一丝喧闹，一尘污染，置身其中，顿生回归大自然之感。', '湖北省黄石市阳新县仙岛湖景区', NULL, NULL, '8:00—18:00', 13593, '四季皆宜', '客车\n武汉傅家坡长途汽车站有直接发往阳新的长途客运汽车，每天一班发车，时间为13：40，里程156。', NULL, '4小时', 'http://travel.qunar.com/p-oi707359-xiandaohu', '3', '', '4', '4', '1', '5', 0, 27919, 89, 54715, NULL, 196988, 177758, 17369, 365483, 110901, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (121, '706980', '通灵大峡谷', NULL, '广西 > 靖西 > 靖西景点 > 通灵大峡谷', '百色', '靖西', 'https://img1.qunarzz.com/travel/d5/1504/45/73e571041cf57c.jpg;https://img1.qunarzz.com/travel/d4/1710/f3/cb444cfca56ebbb5.jpg;https://img1.qunarzz.com/travel/d5/1504/42/e16a63442d0b0f.jpg;https://img1.qunarzz.com/travel/d7/1504/27/47912b15847fe9.jpg;https://img1.qunarzz.com/travel/d6/1704/27/6ab5e92fcb9870b5.jpg;https://img1.qunarzz.com/travel/d2/1504/27/124359ef52d28b.jpg', '4.4', '靖西景点排名第1', '2小时 - 3小时', '33', '23.0273425399', '106.6635536296', '8', '通灵大峡谷全长2.8公里，由通灵峡、念八峡及地下暗河、隧道贯通连接，通天彻地，灵气飘逸。峡谷绝壁千仞，幽幽深邃，巨型钟乳石高崖倒悬，古树老藤遮天蔽日，神秘莫测的洞穴、深潭星罗棋布。水帘洞、地下河及气象万千的瀑布，河涧曲回，流水潺潺，满谷青翠欲滴。通灵大峡谷原来是一个盲谷，由于地壳运动的影响，天行地运，形成一个大天坑，四周悬崖峻峭，气势壮观。高深的峡谷，清澈的溪流，苍翠的植被和188.6米的通灵大瀑布交相辉映，使整个峡谷充满了活力和灵性。', '广西百色靖西县湖润镇新灵村', '0776-6180076', 'http://www.gxtldxg.com/', '8:00-17:00', 19208, '每年的4月至6月，11月至12月为最佳旅游季节。四季皆宜', '客车\n游客可从百色火车站乘摩的先到汽车站（约3元），再在百色汽车站有长途汽车可达景区，票价10元。也可在南宁或百色市内参加旅行社组织的拼团业务，可直接到达通灵瀑布。\n自驾\n从南宁、百色出发到景区约需3个多小时车程。客车\n可从百色火车站乘摩的到汽车站，然后乘长途汽车到峡谷，票价10元。', NULL, '4小时', 'http://travel.qunar.com/p-oi706980-tonglingdaxiagu', '', '', '', '2', '2', '1', 7, 36672, 70, NULL, NULL, NULL, 10368, 36166, 16369, 5801, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (122, '9198063', '金鸡窠漂流', NULL, '安徽 > 东至 > 东至景点 > 金鸡窠漂流', '池州', '东至', 'https://img1.qunarzz.com/travel/poi/1604/42/ffa507aab98e5f7.jpg', '--', '东至景点排名第4', '0.5小时 - 1小时', '0', '30.023579', '117.174731', '0', '金鸡河位于东至县洪方乡洪方村，同春村境内，全长3.5公里，两岸青山翠绿，鸟语花香，奇树怪石，险滩水流湍急，平滩水平如镜，沿途还有金鸡窠、康阁桥，青云桥、同春茶号以及明代崇祯年间吏部尚书郑三俊故里等景点，是休闲渡假的好去处。亲身体验在深山峡谷中穿行的刺激和快乐后，晚食宿仙寓山庄或县城宾馆。传说当年郑三俊年少求学时，路过金鸡窠，只听金鸡啼鸣，路边上石门大开，郑三俊好奇进洞察看，此时“轰隆”一声，石门关上，三俊暗自流泪，自言自语说“家中尚有寡母在堂，待安顿好后，再来”，石门吱呀大开。郑三俊回家后向老母禀明此事，母亲深明大心，让三俊一定守信回去。郑三俊将家事稍作安排，即赶回金鸡窠。谁知石门紧闭，无论他如何叫唤，一直不开，后来在石门外发现一部“天书”，原来上天怜其孝心，让他苦读报国，郑三俊勤奋学习，万历二十六年（公元1598年）中进士，后任吏部尚书。', '安徽省池州市东至县洪方乡洪方村', NULL, NULL, '07:00～17:00开放；', 14763, NULL, '客车\n站名：南京--南京南---马鞍山--芜湖--铜陵东--池州--东至--彭泽-----湖口-----九江--永修--南昌\n时间：9:15 15:13/16 18:52\n站名：南京--南京南--马鞍山--芜湖--铜附东--池州--东至--彭泽--湖口--九江--瑞昌--阳新\n时间：23:48 6:05/08\n站名：黄石--鄂州--华容--武昌\n时间：11:37\n自驾车\n1、南京方向出发\n南京—（宁马高速）马鞍山—（芜大高速）过铜陵池州到东流（下高速）出收费站左转，可以看到景区路牌—仙寓山。\n南京—（宁马高速）马鞍山—（芜大高速）过铜陵池州到东至（下高速出收费站右转，可以看到景区路牌）—南溪古寨。\n2、东至县方向出发\n东至县—（231省道）洋湖丁字路口—（231省道）看到仙寓山景区路牌，丁字路口右转—仙寓山。\n东至县—上东至大桥—（206国道）黄柏—看到景区指示牌左转—到花园里乡过桥左转—南溪古寨。\n上海方向出发\n3、上海—高速公路朱家角下高速，上318国道—平望—(318国道)浙江省长兴县—(高速公路)安徽省宣城，下高速公路，转上318国道—南陵—(318国道)青阳—(318国道)池州—(318国道)殷汇丁字路口左转(吴田方向)—(殷查公路)查桥丁字路口左转—东至县。', NULL, NULL, 'http://travel.qunar.com/p-oi9198063-jinjikepiaoliu', '', '', '4', '2', '', '2', 5, 49602, 175, NULL, NULL, 74169, 18520, NULL, 18631, 11878, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (123, '711044', '南宫山', NULL, '陕西 > 岚皋 > 岚皋景点 > 南宫山', '安康', '岚皋', 'https://img1.qunarzz.com/travel/d7/1608/45/25f0b1e6aff42b5.jpg;https://img1.qunarzz.com/travel/d0/1608/24/12ad6e4a219890b5.jpg;https://img1.qunarzz.com/travel/d6/1608/ae/e659c77ff3338db5.jpg;https://img1.qunarzz.com/travel/poi/1410/22/335f990fe0b01f63ffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d0/1608/c8/e6a92c818d0a5bb5.jpg;https://img1.qunarzz.com/travel/d0/1608/da/d6d1677e964290b5.jpg', '4.5', '岚皋景点排名第1', '3小时 - 3天', '4', '32.2867125581', '109.0692209863', '3', '山上石笋林立，有石皆奇。其形怪异，其姿纷呈，吹之如螺号低鸣，遍传幽谷；击之如钟磬弱奏，清脆悦耳，音韵规整。基本建成的南宫山森林公园，运用园林艺术的传统手法，突出山林野趣，将森林、山水、石林、云雾与文学艺术相结合，寓情于景，情景交织，融森林景观、自然景观、人文景观和野、奇、秀、幽、古于一炉，吸引众多的游客前来探奇揽胜。', '陕西省安康市岚皋县花里镇', '0915-2510888', 'http://www.ngs517.com/index.html', '07:00-15:00；北线:07:00-17:00。', 15693, '安康市四季分明，冬无严寒，夏无酷暑，春暖秋凉。年平均气温15℃左右。最佳出游时间在春秋两季。', '客车：\n从安康乘坐发往岚皋县的客车；到岚皋县城后改乘通往南宫山景区车辆即可，参考车费30元/人。', NULL, '4小时', 'http://travel.qunar.com/p-oi711044-nangongshan', '4', '4', '5', '', '3', '1', 3, 27997, 153, 76887, 105968, 145943, NULL, 99927, 8759, 5091, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (124, '5860228', '锡林郭勒草原', NULL, '内蒙古 > 锡林浩特 > 锡林浩特景点 > 锡林郭勒草原', '锡林郭勒盟', '锡林浩特', 'https://img1.qunarzz.com/travel/d6/1605/95/db7b73719105e1f7.jpg;https://img1.qunarzz.com/travel/d2/1605/14/1a0fb12c7a41caf7.jpg;https://img1.qunarzz.com/travel/d3/1605/ce/3e968df8c9d632f7.jpg;https://img1.qunarzz.com/travel/d6/1605/7f/b5ea69a073a871f7.jpg;https://img1.qunarzz.com/travel/d5/1605/23/a892b40433948ff7.jpg;https://img1.qunarzz.com/travel/d4/1605/c1/92bcc86f80822af7.jpg', '4.8', '锡林浩特景点排名第1', '1天 - 2天', '39', '43.715501', '116.82265', '15', '草原旅游资源丰富、以草原类型完整而著称于世，每当盛夏来临，风光迷人的乌珠穆沁草甸草原是一片绿色的海洋，高贵的芍药花与美的山丹花争奇斗妍，片片白云在无尽的蓝天中飘游，牧人策马，牛羊游动，加上蒙古包缕缕的炊烟与缓缓行驶的勒勒车，定会使溶入大自然的游人顿感心旷神怡，美不胜收。繁花似锦的灰腾锡勒典型草原重现风吹草低见牛羊的美景，锡林河九曲十八湾象是飘落在草原上的洁白哈达，使游客留连忘返。', '内蒙古自治区锡林浩特市境内', NULL, NULL, '全天', 13675, '最佳旅游时间为每年夏季，尤其是6、7、8、9月份，但应注意防雨；秋季冬季较为寒冷，应注意保暖防寒。', '客运\n从北京六里桥汽车站有车直达。发车时间：晚上19:00和20:00各一趟。票价：180元/张，卧铺240元/张。车程约12小时。也可先从北京乘火车至赤峰后，再转乘中巴车前往，汽车车程约10小时，一路上既可看到达里诺尔湖，又可欣赏草原风光。\n\n自驾\n八达岭高速--京张高速--过戴家营收费站前行约10公里见岔路，右转奔张家口--张家口下高速前行约1公里见鑫税加油站 ，左转上110国道直行约13.8公里右转上207国道直行往锡林浩特方向--行至张北见岔路右转沿207国道直行--进入内蒙古界--三号地收费站--锡林郭勒草原', NULL, NULL, 'http://travel.qunar.com/p-oi5860228-xilinguolecaoyuan', '', '', '3', '', '', '1', 2, 31179, 184, NULL, NULL, 56201, NULL, NULL, 9690, 5052, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (125, '5808381', '加井岛', NULL, '海南 > 万宁 > 万宁景点 > 加井岛', '万宁', '万宁', 'https://img1.qunarzz.com/travel/d8/1610/a8/c93be2f73ba338b5.jpg;https://img1.qunarzz.com/travel/d4/1507/92/f418e97f096041.jpg;https://img1.qunarzz.com/travel/d5/1610/12/9c743d74207eabb5.jpg;https://img1.qunarzz.com/travel/d8/1610/cb/eb0c20841204bbb5.jpg;https://img1.qunarzz.com/travel/d9/1610/48/f19258fc93899cb5.jpg;https://img1.qunarzz.com/travel/d8/1501/ca/93883115f93cf3.jpg', '4.7', '万宁景点排名第2', '2小时 - 4小时', '71', '18.654563', '110.297835', '30', '加井岛位于海南东部万宁石梅湾内，距万宁县城30公里，有一个面积约一平方公里的海岛，拥有众多开展野外生存得天独厚的条件，一面树林繁茂，两面怪石林立，形成鼎立之势，浪花簇拥，异常壮观，而岛的另一面则是白沙细软如粉的幽静沙滩，岛上的沙滩会随季节不同发生位移，因此，也有人称之为“灵岛”。加井岛的海水清澈见底，海岸边各式贝壳历历在目。岛南岸曲折险峻，岩石众多；岛北岸和西岸地势平坦，从岛外和向外延伸100米海水仍不可没膝。走入浅海，海星、海参随处可见。岛周边海底分布有崎岖多姿的珊瑚礁，涉水登上珊瑚礁，品种繁多的水生物与色彩斑斓的热带鱼，形成蔚为壮观的海底花园。面积微小，景观多样——加井岛拥有众多原生态的天然条件；由于野外生存训练陆续在这里开展，加井岛有了另一个名字：生存岛。此外，加井岛还是《非诚勿扰2》的拍摄场地之一。', '海南东部万宁石梅湾内', NULL, NULL, '8:00-17:00', 799, NULL, '快艇\n从石梅湾旅游度假区码头乘快艇大约7分钟即到岛上。\n\n\n', '由于加井岛是无人岛，基本呈原始状态，未开发状态，故去加井岛须做好一些必备措施，注意安全：\n1、患有中耳炎，心血管病，心脏病的游客不宜参与潜水活动。\n2、去加井岛前先查清最近几天的天气，避免台风天气出游。\n3、平时穿上夏季的衣服即可，1—3月的时候最好携带几件长袖外套，以备早晚或室外用；由于阳光强烈，太阳镜、帽子为必带物品，女性可别忘了带上防晒霜；最好准备一些防蚊虫的药和晕车、晕船药。', '2-4小时', 'http://travel.qunar.com/p-oi5808381-jiajingdao', '3', '', '3', '3', '4', '5', 0, 21902, 101, 30055, NULL, 97214, 94830, 135222, 95703, 108109, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (126, '710051', '留园', 'Lingering Garden', '江苏 > 苏州 > 苏州景点 > 留园', '苏州', '苏州', 'https://img1.qunarzz.com/travel/d7/1705/71/735b9e251bc6b9b5.jpg;https://img1.qunarzz.com/travel/d1/1504/85/960fc8e46591a1.jpg;https://img1.qunarzz.com/travel/d6/1704/dc/83bfc6fd6de438b5.jpg;https://img1.qunarzz.com/travel/d5/1704/c6/5d7b19c54dabb2b5.jpg;https://img1.qunarzz.com/travel/d3/1506/30/5b583b4c117344.jpg;https://img1.qunarzz.com/travel/d8/1704/b6/cc7b314d0f76c6b5.jpg', '4.5', '苏州景点排名第11', '2小时', '218', '31.321719', '120.598937', '229', '留园位于苏州阊门外留园路338号，以园内建筑布置精巧、奇石众多而知名，与苏州拙政园、北京颐和园、承德避暑山庄并称中国四大名园。留园为中国大型古典私家园林，占地面积23300平方米，代表清代风格，园以建筑艺术精湛著称，厅堂宏敞华丽，庭院富有变化，太湖石以冠云峰为最，有“不出城郭而获山林之趣”。其建筑空间处理精湛，造园家运用各种艺术手法，构成了有节奏有韵律的园林空间体系，成为世界闻名的建筑空间艺术处理的范例。现园分四部分，东部以建筑为主，中部为山水花园，西部是土石相间的大假山，北部则是田园风光。1961年，留园被中华人民共和国国务院公布为第一批全国重点文物保护单位之一。1997年，包括留园在内的苏州古典园林被列为世界文化遗产。2001年，留园作为苏州园林（拙政园、虎丘、留园）扩展景区成为国家5A级旅游景区。', '江苏省苏州市金阊区留园路338号', '0512-65579466', 'http://www.gardenly.com', '7:30-17:00', 2468, '四季皆宜。最佳的时间是3、4、5月和9、10、11月。', '留园路线\n公交\n以下路线请到留园站下车，下车即到。\n游1路、85路、317路、933路\n以下路线请到留园路站下车，沿广济南路向南走30m至留园路后向西走约300m。\n夜5路、7路、34路、36路、44路、64路、70路、85路大站快车、85路、161路、\n304路、315路、317路、318路、406路、415路、522路、800路、921路\n\n地铁\n乘地铁二号线，石路站下车从1号出口出站，沿广济南路向北走50m至留园路后向西走约300m。\n\n艺圃路线\n公交\n以下路线请到皋桥站下车\n31路、54路、501路\n以下路线请到儿童医院景德路院区站下车\n33路、88路、204路、262路、301路、313路、933路、游1路', '如果是雨天，你就可循着园中长廊而游，不湿衣襟而获雨中游园的雅趣。', '1小时', 'http://travel.qunar.com/p-oi710051-liuyuan', '', '', '1', '', '1', '5', 6, 25976, 192, NULL, NULL, 13585, NULL, 5496, 262067, 95842, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (127, '7476837', '月亮湾', NULL, '广东 > 阳西 > 阳西景点 > 月亮湾', '阳江', '阳西', 'https://img1.qunarzz.com/travel/poi/1712/46/0ad5fc8bccab0c37.jpg;https://img1.qunarzz.com/travel/poi/1505/9e/65aa94f7e8bf0.jpg;https://img1.qunarzz.com/travel/d9/1505/f4/de58dfb1896c31.jpg;https://img1.qunarzz.com/travel/d2/1505/3f/a0bb7d6d009945.jpg;https://img1.qunarzz.com/travel/d8/1505/38/0b028c464d643a.jpg', '4.7', '阳西景点排名第50', '1小时 - 3小时', '4', '21.521484', '111.510208', '3', '东方月亮湾位于广东省阳江市距阳西西南部儒洞，地处沙扒镇下窝旅游开发区，东起上洋镇的福湖岭（海岛），沿月亮湾海滨西至北额岭海滨、青州岛净业寺神秘湾。海岸线长近8公里(16里)，总面积33平方公里；如果再加上沙扒湾的3公里(6里)，那么沙扒湾+东方月亮湾上洋海湾的总长度是10.5公里(21里)。7.8公里月亮湾海滩呈弧形展开，海滩两侧悬崖峭壁，礁石奇立，犹如世外桃源，犹如一弯月落的洁白沙滩，非常开阔。故名，其长度之长比起同在阳江海陵岛并且载入吉尼斯记录的十里银滩还长上6.5里(3.25公里)。是全方位、多层次、复合型旅游度假区。', '阳江市阳西县沙扒镇书村南面', '0662-5863911', 'http://www.moon-bay.com.cn/', '8:30-18:30', 9444, '四季皆宜', '码头\n防城港北码头坐渡船至月亮湾。\n公交\n防城港市汽车站、防城汽车站均有到江山半岛的旅游专线车,可乘车至月亮湾下车即到。', NULL, NULL, 'http://travel.qunar.com/p-oi7476837-yueliangwan', '1', '', '2', '1', '2', '1', 1, 44174, 147, 6430, NULL, 24769, 9193, 11821, 17171, 6986, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (128, '5136318', '通湖草原', NULL, '内蒙古 > 阿拉善左旗 > 阿拉善左旗景点 > 通湖草原', '阿拉善盟', '阿拉善左旗', 'https://img1.qunarzz.com/travel/d7/1610/55/c715f408e4c6a3b5.jpg;https://img1.qunarzz.com/travel/d0/1509/81/443a966c5423f9.jpg;https://img1.qunarzz.com/travel/d2/1509/7c/26e9854d48e4e7.jpg;https://img1.qunarzz.com/travel/d2/1601/d1/85d63d22c1a450f7.jpg;https://img1.qunarzz.com/travel/d1/1605/5d/ce6a669564c9d1f7.jpg;https://img1.qunarzz.com/travel/d4/201404/14/80ee363a5a688ccaddb12cfb.jpg', '4.5', '阿拉善左旗景点排名第9', '3天', '41', '37.6036141575', '104.939311872', '7', '通湖草原位于内蒙古和宁夏交界处的腾格里沙漠腹地，东距阿左旗巴彦浩特200公里，南离宁夏中卫市26公里，西望甘肃河西走廊，与“AAAAA”级旅游胜地沙坡头——沙沙相连，直越8.3公里便可到达。这里是古丝绸之北路要塞。古商道、古盐道、大盛魁古驼道。沙漠湖泊、沙漠绿洲、沙漠草原、沙山岩画，还有古长城、古战场、古买卖城遗址；流传千古的故事和传说，以及好客的草原牧民。因此，被誉为沙漠中的“伊甸园”。', '阿拉善盟阿拉善左旗腾格里沙漠腹地', '138****2930', 'http://www.tonghucaoyuan.com/tonghu.asp', '9:00-18:00', 19317, '夏季为最佳出游时间。夏天的腾格里沙漠在阳光的照射下一片金黄，湖泊周边青草荡漾，气温适宜，能最好的观赏到沙漠风光。', '没有直达通湖的车，到中卫后可打车前往。\n自驾\n银川方向：银川-中卫-沙坡头大道（迎水桥铁厂丁字路口，路口有标识牌）-（向北）上迎闫公路（26公里处）-通湖草原。\n内蒙古方向：内蒙乌海220公里-吴忠通湖草原旅游区。', '注意防晒，同时注意防沙尘。\n因气候干燥，润喉片、饮用水是必备的物品。', NULL, 'http://travel.qunar.com/p-oi5136318-tonghucaoyuan', '', '', '', '', '1', '1', 6, 32941, 98, NULL, NULL, NULL, NULL, 10374, 11786, 6775, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (129, '3864517', '年保玉则', NULL, '青海 > 果洛 > 果洛景点 > 年保玉则', '果洛', '果洛', 'https://img1.qunarzz.com/travel/d1/1701/3f/d89a674b2fe76cb5.jpg;https://img1.qunarzz.com/travel/d2/1509/c2/0eeb85df8c3af1.jpg;https://img1.qunarzz.com/travel/d2/1603/67/9cf67215b6fd9ff7.jpg;https://img1.qunarzz.com/travel/d3/1509/15/c422db85af6ece.jpg;https://img1.qunarzz.com/travel/d7/1607/7e/6ba99e65ff138b9a.png;https://img1.qunarzz.com/travel/d8/1706/aa/ff99acd638f871b5.jpg', '4.9', '果洛景点排名第1', '1天 - 2天', '110', '33.2927336775', '101.1116141756', '60', '年保玉则是青海果洛草原的一座神山，主峰海拔5369米。年保玉则的神秘，莫过于变幻莫测的天气，七八月旬，也会领略到春夏秋冬四季之变迁。这个地区处在亚热带季风高原高峰气候区域的边缘，山麓中生长着千百年的云杉、 松柏，成林蔽野，古木参天。山坡上有虫草、贝母、大黄等名贵药材。山脚下，野牦牛、藏羚羊、岩羊、白唇鹿、黑熊等珍贵动物，成群出没于森林草原之中。这里可以感受到真正的大自然。', '青海省果洛州久治县', '0975-8332000', 'http://www.nbyzwhzx.com/', '全天开放', 7752, '6-7月是年保玉则的花季，气候舒适。', '年保玉则景区没有公共交通，一般前来游玩的都是从西宁、兰州等地包车而来。', NULL, NULL, 'http://travel.qunar.com/p-oi3864517-nianbaoyuze', '', '', '5', '4', '4', '3', 9, 42184, 142, NULL, NULL, 208386, 74170, 55910, 87095, 20982, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (130, '5740690', '念青唐古拉山', NULL, '西藏 > 那曲 > 那曲景点 > 念青唐古拉山', '那曲', '那曲', 'https://img1.qunarzz.com/travel/poi/1412/bf/bf43bee7d2da28a8cdb.jpg;https://img1.qunarzz.com/travel/d6/1803/8b/25c212032ef689b5.jpg;https://img1.qunarzz.com/travel/d2/1803/c0/0f968af41743c9b5.jpg;https://img1.qunarzz.com/travel/d9/1803/bf/8293c067193384b5.jpg;https://img1.qunarzz.com/travel/d3/1705/9c/e1fde6799e6089b5.jpg;https://img1.qunarzz.com/travel/d2/1704/8b/565da00090579db5.jpg', '4.7', '那曲景点排名第4', NULL, '78', '30.392642', '90.566551', '105', '念青唐拉大雪山，北沿是纳木错，山顶最高处海拔7117米，终年白雪皑皑，云雾缭绕，雷电交加，神秘莫测，如同头缠锦锻，身披铠甲的英武之神，高高地矗立在雪山，草原和重重峡谷之上。 念青唐古拉山脉是中国青藏高原主要山脉之一。横贯西藏中东部，为冈底斯山向东的延续。在西藏古老的神话里，在苯教或藏传佛教的万神殿中，在当地牧羊人和狩猎者的民歌和传说里，念青唐古拉山和纳木错不仅是西藏最引人注目的神山圣湖，而且是生死相依的情人和夫妇，念青唐古拉山因纳木湖的衬托而显得更加英俊挺拔，纳木错湖因为念青唐古拉山的倒映而愈加绮丽动人，吸引着成千上万的信徒、香客、旅游者前来观瞻朝拜，成为世界屋脊上最大的宗教圣地和旅游景观。', '西藏自治区拉萨市当雄县', NULL, NULL, '全天', 20311, '7-9月是念青唐古拉山最美的季节，除了蓝天白云，还有花有草。', '念青唐古拉山位于拉萨市当雄县，建议自驾前往，也可以包车。', '念青唐古拉山昼夜的温差较大，晚上风力强劲，常会有大雨或冰雹。在夏天，晚上也需要穿上厚厚的羽绒服。', NULL, 'http://travel.qunar.com/p-oi5740690-nianqingtanggulashan', '5', '', '4', '', '3', '3', 7, 32100, 206, 270748, NULL, 59882, NULL, 58447, 66002, 26029, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (131, '9487062', '三娘湾国际海豚公园', NULL, '广西 > 钦州 > 钦州景点 > 三娘湾国际海豚公园', '钦州', '钦州', 'https://img1.qunarzz.com/travel/poi/1607/2b/ff66d34820766db.jpg', '0', '钦州景点排名第13', NULL, '1', '21.628505', '108.764027', '0', '当然这里最大的看点要数”海上大熊猫“中华白海豚。白海豚，国家一级保护动物，被誉为“海上大熊猫”、“海上国宝”。三娘湾正是中华白海豚之乡，这里的海域环境清洁，吸引了一千余头野生中华白海豚长年栖居于此。', '广西壮族自治区钦州市钦南区犀牛脚镇三娘湾景区', '0777-3813879;0777-3816068', NULL, '全天', 17798, NULL, '票价\n在钦州汽车总站坐直达三娘湾的汽车，票价10元/人', NULL, NULL, 'http://travel.qunar.com/p-oi9487062-sanniangwanguojihaitun', '', '', '', '3', '2', '1', 7, 43946, 95, NULL, NULL, NULL, 98399, 20607, 22419, 5916, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (132, '3864493', '祁连山', NULL, '甘肃 > 张掖 > 张掖景点 > 祁连山', '张掖', '张掖', 'https://img1.qunarzz.com/travel/d6/1609/76/f186875bccb5cbb5.jpg;https://img1.qunarzz.com/travel/d3/1609/a3/ee26da3ae0acadb5.jpg;https://img1.qunarzz.com/travel/d6/1610/aa/3d756e561f61f2b5.jpg;https://img1.qunarzz.com/travel/d3/1704/2c/3f5b8e7d9c27ebb5.jpg;https://img1.qunarzz.com/travel/d3/1702/3b/0b6a88d58078a0b5.jpg;https://img1.qunarzz.com/travel/d1/1708/ff/0524159aed6fbdb5.jpg', '4.7', '张掖景点排名第2', '3天 - 6天', '221', '39.046925', '98.892255', '331', '祁连山的平均山脉海拔在4000米～5000米之间，高山积雪形成的硕长而宽阔的冰川地貌奇丽壮观。海拔高度在4000米以上的地方，称为雪线，一般而言，冰天雪地，万物绝迹。然而，祁连山的雪线之上，常常会出现逆反的生物奇观。在浅雪的山层之中，有名为雪山草甸植物的蘑菇状蚕缀，还有珍贵的药材――高山雪莲，以及一种生长在风蚀的岩石下的雪山草。因此，雪莲、蚕缀、雪山草又合称为祁连山雪线上的“岁寒三友”。', '肃南裕固族自治县其他东北部与甘肃省西部边', '0941—6321626', 'http://www.qilianshan.com.cn/client/qlsbhq/html/default.html', '全天开放', 7556, '7月。游祁连山，最好的时节是7月天。当广东已达35℃高温时，祁连山才是“春天”，那时油菜花如大地织锦一般，铺满山坡。按照往年经验，祁连山区的油菜花是在七月十五日到二十五日开放，而且可能是由于长在高原，色泽特别艳丽，非常养眼。', '祁连山脉幅员辽阔，游玩时多从兰州、西宁、张掖等地包车或自驾车前往。汽车\n可在西海镇乘坐班车到达，票价40元左右。或从张掖乘坐班车到达，票价50元左右。', '祁连山周边区域气候多变，早晚温差大，有些地方比较干燥，要备好御寒衣物和防尘、防晒等装备。主要景点\n天祝三峡国家级森林公园\n天祝“三峡”地处甘肃省天祝藏族自治县西南部，深处祁连山腹地，东西横，向63公里，南北纵向68公里，总面积162万亩，占全县土地总，面积的15%。境内山高谷深，地貌奇特，山势峻险，怪石嶙峋。民族风情独特，宗教色彩浓厚。境内海拔2040～4446米，年均气温0.2～0.4℃，聚居着藏、汉、回、蒙古族等多个民族。\n\n祁连冰沟河省级森林公园\n祁连山保护区旅游资源包括自然旅游资源和人文旅游资源2大类6个类型，景点遍布祁连山区。以生态旅游为主的森林公园有天祝三峡国家级森林公园、祁连冰沟河省级森林公园、山丹焉支山省级森林公园、肃南马蹄寺省级森林。\n\n山丹焉支山省级森林公园\n焉支山森林公园位于山丹县城南50公里处的大黄山林区，是以自然风光为依托的游览区，被评为省级森林公园。　园内有唐玄宗天宝年间建的钟山寺旧址，历史上有名的“万国博览会”遗址，复建的宗教胜地玉皇殿、大佛殿。唐代诗人李白曾在此留下“虽居焉支山，不到溯雪寒”的绝句。\n\n肃南马蹄寺省级森林公园\n马蹄寺风景名胜区位于甘肃省河西走廊中段，地处张掖市肃南裕固族自治县马蹄藏族乡境内，距张掖市65公里，肃南县城165公里，平均海拔2580米左右。马蹄寺风景名胜区区位优势明显，被誉为人间仙境的马蹄寺风景名胜区集宗教文化、石窟艺术、民族风情、自然风光为一体。\n\n白云山古时为丽水八景之一，1990年列为省级森林公园。有灵山寺、灵鹫禅寺、钟鼓楼等景观，并开发了白云竹径、水帘藏宝、绿海万象等景点景观。', NULL, 'http://travel.qunar.com/p-oi3864493-qilianshan', '', '3', '5', '', '4', '2', 7, 43429, 98, NULL, 62778, 204100, NULL, 153888, 48277, 12009, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (133, '708046', '镜泊湖', NULL, '黑龙江 > 牡丹江 > 牡丹江景点 > 镜泊湖', '牡丹江', '牡丹江', 'https://img1.qunarzz.com/travel/d1/1508/b6/f66aaae9476d78.jpg;https://img1.qunarzz.com/travel/d2/1508/8d/fadd9b0243add5.jpg;https://img1.qunarzz.com/travel/d9/1509/57/ac7c0c907de946.jpg;https://img1.qunarzz.com/travel/d6/1509/99/80a082337e36a5.jpg;https://img1.qunarzz.com/travel/d3/1507/63/687a6ca136b500.jpg;https://img1.qunarzz.com/travel/d6/1508/c1/193c0c9e78576.jpg', '4.3', '牡丹江景点排名第4', NULL, '112', '44.0501824091', '128.97405333', '79', '镜泊湖位于黑龙江省牡丹江市的西南面，是世界最大的高山堰塞湖。\n镜泊湖是5000年前经多次火山喷发，熔岩阻塞牡丹江古河床而成的火山熔岩堰塞湖。湖水南浅北深，湖面海拔350 米，最深处超过60米，最浅处则只有1 米；湖形狭长，南北长45公里，东西最宽处6 公里，面积约91.5平方公里。\n景区总面积1214平方公里，容水量约16亿立方米。镜泊湖以天然无饰的自然之美而著称，以气势雄浑的吊水楼瀑布、奇丽壮观的地下森林、盛衰疑迷的渤海古国遗址为主要景区。还有珍珠门、道士山、老鹳山、大孤山、小孤山、白石砬子、城墙砬子等景点。现为国家重点风景名胜区。\n镜泊湖花红水碧，鱼跃鸟飞，岚影沉浮，霞光闪耀……一派北国大自然的天姿美色，令游人赞叹不已，这便是镜泊湖之夏。这里空气清新，环境优雅，每年夏季游人如织，来自海内外的游人，尽情享受大自然赐予的天光水色，同时这里还是我国北方著名的避暑胜地，被誉为北方的西湖。在湖的北岩半岛上，有一些小别墅和旅游设施，这就是镜泊湖的游览中心――镜泊山庄。除了镜泊山庄以外，湖周很少有别的建筑物，只有山峦和葱郁的树林，呈现一派秀丽的大自然风光，而这正是镜泊湖的诱人之处。在镜泊山庄眺望，只见湛蓝的湖水，展向天边，一平如镜。', '黑龙江省牡丹江宁安市', '0453-6996721', 'http://www.jingpohu.gov.cn/', '全天', 3889, NULL, '汽车\n1、牡丹江火车站前每天有大巴直达主景区，一般是上午发车、下午返回，单程约25元，车程约2小时。\n2、可从牡丹江长途客运站坐车至东京城镇（约15元），在东京城客运站坐车至主景区北门（约7元），这两段班次较多，全程约需2.5小时。\n\n景区交通\n镜泊湖主景区北门有旅游班车站点，这里有车去火山口地下森林的区间车（往返约40元，淡季时一般会停运），这里也可以拼车（私车或出租车）去地下森林，也是往返每人40元。这里还有去镜泊峡谷的观光小火车（距离约500多米），另外也有去东京城镇、宁安市、牡丹江市的班车。', '镜泊湖一年四季都有景镜泊湖一年四季都有着各自独特分明的景色。春天，满山达子香，满湖杏花水；夏天，绿荫遮湖畔，轻舟逐浪欢；秋天，五花山色美，果甜鱼更肥；冬天，万树银花开，晶莹透琼台。除此之外，还有各项娱乐措施，冬季景区开辟冰雕、雪雕、滑冰、滑雪、滑道、冰上球类、冰上捕鱼、马拉爬犁等游乐项目。体验东北民俗，品尝山珍野味，冰雪风光皎洁鲜亮，冰雪游乐丰富多彩。', '1天', 'http://travel.qunar.com/p-oi708046-jingbohu', '5', '4', '4', '2', '3', '5', 4, 35310, 143, 160388, 64199, 196902, 11497, 52457, 303225, 106883, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (134, '710129', '四明山国家森林公园', NULL, '浙江 > 四明山 > 四明山景点 > 四明山国家森林公园', '四明山', '四明山', 'https://img1.qunarzz.com/travel/d5/1604/2f/1ae0f09c00be4f7.jpg;https://img1.qunarzz.com/travel/d7/1604/2c/d41b871d688bbcf7.jpg;https://img1.qunarzz.com/travel/d9/1604/e8/ce892b1bc0a6ef7.jpg;https://img1.qunarzz.com/travel/poi/1706/c9/35ad8b69402d9937.jpg;https://img1.qunarzz.com/travel/d5/1604/61/ebde32f5691eeff7.jpg;https://img1.qunarzz.com/travel/d1/1607/2e/b8d11d2466bd069a.jpg', '4.3', '四明山景点排名第1', '4小时', '17', '29.742041', '121.126022', '2', '四明山森林公园位于浙东四明腹地的余姚四明山镇，为省级森林公园。公园占地面积6.9万亩，林区内古木参天，千峰竞翠，湖泊连绵，奇岩众多，生态旅游独呈风采。\n主要景点有：以中国水帘洞之最著称的鹁鸪岩，有山中明珠--仰天湖、东谷湖和下坑湖，有三面环壁、泉水叮咚的白龙潭，有横贯深崖中间的石梁--仙人桥，还有第二庐山之称的商量岗等。商量岗主峰奶部山海拔915米，是全市海拔最高点，登临顶峰，纵目四望，林海茫茫，松涛万顷。\n在炎热的夏季，这里是人们避暑的好去处，可与江西庐山相媲美。1935年，蒋介石返乡曾与夫人宋美龄在这里避暑，并建造了消夏别墅--中洋房，别墅旁边还有一个人工湖，幽雅恬静、水平如镜；1937年夏季，蒋经国夫妇和被幽禁中的张学良亦先后来此游览。\n如今，中洋房依然兀立，残阶颓垣记载着逝去的沧桑岁月。游人们在游览之余，可到林场开设的\"四明山庄\"小憩几日，当属避暑休闲良策。', '浙江省宁波市余姚市四明路四明山镇甘竹岭', '0574-62340889', NULL, '8:00-17:00', 16275, '四季皆宜。', '汽车\n1、宁波汽车南站13:35分有一班中巴车直达四明山镇（2个小时），再转到四明山国家森林公园。\n2、宁波汽车南站上午7:00左右赶到奉化江口，乘坐奉化到余姚的中巴到四明山镇。\n3、从余姚走，宁波汽车中巴南站坐余姚中巴车到余姚南站（1个半小时），然后在余姚南站转乘四明山方向的中巴车即可（约2个小时），余姚南站到四明山的始发时间是6:40，末班是下午14:30。', NULL, NULL, 'http://travel.qunar.com/p-oi710129-simingshanguojiasenlin', '', '3', '3', '', '2', '2', 9, 26262, 63, NULL, 76949, 57467, NULL, 17573, 15494, 10411, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (135, '9198850', '海南农垦万嘉果热带植物园', NULL, '海南 > 定安 > 定安景点 > 海南农垦万嘉果热带植物园', '定安', '定安', 'https://img1.qunarzz.com/travel/poi/1604/c8/eb01034a84c437f7.jpg;https://img1.qunarzz.com/travel/d5/1607/a1/6d562b673971009a.jpg', '--', '定安景点排名第7', '1小时 - 2小时', '0', '19.455127', '110.45215', '1', '海南农垦万嘉热带植物园位于海南东线高速速公路黄竹出口处，为国家AAA级旅游景区，是一个集生态、销售、观光、度假、休闲为一体的热带生态农业观光综合景区。它充分利用了景区现有旅游资源优势，将海南部分热带作物资源进行优化组合和利用，开发出富有山、水、园等特色的旅游资源产品，是一个集种植、养殖、酒店、住宿等为一体的多元化观光基地，园区一年四季花果飘香，热带水果、经济作物品种齐全，应有尽有。', '海南省定安县黄竹镇黄竹政府旁', NULL, NULL, '8:30-18:00', 9211, NULL, '汽车\n海口汽车东站乘坐海口—黄竹的汽车，在【黄竹三角路桥】下车。', NULL, NULL, 'http://travel.qunar.com/p-oi9198850-hainannongkenwanjiaguo', '', '', '', '5', '', '1', 3, 35382, 126, NULL, NULL, NULL, 203695, NULL, 11735, 6255, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (136, '3864491', '原子城', 'Atomic City', '青海 > 海晏 > 海晏景点 > 原子城', '海北', '海晏', 'https://img1.qunarzz.com/travel/poi/201406/12/0bce3a61c154fcb2ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/1803/4b/fe7ac961c1e54137.jpg;https://img1.qunarzz.com/travel/d2/1706/7b/6aa6a8e84702bfb5.jpg;https://img1.qunarzz.com/travel/d2/1706/73/e1a529842ea4e7b5.jpg;https://img1.qunarzz.com/travel/d3/1705/73/fd8395462425c8b5.jpg;https://img1.qunarzz.com/travel/d3/1705/5d/5f6917a8c1b1b4b5.jpg', '3.9', '海晏景点排名第1', '4小时', '55', '36.9653126848', '100.906216556', '2', '高原原子城位于西海镇。20世纪50年代，中国在这里创建了第一个核武器研制基地和生产基地。在1700平方公里的神秘禁区内，研制组装成功了中国首枚原子弹和氢弹。如今终于向世人揭开它神秘的面纱，成为中国独具特色的旅游奇观。基地展览馆是利用原爆轰试验而修建的，又称靶场。它是为研制原子弹、氢弹而进行各项爆轰模式和穿甲弹试验。基地展览馆共有七个展室，第一展室：基地创建的背景；第二展室：基地创建的过程；第三展室：两弹爆炸成功；第四展室：辉煌成就；第五展室：光荣退役；第六展室：化剑为犁、和平利用；第七展室：西海新貌。展室通过大量图片生动地再现了基地的建设、发展，吸引着大批中外旅游者来这里观光探秘。', '海北藏族自治州海晏县西海镇', '0970-8644379', 'http://qhyzcjng.qhhb.gov.cn/html/3662/list.html', '1、周二至周日9点至17点，\n2、周一（除国家法定节假日外）闭馆修整。参观凭有效证件（身份证、驾驶证、军官证、导游证）领取当日有效参观券，凭券参观。', 16729, '四季皆宜', '汽车\n西宁长途客运站有直达西海镇的班车，23元/人，2个小时左右，西宁长途汽车站位于建国路北头，距离火车站以南约600米。', NULL, NULL, 'http://travel.qunar.com/p-oi3864491-yuanzicheng', '', '1', '', '3', '2', '3', 1, 48122, 81, NULL, 23435, NULL, 27506, 16046, 48724, 23198, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
