INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (139, '712301', '马蹄寺', 'Matisi Grottoes', '甘肃 > 张掖 > 张掖景点 > 马蹄寺', '张掖', '张掖', 'https://img1.qunarzz.com/travel/d3/1411/57/57fc9027bc826a5d213a9cdb.jpg;https://img1.qunarzz.com/travel/d1/201407/03/4ef2b4610061a240ddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/1803/fe/e33de068f0f82937.jpg;https://img1.qunarzz.com/travel/d2/1803/a1/526d12c816d622b5.jpg;https://img1.qunarzz.com/travel/d5/1803/26/70240048239826b5.jpg;https://img1.qunarzz.com/travel/d4/1803/c3/7f54d0c5695a17b5.jpg', '4.3', '张掖景点排名第4', '3小时', '139', '38.509348', '100.43466', '163', '马蹄寺位于肃南裕固族自治县境内，北距张掖市65公里，是集石窟艺术、祁连山风光和裕固族风情于一体的旅游区。石窟由胜果寺、普光寺、千佛洞、金塔寺、上、中、下观音洞七处组成，共有70余处窟龛，始建于北凉。马蹄寺因传说中的天马在此落有马蹄印而得名。传说中的马蹄印迹现存于普光寺马蹄殿内，成为镇寺之宝。马蹄寺石窟的独特之处在于千佛洞有500多个摩崖佛塔窟龛，规模宠大；金塔寺中的大型肉雕飞天古朴稚雅，为国内仅有；普光寺的三十三天洞，上下五层二十一窟，宝塔形排列，内有佛殿，外有回廊，共开内外窟龛达49孔之多，造型奇特。马蹄寺环境秀丽，山青、水秀、峰奇、洞异堪称四绝。游客在这里旅游还可以住裕固族帐逢，喝青稞酒，吃手抓肉，体验草原游牧民族独特的生活方式和文化特色。', '位于肃南裕固族自治县境内', '0936-8891610', 'http://www.mtsly.cn/', '8:30-18:00', 7388, '游览最好选择在秋季，路边的景色变化大，石窟也赋予了特殊的活力，夏天也可以，但是避开雨天。春天风沙大，看石窟不太适合。', '汽车南站有直达景区的一日游班车，往返21元每人，人少会停开。也可以乘坐往南古方向的车，在马蹄河下，往返19元每人，然后打车上山，一般到景区停车场要20-30元，如果从市区包车前往大概要120元。', NULL, '2-3小时', 'http://travel.qunar.com/p-oi712301-matisi', '', '2', '1', '3', '3', '4', 1, 26428, 85, NULL, 29782, 17925, 62332, 77354, 139746, 41232, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (140, '5859984', '赛里木湖', 'Sayram Lack', '新疆 > 博尔塔拉 > 博尔塔拉景点 > 赛里木湖', '博尔塔拉', '博尔塔拉', 'https://img1.qunarzz.com/travel/d4/1805/b9/3378a090e32b26b5.jpg;https://img1.qunarzz.com/travel/d8/1805/3c/577002576670a5b5.jpg;https://img1.qunarzz.com/travel/d3/1805/56/9daf3276f47e69b5.jpg;https://img1.qunarzz.com/travel/d9/1805/7a/08c9ed875530dab5.jpg;https://img1.qunarzz.com/travel/d4/1805/4c/9db35975e200d5b5.jpg;https://img1.qunarzz.com/travel/d4/1805/36/0a14b1a6ece6acb5.jpg', '4.8', '博尔塔拉景点排名第1', '3天 - 6天', '287', '44.512617', '81.150268', '88', '赛里木湖像一颗璀璨的蓝宝石高悬于西天山之间的断陷盆地中，湖中群山环绕，天水相映；隆冬季节赛里木湖瑞雪飞舞，银装素裹，雪涌水凝，葱萃的苍松与洁白的雪被交相辉映，构成一派北国林海雪原的壮阔景色；春夏季节，湖畔广阔的草地上，牧草如茵、黄花遍地、牛羊如云、牧歌悠悠、毡房点点、构成一幅充满诗情画意的古丝路画卷，可以使人们充分领略回归自然的浪漫情怀与寒外独特的民族文化。赛湖长期以来还流传着湖怪、湖心风洞、旋涡与湖底磁场等传说，这给美丽的赛里木湖又蒙上了一层极富想象力的神秘面纱。有人总结了“赛里木湖十景”，即“金缎镶边”、“科山观松”、“净海七彩”、“湖心情侣”、“激浪拥堤”、“绿海珍珠”、“乌孙古冢”、“富士东峙”、“赛湖跃金”和“松头雾瀑”。赛里木湖是青年情侣结婚度假的绝佳圣地。', '博乐市东北48公里怪石沟景区内', '0909-7688552', 'http://www.xjslmh.com/defaults/Default.aspx', '全天开放', 15779, NULL, '前往赛里木湖及伊犁等地游玩，大多是从乌鲁木齐开始包车。常规线路最短是七天左右，将伊犁河谷内的巴音布鲁克景区、喀拉峻草原、昭苏草原、特克斯、那拉提草原、霍城薰衣草等景点一并游玩，也是游玩伊犁最经典的方式。乌鲁木齐旺季包小车的价格约为每车每天500元左右。', '1. 在当地骑马时，最好提前将全程价格讲好，不然有时会出现时长大幅超出马夫介绍的时间，被加收费用。\n2. 赛里木湖区域气温较低，即使是夏季，夜晚时也会比较冷，建议多带衣物保暖。', '1-2天', 'http://travel.qunar.com/p-oi5859984-sailimuhu', '', '', '4', '4', '', '4', 9, 28244, 130, NULL, NULL, 124866, 117576, NULL, 106786, 49084, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (141, '9636519', '钟祥莫愁湖', 'Mochou Lake', '湖北 > 钟祥 > 钟祥景点 > 钟祥莫愁湖', '荆门', '钟祥', 'https://img1.qunarzz.com/travel/d5/1701/15/74d0738369713fb5.jpg;https://img1.qunarzz.com/travel/poi/1705/29/b6d9f4906a226d37.jpg;https://img1.qunarzz.com/travel/d7/1701/86/f7bacc50bf5b9fb5.jpg;https://img1.qunarzz.com/travel/d6/1701/b1/16fe68d2f18de2b5.jpg', '--', '钟祥景点排名第16', NULL, '2', '31.19158', '112.61789', '1', '莫愁女是楚国第一美女，楚国歌舞一代宗师。她貌若芙蓉，身如柳枝，可谓美貌无双；她能歌善舞，唱起歌来如流水行云，跳起舞来如嫦娥奔月；她在屈原、宋玉的指导下完美演绎了《阳春》、《白雪》，并传唱入歌，将楚国歌舞艺术推向颠峰；她与青年才俊王襄哥青梅竹马，倾心相爱，产生了一段可歌可泣的爱情。后来，楚襄王欲召其进宫，强纳为妃，但她品性高洁，不为财富所动，不为淫威所屈，毅然投江，以保名节，被誉为绝世“圣女”。', '荆门钟祥市境内', NULL, NULL, NULL, 16234, NULL, '市内可乘坐6路公交车到达莫愁湖景区', NULL, NULL, 'http://travel.qunar.com/p-oi9636519-zhongxiangmochouhu', '1', '', '2', '4', '3', '5', 5, 41937, 186, 8189, NULL, 41418, 165371, 85933, 309962, 84316, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (142, '9206744', '九天大峡谷', NULL, '山东 > 莱芜 > 莱芜景点 > 九天大峡谷', '莱芜', '莱芜', 'https://img1.qunarzz.com/travel/poi/1605/92/5d0b6dc01a92eef7.jpg', '--', '莱芜景点排名第10', NULL, '0', '36.390869', '117.421331', '0', '九天大峡谷位于莱芜市境内，地处泰山山脉的连绵群山之中，是山东省海拔最高的村庄，共73户，256口人，山场面积23000亩。这里有高山之巅的淳朴民俗风情，有古老原始的深谷幽林，有高峡平湖的秀美景致。在蓝天白云、青山碧水的映衬下，整个村落如云中山寨、人间仙境，故被誉为“天上人家”。莱芜天上人家位于齐鲁大峡谷旅游区中部的群山环抱之中，区内自然旅游资源丰富，主要有“三谷两湖一天村”，“三谷”即九天大峡谷、王石门大峡谷、槐花谷，“两湖”即九天湖、九龙湖，“天村”即王石门村。九天大峡谷下至龙尾湖，上至王石门村，全长约10公里。谷内呈原生状态，飞瀑、流泉、奇石、险峰、密林，多姿多彩，适宜探险、观光、避暑等。', '山东省莱芜莱城区王石门', NULL, NULL, '10:00-17:00', 13332, NULL, '适合自驾或打车前往。', NULL, NULL, 'http://travel.qunar.com/p-oi9206744-jiutiandaxiagu', '4', '4', '3', '1', '5', '2', 6, 44955, 181, 43294, 123092, 55692, 7571, 167373, 42640, 10030, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (143, '9205432', '一马树森林公园', NULL, '吉林 > 双辽 > 双辽景点 > 一马树森林公园', '四平', '双辽', 'https://img1.qunarzz.com/travel/poi/1605/44/3b69a09798c97ef7.jpg', '--', '双辽景点排名第1', NULL, '0', '43.520841', '123.623274', '0', '一马树森林公园是以丰富针叶林资源为依托的城市近郊省级森林公园。总占地面积13500亩，森林覆盖率95.7%。园内动植物种类丰富，针、阔、乔、灌各类植物遍布其中，30余种野生动物在此繁衍生息。公园内有以森林氧吧、森林浴场、动、植物观赏为主的养生康体休闲区；以滑索、沙地摩托、CS野战、赛狗场等17个游乐项目为主的综合游乐区；以吃农家风味菜肴、干乡村田园农活、住农家土炕等充满农村乡土气息生活为主的农家乐景区；以“篝火晚会”为载体的民族歌舞表演，独具风味的“烤全羊”、“风干牛肉”等蒙式餐饮为主的蒙族民俗风情区和以观赏科尔沁沙地原始地貌、沙地探险、沙滩娱乐为主的白沙坨景区。一马树森林公园林、石、水交相辉映，情、景、韵浑然一体，是人们休闲度假、观光旅游的理想场所。', '吉林省四平双辽市红旗街', NULL, NULL, '8:00-17:00', 17459, NULL, '适合自驾或打车前往。', NULL, NULL, 'http://travel.qunar.com/p-oi9205432-yimashusenlingongyuan', '3', '', '3', '4', '4', '1', 8, 48963, 135, 46131, NULL, 86059, 114128, 81709, 6421, 6530, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
