INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (145, '7628125', '黄河大峡谷', NULL, '宁夏 > 青铜峡 > 青铜峡景点 > 黄河大峡谷', '吴忠', '青铜峡', 'https://img1.qunarzz.com/travel/poi/1501/35/7cfe7dc4c33a09.jpg;https://img1.qunarzz.com/travel/poi/1803/fd/1da30cf43f62e637.jpg;https://img1.qunarzz.com/travel/d7/1706/b8/0dc0712ee8a8c8b5.jpg;https://img1.qunarzz.com/travel/d7/1706/2b/5b1f2ed87cde4fb5.jpg;https://img1.qunarzz.com/travel/d6/1706/6e/5f7e4b6511c802b5.jpg;https://img1.qunarzz.com/travel/d6/1706/da/2558d787dc8e76b5.jpg', '5', '青铜峡景点排名第5', '2小时 - 4小时', '18', '37.896476', '106.006387', '5', '青铜峡黄河大峡谷景区位于宁夏中部，北邻沙湖，南接沙坡头，距滨河水韵、回族之乡吴忠市12公里，是具有着丰富旅游资源的新兴景区之一。黄河从黑山峡流入后，一路雄风，进入青铜峡大峡谷。它是黄河上游的最后一道峡谷，素有“黄河上游小三峡”之称。大峡谷内山河相依，两岸峭壁奇景纷呈。景区内分布着青铜古镇、黄河铁桥、拦河大坝、百零八塔、秦王古渡、黄河圣坛、库区鸟岛和牛首山等景点，宛如镶嵌在黄河金岸中的颗颗明珠，使游客目不暇接、留恋忘返。正是“十里长峡十里景，十里画廊在青铜”。峡谷里的每一处景观都有着一段久远的历史、一个美丽的神话。只有当大家游览于峡谷之中，才能领略到塞上江南的魅力，体验到古峡文化的精髓。', '吴忠青铜峡市青铜峡镇', '0953-3012868', 'http://www.qtxhhdxg.com/', '8:00-17:00', 1122, NULL, '适合自驾前往。', NULL, NULL, 'http://travel.qunar.com/p-oi7628125-huanghedaxiagu', '2', '', '', '4', '', '3', 8, 42862, 63, 38791, NULL, NULL, 194529, NULL, 25616, 25903, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (146, '9137981', '龙虎山崖墓', NULL, '江西 > 鹰潭 > 鹰潭景点 > 龙虎山崖墓', '鹰潭', '鹰潭', 'https://img1.qunarzz.com/travel/d4/1605/cd/0c4f4c339b1d22f7.jpg;https://img1.qunarzz.com/travel/d2/1708/99/220fb816d6713eb5.jpg;https://img1.qunarzz.com/travel/d3/1708/c4/5bd15990d5f848b5.jpg;https://img1.qunarzz.com/travel/d6/1708/49/18e725a8aab425b5.jpg;https://img1.qunarzz.com/travel/d5/1702/b1/a5134357ae6a74b5.jpg;https://img1.qunarzz.com/travel/d5/1702/3e/47aac0f8d0192db5.jpg', '5', '鹰潭景点排名第3', '1小时 - 2小时', '8', '28.095438', '116.96977', '7', '龙虎山崖墓数以百计，全部镶嵌在仙水岩一带的悬崖峭壁之上。仙水岩诸峰峭拔陡险，岩壁光滑平展，岩脚下便是泸溪河，临水悬崖绝壁上市满了各式各样的岩洞，独特的地理环境是龙虎山崖墓的基本成因。从泸溪河舟中或地面眺望，隐约望见一个个岩洞口或钉木桩，或封木板，“藏一棺而暴其半者”多处可见。这些崖墓大多是二千五百多年前春秋战国时期古越人的崖墓悬棺，其葬位离水面20-50米以上，高的达300余米。在大片岩壁上，洞穴星罗棋布，星星点点，或高或低，或大或小，数以百计。因这些洞穴高不可攀，无人入内，其中所藏之物，多少个世纪以来一直是一个不解之谜。', '鹰潭市贵溪市龙虎山风景名胜区(206国道旁)仙水岩仙女岩内', '400-8853-766', 'http://www.longhushan.com.cn', '8:00-16:00', 4711, '四季皆宜。', '位于龙虎山景区内部，步行可达。', NULL, NULL, 'http://travel.qunar.com/p-oi9137981-longhushanyamu', '1', '4', '4', '1', '', '1', 0, 44462, 179, 16457, 65039, 153623, 5277, NULL, 11899, 6887, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (147, '712455', '瀛湖', NULL, '陕西 > 安康 > 安康景点 > 瀛湖', '安康', '安康', 'https://img1.qunarzz.com/travel/poi/1410/22/2c8d220d1059eb2fffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d5/1804/95/e5eb80ec325359b5.jpg;https://img1.qunarzz.com/travel/d4/1804/96/65aeb38ae09007b5.jpg;https://img1.qunarzz.com/travel/d3/1804/db/34d661595f5d3bb5.jpg;https://img1.qunarzz.com/travel/d2/1804/40/80ad01583b7d5bb5.jpg;https://img1.qunarzz.com/travel/d1/1804/ec/dc2a60a6f0006fb5.jpg', '3.4', '安康景点排名第4', '3小时 - 4小时', '8', '32.6105560124', '108.8981591665', '5', '驱车观光，首先抢眼的是雄踞火石岩峡谷中岿然锁蛟龙的电站大坝，有“陕西第一坝”的美称。固若金汤，气势宏伟，引人瞩目。当泄洪时，飞瀑高悬，喷珠溅玉 ，雷霆万钧，蔚为壮观，万丈豪情油然而生。坝侧是久负盛名的佛教胜地天柱山白云寺，坝下不远是一座我国自行设计并建造的特大钢构斜拉桥，具有世界先进水平。', '陕西省安康市平利县八仙镇与镇坪县临界处', NULL, NULL, '营业时间：8:30-16:30；班船时间：11:30、12:30、13:30、14:30。', 19689, '千家坪森林公园年平均气温6.6ºC，夏季气候凉爽怡人，是避暑度假的好去处。春、秋两季雨量充佩,冬季积雪深厚，美丽多变的雾景、雨景、霜景、雪景更是丰富了森林旅游观赏内容与情趣，因此一年四季适宜观赏游玩。', '小巴：\n在安康市安火路口乘坐前往瀛湖的小巴可直接到达，车费5元/人（旺季时班车3元/人）；\n也可以直接包夏利或小面包前往，参考价格在10－20元/车。', NULL, '4小时', 'http://travel.qunar.com/p-oi712455-yinghu', '5', '2', '5', '5', '4', '5', 7, 43722, 195, 229697, 23871, 315345, 367661, 49284, 129970, 96189, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (148, '9220342', '妖女湖', NULL, '青海 > 久治 > 久治景点 > 妖女湖', '果洛', '久治', 'https://img1.qunarzz.com/travel/d1/1705/f9/f122f589faaf49b5.jpg;https://img1.qunarzz.com/travel/d5/1705/11/4d0194696cf973b5.jpg;https://img1.qunarzz.com/travel/d3/1705/6e/71efc3327e1e3ab5.jpg;https://img1.qunarzz.com/travel/d2/1705/39/2bcbd21690bb9eb5.jpg;https://img1.qunarzz.com/travel/d6/1705/79/b7a800cbb02787b5.jpg;https://img1.qunarzz.com/travel/d4/1705/af/005f686e355a25b5.jpg', '5', '久治景点排名第2', NULL, '6', '33.348914', '101.11505', '1', '妖女湖位于年保玉则主峰的西南部，被年保玉则的侧峰挤压的狭而长，这里的湖水呈绿色，景色也非常漂亮。正因为她的名字和形状都与仙女湖有了如此大的反差，才使得妖女湖更加的神秘莫测，从仙女湖徒步到妖女湖单程需要3-4小时，往返7小时以上。年保玉则位于青海果洛州久治县，主峰海拔5396米，是果洛藏族传说中的发源处，也是当地人心中的一座神山。年保玉则山下有多个湖泊，每到夏季时还有开满鲜花的五色草地，雪山、湖泊、花海映衬的风景堪称绝美，是很多摄影爱好者心目中的天堂，来此观光拍照十分不错。另外，山周围面积广大，其中大部分都是未经开发的原始风景，景区内可以扎营，很多户外爱好者也把这里当做徒步的优质线路。', '青海省果洛州久治县年保玉则景区内(仙女湖以南)', NULL, NULL, '8:00-18:00', 3815, NULL, '需先到达年保玉则景区，年保玉则景区没有公共交通，一般前来游玩的都是从西宁、兰州等地包车而来', NULL, '徒步仙女湖至妖女湖段1-2天，若只骑马到妖女湖2.5单程小时，步行单程3小时', 'http://travel.qunar.com/p-oi9220342-yaonu:hu', '4', '', '1', '4', '2', '1', 3, 35227, 78, 199169, NULL, 22420, 110259, 29411, 15231, 5843, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (149, '5740656', '特克斯八卦城', NULL, '新疆 > 伊犁 > 伊犁景点 > 特克斯八卦城', '伊犁', '伊犁', 'https://img1.qunarzz.com/travel/poi/201407/30/817a2bc2f3859baac8d65eac.jpg;https://img1.qunarzz.com/travel/d6/1804/2a/8a85d8b63afa9b5.jpg;https://img1.qunarzz.com/travel/d9/1804/a3/b36330e4a4703bb5.jpg;https://img1.qunarzz.com/travel/d8/1804/e6/a45acaab741859b5.jpg;https://img1.qunarzz.com/travel/d2/1804/d2/2d5dfaf68473ccb5.jpg;https://img1.qunarzz.com/travel/d7/1804/51/d573e0e37547f1b5.jpg', '4.1', '伊犁景点排名第15', '4小时 - 6小时', '52', '43.221326', '81.846224', '45', '特克斯是一座“天地交而万物通，上下交而万物同”的城市；这是一座被上海吉尼斯总部授予“现今世界最大规模的八卦城”的城市；这是一座历史上塞种、月氏、乌孙、回纥、突厥、蒙古、柯尔克孜、哈萨克、维吾尔、汉等22个民族的人民都曾繁衍生息的城市；这是一座我国历史上第一位和亲公主细君公主生活的城市；这是一座诞生了我国历史上第一位女外交家的城市；这是一座传说由“长春真人”邱处机设计的城市；这是一座现在在我们这个国家里可能绝无仅有的一座不需要红绿灯的城市（有关部门1996年取消道路上的红绿灯，八卦城由此成为一座没有红绿灯的城市）。', '新疆维吾尔自治区伊犁哈萨克自治州特克斯县', NULL, NULL, '全天', 19507, '七、八月', '伊宁有到特克斯的班车，8:30-18:40每半小时一趟，车票20元；自驾从伊宁经G218，X706抵达特克斯县，全程100km左右。', NULL, NULL, 'http://travel.qunar.com/p-oi5740656-tekesibaguacheng', '', '2', '', '1', '2', '4', 7, 24970, 196, NULL, 20009, NULL, 18671, 44528, 173111, 45360, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (150, '9785340', '毛主席像章馆', NULL, '宁夏 > 石嘴山 > 石嘴山景点 > 毛主席像章馆', '石嘴山', '石嘴山', 'https://img1.qunarzz.com/travel/poi/1708/8c/9da4a88c55bfc637.jpg', '--', '石嘴山景点排名第30', '0.5小时 - 1小时', '0', '38.808302', '106.367186', '0', '位于南沙滩的毛泽东像章馆内陈列着由收藏家杨维福收藏的20万枚毛泽东像章精品，和“文化大革命”期间全国29个省、市、自治区制造的金、银、铜、铁、铝、木等30多种材质像章，以及系列像章和专题像章等。毛泽东像章是中国像章艺术的精华，是世界徽章艺术瑰宝，被称为世界第九大奇迹。', '宁夏石嘴山市平罗县沙湖景区内', '0952-6684025', 'http://www.nxshahu.com', '旺季8：00-18：00；\n淡季8：30-17：30。', 5592, NULL, '银川至沙湖\n1、银川北门旅游汽车站开通沙湖旅游专线，发车时间：4月20日至10月20日，早8点至10点30分每半小时一发，单程车费15元，行程约1小时，返程时间13点至18点；\n2、也可乘坐发往大武口、惠农、红果子、汝箕沟、隆湖、平罗、姚伏、沙湖等地途经或直达沙湖班车，发车时间：早8点至下午5点均有大巴前往，具体事宜及票价请咨询北门旅游汽车站售票口。\n景区内交通\n可在景区游船港乘坐游船、游艇等水上交通前往。', NULL, NULL, 'http://travel.qunar.com/p-oi9785340-maozhuxixiangzhangguan', '', '', '', '', '', '2', 4, 29168, 87, NULL, NULL, NULL, NULL, NULL, 36716, 12919, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (151, '5159603', '沙湖', NULL, '宁夏 > 平罗 > 平罗景点 > 沙湖', '石嘴山', '平罗', 'https://img1.qunarzz.com/travel/d3/1707/df/f6b61b18a0aad4b5.jpg;https://img1.qunarzz.com/travel/d5/1707/f7/832c173bb5b7cb5.jpg;https://img1.qunarzz.com/travel/d4/1707/6e/02799991607fabb5.jpg;https://img1.qunarzz.com/travel/d9/1611/5f/4b12c67684c4e1b5.jpg;https://img1.qunarzz.com/travel/d8/1507/5f/6c61fe9b31fda3.jpg;https://img1.qunarzz.com/travel/d7/1707/e3/5c9c85fd7e9ce8b5.jpg', '4.3', '平罗景点排名第1', '3天', '118', '38.820453', '106.389074', '0', '沙湖旅游区是鸟的天堂、鱼的世界、游人的乐园。百鸟乐园景区、万亩荷花精品园景区、尼罗河风情景区、大漠丝路奇观景区为沙湖四大景区。沙湖水产丰富，无污染，是鸟类栖息、繁殖的理想之地。百万只鸟类在这里繁衍栖息，成为沙湖绝妙一景。沙湖还开发了许多特色水上运动项目，如水上摩托艇、冲浪、水上降落伞等等，给热爱运动喜欢冒险的游人提供了一个一试身手的机会。', '宁夏石嘴山市平罗县附近前进农场', '0952-6684025', 'http://www.nxshahu.com', '旺季8：00-18：00；\n淡季8：30-17：30。', 8939, '春秋最佳。', '银川至沙湖:\n1、银川北门旅游汽车站开通沙湖旅游专线，发车时间：4月20日至10月20日，早8点至10点30分每半小时一发，单程车费15元，行程约1小时，返程时间13点至18点；\n2、也可乘坐发往大武口、惠农、红果子、汝箕沟、隆湖、平罗、姚伏、沙湖等地途经或直达沙湖班车，发车时间：早8点至下午5点均有大巴前往，具体事宜及票价请咨询北门旅游汽车站售票口。\n\n石嘴山至沙湖:\n1、在大武口区汽车站乘坐途经沙湖前往银川的班车，行程约20分钟，乘车前向售票窗口询问清楚；\n大武口至姚伏途经沙湖中巴公交车，每半小时一趟；\n2、也可打的前往，单程车费约30元。\n\n平罗至沙湖:\n在平罗西门车站乘平罗至沙湖的专线车，车费4.5元，每一小时一趟。\n\n银川旅游汽车站：位于银川市区的汽车站，所发车辆大多前往银川周边的旅游景点，如沙湖、西部影视城、王陵、苏峪口、岩画等等，又被称为\"北门旅游汽车站\"\n地址: 银川市兴庆区清和北街‎570号\n电话: 0951-6738245', '夏天来的话一定要做好防晒措施。', NULL, 'http://travel.qunar.com/p-oi5159603-shahu', '5', '', '4', '', '3', '1', 1, 20930, 87, 267323, NULL, 193517, NULL, 97607, 15460, 5554, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (152, '9553481', '永兴岛', NULL, '海南 > 西沙群岛 > 西沙群岛景点 > 永兴岛', '三沙', '西沙群岛', 'https://img1.qunarzz.com/travel/poi/1701/ae/3ebf8609b62b4437.jpg', '--', '西沙群岛景点排名第2', NULL, '1', '16.8', '112.3', '1', '永兴岛是西沙群岛、南沙群岛、中沙群岛三个群岛的经济、军事、政治中心，是三沙市人民政府和众多上级派出机构、市级单位以及永兴工委管委驻地。岛上有政府大楼、学校 、银行、邮政、医院、商店、 机场、码头港口、气象站、驻军等。有完善的生产和生活配套设施，是名符其实的海岛新都市。', '西沙群岛里宣德群岛的永兴礁', NULL, NULL, '全天', 7418, NULL, '永兴岛目前没有对旅行者正式开放。普通旅行者去西沙群岛只能以特种考察的名义去。\n到达方式：\n1.乘坐每月20日前后从海南岛文昌市清澜港出发的“琼沙2号”补给船。单程航程13--15个小时。“琼沙2号”每月一次往返西沙群岛的的永兴岛，17：00开船，第二天8：00到10：00左右在永兴岛靠岸，停留一个晚上，第三天17：00返航。上船的名额有限，去之前一定要提前联系。\n2.租渔船去西沙群岛，往返7天左右，费用3万元。。', NULL, NULL, 'http://travel.qunar.com/p-oi9553481-yongxingdao', '', '', '1', '', '4', '5', 3, 27145, 115, NULL, NULL, 13324, NULL, 165774, 279967, 84001, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (153, '702426', '天下第一墩', NULL, '甘肃 > 嘉峪关 > 嘉峪关景点 > 天下第一墩', '嘉峪关', '嘉峪关', 'https://img1.qunarzz.com/travel/d7/1612/4d/2df7427945d01eb5.jpg;https://img1.qunarzz.com/travel/d6/1708/4b/288346fd53d2a3b5.jpg;https://img1.qunarzz.com/travel/d6/1606/9a/41d094e886e398f7.jpg;https://img1.qunarzz.com/travel/d1/1606/cc/8e75aa25713673f7.jpg;https://img1.qunarzz.com/travel/d0/1505/1d/a88b17afa99499.jpg;https://img1.qunarzz.com/travel/d8/1505/b7/4af3239d6ac846.jpg', '4.1', '嘉峪关景点排名第5', '0.5小时 - 1小时', '72', '39.7510483362', '98.2075973729', '93', '长城第一墩，又称讨赖河墩，是嘉峪关西长城最南端的一座墩台，也是明代万里长城最西端的一座墩台。长城第一墩于明嘉靖十八年（公元1539）年由肃州兵备道李涵监筑，北距关城7.5公里，墩台矗立于讨赖河边近56米高的悬崖之上。对于它的雄险，著名长城专家罗哲文先生曾经有过生动的吟咏：“嘉峪关，雄险画皆难，墩堡遥遥相互望，长城道道连关山，猿臂也难攀”。在一万多华里的明代长城线上，墩台无数，密如繁星，而嘉峪关长城第一墩与河北山海关渤海之滨的“老龙头”遥相呼应，共同构筑起中华长城“龙”的首尾，成就了中华民族“龙”的美名。如果说长城是中华民族的丰碑，那么长城第一墩就是这座丰碑崛起的地方。现在，这座墩台残高约10米，其底部呈不规则正方形，底宽14米，内部仍呈正方形，长宽均为6米。用黄土夯筑而成，夯土层厚10～20厘米。“长城第一墩”碑文由已故的西路军将领魏传统题写，字迹飘逸洒脱，清秀隽永。\n依托长城第一墩开发建设的旅游景区，自然景致壮观，东边是群楼林立的嘉峪关市区，西边是浩瀚无际的大漠戈壁，北边与雄伟壮观的嘉峪关相连，南面为连绵起伏的祁连雪山，脚下是发源于祁连山的讨赖河。长城第一墩旅游景区，以长城文化和丝绸之路文化为内涵，以戈壁风光和西北民俗风情为基础，是一处观光、探险、休闲、娱乐、怀古游历和拍摄影视场景的好地方。', '嘉峪关市关城南7公里处', '0937-6221114', NULL, '旺季（5月1日-10月31日）8:30-20:00\n淡季（11月1日-次年4月30日）8:30-18:00', 9772, '每年5月至10月，是嘉峪关市最佳旅游时节。', '由市区乘4路公交即可到达，1元/人。也可打车前往，8-10元/车。', NULL, NULL, 'http://travel.qunar.com/p-oi702426-tianxiadiyidun', '3', '', '', '1', '1', '4', 2, 22937, 106, 40489, NULL, NULL, 12526, 10380, 121476, 53635, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (154, '706220', '云门山', NULL, '山东 > 青州 > 青州景点 > 云门山', '潍坊', '青州', 'https://img1.qunarzz.com/travel/d3/1801/25/0ad4c4cfb7e5c7b5.jpg;https://img1.qunarzz.com/travel/d1/1605/dc/e94137fef62d62f7.jpg;https://img1.qunarzz.com/travel/d8/1801/e1/7dd83d4ae58841b5.jpg;https://img1.qunarzz.com/travel/d1/1801/2a/8cb131ee80a559b5.jpg;https://img1.qunarzz.com/travel/d8/1604/dc/8ffc15b1fb967df7.jpg;https://img1.qunarzz.com/travel/d6/1604/ca/22978620e33ce7f7.jpg', '4', '青州景点排名第3', '4小时', '12', '36.6763489688', '118.4749854902', '7', '主峰大云顶，有洞如门，高阔过丈，南北相通，远望如明镜高悬，夏秋时节，云雾缭绕，穿洞而过，如滚滚波涛，将山顶庙宇托于其上，若隐若现，虚无缥缈，宛若仙境，蔚为壮观，谓之“云门”，或称“云门仙境”。古“青州八景”称之为“云门拱壁”。登临山巅，便可饱览“驼岭千寻”、“劈峰夕照”和“三山联翠，障城如画”的壮丽景观。在主峰云门洞南西侧有一天然石罅，深不可测，当气压低时，常有蒸气泛出，名曰“云窟”。', '山东潍坊青州市城南2.5公里', '0536-3276728', NULL, '8:30-17:30', 6110, '春夏最佳，春夏草木葱郁，山顶凉爽，风景优美。', '云门山位于潍坊青州市南，您到达潍坊后，可选择下面的方式：\n1、在汽车站乘坐潍坊至【云门山】的直通旅游巴士，票价10元，每天流水发车，旅游旺季还有增发车辆，非常方便。\n2、在汽车站乘坐潍坊至【青州】的巴士，票价8元，也是每天流水发车，到达青州汽车站后再乘青州到云门山景区的客车前往。也可在青州市区打车前往。', NULL, NULL, 'http://travel.qunar.com/p-oi706220-yunmenshan', '', '2', '4', '5', '2', '3', 2, 43251, 125, NULL, 18434, 146718, 187220, 44407, 41907, 23922, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (155, '706679', '金滩', NULL, '广西 > 东兴 > 东兴景点 > 金滩', '防城港', '东兴', 'https://img1.qunarzz.com/travel/d2/1703/99/d094dc3487e4f7b5.jpg;https://img1.qunarzz.com/travel/d9/1703/9d/1e225699467afeb5.jpg;https://img1.qunarzz.com/travel/d1/1703/11/2ef2cd28f5af2eb5.jpg;https://img1.qunarzz.com/travel/d1/1611/c2/e03f9564d301feb5.jpg;https://img1.qunarzz.com/travel/d9/1611/82/f56d5e77eda7f7b5.jpg;https://img1.qunarzz.com/travel/d9/1607/60/e1db6772719125b6.jpg', '4.2', '东兴景点排名第1', '3小时 - 5小时', '29', '21.5306348114', '108.1710787959', '7', '金滩岛上草木繁茂，四季常绿，全长15公里，宽阔坦荡，没有礁石，沙质细柔金黄，踩上去非常舒服。绿岛、长滩、碧海、阳光，构成金滩如画的景色，是天然的海滨浴场，旅游度假胜地。金滩海面较为平静，很适合游泳，海边还有几艘木船供游客拍照，还有渔民在海边捕鱼，生活气息很浓厚。景区里有出租伞、凳子、桌子等，租一把太阳伞，一家人或三朋四友围着一张桌子吃点零食，在沙滩上晒晒太阳，十分惬意。', '防城港市东兴市京岛旅游度假区万尾岛', NULL, NULL, '全天开放', 731, NULL, '在防城港汽车站乘坐到东兴的巴士到东兴市，再乘东兴到万尾的班车到万尾金滩，20分钟即达。', NULL, NULL, 'http://travel.qunar.com/p-oi706679-jintan', '2', '', '1', '2', '3', '4', 4, 37442, 98, 26271, NULL, 14362, 31927, 50234, 48046, 44096, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (156, '8457694', '坎儿井博物馆', NULL, '新疆 > 吐鲁番 > 吐鲁番景点 > 坎儿井博物馆', '吐鲁番', '吐鲁番', 'https://img1.qunarzz.com/travel/d3/1704/d2/504740738bca93b5.jpg;https://img1.qunarzz.com/travel/d6/1704/e2/555193ca9d994b5.jpg;https://img1.qunarzz.com/travel/d3/1704/d8/6dbbd054c713b4b5.jpg;https://img1.qunarzz.com/travel/d4/1701/7c/69b8a4a1c53985b5.jpg;https://img1.qunarzz.com/travel/d4/1612/f8/cd1b5a6abcad2eb5.jpg;https://img1.qunarzz.com/travel/d6/1612/bf/17ee0a03e39db2b5.jpg', '4.5', '吐鲁番景点排名第44', NULL, '9', '42.95895', '89.155395', '10', '吐鲁番坎儿井博物馆，位于新疆吐鲁番市亚尔乡新城西门村888号的坎儿井民俗园内，这座投资240万元的博物馆于2007年6月落成开放，是一座独具特色的专题博物馆。博物馆分为地上和地下部分，包括100多米长的地下参观通道、500平方米的地面陈列馆、坎儿井分布掏挖坎儿井图、剖面图、葡萄长廊和葡萄干晾房等，通过大量的图片、实物、模型，以通俗易懂的方式向游客展现了坎儿井的结构、分布区域、功能和研究成果，让游客能够真切地感受到\"地下长城\"的历史变迁、修建过程。走进博物馆，首先看到的是一座沙盘，直观地向游人展示了一条条坎儿井穿越戈壁，将天山的地下雪水引到一片片绿洲的情景。', '新城路新城西门村888号', '(0995)8557390', NULL, NULL, 4828, '四季皆宜。', '在市区可乘坐1、101、102路公交车前往。', NULL, NULL, 'http://travel.qunar.com/p-oi8457694-kanerjingbowuguan', '3', '3', '', '', '', '4', 6, 37460, 204, 44054, 71207, NULL, NULL, NULL, 155800, 51573, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (157, '7644115', '库木塔格沙漠', NULL, '新疆 > 鄯善 > 鄯善景点 > 库木塔格沙漠', '吐鲁番', '鄯善', 'https://img1.qunarzz.com/travel/d9/1504/10/7afb21eaf53235.jpg;https://img1.qunarzz.com/travel/d3/1504/d2/91ee8217fe412.jpg;https://img1.qunarzz.com/travel/d2/1611/6b/a42d161c78743b5.jpg;https://img1.qunarzz.com/travel/d4/1611/da/99a8e0b7ded56fb5.jpg;https://img1.qunarzz.com/travel/d4/1703/c9/bf3fe47ca62b34b5.jpg;https://img1.qunarzz.com/travel/d5/1509/e2/d0bf29e4ffd5f5.jpg', '4.7', '鄯善景点排名第1', '3小时 - 4小时', '69', '42.857452', '90.214993', '11', '库姆塔格沙漠位于新疆吐鲁番盘地的东缘，距离吐鲁番市区90公里，是世界上距离城市最近的沙漠。库木塔格是一座流动型沙漠，有众多的沙窝、沙山等，景色多样壮观，维吾尔语“库木塔格”也是“沙山”的意思。 对于一般游客而言，游玩库木塔格沙漠即是指吐鲁番鄯善县县城南侧的沙山公园（库木塔格沙漠公园），这里是接近沙漠，在沙漠中游玩最方便的地方。公园内有滑沙、沙漠越野车等众多的娱乐项目，都可以付费体验，在这里游玩3-4小时为宜。', '新疆省吐鲁番鄯善县公园路999号', '0995-8388799', NULL, '4月21日-10月20日8:00-21:00， 10月21日-次年4月20日10:00-18:30', 1447, '每年的春季和秋季是合适的徒步季节。夏季库姆塔格沙漠中温度会在40。C以上。冬季沙漠的夜晚很冷，也不适合徒步。', '在吐鲁番客运站包车前往迪坎尔村：迪坎尔村子位于沙漠的边缘，徒步穿越沙漠到达鄯善栏杆后，有乡村汽车可以直接送到鄯善县城，然后乘坐大巴返回乌鲁木齐。全程包车是个简捷的办法。', '必备装备：\n徒步库姆塔格，需要标准的徒步露营装备，包括帐篷、睡袋、防潮垫、徒步鞋、炉头、套锅、GPS、指南针、对讲机、徒步衣服等；此外，还有一些特殊的装备，这些装备在一般的徒步活动中可以没有，但沙漠徒步是必须的。\n防雪套：用来防止沙子进人鞋里；徒步手杖：沙漠很松软，手杖是必不可少的，最好带两根；塑料袋：用来保护各种装备和衣物不至于粘土大量的沙子：摄影器材尤其要保护好！GPS和指南针，用来辨别方向：沙漠中昼夜温差大，夜间使用的睡袋必须足够保暖。\n沙漠中几乎没有任何植物和动物，也没有人居住，更没有水源，所以全程必须携带足够的食物和饮水，每人大约8-10瓶水。偶尔会碰到人类活动留下的痕迹，例如纸箱子、玻璃瓶等。', '半天-1天', 'http://travel.qunar.com/p-oi7644115-kumutageshamo', '', '', '1', '4', '4', '5', 9, 24974, 68, NULL, NULL, 6838, 87707, 161018, 289923, 83436, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (158, '722801', '长白山', NULL, '吉林 > 长白山 > 长白山景点 > 长白山', '长白山', '长白山', 'https://img1.qunarzz.com/travel/d5/1509/fb/748a6f12ba9417.jpg;https://img1.qunarzz.com/travel/d1/201403/07/35845e368287ec40ddb12cfb.jpg;https://img1.qunarzz.com/travel/d3/1504/dd/f79201ba1caa7a.jpg;https://img1.qunarzz.com/travel/d4/1505/35/6b9a40dc9d9035.jpg;https://img1.qunarzz.com/travel/d2/1712/c8/86e73c84b51438b5.jpg;https://img1.qunarzz.com/travel/d1/1509/f8/ef9f23ce41c3c6.jpg', '4.8', '长白山景点排名第1', '2天 - 3天', '636', '42.1288491957', '128.1104797791', '20', '长白山是一座神奇的山，也是一座秀丽的山。每当春风乍暖，山下万树含烟，众绿齐发，山顶却依然白雪皑皑，宛如一位玉骨冰肌的仙女，亭亭玉立在茫茫林海；犹如一朵春蕾绽开的雪莲，嫣然怒放于万绿丛中。长白山古木参天，遮天蔽日，在密林深处，有数不尽的物华天宝。\n长白山经过大自然漫长岁月的雕塑，群峰竞秀，万态千姿，有的像直插青天的宝塔，有的似虎豹雄狮，有的如仙人驾鹤翱翔，有的似雄鹰衔石，其景致极为壮观。长白山陂陀千里，势如蛟龙，充分展现了大自然的奇迹。它隐居于茫茫林海之中，却又突兀于丛山峻岭之上，它有时显得壮阔、粗犷，有时又极为绚丽动人。\n长白山既神奇又壮美，有许多美妙动人的诸如“神女浴躬池”的传说故事，其垂直自然景观更为绚丽多姿，真乃“一日可历四季景”。冬游长白山已不再是神话，一年四季都吸引海内外无数游人前来揽胜。\n长白山有九大名景：天池、乘槎河、长白瀑布、聚龙泉、长白湖、地下森林、岳桦林景观带、高山苔原景观带、黑风口。', '吉林省安图县二道白河镇', '0433-5755168', 'http://www.cbs.travel/', '1月1日-4月30日、10月16日-12月31日：8:30-15:30\n5月1日-7月15日：7:30-16:30\n7月16日-8月15日：6:30-17:00（周末售票时间提前30分钟）\n8月16日-10月15日：7:30-16:00（周末售票时间提前30分钟）\nTips：\n1、节假日期间的门区开放时间和停止售票时间，根据游客接待量情况另行通知。\n2、一般到冬季南坡及西坡会关闭，北坡正常开放。\n3、长白山天气多变，如遇雨雪天气，景区会封山。\n4、西景区峡谷、王池景点关园时间调整为每天16：00，每日13：30时之后检票入区的游客只能游览一个景点。', 12250, '长白山属于温带大陆性山地气候，特点是冬季漫长寒冷，夏季短暂凉爽且天气变化无常。\n夏秋两季是长白山旅游的旺季。每年6-9月份，是长白山度假旅行最舒适的季节，也是绿色植被和五彩鲜花的海洋。秋季9-10月的长白山林海，秋高气爽、野果飘香、层林尽染、姹紫嫣红，是摄影爱好者的天堂，由于此时雨水少，看到天池的几率大，所以也是观看天池的最佳季节。\n春冬两季是长白山旅游的淡季，从11月份开始，长白山进入雪季，直到来年4月底。此时山顶严寒大雪，气温逐渐降到零下-35℃至-40℃之间，不适合旅行。一直到来年5月初，长白山天池依然冰封，但此时是一年之中看“冰封天池”的最温暖时机。有兴趣的话可以前往观看。\n6-9月最佳。 此时的长白山是避暑胜地，风光秀丽、景色迷人。受山地地形垂直变化的影响，从山脚到山顶随着海拔的升高形成由温带到寒带的4个景观带，正所谓是“一山有四季，十里不同天”。', '长白山有东西南北四个坡，因为东坡在朝鲜境内，所以只有南坡、北坡、西坡可以进入游览，其中北坡为大多数游览者的选择。离长白山北坡最近的城镇为二道白河镇，离西坡最近的城镇为松江河，但由于松江河的交通与住宿都不及二道白河方便，一般游览者会选择由二道白河镇前往长白山游玩。长白山有自己的机场——长白山机场，位于白山市抚松县松江河镇长白山保护开发区池西区，距离长白山西风景区约15公里、长白山北风景区约120公里、长白山南风景区约135公里，目前主要有北京、长春、延吉、沈阳、及上海5条航线。\n周边城市中，延吉机场离长白山最近，大约3、4个小时车程。\n【长白山机场】\n地址：白山市抚松县松江河镇长白山保护开发区池西区。\n电话：0439-6259777、0439-6259322\n【延吉朝阳川国际机场】\n地址：延吉市长白西路6666号\n电话：0433-2234433乘火车去长白山，有两个火车站可以选择：一个是距二道白河镇160公里的安图站，有直达长春、吉林、沈阳、哈尔滨、大连、北京的火车，从安图坐客车到长白山大约3小时；另一个是二道白河镇上的白河站，有直达沈阳、长春、吉林、通化的火车。\n【安图站】\n地址：延边州安图县明月沟镇明安街187号\n【白河站】\n地址：延吉市二道白河镇(近客运站)\n电话：0433-6176142到长白山必到白河镇，从长春、敦化、延吉、安图等地都有到二道白河的长途客车。\n【二道白河客车站】\n地址：延边州安图县池北大街附近\n电话：0433-5751533\n网址：www.changbaishankeyun.com到长白山必先到二道白河镇，201国道、202省道经过白河镇。从敦化走201国道、从延吉走202省道、从通化走201国道都可到白河。出租车起步价为6元。\n\nTips：在二道白河打车到北坡一天接送来回80起，去西坡来回价格是240起。二道白河有比较多的小轿车，游客可包车前往，一般在80元至300元不等，视游客所去目的地的路程远近而有所不同。长白山旅游专线主要是两个线路：美人松园（二道白河）—北景区、北景区—西景区。\n经过机场的是北景区到西景区的旅游专线，从北坡景区出发，经过机场时间分别是11:00、17:10、21:00，在机场停留30分钟然后前往西景区。进入长白山景区后各景点之间交通主要为环保大巴，为必消费项目。\n环保大巴基本上贯穿北坡、西坡的景点和南坡的一些景点，票价为85元/人。一般从北山门或西山门从发，沿途经过景点后停车，然后停留一段时间后前往下一个景点，在任意景点可以选择乘坐各方向的大巴，上车前请于司机确认是否到达想去的地方。去天池一般需要在天池路口换乘越野车，因为山势险峻，一般的车无法保证安全，主峰越野车来回80元/人。', '1、夏季出游，要将袖口、领口以及裤脚扎紧，防止昆虫。\n2、天池顶部风大，避免撑伞，以免出现危险。\n3、冬季特别注意保暖，不要有肌肤裸露在外；雪地棉鞋一定要能防滑，一定不能买硬塑料底，低温下会变脆。\n4、天池为中朝界湖，游客必须在规定区域内游览，严禁越境。\n5、相机或摄影机在-20度的温度下容易出现放电现象，冬天在户外要及时放入口袋中，并且最好多备电池，防止失灵。\n6、地下森林常常有小松鼠出没，请不要随意喂食。', NULL, 'http://travel.qunar.com/p-oi722801-changbaishan', '', '3', '', '4', '4', '5', 5, 22488, 116, NULL, 46804, NULL, 67762, 107771, 209713, 85179, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (159, '8676615', '鸭公岛', NULL, '海南 > 西沙群岛 > 西沙群岛景点 > 鸭公岛', '三沙', '西沙群岛', 'https://img1.qunarzz.com/travel/d9/1612/36/d5f5592c8d8718b5.jpg;https://img1.qunarzz.com/travel/d8/1612/c1/c0f0c2a1c6780cb5.jpg;https://img1.qunarzz.com/travel/d2/1701/20/0f090f5f982139b5.jpg;https://img1.qunarzz.com/travel/d3/1612/a1/d2df53b5f06e38b5.jpg;https://img1.qunarzz.com/travel/d4/1612/14/4191b1aa054284b5.jpg;https://img1.qunarzz.com/travel/d0/1507/12/18cad8531fe383.jpg', '5', '西沙群岛景点排名第8', '2小时 - 1天', '15', '16.43686', '111.709605', '3', '该岛处于全富岛所在礁盘与银屿所在礁盘之间的“银屿门”的通路上，岛由礁石构成，岛中有一个随海潮涨落的小湖。岛上有中国渔民搭建简易房子。设有“中国·西沙鸭公岛警务区”的警务碑。鸭公岛在西沙群岛的永乐环礁中，该岛四周水深较浅，且礁盘分布的范围极大，大中型船只无法通航，进出鸭公岛附近海域只能通过渔船或小艇。该岛发育在点礁上，岛四周即为深水区。这种礁体是在泻湖中各自孤立生长的，尤其在“门”的通路上最易形成，因为在“门”。附近潮汛急，风浪大，外海或泻湖内的浮游生物送来亦多，有利于礁头的生长和合并，并形成小岛。鸭公岛即正当银屿门区。岛屿呈新月形，体观出东北风对礁体发育的影响。', '海南三沙市中国西沙群岛永乐群岛北部', NULL, NULL, '全天开放。', 16410, '春季夏季最佳。', '直升机\n可以在三亚坐直升飞机上岛，在三亚的军用机场有到西沙群岛的鸭公岛的直升飞机。\n轮渡\n乘坐轮渡在各岛之间通行。', '切勿私自下水，注意人身安全。', NULL, 'http://travel.qunar.com/p-oi8676615-yagongdao', '1', '1', '1', '2', '', '1', 2, 47521, 167, 12717, 9041, 6104, 35142, NULL, 6606, 5242, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (160, '8676609', '银屿', NULL, '海南 > 西沙群岛 > 西沙群岛景点 > 银屿', '三沙', '西沙群岛', 'https://img1.qunarzz.com/travel/d8/1701/4b/31e79eda943f5eb5.jpg;https://img1.qunarzz.com/travel/d8/1612/c0/ac7b991dbc5feeb5.jpg;https://img1.qunarzz.com/travel/d2/1701/7c/1cfecf1f4aeda9b5.jpg;https://img1.qunarzz.com/travel/d6/1701/58/10dd88cca5f03b5.jpg;https://img1.qunarzz.com/travel/d2/1701/bd/8132426a527728b5.jpg;https://img1.qunarzz.com/travel/d3/1612/5d/d2815588aaa3e1b5.jpg', '5', '西沙群岛景点排名第1', '2小时 - 1天', '13', '16.254775', '111.663612', '3', '银屿是西沙群岛石屿门北岸的一个礁岛，也称为“银峙”。据说是由于清代在这里拾获许多银子而得名的；还有一说是清未有沉船在此，留有银而得名。银屿是一小沙洲，有草无木，不时有海鸟栖息。海拔只有2米，故高潮时大部分被淹没。本沙洲地形易变，海图上难于表明。在银屿的礁盘上，地形也很具特色，如有一处深坑，坑口面积有如篮球场大小，水深在20米以上，水色蓝黑。坑口附近水温特冷，表明坑内海水是由外海深层冷水透入的。退潮时坑口外缘的礁盘可露出。这种深坑是由礁盘外缘珊瑚礁发育快速，而其内部珊瑚礁来不及发育而形成。银屿南部还有一小沙洲存在，组成物质亦以珊瑚沙为主，占62.3%，贝壳沙次之，占25.2%．余为石灰藻和有孔虫。', '三沙市西沙群岛永乐群岛中', NULL, NULL, '全天开放。', 4802, '春季和夏季。', '直升机\n可以在三亚坐直升飞机上岛，在三亚的军用机场有到西沙群岛的银屿的直升飞机，一小时左右到达。\n轮渡\n乘坐轮渡在各岛之间通行。', '切勿私自下水，注意人身安全。', NULL, 'http://travel.qunar.com/p-oi8676609-yinyu', '', '', '2', '2', '', '3', 3, 30142, 129, NULL, NULL, 33657, 29929, NULL, 32687, 21901, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (161, '8676588', '全富岛', NULL, '海南 > 西沙群岛 > 西沙群岛景点 > 全富岛', '三沙', '西沙群岛', 'https://img1.qunarzz.com/travel/d9/1701/41/e84e086be02767b5.jpg;https://img1.qunarzz.com/travel/d4/1701/39/36e45de3b6777cb5.jpg;https://img1.qunarzz.com/travel/d5/1701/b1/2a4d6a650739efb5.jpg;https://img1.qunarzz.com/travel/d7/1701/61/05a26f55246e9ab5.jpg;https://img1.qunarzz.com/travel/d9/1612/9e/047bec9fce5c09b5.jpg;https://img1.qunarzz.com/travel/d5/1612/95/29bbbed22ab565b5.jpg', '5', '西沙群岛景点排名第4', '2小时 - 1天', '13', '16.597104', '111.700669', '3', '全富岛0.02平方公里。属于西沙群岛。位于永乐环礁的北缘，地理坐标为北纬16°32′，东经111°40′，海南渔民称做“全富峙”或“曲手”。1983年公布全富岛为标准名称。全富岛是因为岛区水产丰富而得名：这里珊瑚礁区生物种类丰富，如浅水造礁珊瑚一项，即有56种之多。但是岛屿面积小，东西长360米，南北宽240米。沙堤不宽，因此，本岛淡水含量也不多。北面沙堤最高，岛南较不明显。本岛海滩岩发育不坚固，可是已能保护沙堤，使沙堤固定。洼地出现在岛的南部,高潮时还可以被海水淹没。本岛地势不高，沙脊出水时也只有1米左右。因而常被人们认为是刚刚出水的新沙洲。', '西沙群岛永乐群岛 珊瑚岛东北部4.5海里处', NULL, NULL, '全天开放。', 14280, '春季、夏季最佳。', '直升机\n可以在三亚坐直升飞机上岛，在三亚的军用机场有到西沙群岛的永兴岛的直升飞机，一小时左右到达。\n轮渡\n乘坐轮渡在各岛之间通行。', '请注意人身安全，切勿私自下水。', NULL, 'http://travel.qunar.com/p-oi8676588-quanfudao', '1', '', '', '', '2', '1', 9, 48149, 84, 9217, NULL, NULL, NULL, 38453, 21652, 6548, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (162, '707042', '照金香山', NULL, '陕西 > 铜川 > 铜川景点 > 照金香山', '铜川', '铜川', 'https://img1.qunarzz.com/travel/poi/1803/b7/a3da21fcf593b837.jpg;https://img1.qunarzz.com/travel/d9/1605/ee/518bb782f0b676f7.jpg;https://img1.qunarzz.com/travel/d2/1605/76/6de60f92bd532cf7.jpg;https://img1.qunarzz.com/travel/d9/1605/cf/960d51ef4ffecff7.jpg;https://img1.qunarzz.com/travel/d4/1605/4a/df38eb73e6528af7.jpg;https://img1.qunarzz.com/travel/d2/1605/bc/9b3d6cb0919f2ef7.jpg', '4.8', '铜川景点排名第10', '4小时', '6', '34.9221190762', '108.9825418167', '1', '风景区内山峦叠嶂，河谷纵横，呈东北、西南方向分布的白垩级砂砾岩地质地貌，景观独特，森林覆盖率85%以上，是保存完好的天然次生林区，有野生动物250余种，植物800余种，具有较高的科研教学和游览观赏价值。\n香山位于耀县城西北的庙湾镇，雄居梁山和乔山山脉之间。东、西、中三峰耸立古称“三石山”。有诗赞曰：“太华山头旧咏诗，砚池即取仰天池。更将三石三峰借，架我凌云笔一枝。”故亦称笔架山，清嘉庆二十三年，重修寺院，更名为香山。', '陕西省铜川市耀州区照金香山旅游景区', NULL, NULL, '8:00-16:50', 16494, NULL, '中巴：\n从铜川市耀州区城内乘中巴可达。', NULL, NULL, 'http://travel.qunar.com/p-oi707042-zhaojinxiangshan', '5', '', '3', '5', '', '3', 6, 40317, 124, 247522, NULL, 57936, 393118, NULL, 97713, 25741, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (163, '9200496', '天意谷', NULL, '四川 > 邻水 > 邻水景点 > 天意谷', '广安', '邻水', 'https://img1.qunarzz.com/travel/d4/1707/ed/4c2f32e79278fab5.jpg;https://img1.qunarzz.com/travel/d8/1707/7f/62133e2dcd4375b5.jpg;https://img1.qunarzz.com/travel/d3/1707/f1/31a4bf0ec407a2b5.jpg;https://img1.qunarzz.com/travel/d7/1707/dd/ca8e43c8681dafb5.jpg;https://img1.qunarzz.com/travel/d2/1707/78/adb8779684b6cb5.jpg;https://img1.qunarzz.com/travel/d4/1707/c9/115924898cd62b5.jpg', '0', '邻水景点排名第1', NULL, '3', '30.261716', '106.830561', '2', '天意谷是聚华蓥山千峰万壑之水于一流，与卡斯特地貌的断裂地缝相遇后形成的地质奇观、在一条约5000米长的Y字型断裂地缝河谷内、形成了无数的溶洞奇观和瀑布群。由于这里沟壑纵横、洞连地天、地缝交错、暗河深潭，在峡谷内构成了一个水石交融、高深莫测的神秘世界。传说是当年秦军灭巴国，巴王子败退时、藏身藏宝，消灾避难之地。自然奇观加历史神话传说，更让这里成为了举世无双的神话世界。谷内有大小溶洞与瀑布200处以上。其中由近千米叠瀑形成的洞中天河，乃举世无双。由亿年滴水溶岩形成的天然巨佛、乃世界之最、数十公里长的巴人阴阳洞与被称为世外桃源的洞里人家，乃人间罕见。主要景点有拂尘瀑、飞瀑迎宾、石门关、老龙潭瀑布、地缝峡、天生桥、天心洞、洞中天河、望月瀑布、天意谷-天佛、穿心洞、天母、天佛、天河等。', '四川省广安市邻水县甘坝乡苏家村', '0826-3442555', NULL, '4月～10月：08:30～17:30开放；11月～3月：08:30～17:30开放；', 19130, NULL, '自驾\n1、成都-成(南)广高速288公里-广邻高速20公里-天池站(下高速)-左转上景区旅游公路16公里-华蓥山景区。\n2、重庆-渝邻高速转广邻高速83公里处-天池站(下高速)-左转上景区旅游公路16公里-华蓥山景区。', NULL, NULL, 'http://travel.qunar.com/p-oi9200496-tianyigu', '4', '', '', '3', '2', '2', 5, 37139, 158, 158413, NULL, NULL, 49144, 49042, 45663, 12502, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (164, '8595801', '賨人谷', NULL, '四川 > 达州 > 达州景点 > 賨人谷', '达州', '达州', 'https://img1.qunarzz.com/travel/poi/1604/38/8848d915750abef7.jpg;https://img1.qunarzz.com/travel/d4/1601/8b/054a4b675aef57f7.jpg;https://img1.qunarzz.com/travel/d0/1601/3f/5dbabecdcbb5f0f7.jpg;https://img1.qunarzz.com/travel/d0/1601/e8/b5e63f31295b86f7.jpg;https://img1.qunarzz.com/travel/poi/1507/40/e80d2a9b81a4f8.jpg', '4.5', '达州景点排名第4', '2小时 - 4小时', '4', '30.863493', '107.092754', '2', '賨（cóng）人谷景区位于四川省达州市渠县境内，距达渝高速公路35公里，距成都3个半小时车程，隶属华蓥山脉，面积41平方公里，是国家AAAA级旅游区、省级风景名胜区、省级森林公园、省级生态旅游示范区[1] 。拥有全国唯一的賨人穴居部落遗址和賨人文化陈列馆，集奇山、秀水、幽洞、丽峡、飞瀑、怪石、古栈、茂林为一体。拥有古賨文化与秀丽的自然风光，所以有“奇山奇水奇石景，古賨古洞古部落”的美誉。賨人谷属于亚热带季风气候，景区海拔在1000米左右，森林覆盖率达95%。', '四川达州渠县龙潭乡老龙村', '0818-7432111', NULL, '08:30--17:30', 5669, NULL, '自驾\n1、达州方向:\n达州(G65包茂高速)——大竹出口61.4KM(G65包茂高速)——往渠县27KM(G318)——往临巴9.6KM--賨人谷2.7KM。\n2、重庆方向:\n重庆(G65包茂高速)——大竹出口130.8KM(G65包茂高速)——往渠县27KM(G318)——往临巴9.6KM--賨人谷2.7KM。\n3、成都方向:\n成都(G42沪蓉高速)——南江/渠县出口224.9KM(G42沪蓉高速)——往渠县73.1KM(G318)——往临巴23.3.3KM--賨人谷2.7KM。', NULL, NULL, 'http://travel.qunar.com/p-oi8595801-congrengu', '', '', '', '3', '1', '2', 5, 44743, 209, NULL, NULL, NULL, 38726, 15780, 28168, 13753, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (165, '7475306', '古窑民俗博览区', NULL, '江西 > 景德镇 > 景德镇景点 > 古窑民俗博览区', '景德镇', '景德镇', 'https://img1.qunarzz.com/travel/d2/1604/e7/e7e71c7ee32b37f7.jpg;https://img1.qunarzz.com/travel/d3/1505/fe/2ed0e54ebcdfd7.jpg;https://img1.qunarzz.com/travel/d0/1604/d0/54c1488241e4f0f7.jpg;https://img1.qunarzz.com/travel/d1/1701/55/6ef721e45e1871b5.jpg;https://img1.qunarzz.com/travel/d9/1701/fb/489cd84f6f8ecdb5.jpg;https://img1.qunarzz.com/travel/d2/1708/3b/97e2ff88d614bbb5.jpg', '4.5', '景德镇景点排名第2', '2小时 - 3小时', '62', '29.28948', '117.190701', '47', '景德镇古窑民俗博览区分为历史古窑展示区、陶瓷民俗展示区和水岸前街创意休憩区，这里保存了中国乃至世界上最为丰富独特的、最为完整的陶瓷文物遗存：古建筑、古作坊、古工艺、古窑房、古瓷俗、古园林，以实物形式生动地再现了十五、十六世纪瓷都景德镇的历史风貌，成为景德镇悠久陶瓷历史文化的缩影。历代古窑展示区内有古代制瓷作坊、世界上最古老制瓷生产作业线、清代镇窑、明代葫芦窑、元代馒头窑、宋代龙窑、风火仙师庙、瓷行等景点，向人们展示了古代瓷业建筑，明清时期景德镇手工制瓷的工艺过程以及传统名瓷精品。', '景德镇市昌江区瓷都大道古窑路1号', '0798-8534444', 'http://www.chinaguyao.com/view/', '8:00-17:00', 10455, '四季皆宜。', '自驾\n1、高速路口景德镇西→迎宾大道→西客站右转→瓷都大道→开门子大酒店斜对面→右转进入古窑民俗博览区。\n2、高速路口景德镇北→金岭大道→紫晶路→西山路→左转瓷都大道→开门子大酒店斜对面→左转进入古窑民俗博览区。\n公交\n1路、1路区间、2路内环、2路外环、6路、9路、11路、16路、103路内环、103路外环、123路、303路、K106路“枫树山”站下。', NULL, '2-3小时', 'http://travel.qunar.com/p-oi7475306-guyaominsubolanqu', '2', '', '4', '5', '', '4', 2, 32299, 63, 44983, NULL, 113037, 363932, NULL, 114866, 53578, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (166, '7469388', '金水台旅游度假区', NULL, '广东 > 新兴 > 新兴景点 > 金水台旅游度假区', '云浮', '新兴', 'https://img1.qunarzz.com/travel/poi/1803/ed/82d4f53709a9a137.jpg', '4', '新兴景点排名第1', '3天', '1', '22.6208666152', '112.4860624988', '0', '天上仙境有瑶台，禅宗福地金水台。金水台旅游度假区坐落于中国禅宗六祖慧能的故乡，是一所按国际五星级标准设计与规划的集旅游度假、商务会议、餐饮住宿、健身休闲、温泉疗养、中外贵宾接待为一体的特色大型温泉度假村。\n度假村包含金水台温泉、金水台漂流和水上乐园，来到这里，不但可以饱览这里优美的湖光山色，还可以以天然负离子清洗心肺，聆听百鸟欢唱，其中滋味，须身临其境而得之。 金水台度假村水上乐园堪称粤西地区最大的水上乐园，拥有超级水上娱乐设施，节假日期间，可以带上家里的小朋友一同游玩，一定会其乐融融。\n金水台温泉池区面积达3万多平方米，建有30多个特色不一的温泉池，温泉冲浪池、温泉滑梯、大型温泉水疗池等。金水台漂流，如入仙境博舟，似在画中畅游，激流险滩迭起，闲情雅致逍遥、整个漂流让人心肠回荡！\n金水台度假村水上乐园堪称粤西地区最大的水上乐园，拥有超级水上娱乐设施。', '广东省云浮市新兴县水台镇', '0766-2513300', NULL, '金水台温泉：8:00-24:00。', 14770, '四季皆宜', '自驾\n1、九江→龙高路→高明大桥→肇庆方向至杨和，江门方向→杨梅镇，宅梧方向18公里至金水台漂流\n共和出口→鹤城方向→双和公路→彩虹隧道至宅梧右转按指示牌行走至20公里左右到达金水台漂流 佛山→金水台漂流\n佛开高速→高明大桥→肇庆方向至杨和江门方向→杨梅镇，宅梧方向18公里至金水台漂流\n共和出口→鹤城方向→双和公路→彩虹隧道至宅梧右转按指示牌行走至20公里左右到达金水台漂流\n二环→高明大桥→肇庆方向至杨和，江门方向→杨梅镇，宅梧方向18公里至金水台漂流\n3、中山→中江高速→江鹤高速→共和出口→鹤城方向→双和公路→彩虹隧道至宅梧右转按指示牌行至20公里左右到金水台漂流江门→江鹤高速→共和出口→鹤城方向→双和公路→彩虹隧道至宅梧右转按指示牌行至20公里左右到达金水台漂流\n4、深圳→广深高速→虎门大桥→中江高速→江鹤高速→共和出口→鹤城方向→双和公路→彩虹隧道至宅梧右转按指示牌行至20公里左右到达金水台漂流\n5、珠海→江珠高速→中江高速→江鹤高速→共和出口→鹤城方向→双和公路→彩虹隧道至宅梧右转按指示牌行至20公里左右到达金水台漂流　　新兴→双和公路→宅梧左转按指示路牌行至20公里左右到金水台漂流　　东莞→虎门大桥→中江高速→→江鹤高速→共和出口→鹤城方向→双和公路→彩虹隧道至宅梧右转按指示牌行至20公里左右到达金水台漂流　　阳江→开阳高速→水口落（共和落）→宅梧方向18公里至金水台漂流。', NULL, NULL, 'http://travel.qunar.com/p-oi7469388-jinshuitailu:youdujia', '3', '', '1', '', '2', '1', 4, 37268, 77, 78803, NULL, 5008, NULL, 39853, 23440, 6017, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (167, '9555231', '山东香山国际旅游度假区', NULL, '山东 > 莱芜 > 莱芜景点 > 山东香山国际旅游度假区', '莱芜', '莱芜', 'https://img1.qunarzz.com/travel/poi/1701/97/cbe61e4a6a3df337.jpg', '--', '莱芜景点排名第29', NULL, '0', '36.396725', '117.445793', '0', '山东香山国际旅游度假区地域广阔，气候湿润，地质生成年代久远，其博大的山体内外，孕育着无尽的自然资源，旅游区内的香山和王石门天上人家是主要游览景区。香山是莱芜市较高的山，傲立于鲁中地区的连绵群山之中，主峰918.7米，因山中盛产香草得名。香山拥有玉皇极顶、香山行宫、奇伟自然造型的“仰佛观天”、“紫霞望月”等名胜。王石门天上人间的万米仿古长城石砌步游道，蔚为壮观；300亩。天上牡丹，雍容华贵；万亩槐花谷，似皑皑雪原，香酽醉人；九天画廊，溪流潺潺，植被茂密，古树、奇石、飞瀑，一步一景，似漫漫展开的浓彩画卷。天潭、天池、石门湖、天峡、香潭等水库、塘坝，像一个个璀璨的明珠，碧波荡漾，熠熠生辉。山东香山国际旅游度假区是集观光、游憩、休闲、度假、会议、拓展等为一体的生态旅游度假区。', '莱芜市莱城区大王庄镇政府驻地', '0634-5871999', 'http://www.xiangshanlvyou.com/lxwm.shtml', '08:00～18:00', 1986, NULL, '自驾\n1、莱芜北出口，经汇河大道，S244到大王庄镇政府驻地进入景区。\n2、泰莱高速杨庄出口，沿S244直行到大王庄镇政府驻地进入景区。\n3、市内驾车经莱城大道、汇河大道、S244到大王庄镇政府驻地进入景区。\n\n', NULL, NULL, 'http://travel.qunar.com/p-oi9555231-shandongxiangshanguoji', '', '', '3', '', '', '4', 4, 39442, 135, NULL, NULL, 20013, NULL, NULL, 66930, 41170, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (168, '710663', '沙家浜', NULL, '江苏 > 常熟 > 常熟景点 > 沙家浜', '苏州', '常熟', 'https://img1.qunarzz.com/travel/d7/1603/8b/8ee465a20f4fddf7.jpg;https://img1.qunarzz.com/travel/d5/1610/70/7e47fcda617850b5.jpg;https://img1.qunarzz.com/travel/d6/1605/95/53a4479652ff5f7.jpg;https://img1.qunarzz.com/travel/d4/1508/99/8c74ff46ddc5fc.jpg;https://img1.qunarzz.com/travel/d3/1502/7e/5e87d7342d7c0.jpg;https://img1.qunarzz.com/travel/d9/1502/2a/5d39713a8a56c9.jpg', '4.7', '常熟景点排名第20', '2小时 - 3天', '37', '31.559447', '120.807985', '0', '沙家浜风景区是全国爱国主义教育示范基地、全国百家红色旅游经典景区、国家AAAA级旅游区、华东地区最大的生态湿地之一，一区占地1000多亩，已建成革命传统教育区、水生植物观赏区、红石民俗文化村、芦苇水陆迷宫、美食购物区等功能区域和竹林幽径、阡陌苇香、柳堤闻浪、隐湖问渔、双莲水暖等一批景点。2004年底，为配合好30集电视连续剧《沙家浜》的拍摄，投资1500多万元，建成了沙家浜水乡影视基地。', '江苏省苏州市常熟市芦苇荡路沙家浜镇', '0512-52500000', 'http://www.shajiabang.com/', '3月-11月8:00-17:00，12月-次年2月8:00-16:30。', 5118, '沙家浜一年四季都是景。四季前往皆可\nTips:因为毗邻阳澄湖，所以9、10月，可以品尝到正宗的大闸蟹.', '自驾\n1、上海出发：上海—沪嘉高速（A11）—沿江高速—苏嘉杭高速—沙家浜出口下，上海—沪宁高速（苏州段）—苏嘉杭—沙家浜出口下，行程约1小时。\n2、杭州出发：杭州——苏嘉杭高速——沙家浜出口下，行程约1.5小时。\n3、南京出发：沪宁高速—转沿江高速（常州段）—苏嘉杭高速—沙家浜出口下，行程约2小时。\n4、常州出发：常州—沪宁高速—苏嘉杭高速—沙家浜出口下高速直行第二个红灯左拐，即到沙家浜镇，行驶约1.5小时。\n\n公交\n各地到常熟的大巴，转乘沙家浜专线车5路（1元）到【沙家浜风景区】下，或直接打的40元左右，请说明是到沙家浜风景区而不是沙家浜镇。', NULL, NULL, 'http://travel.qunar.com/p-oi710663-shajiabang', '', '', '', '5', '', '3', 9, 35406, 86, NULL, NULL, NULL, 240304, NULL, 50508, 27000, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (169, '711839', '壶口瀑布', NULL, '陕西 > 宜川 > 宜川景点 > 壶口瀑布', '延安', '宜川', 'https://img1.qunarzz.com/travel/d8/1502/41/f9f67a9a76f762.jpg;https://img1.qunarzz.com/travel/d9/1703/89/ee72236a13350fb5.jpg;https://img1.qunarzz.com/travel/poi/1803/84/afe65fec50fcac37.jpg;https://img1.qunarzz.com/travel/d8/1706/af/ed57901837fc09b5.jpg;https://img1.qunarzz.com/travel/d6/1706/1a/dcc6caf1a5a093b5.jpg;https://img1.qunarzz.com/travel/d9/1706/12/bbdcfef46c5f15b5.jpg', '4.7', '宜川景点排名第1', '1.5小时 - 4小时', '137', '36.119217', '110.461441', '5', '壶口瀑布是黄河中游流经秦晋大峡谷时形成的一个天然瀑布，是中国仅次于贵州省黄果树瀑布的第二大瀑布，也是世界上最大的黄色瀑布。以壶口瀑布为中心的风景区，集黄河峡谷、黄土高原、古塬村寨为一体，展现了黄河流域壮美的自然景观和丰富多彩的历史文化积淀。由于四季气候和水量的差异，壶口景色也有所改变。壶口景色，四季各异，严冬时江水歇息，冰封河面；春来则凌汛咆哮，如雷贯耳；盛夏则大水盈岸，颇为壮观；秋季则浩浩汤汤，彩虹通天。一般而言壶口瀑布最佳观赏期有2个，一是春季4～5月份，漫山遍野的山桃花盛开，冻结的冰水消融，称为“三月桃花汛”；二是秋季9~11月份雨季刚刚过去，周边众多山泉小溪，汇集大量清流，阵阵秋风吹过，常有彩虹出现，叫做“壶口秋风”。这两个时期，水大而稳，瀑布宽度可达千米左右。陕西侧的壶口瀑布是侧面视角，属于壶口瀑布的上游，从高处往下看，可以看到黄河注入壶口，特别是水量大的时候，气势足。', '延安市宜川县壶口乡境内', '0911-4838030', NULL, '景区开放时间：08：00～18：00', 19132, '壶口瀑布最佳观赏期分为两段，一是春季4-5月份，正值农历三月间，漫山遍野的山桃花盛开，岸边冻结的冰崖消融，称为“三月桃花汛”；二是秋季9-11月份雨季刚过去时，河边众多山泉小溪，汇集大量清流，阵阵秋风吹过，常有彩虹出现，叫做“壶口秋风”。这两个时期，水大而稳，瀑布宽度可达千米左右。', '自驾\n1、西安——壶口自驾游：由西安未央北高速入口上包茂高速，在洛川附近从太原/兰州出口离开，朝临汾/太原方向进入青兰高速，到壶口出口下高速，进入309国道行驶约4.6公里即可到达主景区。全程约300公里，需时3小时40分钟，沿途路标清楚，有铜川、宜君、黄陵、洛川、壶口等服务站提供加油、餐饮等服务。\n2、西安——壶口自助游：西安城东客运站有西安至壶口专线大巴，每天上午8点整和下午1点20各一班，分别在下午1点30左右和6点左右到达壶口景区，中途会在宜川县城做10~30分钟休整，为乘客留餐饮时间。全价车费随车型、站务管理费和燃油附加费变化在110元左右波动。城东客运站咨询电话(029)82525048。\n3、延安——壶口自驾游：可由延壶旅游专线直达壶口，全程124公里，标示限速60公里，路况良好。延壶公路起于延安南约5公里，路口右转至燕沟，有路标指示壶口方向。沿途路标清楚，有大的村镇提供加油、餐饮、购物等服务。经过村镇时需注意行人。过云岩镇后大车增多，驾驶需谨慎。\n4、延安——壶口自助游：延安市宝塔区汽车南站乘坐延安至壶口旅游专线中巴，每天上午7：30、8：20，下午3:30各一班，分别在中午12：00、下午2:00、次日上午7:30返程。随车乘务员会为旅客介绍壶口旅游注意事项。', '安全注意事项\n1、瀑布两边的河岸全是较为平整的大岩石，表面覆盖着一层黄土，瀑布没有围栏，所以要特别注意安全，不要站得太靠近；路途较远，最好带些食品，路上食用；\n2、参观瀑布时，应注意安全，不要靠近防护栏杆，更不能靠压栏杆，听从工作人员安排；\n3、夏季天气较热，应该注意防暑降温；秋季，涨水季节，参观时应该注意听从景区工作人员安排，警惕涨水带来的危险；\n4、壶口瀑布景区旺季（每年4月至10月底）观光车往返收费为25元/人，淡季（每年11月至次年3月底）观光车往返收费为20元/人，壶口瀑布至克难坡观光车往返收费为30元/人。\n摄影注意事项\n1、喜欢摄影留念的朋友要特别注意，由于壶口水雾极大，只要相机对准瀑布拍摄，必会满身泥点。为此必须采取预防措施，可将塑料袋底剪开，套在相机上，用橡皮筋在镜头处一勒，镜头前再加一片uv镜。\n2、每年十月是壶口瀑布水量最大的时候，也是去壶口旅游的最好的时候，由于气温较低，须携带后衣服。摄影的最佳时候在5月到7月之间，由于瀑布附近水雾较大注意摄影器材的防潮。', NULL, 'http://travel.qunar.com/p-oi711839-hukoupubu', '', '', '1', '5', '', '5', 3, 38705, 115, NULL, NULL, 21727, 349727, NULL, 98800, 95586, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (170, '705873', '三岔湖', NULL, '四川 > 资阳 > 资阳景点 > 三岔湖', '资阳', '资阳', 'https://img1.qunarzz.com/travel/d1/1608/b9/9b8212294c3bf6b5.jpg;https://img1.qunarzz.com/travel/poi/1409/26/732c655b9d8bf779ffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d6/1803/54/e67fb2334aa75fb5.jpg;https://img1.qunarzz.com/travel/d1/1803/a4/b07a5496995a82b5.jpg;https://img1.qunarzz.com/travel/d5/1803/4b/882f363e4ad204b5.jpg;https://img1.qunarzz.com/travel/d1/1803/d9/b91b88340dde3cb5.jpg', '4.1', '资阳景点排名第2', '2小时', '10', '30.302469', '104.273386', '0', '三岔湖是四川省第二大湖泊，湖区水域面积27平方公里，湖岸线长240公里。湖面及周边风景优美，号称“天府明珠”。湖区以人工湖泊为中心，湖面汊道纵横、岛屿密布，有大小岛屿113个，以岛为主的景点有天鹅山庄、花岛、月亮岛、鸟岛、斩龙岛、女祸石、白虎等10余处；湖周山区有幽美险峻的丹景山、张飞营、乾封庙、三峨眉、牛角寨、石洞沟和古老的摩岩石刻等景观。三岔湖景区湖水晶莹、山水交融、群岛争秀，加之气候宜人、四季分明、各种服务设施一应齐全，是理想的旅游、疗养、度假、烧烤、水上运动的好去处。', '四川省简阳市三岔镇', NULL, NULL, '全天', 19806, '夏季最为适宜。', '自驾\n1、走天府第一高速公路—成渝高速公路从资阳市北郊穿过。\n2、成渝铁路、321国道（广州至成都）、319国道（厦门至成都）、318国道（上海至拉萨）亦可到达。', NULL, NULL, 'http://travel.qunar.com/p-oi705873-sanchahu', '', '2', '2', '', '', '2', 9, 37307, 106, NULL, 22142, 34030, NULL, NULL, 33851, 13262, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (171, '715227', '黄花沟', NULL, '内蒙古 > 乌兰察布 > 乌兰察布景点 > 黄花沟', '乌兰察布', '乌兰察布', 'https://img1.qunarzz.com/travel/d8/1707/cb/1a2cabefb4102cb5.jpg;https://img1.qunarzz.com/travel/d2/1802/24/730fcc51c1735fb5.jpg;https://img1.qunarzz.com/travel/d8/1802/57/b93eb1fd056a6db5.jpg;https://img1.qunarzz.com/travel/d7/1802/b8/0f34aae19506e0b5.jpg;https://img1.qunarzz.com/travel/d3/1707/93/337e0e12076044b5.jpg;https://img1.qunarzz.com/travel/d0/1707/75/edb6a63c938dbbb5.jpg', '3.4', '乌兰察布景点排名第4', '2小时 - 3小时', '20', '41.150496', '112.549464', '10', '黄花沟由于它具有得天独厚的秀丽景色和便利的交通，吸引了大批的中外游人。50多平方公里的辉腾席勒草原，土地肥沃，降雨充沛，每到5月至9月间，整个草原水草丰盛，鲜花遍野，牛羊游动，牧歌撩亮，香风温馨，绘织成一幅美丽的草原春画。而被游人誉为“塞外江南”的黄花神葱沟，更有一番诗情画意，它集山水灵秀于一体，山势险峻，奇峰突兀，曲径通幽，鸟语花香，水流潺潺，鹰翔长空。“双驼峰”、“卧龙峰”、“剑门山”、“佛山”、“神龟岭”、“挂瀑崖”、“仙人洞”、“三叠泉”、“一镜天”、“木鱼台”和“神葱岩”，堪称塞外奇观。5000余亩的独贵林区，森林茂密，古柏参天，那林间富有弹性的绿色草坪中，白蘑菇、黄花、百合花点缀其间，这更为大自然增添了美丽色彩。奇观森林和厅花异草组成了天然的大花园。黄花神葱沟接待部，有蒙古多顶，其中有大厅一个，中包一个小包多个，可以就餐、休息和娱乐，这里还为游人开设骑马、射箭等娱乐活动，举办歌舞和篝火晚会。', '乌兰察布市察哈尔右翼中旗辉腾锡勒草原上', NULL, NULL, '淡季（4月15-6月14日，9月1-10月10日）：8:00-17:00；旺季（6月15-8月31日）：7:30-17:30', 1137, NULL, '自驾\n北京向西延京藏高速——乌兰察布——从卓资/凉城出口离开靠右进入卓资山立交——进入X560——辉腾锡勒。', NULL, NULL, 'http://travel.qunar.com/p-oi715227-huanghuagou', '4', '', '4', '2', '3', '3', 8, 20422, 126, 154559, NULL, 85032, 34219, 98351, 99401, 21778, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (172, '9281452', '广西烟霞山风景区', NULL, '广西 > 灵山 > 灵山景点 > 广西烟霞山风景区', '钦州', '灵山', 'https://img1.qunarzz.com/travel/poi/1606/e8/9db658cb57b979a.jpg', '--', '灵山景点排名第3', NULL, '0', '22.482641', '109.091023', '0', '广西烟霞山风景区位于广西钦州市灵山县烟墩镇，其地貌是由红色砂岩构成（形成于6000万年前的河、湖中），经过百千年的流水塑造形成巍峨独特的地形，地理学称为丹霞地貌。且发育典型、类型齐全、形态丰富、具有雄、奇、秀、险、幽、奥、旷等特点，如神似老人脸庞的老子峰、阳刚奇特的擎天一柱、秀丽雄伟的花龙岭等丹霞奇观。奇山亦有妙水，如流觞曲水般的银柳瀑布，柳暗花明的洞天瀑布，潺潺如歌的梦蝶瀑布，无一不显丹霞生态景观之美。烟霞山风景区自然生态动植物资源非常丰富，如野猪、猪仔狸、白鹭、豹猫等多种珍稀野生动物及野生石斛、金银花、田七、灵芝草等多种名贵中草药植物。景区秉持着乡村与生态相结合的建设理念，融入本土民俗文化特色，开展跳岭头、烟墩大鼓表演等非遗文化项目展示，设有民俗博物馆、各类科普教育活动场地。配备野外烧烤、农家乐、荔枝等农作物采摘、野外露营、运动拓展、动物逗趣等野趣项目。实属亲子游玩、野外拓展采风、乡村生态游玩休闲度假的上佳选择。', '广西钦州市灵山县烟墩镇', NULL, NULL, '9:00-17:30', 5766, NULL, '自驾\n朝南宁国际会展中心/G80/广昆高速/G72方向-直行进入泉南高速公路-朝柳州/梧州/G80/G72方向-朝柳州/桂林/梧州/广州方向-朝钦州港/防城港/北海/湛江方向，靠右进入六钦高速公路-从新福/沙坪/刘圩出口离开-S103-朝灵山方向，靠右-根据景区指示牌指引，到达游客服务中心。', NULL, NULL, 'http://travel.qunar.com/p-oi9281452-guangxiyanxiashanfeng', '4', '5', '5', '1', '2', '4', 2, 30188, 102, 97557, 348418, 244425, 21617, 42140, 110054, 53118, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
