INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (174, '707083', '壶口瀑布', NULL, '陕西 > 宜川 > 宜川景点 > 壶口瀑布', '延安', '宜川', 'https://img1.qunarzz.com/travel/d5/1611/ca/a65a7adcef3255b5.jpg;https://img1.qunarzz.com/travel/d7/1508/4e/5e526c847a5575.jpg;https://img1.qunarzz.com/travel/d6/1704/d1/f7fed801d5e278b5.jpg;https://img1.qunarzz.com/travel/d6/1704/56/907d1f0eb205c6b5.jpg;https://img1.qunarzz.com/travel/d6/1704/5d/f801e8d90fec86b5.jpg;https://img1.qunarzz.com/travel/d5/1805/6a/4660605ca194eb5.jpg', '4.6', '宜川景点排名第1', '3小时', '131', '36.15146', '110.455452', '12', '黄河水从两岸形状各异的冰凌、层层叠叠的冰块中飞流直下，激起的水雾在阳光下映射出美丽的彩虹，瀑布下搭起美丽的冰桥，令人不禁慨叹大自然的鬼斧神工。地理位置为北纬36度8分10秒，东经110度26分40秒，海拔448.1米。以壶口瀑布为中心，北至马粪滩，南至小船窝，西至峡谷地域，东至人祖山，总面积100公里。东距吉县县城45公里，距尧都临汾市169公里；西距陕西宜川县城49公里；距革命圣地延安170公里；北距山西省省会太原387公里；南距陕西省省会西安449公里。山西侧的壶口瀑布在壶口的正面，可以下到瀑布下近距离观赏瀑布和黄河，山岩多。视角好。景区内有观瀑洞（龙洞），是一天然石洞，在瀑布的下方，从下往上看瀑布，瀑布壮观无比。', '临汾市吉县壶口镇内', '0357-7922673', 'http://www.hukoupubu.cc', '08：00－18：00　', 6811, '4月~5月称为“桃花汛”，9月~10月称为“壶口秋风”，这个时期水量大而稳定，瀑布宽度可达千米左右，是去壶口瀑布旅游的最佳时间。', '自驾\n从临汾市驾车沿临吉高速向吉县方向行驶约100公里至吉县县城，再行车45公里至壶口镇即到壶口瀑布景区，全程约1.5小时。\n\n', '壶口瀑布景区旺季（每年4月至10月底）观光车往返收费为25元/人，淡季（每年11月至次年3月底）观光车往返收费为20元/人，壶口瀑布至克难坡观光车往返收费为30元/人。', NULL, 'http://travel.qunar.com/p-oi707083-hukoupubu', '3', '', '3', '3', '', '2', 3, 27812, 153, 72852, NULL, 66256, 26197, NULL, 34752, 11053, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (175, '8446355', '后柳古镇', NULL, '陕西 > 安康 > 安康景点 > 后柳古镇', '安康', '安康', 'https://img1.qunarzz.com/travel/poi/1507/46/387560fee4dfcf.jpg', '5', '安康景点排名第2', NULL, '1', '32.927946', '108.231844', '0', '后柳镇位于石泉县南部，从县城顺汉江而下20公里处，石泉至紫阳公路穿境而过，交通便利，素有汉江水码头之称。南下20公里至燕子洞景区，往西10公里可至中坝大峡谷景区。镇内文物古迹众多，人文资源荟萃，文化底蕴深厚，旅游资源丰富。站在高坡上俯视小镇，就会被小镇溢出的种种美丽所陶醉，看小镇的临江处，是小镇古建筑的集中区。最东角处，有几间土墙青瓦民居，土旧的程度少说也有百年之久了，其间有一棵大树从房屋中间昂然而起，直出屋顶，伞盖全房，被人称为“屋包树”。屋包树的旁边便是一条曲折石阶小径，弯弯折折向上，形成一条小街。小街旁边，有一火神庙，至今保存。还有一家王氏，上个世纪家中出了一名抗日名将王范堂，在台儿庄战役中以敢死队长的身份带领敢死队杀入日寇群，大获全胜，美名远播，至今王范堂的英雄大名还留存在台儿庄战役纪念馆内。', '陕西省安康市石泉县南部', NULL, NULL, '全天。', 18788, '一年四季风景各异，春秋两季温度适宜，适合出游。', '自驾\n从县城自驾顺汉江而下20公里处，石泉至紫阳公路穿境而过。', NULL, NULL, 'http://travel.qunar.com/p-oi8446355-houliuguzhen', '3', '', '5', '5', '5', '5', 8, 30037, 184, 98707, NULL, 196088, 89433, 110012, 104858, 110393, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (176, '8667998', '大漠风情园', NULL, '宁夏 > 中卫 > 中卫景点 > 大漠风情园', '中卫', '中卫', 'https://img1.qunarzz.com/sight/p0/1507/50/e3fa2783d34baa7633ca64732373c3d1.water.jpg', '4.7', '中卫景点排名第55', NULL, '0', '37.504315', '105.099971', '0', '大漠风情园是集丝路风情、伊斯兰风情和大漠风光为一体的沙漠生态休闲度假旅游区，毗邻国家AAAAA级旅游区-----沙坡头，处于沙坡头景区、通湖草原景区、金沙岛休闲度假区和沙坡头旅游新镇交集的中心位置。景区旅游项目丰富多样，主要景点有大漠观海楼、丝路长街、狂野地带、星辰帐篷酒店、水上世界等，独具特色，将成为西部最大的沙漠项目体验俱乐部和休闲度假的大本营。', '宁夏中卫市迎闫公路与小湖路交汇处', NULL, NULL, '8:00—18:00', 13009, '此地夏季炎热，冬季寒冷，建议春、秋两季前往游览。', '自驾\n从中卫市出发，沿中央西大街向西行驶约9公里，到达目的地。', NULL, NULL, 'http://travel.qunar.com/p-oi8667998-damofengqingyuan', '', '', '5', '5', '', '4', 1, 46747, 103, NULL, NULL, 167169, 83998, NULL, 57229, 55052, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (177, '3864255', '小浪底黄河三峡', NULL, '河南 > 济源 > 济源景点 > 小浪底黄河三峡', '济源', '济源', 'https://img1.qunarzz.com/travel/d5/1505/69/31512f5f6cee09.jpg', '3.6', '济源景点排名第1', NULL, '7', '35.1355188886', '112.561099539', '1', '景区位于小浪底大坝上游20公里处，总面积40平方公里。地处北部山岳旅游带与南部黄河旅游带（三点一线）的水陆接合部、国家级风景区王屋山、小沟背景区与小浪底游线的枢纽地段。\n黄河三峡群峰竞秀、山水交融，三条峡谷各有千秋。孤山峡鬼斧神工、群峰竞秀；龙凤峡九曲十折、峡深谷幽；八里峡，峭壁如削、雄伟壮观，号称“万里黄河第一峡”。不仅山、水、崖、洞和谐交融，自然资源丰富，而且还有鲧山禹斧、犀牛望月、孟良活地、京娘化凤、石人顶石山、章公背章婆等自然人文景观，景点达80余处。是小浪底旅游资源之精华所在。水绕青山山绕水，船在青山顶上行。景区山水交融、港湾交错、高峡平湖、奇峰林立，构成一幅北方少有的江南之美、水乡之秀的壮丽画卷，完全可以和长江三峡媲美。登上被称之为“万里黄河第一楼”的大河楼，但见水天一色，满山挂翠，大河如带，游船穿梭，水鸟翻飞，美不胜收。\n小浪底黄河三峡，峰峦雄崎，危崖耸立，似鬼斧神工；林海浩瀚，烟笼雾锁，如飘渺仙境；高峡平湖，山水一色，有漓江神韵。既有南国山水的柔媚与婉约，又不失北方山水的雄健与阳刚，集南北山水之大成。荡舟碧波上，仿佛进入了连绵不断的画卷，真是“舟行碧波上，人在画中游。”', '河南省济源市西环路中段28号', '0391-6678888', NULL, '7:00-18:00', 8181, NULL, '自驾\n到济源市后走济邵公路（S312）邵原、垣曲方向或转济邵高速（S28）邵原方向至王屋山站下高速，右转7公里上济邵公路（有路标）\n大巴\n抵达济源市后乘坐市区—黄河三峡景区大巴直达景区', NULL, NULL, 'http://travel.qunar.com/p-oi3864255-xiaolangdihuanghesanxia', '', '', '', '4', '5', '2', 4, 33625, 201, NULL, NULL, NULL, 195688, 287941, 22206, 13476, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (178, '703251', '白水洋', NULL, '福建 > 屏南 > 屏南景点 > 白水洋', '宁德', '屏南', 'https://img1.qunarzz.com/travel/d3/1505/1e/6fbc134453906e.jpg;https://img1.qunarzz.com/travel/poi/1803/7a/097aebb1d7d5c837.jpg;https://img1.qunarzz.com/travel/d1/1702/73/30698875e1b50ab5.jpg;https://img1.qunarzz.com/travel/d9/1702/7a/7e1a1252c64a37b5.jpg;https://img1.qunarzz.com/travel/d8/1702/3b/90a5c48bc8aa77b5.jpg;https://img1.qunarzz.com/travel/d6/1702/27/c829ed08a2adffb5.jpg', '4.7', '屏南景点排名第1', '4小时 - 6小时', '9', '27.070343', '119.058681', '2', '白水洋景区因其奇特的地质地貌现象而被誉为“天下绝景，宇宙之谜”。这里有“百米冲浪滑道”，长约百米，宽近60米，游客只需要仰面躺在滑道上，就可以凭借溪水冲力向下滑去。白水洋经地质专家考察，确认为世界稀有的浅水广场，由一块巨石铺展水底，面积达8万平方米，且与周围的山体相连，浑然天成，石面平滑如砥，无缝无苔；石上布水均匀，仅没脚踝，清水在阳光照耀下，波光潋滟，色似白银，令人称奇。白水洋地质公园还拥有全球唯一的鸳鸯弥猴自然保护区。白水洋地质公园园区总面积达77.34平方公里。', '宁德市西北部屏南县境内', '0593-3309388', 'http://www.yyx.com.cn', '8:00-17:00', 1380, '宁德温暖湿润，四季分明，每年的4月至12月是最佳旅游时节。', '自驾\n福州-屏南\n1. 福州市区可从福州东（马尾），福州北（五四北）上福宁高速至漳湾出口（宁德出口的下一个出口）下高速。\n2. 104国道到八都，再走宁屏二级路到屏南\n\n厦门-屏南\n1. 沈海高速往温州方向至漳湾出口（宁德出口的下一个出口）下高速。\n2. 104国道到八都，再走宁屏二级路到屏南\n\n温州-屏南\n1. 南白象入高速上沈海高速往福州方向至漳湾出口（宁德出口的前一个出口宁德北出口）下高速。\n2. 104国道到八都，再走宁屏二级路到屏南。', NULL, '1天', 'http://travel.qunar.com/p-oi703251-baishuiyang', '', '', '1', '5', '4', '5', 7, 37883, 187, NULL, NULL, 8612, 391690, 175799, 147894, 89208, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (179, '9195523', '九龙洞', NULL, '山西 > 壶关 > 壶关景点 > 九龙洞', '长治', '壶关', 'https://img1.qunarzz.com/travel/poi/1604/fb/1751924a2401bef7.jpg', '--', '壶关景点排名第9', NULL, '0', '35.923451', '113.583979', '0', '九龙洞位于万佛寺之南鹅屋乡五里沟村圈马窑自然村，在村西九头山的山腰间，是一个天然石灰岩溶洞。20世纪30年代末始被人发现，因洞内有9个支洞，洞内套洞，洞洞相通，洞中有泉，水滴成乳，钟灵毓秀，脉真气胜，故名九龙洞。主洞右侧壁上有状如莲花、牡丹、石榴花等花卉的钟乳石图案。左侧洞中间凸出恰似龙体，其鳞片如人工雕刻，排列有序，龙体将三洞连接，内洞顶端状如一只鼓眼宽嘴青蛙从洞顶伸出，直视洞口，一股20余米高，1.5米宽的飞瀑从口中喷射而出。', '山西省长治市壶关县桥上乡', NULL, NULL, '8:00-18:00', 1856, NULL, '自驾\n附近无公交站，适合自驾前往。', NULL, NULL, 'http://travel.qunar.com/p-oi9195523-jiulongdong', '', '2', '', '3', '3', '2', 3, 38540, 122, NULL, 45408, NULL, 74807, 81055, 36776, 12223, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (180, '7475735', '风车岛', NULL, '广东 > 汕尾 > 汕尾景点 > 风车岛', '汕尾', '汕尾', 'https://img1.qunarzz.com/travel/poi/1409/16/55b6b2a1e369b612ffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/poi/1803/67/db0e9666e4e44c37.jpg;https://img1.qunarzz.com/travel/d3/1602/6b/c00de36026fc62f7.jpg;https://img1.qunarzz.com/travel/d5/1602/f4/55030cb670b5d8f7.jpg;https://img1.qunarzz.com/travel/d5/1602/87/c8597d0fbea892f7.jpg;https://img1.qunarzz.com/travel/d5/1602/31/17b29007bf3ff3f7.jpg', '4.6', '汕尾景点排名第40', '2小时 - 3天', '7', '22.667721', '115.577697', '7', '施公寮村即风车岛，位于汕尾市红海湾遮浪半岛附近，风车岛海水清澈透明，自然景光十分迷人。岛上海滩基本上人迹罕至，在万里无人的海滩，有草亭伶仃的立着，甚至有一种爱琴海的味道。在岛上，任何所见都成了入镜的元素，是一个非常适合拍照的“梦幻之岛”，这里也是情侣度假、拍摄婚纱照的胜地，相信来到这里，你会被它的美深深打动。风车岛也是一个三面环海的半岛，里面有3个自然村，有个风力发电场，建有不少雪白的风车。风车在风车岛的内海一侧，风平浪静，浪头一般不高于5公分。碧海蓝天风车白鹭．这里海滩的沙子较为坚实，且非常白细。该海岸因人烟较少，长年的冲积，积满了各色各样的贝壳。岛上的风车群是风力发电群，风车塔杆的高度大约与都市中的15层楼相当， 十分宏伟。', '广东省汕尾市红海湾附近', NULL, NULL, '全天', 4640, '夏季旅游最佳，能够下海体验。', '自驾\n广汕公路－－北二环高速－－广惠高速－－深汕高速－－汕尾浦边出口－－进入省道，约2公里后转左，约22公里可到红海湾，在距离遮浪还有6公里处会看到一个路牌，指示前往施公寮的，按路牌提示即可。\n巴士\n1.位于遮浪半岛的东侧，在南澳可以坐三轮车前往，费用15元每辆，最多可坐5人，需20分钟左右\n2.如果是在汕尾市区，可乘巴士前往，车程40分钟即可到达。', NULL, '1天', 'http://travel.qunar.com/p-oi7475735-fengchedao', '', '2', '2', '5', '', '5', 3, 29055, 139, NULL, 30614, 19181, 346457, NULL, 344894, 98861, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
