INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (183, '9196174', '八路军文化园', NULL, '山西 > 武乡 > 武乡景点 > 八路军文化园', '长治', '武乡', 'https://img1.qunarzz.com/travel/d4/1605/f7/e140b20a8ec8d9f7.jpg;https://img1.qunarzz.com/travel/d9/1605/d0/563aec9e7e862f7.jpg;https://img1.qunarzz.com/travel/d3/1605/9d/80dd71dfdec018f7.jpg;https://img1.qunarzz.com/travel/d6/1605/58/7f1f4f0a88197bf7.jpg;https://img1.qunarzz.com/travel/d2/1605/8a/bfc2b80a6fe0e8f7.jpg;https://img1.qunarzz.com/travel/d4/1605/f3/e948372a366887f7.jpg', '5', '武乡景点排名第3', NULL, '5', '36.840071', '112.864215', '2', '八路军文化园位于武乡县城太行西街，背靠凤凰山，东临马牧河，是在抗日战争和民族革命战争的背景下，依靠太行山区特殊的地理环境，通过物化八路军文化，用珍贵的革命文物和大量仿制的生活用品，生动地反映抗日战争时期八路军和太行人民在太行山浴血奋战、艰苦创业的光辉历程。游客可以亲临其境，亲身体验八路军当年战斗、生产、工作、学习、生活、娱乐及与当地民众鱼水情深的艰苦奋斗精神。景区内还设有八路村、军艺社、抗战雕塑等主题景观和情景剧《反扫荡》、影视蒙太奇体验剧《太行游击队》等精彩演艺，以及水、陆、空多种拓展项目。同时，还承办中、小型商务会议、年终会议以及各类旅游、会议接待及业务培训。在这里，游客可以通过视觉、听觉等多种感官，如临其境品味八路军文化的魅力，接受一次不同以往的体验式红色革命传统教育。', '山西省长治市武乡县太行西街', '0355-6456666', NULL, '9:00-17:30', 4370, NULL, '自驾\n建议自行驾车前往。', NULL, NULL, 'http://travel.qunar.com/p-oi9196174-balujunwenhuayuan', '', '4', '1', '3', '3', '5', 7, 46529, 77, NULL, 96607, 13562, 35446, 90220, 240788, 82680, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
