INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (187, '9483454', '古灵山', NULL, '河南 > 淇县 > 淇县景点 > 古灵山', '鹤壁', '淇县', 'https://img1.qunarzz.com/travel/poi/1606/a6/b1ae5ff0f863d89a.jpg', '--', '淇县景点排名第3', NULL, '0', '35.6832', '114.130033', '0', '河南淇县古灵山风景区位于县城西北8公里处，是国家AAAA级景区、《封神榜》故事发生地，女娲修真处，可游览面积25平方公里，由主景区、朝阳寺、清凉庵、灵湖、铜顶、凉水泉清代古民居石头城六大版块组成。现有景点130多处。相传，人类始母—女娲在古灵山炼石补天，捏土造人，殷纣王降香女娲宫。古灵山景区悠久的历史文化与优美的山水景观交织一起，成为休闲度假、登山探险、祭祖朝拜的游览胜地。主题文化是女娲文化和殷商文化。主要由六大游览版块组成：纣王降香处——女娲宫主景区，纣王采暖行宫——朝阳寺，纣王避暑行宫——清凉庵，清代古民居石头城——凉水泉，天下第一铜顶，佛教圣地——灵光阁，现存有主要景点有：女娲宫、女娲峰、女娲池、太公湖、玉带河、醒目泉、古佛洞、补天阁、灵峰、财神沟、龙潭峡等2百余处，自古就有“灵山抱妙寺，神泉涤心埃”的美誉。古灵山自古就是群仙聚汇的风水宝地，始建于殷商时期，是封神榜故事发生地，人祖女娲修真处。南北朝梁普通年间，由曾出任京城昭统寺大统、纲领全国僧尼四十年的著名高僧、朝歌人法上在此修行。唐永微六年，高宗皇上曾特召灵山寺长老法一到长安垂询佛事，唐开元年间，灵山寺又得以重修。这期间，寺院所辖面积500余亩，高僧200余人，古磬阵阵，木鱼声声，游客云集，香火旺盛。寺西侧有女娲宫，相传是纣王降香的地方。明代嘉靖年间淇县县令于慧题写了灵山十景：“危岩少进，群峰耸翠，列柿流丹，一经蓬壶，半岩风雨，九天鸣佩，巨崖走蛟，双剑横秋，东海龙吟，西山虎啸”。明崇祯时监察御史孙徵兰摩崖题诗曰：“山卓碧云插汉，泉滃绿玉飞花，三仁六七贤圣，灵杰千古同嘉”。', '河南省鹤壁市淇县县城西北部', '0392-7283836', NULL, '全天', 18272, NULL, '自驾\n京港澳高速，在淇县高速路口下，沿途路标都很明显的。\n\n公交\n淇县1路公交车能直接到灵山，车站门口就能等到，淇县社会汽车站门口还有好多小面包车是去灵山的。', NULL, NULL, 'http://travel.qunar.com/p-oi9483454-gulingshan', '2', '5', '5', '', '2', '2', 0, 27823, 150, 15557, 384997, 379220, NULL, 41143, 27264, 10689, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
