INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (189, '7432202', '昆仑山口', NULL, '青海 > 曲麻莱 > 曲麻莱景点 > 昆仑山口', '可可西里', '曲麻莱', 'https://img1.qunarzz.com/travel/poi/1409/10/d0f309a7a0c08b29ffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d5/1607/30/9ae1e225aa72789a.jpg;https://img1.qunarzz.com/travel/d2/1607/7b/d8e98d5972e9e59a.jpg;https://img1.qunarzz.com/travel/d6/1607/58/48db930a3c06709a.jpg;https://img1.qunarzz.com/travel/d8/1704/73/b42e330596653fb5.jpg;https://img1.qunarzz.com/travel/d2/1701/2b/11b701e9fd2fa9b5.jpg', '4.9', '曲麻莱景点排名第1', '1小时 - 2小时', '48', '35.645951', '94.076317', '2', '昆仑山山口是青藏公路穿越昆仑山脉的必经之地，咽喉之所，是世界屋脊汽车探险线的必经之地，昆仑六月雪观光的重要景点。昆仑山口属多年冻土区荒漠地貌，地质系古代强烈侵蚀的复杂变质岩所构成，间有第三纪沉积物构成的丘陵低山和丘垅。山坡谷地生长梅、虎爪耳草、等高原冻土荒漠野生植物。登临山口，巍巍昆仑的千峰万壑如同披着银灰色铠甲的群群奔马，随着风起云涌，滚滚向前。', '昆仑山中段格尔木市区南160公里处', NULL, NULL, '全天', 3787, '最佳游览时间是夏季，因为昆仑山口海拔较高，夏季的气温较为舒适。', '自驾\n昆仑山口位于格尔木市区南160公里处，毗邻109国道，无直达公共交通，需自驾前往。', NULL, NULL, 'http://travel.qunar.com/p-oi7432202-kunlunshankou', '', '4', '3', '', '1', '4', 2, 43946, 108, NULL, 87917, 77436, NULL, 22759, 51203, 44597, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (190, '7645467', '大柴旦', NULL, '青海 > 海西 > 海西景点 > 大柴旦', '海西', '海西', 'https://img1.qunarzz.com/travel/d6/1702/38/dfe90ddc38999bb5.jpg;https://img1.qunarzz.com/travel/d6/1610/75/5abeff8a72e1adb5.jpg;https://img1.qunarzz.com/travel/d4/1508/30/dec78b2840df38.jpg;https://img1.qunarzz.com/travel/d9/1611/f3/31b141cc6b7423b5.jpg;https://img1.qunarzz.com/travel/d3/1606/d8/9b8309862d4298f7.jpg;https://img1.qunarzz.com/travel/d9/1612/d5/81dfcd3f9b0f4bb5.jpg', '4.1', '海西景点排名第3', NULL, '98', '37.85858', '95.371984', '194', '大柴旦位于青海省海西州境中北部，北与甘肃省毗连。蒙古语伊克柴达木，意为大盐湖，地处举世闻名的柴达木盆地北缘。大柴旦行政区地域辽阔，位于青海省西北部，地处柴达木盆地北缘，为海西蒙古族藏族自治州辖区。东部自科克希里山脉的羊肠子沟山顶与德令哈相连；南部自达布逊湖北堤至西台吉乃尔湖与格尔木市接壤；东南部自埃姆尼克山西段至达布逊湖北堤与都兰县相接；西部自当金山口经赛什腾山中部，西台吉乃尔湖西岸与冷湖镇分界；北部自当金山口经党河南山西段与甘肃省阿克赛哈萨克族自治县和肃北蒙古族自治县毗邻。', '青海省海西蒙古族藏族自治州大柴旦', NULL, NULL, '全天开放', 10975, NULL, '自驾\n青新（315国道）与柳格（215国道）公路贯穿大柴旦行政区全境，大柴旦城市并于柴旦镇交汇。', NULL, NULL, 'http://travel.qunar.com/p-oi7645467-dachaidan', '5', '4', '', '1', '1', '3', 3, 33283, 169, 355641, 109764, NULL, 17683, 21457, 29206, 26119, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (191, '9198905', '铁寨', NULL, '湖北 > 大悟 > 大悟景点 > 铁寨', '孝感', '大悟', 'https://img1.qunarzz.com/travel/poi/1604/b1/f86c13aa31f0fef7.jpg', '--', '大悟景点排名第1', NULL, '0', '31.371918', '114.311634', '0', '在黄陂交接的大别山余脉中，在大悟这片红色的热土里，有一颗晶莹剔透的绿色明珠——铁寨风景区。景区版图面积4000亩，主峰铁砦，孤峰耸立，傲视群莽；众山环绕间，绿树掩映下，金鼓天池水波如镜，宛若一块极品翡翠，恰似温婉的处子，静谧而多情。 风景区内奇石遍布，或如狮，或似虎，像海豚戏珠、大象哭冢；如老僧面壁、神龟探海，无不神形具备、惟妙惟肖，堪称鬼斧神工，不愧为湖北的奇石大观园。更有千百年来流传的民俗神话故事，八仙聚首在此扶危救困、降妖伏魔；财神在此普济众生，更有太平军智斗清兵，铁寨寺历代藏宝洞等等，枚不胜举。 铁寨风景旅游区山体属喀斯特地貌，岩石裸露，素有“鄂北小黄山”之称。山上奇石遍布，奇形怪状，象形附会，惟妙惟肖，奇石“大象哭冢”、“八戒拱天”、“神龟探月”、“金蟾观天”、“雄狮巡山”、“关公拜佛”、“麒麟啸天”、“张果老脚印石”、“一线天”等等不一而足，千姿百态，妙趣横生。', '湖北孝感市大悟县刘集镇铁寨村西部', NULL, NULL, '09:00～17:00开放；', 3013, NULL, '自驾\n武汉—岱黄高速—木兰转盘—s108大悟方向直走—河口—s304大悟方向直走—刘集—刘集广场路口左转直走—铁寨风景区。', NULL, NULL, 'http://travel.qunar.com/p-oi9198905-tiezhai', '', '1', '1', '4', '2', '4', 0, 44576, 161, NULL, 23799, 15168, 198079, 29292, 45416, 55198, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (192, '710764', '海陵岛', NULL, '广东 > 阳江 > 阳江景点 > 海陵岛', '阳江', '阳江', 'https://img1.qunarzz.com/travel/d5/1606/92/c30cc463dd7dbd9a.jpg;https://img1.qunarzz.com/travel/d0/1606/3a/fac341a54d5f599a.jpg;https://img1.qunarzz.com/travel/d8/1606/64/6c7c0418f270a89a.jpg;https://img1.qunarzz.com/travel/d2/1606/36/ea9d220b3240ce9a.jpg;https://img1.qunarzz.com/travel/d8/1606/f8/bc154845a175a19a.jpg;https://img1.qunarzz.com/travel/d8/1606/89/93359b8f87af4f9a.jpg', '4.6', '阳江景点排名第1', '1天 - 2天', '42', '21.630377', '111.919629', '54', '海陵岛位于阳江市区西南沿海，地处广东、广西、海南等省区水陆交通要道，是国内外不可多得的自然生态海岛，目前已整合大角湾景区和广东海上丝绸之路博物馆建成海陵岛大角湾海上丝路5A旅游区。海陵岛还有丰富的人文和历史景观，如太傅庙、灵谷庙、古炮台、镇海亭、观音阁等10多处，处处都有着一段悲壮动人的故事。十里银滩中段，银海城内还有一座面积6万平方米，中国最大的仿宋建筑――宋城，城内有53座仿宋建筑，无论远观近瞻，都百态千姿，尉为壮观，更让人亲身体会宋王朝的气派雄伟。此外，岛上还有各具特色的连绵山峦，尤为神奇的是草王山，山上有山，山内有山，山山相叠，峰奇山趣，雄壮中且细腻，阔大而又缠绵，令人留连忘返，暇思无限。', '阳江市海陵岛闸坡旅游大道108号', '0662-3888240', 'http://www.mlhld.com', '全天开放', 1641, '阳江市地处广东西南沿海，属亚热带海洋季风气候，冬暖夏凉，一年四季适宜出游。', '自驾\n由广州北环高速公路经沙贝立交上广佛高速公路，再经324国道可至阳江，车程需3个多小时。阳江至闸坡长45公里，半小时车程。\n\n市区车站及二运站与闸坡汽车总站之间每隔15分钟对开一班直达快车，票价12元。岛内内有中巴、电瓶车、黄包车，以及载人机动艇、快艇等交通工具，十分方便。', '节假日期间，大鹏半岛的游客大量增加，大鹏半岛的海鲜价格普遍上涨，宰客现象时有发生，正常情况下，两个人一顿海鲜餐大概200-300元之间。', NULL, 'http://travel.qunar.com/p-oi710764-hailingdao', '', '1', '3', '', '3', '4', 1, 43030, 91, NULL, 17437, 93457, NULL, 27936, 193470, 54199, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (193, '8672946', '乌拉盖草原', NULL, '内蒙古 > 锡林郭勒盟 > 锡林郭勒盟景点 > 乌拉盖草原', '锡林郭勒盟', '锡林郭勒盟', 'https://img1.qunarzz.com/travel/d7/1704/30/df3d912a3a60e4b5.jpg;https://img1.qunarzz.com/travel/d2/1704/48/31218fda7c87ebb5.jpg;https://img1.qunarzz.com/travel/d8/1704/9a/c57b27649cab4bb5.jpg;https://img1.qunarzz.com/travel/d1/1704/57/1046a17fe17070b5.jpg;https://img1.qunarzz.com/travel/d5/1703/75/d1d770b2f4c02cb5.jpg;https://img1.qunarzz.com/travel/d8/1703/88/d0d0f101e6a48eb5.jpg', '5', '锡林郭勒盟景点排名第48', NULL, '12', '45.726482', '118.82394', '13', '乌拉盖草原因乌拉盖河得名，地处锡林郭勒盟、兴安盟和通辽市三盟市的交界处，动植物种类繁多，有“天边草原”的美誉。草原可利用面积4350.53平方公里，以2、3、4等草地为主，其中产量以3、4、5级属多，总体表现草地质量优良，产量中高。境内植物类型多样，有62科501种，以木科豆科为主，牧草种类优良，除优良牧草外，具有不同价值的用途的野生植物较为丰富，中蒙医药用植物较多，主要有大黄、桔梗、甘草、柴胡等。野生动物有黄羊、草原狼、骆驼、狐狸、獾、野兔等。\n乌拉盖草原的名字由来，是因乌拉盖河而得名。这条全长300多公里的乌拉盖河，为全国第二大内陆河，她从大兴安岭发源，一路游走，最终抵达牧民口中这片“天边的草原”。乌拉盖河及其支流色也勒吉河纵贯全境，大小湖泊12个，泉水23处。乌拉盖河最美的景致在一早一晚，朝霞和余晖里的剪影，不须修饰就可以入画。也许你从未听说这个名字，她位于内蒙古锡林郭勒盟东北部最深处一片湿地，保留最古朴的草原民俗特色。乌拉盖草原是世界上保存最完好的天然草原，境内不仅有草原，还有湖泊、湿地、白桦林、芍药沟、黄花沟等独特的草原风光自然景观，还有独特的乌珠穆沁部落蒙古族民俗风情文化，是未经大规模商业开发的天然草原，可以预见，这里将成为下一个热门旅游地。', '内蒙古锡林郭勒盟东北部', NULL, NULL, NULL, 18668, '一年四季皆可，春夏最佳。', '自驾\n游客可自驾沿S101省道进入，同时通往周边的霍林郭勒市、兴安盟、阿尔山市、白音华矿区和珠恩嘎达布其口岸的道路全部打通。', NULL, NULL, 'http://travel.qunar.com/p-oi8672946-wulagaicaoyuan', '', '', '5', '4', '', '3', 6, 31424, 62, NULL, NULL, 86924, 124272, NULL, 29585, 22924, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (194, '3864456', '柴达木盆地', NULL, '青海 > 格尔木 > 格尔木景点 > 柴达木盆地', '海西', '格尔木', 'https://img1.qunarzz.com/travel/d1/201407/07/23a46f930e3d4b3cddb12cfb.jpg;https://img1.qunarzz.com/travel/poi/1409/12/f99d948ce9174152ffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/poi/1409/12/931f16b3d50810e2ffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/poi/1803/fd/082e402856622837.jpg;https://img1.qunarzz.com/travel/d7/1705/f3/11819f004b53f2b5.jpg;https://img1.qunarzz.com/travel/d8/1705/d8/8cf9a339650f2b5.jpg', '4.4', '格尔木景点排名第5', '4小时 - 1天', '94', '36.776337', '95.144683', '12', '柴达木盆地是青藏高原上地势最低的断线盆地，地理环境以干旱为特征，地貌由周边向中心依次呈现高山、风蚀丘陵、戈壁、沙漠和湖沼五个环带状结构。\n柴达木盆地主要旅游景区（点）有察尔汗盐湖及“万丈盐桥”、南八仙、大风山一带的雅丹地区、德令哈柏瀑布、达布旦温泉、达布逊湖“湖中湖”、茶卡柯柯盐湖、巴隆国际狩猎场等，还有新兴的城镇和绿洲是开展“万丈盐桥”、“戈壁沙漠”、“雅丹在貌”、“绿州农业”。柴达木盆地是观光、狩猎和民俗考察等旅游活动的好地方。', '格尔木到甘肃敦煌的215国道两侧', '0979-13762', NULL, '全天开放', 7915, NULL, '自驾\n暂时无公共交通方式前往柴达木盆地，建议自驾或者跟团前往。西宁经德令哈到敦煌的路上会经过柴达木盆地，沿途会看到很多盐湖、沙漠、戈壁。', NULL, NULL, 'http://travel.qunar.com/p-oi3864456-chaidamupendi', '', '', '4', '', '5', '5', 7, 38030, 128, NULL, NULL, 46052, NULL, 253664, 283926, 80060, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
