INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (196, '9555659', '花溪谷', NULL, '四川 > 资阳 > 资阳景点 > 花溪谷', '资阳', '资阳', 'https://img1.qunarzz.com/travel/poi/1701/f6/6919042ce9ead637.jpg', '--', '资阳景点排名第7', NULL, '0', '30.194146', '104.742514', '0', '花溪谷位于雁江区保和、中和、老君等镇乡，流域面积199.4平方公里，境内长达70.4公里。花溪谷内幽谷狭窄，重岩叠嶂奇山异峰，各种动植物品种多，天然植被好，自然、人文景点多（已发现自然景点51处，人文景观24处）。夏天激流翻滚响水滩，形成高15米、宽50米的白龙瀑布，春、秋、冬溪水清澈透明，小鱼成群，并有鸟类在深谷啼鸣，沿岸奇坡异石，峭壁似画，湖面平静，独特的地理环境形成了秀丽景观。', '资阳市雁江区', NULL, NULL, '全天开放', 11123, NULL, '自驾\n资阳市区出发——S106——板永路——花溪谷', NULL, NULL, 'http://travel.qunar.com/p-oi9555659-huaxigu', '5', '3', '', '1', '4', '3', 1, 35296, 182, 315641, 80402, NULL, 6894, 195685, 92784, 24554, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (197, '9709313', '甘泉雨岔大峡谷', NULL, '陕西 > 甘泉 > 甘泉景点 > 甘泉雨岔大峡谷', '延安', '甘泉', 'https://img1.qunarzz.com/travel/d3/1708/6a/0afd2e71ce3a9ab5.jpg;https://img1.qunarzz.com/travel/d1/1708/20/02f0ca0eef453ab5.jpg;https://img1.qunarzz.com/travel/d4/1708/fa/4c8e8dc62ce550b5.jpg;https://img1.qunarzz.com/travel/d8/1708/1a/f000cea7a6cf7fb5.jpg;https://img1.qunarzz.com/travel/d9/1708/9b/e6bf03c50a7457b5.jpg;https://img1.qunarzz.com/travel/d6/1805/20/98e7d0f738a4adb5.jpg', '4.4', '甘泉景点排名第1', '1小时 - 4小时', '10', '36.524714', '109.052224', '3', '甘泉雨岔大峡谷，位于甘泉县城西北56公里的下寺湾镇雨岔沟.目前发现大峡谷三处：分别位于雨岔的桦树沟、花豹岔、南河牡丹沟。三处地貌和景观各不相同，峡谷的形成初步认为：大约在亿万年前，陕北曾发生过一次强烈地震，黄土大山分开一条条裂缝，有宽有窄，又经过上亿年高原暴雨导致山洪暴发，地表被切割和冲刷后慢慢形成这样独特的峡谷地貌。\n走近峡谷，峡壁呈现凹凸不平、线条流畅，如波浪从你身边划过，宽的地方几人并排可行，窄的地方一个人要挤着才能过去。专家称为“黄土高原自然地缝奇观”，属于陕北独一无二地质美景，胜似美国亚利桑那佩奇附近的羚羊大峡谷。', '陕西省延安市所辖甘泉县城西北50公里的下寺湾镇雨岔乡', NULL, NULL, '目前全天开放。属于未被开发的景点，当地政府筹备开发中。', 5893, NULL, '自驾。该景观位置较偏僻，到达下寺湾镇后需换乘当地的越野车走十几公里的土路。', NULL, NULL, 'http://travel.qunar.com/p-oi9709313-ganquanyuchadaxiagu', '3', '', '', '1', '3', '4', 7, 48850, 114, 88914, NULL, NULL, 16425, 89021, 151103, 49401, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (198, '7528476', '忆江南温泉', NULL, '辽宁 > 营口 > 营口景点 > 忆江南温泉', '营口', '营口', 'https://img1.qunarzz.com/travel/poi/1604/8c/cb639296e78e3ff7.jpg;https://img1.qunarzz.com/travel/poi/1411/69/69da21d4e712ce7213a9cdb.jpg', '5', '营口景点排名第4', '4小时', '1', '40.17925', '122.162084', '0', '大型温泉会馆无色无味清澈透明的温泉水温高达90度，富含200多种有益于人体健康的矿物元素，各种药浴、鲜花浴、红酒浴、牛奶浴、咖啡浴、石板浴等，共有90多个40余款不同种类的室内外温泉泡池，满足客人不同需求和好奇的心理。还配有国际标准泳池、SPA会馆、水上娱乐中心，康乐中心、美容美发、休闲按摩、温泉酒吧、自助餐厅、贵宾客房、演艺大厅、休息大厅等。', '辽宁营口经济技术开发区', NULL, NULL, '8:00-18:00', 15595, NULL, '自驾：\n（大连出发）上G201 - 沈海高速公路 - 从熊岳服务区出上匝道 - 沿匝道走530米右转进入山海大道 - 上山海大道走2.3公里右转入G202 - 沿G202行驶2.3公里，左后方转弯 - 行驶700米，右转 - 行驶460米，过金星村约140米后左转 - 行驶1.5公里，到达终点(在道路左侧)。\n出租：\n熊岳城火车站- 乘坐出租车大概5元左右，约5分钟，2公里左右。\n鲅鱼圈客运站- 乘坐出租车大概10元左右，约15分钟，17公里左右。\n营口市中心- 乘坐出租车大概40元左右，约50分钟，65公里左右。', NULL, NULL, 'http://travel.qunar.com/p-oi7528476-yijiangnanwenquan', '', '1', '', '1', '2', '4', 4, 28362, 114, NULL, 10788, NULL, 16442, 47293, 135107, 44408, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (199, '7528443', '梁家寨大汖温泉度假村', NULL, '山西 > 盂县 > 盂县景点 > 梁家寨大汖温泉度假村', '阳泉', '盂县', 'https://img1.qunarzz.com/travel/d0/1512/34/01146e73e30c17f7.jpg;https://img1.qunarzz.com/travel/poi/1411/86/86930532d6c724d5213a9cdb.jpg', '5', '盂县景点排名第2', '4小时', '1', '38.444987', '113.483169', '0', '大汖温泉东连著名革命圣地西柏坡；北靠佛教名山五台山；南接赵氏孤儿发源地藏山，是难得的休闲旅游度假景区。\n山西大汖温泉采自地下100米深处，出水温度60度，属于高氡硫酸钠泉，温泉水中富含氡、钠、钙、镭、镁等四十多种对人体有益的微量矿物质元素，氡含量：1291贝克/升，在全国氡泉是名列第二，是我国稀有的“高温高氡泉”医疗矿泉，水质好、水量多、水性温和，能有效促进血液循环、快速消除疲劳，具有养生、美容等功效，同时对心血管系统疾病、新陈代谢疾病、风湿性疾病、神经系统疾病等有良好的理疗保健作用。', '阳泉市盂县梁家寨乡寺平安村', NULL, NULL, '9:00-24:00', 19800, NULL, '自驾：\n1、石家庄——西柏坡高速——207国道下槐镇——S301省道直行——山西省寺平安村——梁家寨大汖温泉度假村；\n2、太原——太阳高速（盂县南）转阳五高速——（阳五高速）梁家寨出口——高速延伸线——梁家寨大汖温泉度假村；\n3、五台山——阳五高速——梁家寨出口(阳五高速）——高速延伸线——梁家寨大汖温泉度假村。', NULL, NULL, 'http://travel.qunar.com/p-oi7528443-liangjiazhaidapaiwenquan', '3', '3', '', '1', '', '2', 6, 35261, 167, 28263, 81596, NULL, 7934, NULL, 15989, 10777, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (200, '5857890', '祁连山草原', 'Qilian mountain grassland', '青海 > 祁连 > 祁连景点 > 祁连山草原', '海北', '祁连', 'https://img1.qunarzz.com/travel/d9/1707/11/cde94a0cb12c62b5.jpg;https://img1.qunarzz.com/travel/d2/1510/16/37f9e8564a8933.jpg;https://img1.qunarzz.com/travel/d4/1603/3f/78c6e6835063c1f7.jpg;https://img1.qunarzz.com/travel/d7/1603/c4/e7fbab2f871deaf7.jpg;https://img1.qunarzz.com/travel/d7/1608/11/c5420886f3fe88b5.jpg;https://img1.qunarzz.com/travel/d5/1611/df/6d7fcf6e4a7476b5.jpg', '4.6', '祁连景点排名第2', '1小时 - 2小时', '174', '38.676815', '99.153058', '59', '祁连山的平均山脉海拔在4000米～5000米之间，高山积雪形成的硕长而宽阔的冰川地貌奇丽壮观。海拔高度在4000米以上的地方，称为雪线，祁连山的雪线之上，常常会出现逆反的生物奇观。在浅雪的山层之中，有名为雪山草甸植物的蘑菇状蚕缀，还有珍贵的药材——高山雪莲，以及一种生长在风蚀的岩石下的雪山草。因此，雪莲、蚕缀、雪山草又合称为祁连山雪线上的“岁寒三友”。祁连山草原于2005年曾被评为中国最美六大草原之一，在这里游客可以感受到天高云淡，山清水秀的绝美自然景观，以及‘风吹草低见牛羊’的草原风光。', '海北藏族自治州祁连县', NULL, NULL, '全天开放', 11716, NULL, '自驾或包车\n建议从西宁自驾或包车前往，在祁连山草原可以找一位当地的向导带领，包车参考价格2元/公里，具体价格需要视情况而定。', NULL, NULL, 'http://travel.qunar.com/p-oi5857890-qilianshancaoyuan', '1', '', '3', '3', '4', '1', 2, 41220, 137, 22935, NULL, 54980, 77452, 91028, 17846, 5404, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (201, '5740655', '喀拉峻草原', NULL, '新疆 > 伊犁 > 伊犁景点 > 喀拉峻草原', '伊犁', '伊犁', 'https://img1.qunarzz.com/travel/d7/1804/ea/1041e9ed08cb58b5.jpg;https://img1.qunarzz.com/travel/d6/1804/36/89d0785f06fd65b5.jpg;https://img1.qunarzz.com/travel/d3/1804/a3/55954731b68137b5.jpg;https://img1.qunarzz.com/travel/d5/1804/f9/403a3dc7c36c30b5.jpg;https://img1.qunarzz.com/travel/d1/1804/77/f786ca32959cb4b5.jpg;https://img1.qunarzz.com/travel/d1/1804/43/fb28fcf2ca2536b5.jpg', '4.9', '伊犁景点排名第2', '2小时', '72', '43.082263', '82.15649', '67', '喀拉峻大草原位于新疆伊犁河谷的特克斯县境内。是西天山向伊犁河谷的过渡地带。喀拉峻大草原属典型的高山五花草甸天然大草原，海拔在2000-3600米之间，东西长89公里、南北宽32公里，总面积达2848平方公里。这里降水丰富，气候凉爽，土质肥厚，十分适宜牧草的生长，生长有上百种优质牧草。\n据传，哈萨克人对这片草原还有“汗加伊寮”的说法。“汗”是国王、郡王的意思，“加伊寮”是指夏牧场。另一层意思是，“汗”代表草原的等级，冠以“御用”的草原。相传，乌孙王曾把这片水草丰茂的草原作为夏牧场，为这片草原赋予了更多的神秘色彩 。', '新疆维吾尔自治区西北部，特克斯县的南部天山之中', NULL, NULL, '全天', 18679, '每年5月至9月，不同颜色的花轮流开放，鲜花，草原，雪山，蓝天，白云形成一幅俊美的画面，雨后的彩虹更迷人。', '自驾路线：\n从伊犁向正西方向出发，沿斯大林东路行驶450米，左后方转弯进入青年街，沿青年街行驶300米，过右侧的温州商贸城约60米后，右转进入新华西路，沿新华西路行驶4.2公里，左前方转弯进入S313，沿S313行驶410米，直行进入伊犁河大桥，沿伊犁河大桥行驶12.4公里，左前方转弯，行驶20米，右前方转弯进入伊昭公路，沿伊昭公路行驶101.0公里，直行进入工矿路，沿工矿路行驶1.2公里，左转，行驶10米，直行进入S220，沿S220行驶60.8公里，左前方转弯进入阿扎提街四环路，沿阿扎提街四环路行驶1.7公里，右前方转，进入环岛，沿环岛行驶140米，在第4个出口到达景区(在道路左侧)。', '1、新疆早晚温差大，旅行期间白天气温可达31摄氏度，夜间为20摄氏度。\n2、新疆气温虽较内地略低，但紫外线照射强烈，应准备防晒霜，同时配备清热、解渴、滋润的药物或冲剂，如夏桑菊冲剂等，以免难以承受干燥和酷热的气候。\n3、新疆旅游行程长，温差大，一时难以适应，可能会出现水土不服症状，应携带一些常备药品，如感冒药或治疗腹泻的药物如泄痢停、创可贴等。\n4、新疆气候特点属典型的大陆干旱性气候，因此，晚上睡觉时应将空调开到适度，不要过凉，睡觉时要盖棉被，以免感冒。\n5、新疆是瓜果之乡，吃瓜果要记住不要在吃完后立即喝热茶，以免造成腹泻。\n6、新疆信仰伊斯兰教的穆斯林有许多禁忌，我们要尊重他们的风俗习惯。', '1-2天', 'http://travel.qunar.com/p-oi5740655-kalajuncaoyuan', '', '3', '5', '2', '2', '1', 3, 20317, 121, NULL, 66774, 176962, 31310, 27363, 21476, 5589, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
