INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (203, '10023011', '佛祖山', NULL, '湖北 > 天门 > 天门景点 > 佛祖山', '天门', '天门', NULL, '--', '天门景点排名第2', '2小时 - 3小时', '0', '30.791345', '113.02015', NULL, '佛子山位于湖北省天门市西北角的佛子山镇，根据天门县志记载，1961年前又名佛祖山，相传为佛祖释加牟尼的道场，有着悠久而丰富的文化积淀。早在佛教传入中国之初，佛祖山即建有庙宇，明代鼎盛时期，佛祖山周边建有庙宇、道观、书院200多座，至民国初年大部分毁于战火。重建佛祖寺及周边庙宇，是广大佛教信众和天门人民多年的夙愿。\n该项目按国家5A级风景旅游区标准设计规划。景区占地35000亩（其中天门部分面积22000亩，京山部分13000亩），以天门市为主体开发。项目由佛祖文化区、楚文化区、休闲度假区等3个旅游资源景区组成。', '天门市佛子山镇', NULL, NULL, NULL, 12196, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10023011-fozushan', '5', '', '4', '', '1', '3', 3, 38681, 117, 111281, NULL, 137655, NULL, 14008, 82995, 21975, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
