INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (209, '10001608', '七连屿', NULL, '海南 > 西沙群岛 > 西沙群岛景点 > 七连屿', '三沙', '西沙群岛', 'https://img1.qunarzz.com/travel/poi/1712/2a/26f2a527aebde637.jpg', '--', '西沙群岛景点排名第5', '5小时', '1', '16.977025', '112.305933', '1', '七连屿并不是一个岛，而是位于西沙永兴岛的西南方，七座相邻不远的岛屿（事实上有十个），属于宣德群岛的一部分。这七座岛礁紧密相联，相隔很近，就象十颗璀璨的珍珠如弧线般抛洒在大海里，环绕着永兴岛。远古来此打鱼的渔民因此形象地把这七个岛屿统称为“七连屿”。\n七连屿也是世界上最适宜潜水观光的海底公园。海底会呈现大片的礁盘和错落的礁石，美丽的珊瑚、海葵遍洒其间，海星、海胆还有不知名的贝壳星罗棋布。还可以看到一簇簇的珊瑚像盛开的鲜花一样美丽，有金黄色的鹿角珊瑚、雪白的葵花珊瑚和鲜艳的红珊瑚。五光十色的鱼儿成群结队地游来有去，景象很奇异。\n“七连屿”的七个小岛：西沙洲、北岛、中岛、三峙仔、北沙洲、中沙洲、南沙洲，每个岛屿面积约有10个足球场大小，都在海拔高度在三、四米左右。七连屿七个个小岛上都没有任何淡水，其中的西沙洲、南沙洲基本上是荒沙滩地。近年来，因为台风的关系，在最南边新形成了两个沙洲——西新沙洲和东新沙洲。', '三沙市西沙群岛西沙永兴岛的西南方', NULL, NULL, '全天开放', 12994, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10001608-qilianyu', '4', '2', '', '5', '4', '5', 6, 37721, 100, 187071, 31428, NULL, 306699, 52419, 111859, 81842, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
