INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (238, '702951', '越州天湖', NULL, '广西 > 浦北 > 浦北景点 > 越州天湖', '钦州', '浦北', 'https://img1.qunarzz.com/travel/poi/1803/13/6df06096e040d437.jpg', '--', '浦北景点排名第1', NULL, '0', '22.1508817551', '109.5457209884', '0', '越州天湖，湖水清澈，烟波浩渺，远近山色碧黛，环湖荔枝、芒果园已成规模。它既是浦北县新辟的荔枝、龙眼生产和淡水鱼虾养殖基地，也是供人们旅游休闲的好去处，内有仙鹤岛、东坡渡、伏波滩、望夫岭、清浊湖、对歌坡、三帝庙等自然景点。\n仙鹤岛上栖息着成千上万只白鹤，还有天湖大渡槽和香翰屏将军的故居等人文景观。故居建筑艺术中西合璧，亭台楼阁依山傍水而建。距香翰故居6公里，即是石镇仰天湖。这是距今已有1500多年历史的越州古城。史籍记载，越州于南朝时期管辖范围东至广东茂名，南至雷州半岛全部，西与钦州接壤，北达容县一带，是当时相当于广州那样的省级军事、行政单位。越州天湖得天独厚，一日之游，不但使你能够饱览桂东南独有的湖光山色，还能让你涉足祖国1500多年的历史名城。', '广西钦州市浦北县', '0777-3688759', NULL, '8:00-19:00', 10442, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi702951-yuezhoutianhu', '1', '1', '2', '5', '5', '2', 2, 24140, 136, 10503, 11969, 19974, 389179, 294103, 21635, 12382, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
