INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (257, '706567', '龙山', NULL, '山东 > 莱芜 > 莱芜景点 > 龙山', '莱芜', '莱芜', NULL, '--', '莱芜景点排名第15', NULL, '0', '36.7423927529', '117.3833772324', NULL, '山东龙山风景区，位于莱芜市莱城区东北茶业口镇境内，总面积 240 万平方米。龙山因其独特的自然形态而得名。当你登山观望，蜿蜒起伏的自然山势，犹如一条巨龙静静盘卧，山脚下的龙潭，恰似一颗镶嵌在崇山峻岭中的明珠，形成相为呼应，蔚为壮观的“游龙戏珠”的奇特景象。龙山景区，原始的植被，林木茂密，溪水淙淙，奇花异葩俯拾皆是；百米大峡谷，层瀑叠泉，白云缭绕，石趣天然，百鸟互鸣。金龙潭--碧波荡漾，锦粼波动，潜心垂钓，美不胜收。谭龙道场--古建筑群，气势恢宏，神灵活现，栩栩如生，游人可进行宗教朝觐，许香还愿，祈求福寿平安。老年公寓--万善孝为先，全省一流的老年公寓就建设在这里，可供养社会鳏寡孤独老人生养休息、安度晚年。万亩樱桃园--“樱桃好吃树难栽”，观万亩园，赏百年树，随心所欲品樱桃，情趣盎然。桂花园--集中了中国各种名贵桂花品牌品种 500 余盆，其他造型各异、异彩纷呈的花卉、根雕 400 余盆，可供游人欣赏。美术馆--以红色瓦琉璃瓦镶嵌的宫殿式仿古建筑，馆内有全国各地名家作品常年展出。游乐广场--秋千、荡绳、钢链、吊桥、网山、吊床等一应俱全。石雕、龙凤树、如意宝葫芦等更为一绝。民俗--驴拉碾、驴拉磨、吃农家饭，住农家房，饮山泉水，品味绿色生活环境，享受大自然之美。今天的龙山，山清水秀，空气清新，鸟语花香；松柏林下，幽幽谷内，赏奇景，品野味，是游人的好去处。', '莱芜市莱城区茶业口镇南嵬石村', NULL, NULL, '8:00-18:00', 6763, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi706567-longshan', '4', '', '2', '3', '5', '5', 1, 33181, 101, 130482, NULL, 40875, 83997, 214223, 82625, 103066, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
