INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (259, '10005766', '慈航寺', NULL, '辽宁 > 营口 > 营口景点 > 慈航寺', '营口', '营口', 'https://img1.qunarzz.com/travel/poi/1712/8e/7b41347e375e4937.jpg', '--', '营口景点排名第14', '1小时', '0', '40.4396591187', '122.4299762941', '0', '慈航寺位于盖州市东城办事处虎斗马峪村，距盖州市东北4公里。慈航寺坐北朝南，建于山腰，背依秀美的盖州城北第一高峰老青山，东、西、北三面环山，南临虎斗马峪水库，山峰叠翠，水波潋滟。慈航寺现为辽宁省重要的宗教活动场所之一。慈航寺始建于清朝，当时内建有观音阁三楹，东西皆有配房各两间做藏经室，檀越室为上殿，循山低下依次修有厢房，东西各三间及山门，规模大，香火终年不断，是当时东北地区颇有名气的佛教寺院。后毁于战乱，年久失修，庙宇坍塌，至今仅存古碑一座立于原址老青山北麓。如今慈航寺于1997年夏移址老青山南麓复建而成，整个建筑群依山而建，一重高过一重，布局严谨有序，气势雄伟。以一条中轴线纵贯当中，左右两侧基本对称，大体可分为三个部分，中路依次：山门殿、天王殿、大雄宝殿、经堂、藏经阁、大悲阁、佛塔；东西两路有钟鼓楼、五百罗汉堂、地藏殿、药师殿、伽蓝殿、居室楼、文殊殿等；更显雄伟自然。整个寺庙的规模列居东北地区前列。', '营口盖州市东城办事处虎斗马峪村', NULL, NULL, NULL, 17595, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10005766-cihangsi', '3', '1', '3', '5', '3', '2', 1, 48369, 115, 79200, 16113, 94908, 394123, 25816, 17437, 13969, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (260, '715205', '龙门群岛', NULL, '广西 > 钦州 > 钦州景点 > 龙门群岛', '钦州', '钦州', NULL, '5', '钦州景点排名第16', NULL, '3', '21.9028487526', '108.6218982617', NULL, '龙门群岛位于广西壮族自治区钦州市钦州港经济开发区西北部。钦州至钦州港公路可达。距钦州市区约25公里。是集自然景观和人文景观于一体的滨海型旅游区。明清时期一直是钦州的八景之一而备受赞誉。龙门群岛是由100多个大小各异的岛屿镶嵌在方圆约50平方公里的钦州湾海面上而形成。景区内山环水绕，扑朔迷离，风光旖旎，奇特秀丽，享有“南国蓬莱”之盛誉。主要由仙岛公园、七十二泾、茅尾海、龙门岛、亚公山、青菜头、绿岛、五马归槽等景点组成。而亚公山则因清康熙年间（公元1662年）岛上修建大皇庙，庙内塑大皇公神像而得名。岛南半崖上栖着大群水鸟，日落归巢时煞是壮观。岛北面还有一棵许愿树，飘舞的彩带寄托着渔民们祈求平安富足的美好愿望。 绿岛：又名六墩岛。酷似一条穿越碧波畅游大海的小青龙。岛上沙滩、奇石、树林，富有热带风情，岛上度假木屋、酒吧、亭台楼阁鳞次栉比，是一个别有情调的度假小岛。', '钦州市钦州港经济开发区西北部', '0777-3883633', NULL, NULL, 20446, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi715205-longmenqundao', '2', '', '2', '', '2', '2', 2, 49099, 108, 45340, NULL, 39898, NULL, 17133, 46337, 12738, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
