INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (264, '10001607', '石岛', NULL, '海南 > 西沙群岛 > 西沙群岛景点 > 石岛', '三沙', '西沙群岛', 'https://img1.qunarzz.com/travel/poi/1712/a1/5281683a6cff5137.jpg', '--', '西沙群岛景点排名第6', '5小时', '0', '16.941818', '112.325825', '0', '石岛是南海诸岛中地势最高的岛，最高处海拔13米。整个石鸟全然不像永兴岛，没有高大的树木，只有岩石间生长的青草和荆棘。也与西沙其他的岛不同，它不是由珊瑚樵构成的，它的成分是礁石，这里的礁石形状千奇百怪，具有较高的观赏价值。蓝色的水面、白色的浪花、湿润的海风、哗哗的涛声构成了一幅南天小岛的自然画卷。\n石岛与永兴岛与永兴岛同位于一个礁盘上，两岛之间低潮时可涉水来往,中间有个近200米长的水泥桥相连，桥两侧全是一望无际澄明如镜的浅海。走过石桥，可见一块几丈高的珊瑚礁，就是石岛的大门。石岛由于高达15．9米，故成宣德环礁中的最有用的了望点。它能观察东西海面上来往的船只，好似永兴岛的桥头堡垒，在军事上非常重要。\n此外，在永兴岛西南的浅海上，还有一群沉水滩礁，滩呈西北到东南方向延伸，水深大约为14米。它是构成宣德环瞧南翼的主要滩礁。', '三沙市西沙群岛东部的宣德群岛中部永兴岛东边', NULL, NULL, NULL, 6465, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10001607-shidao', '', '', '', '', '1', '2', 3, 26462, 157, NULL, NULL, NULL, NULL, 16701, 17868, 10699, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
