INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (268, '701981', '屯垦文化公园', NULL, '新疆 > 阿拉尔 > 阿拉尔景点 > 屯垦文化公园', '阿拉尔', '阿拉尔', NULL, '--', '阿拉尔景点排名第2', NULL, '0', '40.550541', '81.286387', NULL, '屯垦文化公园占地88公顷,一望无际，仿三大水库微型景观的广场湖泊水波荡漾，大型音乐喷泉瀑布和着激昂的音乐翩翩起舞，各色彩灯交织、变幻，美仑美奂，让人激情涌动，遐思飞扬。屯垦文化公园是人民城市人民建的精典之作。屯垦文化公园的许多工程都是阿拉尔人投入大量的义务工建市起来的。在这里，人们挖掘了农一师三大水库和塔里木河的微缩景观，以纪念军垦老一代对农一师做出的不朽贡献，弘扬丰富的屯垦文化内涵；挖出的土，人们巧妙地堆成山，绵延不断，出其不意地营造成山水共绿、人与自然和谐的意境和城市景观。城市的市民，又是城市的使用者，知道如何真正建一个自己喜欢居住的城市。人们栽花种草，铺设游道，安装音乐喷泉、霓虹灯，仅仅几个月的时间，棉田、渠道消失了，取而代之的是一个花红柳绿、碧波荡漾、霓虹闪烁、景色宜人的公园。为了回报广大群众，阿拉尔市在屯垦文化公园建成之后将其定全开放性公园，供市民、游人休闲、娱乐、纳凉。', '新疆阿拉尔市市区', NULL, NULL, '全天', 10905, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi701981-tunkenwenhuagongyuan', '1', '3', '', '4', '5', '2', 3, 29503, 126, 21045, 53597, NULL, 163410, 383305, 32456, 10646, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (269, '8447889', '三五九旅屯垦纪念馆', NULL, '新疆 > 阿拉尔 > 阿拉尔景点 > 三五九旅屯垦纪念馆', '阿拉尔', '阿拉尔', 'https://img1.qunarzz.com/travel/d2/1410/66/663e468a7744380a213a9cdb.jpg;https://img1.qunarzz.com/travel/d4/1410/67/6724fdc830165dad213a9cdb.jpg;https://img1.qunarzz.com/travel/d9/1410/b5/b5a32261f6deeb3c213a9cdb.jpg', '0', '阿拉尔景点排名第4', '2小时 - 1天', '1', '40.554535', '81.287174', '0', '三五九旅屯垦纪念馆位于新疆南部阿克苏地区的新疆生产建设兵团农一师阿拉尔市。纪念馆馆内分为7个区域：地下一层为胡杨沙漠景观区、3个大展厅和1个多功能厅；二层设1个大展厅和3个小展厅，三层为书画厅。展馆设置了八个展陈主题区，分别是：1、西域屯垦，源源流长；2、英雄部队，功勋卓著；3、艰苦创业，屯垦荒原；4、五湖四海，投身兵团；5、建设大军，铸就辉煌；6、中流砥柱，铜墙铁壁；7、建设城市，勾画家园；8、构建和谐，奔向小康等内容。再现了20世纪50年代以来新疆兵团军垦战士在塔里木屯垦戍边的艰苦历史。', '阿拉尔市军垦大道', '0997-4788022', NULL, '8:30-17:30\n每周周三、周四、周五、周六、周日五天向社会开放', 5053, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi8447889-sanwujiulu:tunkenjinian', '', '', '2', '3', '5', '2', 7, 48143, 91, NULL, NULL, 26867, 43006, 148941, 38645, 10787, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (270, '709463', '房干生态旅游区', NULL, '山东 > 莱芜 > 莱芜景点 > 房干生态旅游区', '莱芜', '莱芜', NULL, '5', '莱芜景点排名第13', NULL, '2', '36.4377539541', '117.454476177', NULL, '景区是国家AAAA级景区，有23500亩山地，全部绿化。景区有近百类植物，20多野生动物和百余种鸟类，八座水库。以生态环保为特色、绿色为主题，汇山、水、林、泉、潭、瀑、峡、洞、石等自然景观于一处，构成了生态环保、自然景观、民俗风情三大板块的旅游内容，被国际环保专家誉为“绿色天堂”、“山区明珠”。房干主要景观有九龙大峡谷、梦幻情人谷、金泰山、石云山、天门峡、桃花源、万寿崖、日观峰等。每年7月26日份举办“房干生态旅游节”，内容有观赏自然风光、体验农家生活风俗、观看文艺演出等。九龙大峡谷全长 20公里，谷中古树参天，两边奇峰突兀，山、泉、潭、瀑、洞分布于峡谷，至今保持原始风貌。金泰山山势雄奇，形似泰山，山顶有拱北石、罗汉石、巨蟒岩等景观。石云山海拔840米，山上奇石遍布。天门峡在石云山北麓，长约5华里，峡中谷壑交错，内有情人谷、龙凤谷、鹿鸣谷和蝴蝶谷，还有饺子石、元宝石、花盆石等景观。该区门票每人50元。服务设施完备，已建成鹿鸣山庄、金泰山、房干村三大生活服务区，服务区内既有能够满足中高档客人需求的宾馆，也有民俗游所需的农家客栈。整个服务区可同时接待500名游客食宿，并有容纳200人会议厅。游客可食用、选购山中散养的柴鸡以及柴鸡蛋、山野菜、山果等。交通情况良好，房干生态旅游区已修筑了60公里的环山公路、20公里人行便道和停车场一处。', '山东省莱芜市雪野镇房干村南', '0634-6386366', NULL, '7：00——18：00', 12053, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi709463-fangganshengtailu:you', '', '4', '1', '3', '5', '3', 8, 42205, 169, NULL, 186217, 10671, 70034, 154790, 83208, 27237, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
