INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (272, '702383', '月亮峡', NULL, '甘肃 > 徽县 > 徽县景点 > 月亮峡', '陇南', '徽县', 'https://img1.qunarzz.com/travel/poi/1803/e6/a5d7cf9dafc20637.jpg;https://img1.qunarzz.com/travel/d3/1508/a9/67e0ee17d6e7fe.jpg;https://img1.qunarzz.com/travel/d8/1508/2a/c2e6e6f603380.jpg;https://img1.qunarzz.com/travel/d5/1508/56/f06cb19f3ce687.jpg;https://img1.qunarzz.com/travel/d7/1508/60/250d9d2a075ea7.jpg;https://img1.qunarzz.com/travel/d2/1508/13/e07f6c3f7950de.jpg', '5', '徽县景点排名第1', NULL, '3', '33.7693551275', '106.0934159476', '0', '月亮峡渡假村位于甘肃南部的徽县境内，也是三滩风景名胜区的主要景区之一，月亮峡全长65公里，清澈的月亮河穿峡而过。有“峭壁拔空远千仞，鸟瞰嘉陵一线江。”之势，峡谷内原始森林密布，植被覆盖率98.5％以上，一年四季“林荫遮天蔽曰，清泉飞流山涧，长峡千仞可虎跳，翠谷百里尽鸟啼”是月亮峡真实的写照。它以峡谷幽深、密林青翠、泉水清纯、环境幽静为基调，其最具个性、最具特色、最具魅力、最吸引人的地方在于由林海绿波、溪泉清纯和新鲜空气形成的幽雅环境，是避暑度假、山水观光、休闲娱乐的理想之地。峡谷内除青山对峙、溶洞山泉众多、水质清洌、植被优良外，三国古道，流泉飞瀑，溶洞石窟，深山峡谷，峭壁危崖，无不让人留恋忘返。', '甘肃-陇南　', NULL, NULL, NULL, 6360, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi702383-yueliangxia', '3', '5', '', '3', '4', '4', 6, 26362, 181, 22532, 127791, NULL, 41152, 194700, 129836, 44083, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (273, '10016701', '阳坝自然风景区', NULL, '甘肃 > 陇南 > 陇南景点 > 阳坝自然风景区', '陇南', '陇南', 'https://img1.qunarzz.com/travel/poi/1712/c8/a6666c2bd9de4937.jpg', '--', '陇南景点排名第20', '4小时 - 6小时', '0', '33.049749', '105.766516', '0', '康县阳坝生态旅游风景区位于陇南市康县东南部，是一处山清水秀的自然风景区。这里气候湿润，风光旖旎，是一处名副其实的“陇上江南”，也是兰州及陇南当地居民观赏山水、消夏游玩的好去处。\n游客游玩阳坝景区大多是从兰州、陇南等地前来，景区内多个景点，需要乘坐区间车游览，大约需要大半天时间。一般游客都会在阳坝景区所在的阳坝镇上住宿一晚。\n景区内的主要游玩区域大致有河边的木制栈道、天鹅湖、茶园等地。木制栈道位于峡谷底的河边，这里山势清秀，水流也非常清澈，游客可以下到河边赤脚戏水，在炎热的夏季感觉非常惬意。天鹅湖则是一处碧绿优美的湖泊，这里可以乘坐游船游玩，也可以观景漫步，周围青山绿水景色非常漂亮。茶园则是阳坝的亮点之一，这里曾在下乡知青的影响下开始种茶，结果种出的茶叶品质非常好，于是建立的茶叶基地，来此可以观看茶园风光，优美的景色让人仿佛置身于江南一带，在茶园旁有几家农家乐可以吃饭，不过价格也稍贵，建于游玩前自备一些干粮和饮用水。', '陇南市康县阳坝镇阳坝自然风景区', '0939-5122398', NULL, '7:00-18:00', 17491, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10016701-yangbaziranfengjingqu', '', '4', '1', '4', '', '5', 9, 32024, 69, NULL, 153939, 8047, 182389, NULL, 339701, 96816, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (274, '701957', '高峰山', NULL, '四川 > 蓬溪 > 蓬溪景点 > 高峰山', '遂宁', '蓬溪', NULL, '3', '蓬溪景点排名第1', NULL, '1', '30.9268754452', '105.6586087484', NULL, '高峰山位于蓬溪县城北25公里处的高峰上，山势峥嵘，古柏参天，历史悠久，道风远播，楼阁勾连，曲径通幽，号称“川北迷宫”。早在20世纪50年代，川北行署主任胡耀邦同志就命令保护好该山林木古迹，勿使破坏。为了开发蓬溪旅游资源，弘扬蓬溪历史文化，中共蓬溪县委统战部、蓬溪县政协文史委和高峰山管委会联合联纂了《川北名胜高峰山》一书。该书于近日出版，是第一本全面系统介绍高峰山文存、诗词、匾联、建筑、传说、特产的书籍，对人们了解高峰山历史文化，提升蓬溪旅游形象，促进旅游业发展，都有重要作用。', '遂宁市蓬溪县', '0825-5451021', NULL, '全天', 7875, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi701957-gaofengshan', '', '', '', '4', '1', '4', 5, 31035, 192, NULL, NULL, NULL, 61718, 11276, 41897, 53509, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
