INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (282, '714372', '叶赫那拉城遗址', NULL, '吉林 > 四平 > 四平景点 > 叶赫那拉城遗址', '四平', '四平', 'https://img1.qunarzz.com/travel/d0/1711/f8/8d1e52a79f126b5.jpg;https://img1.qunarzz.com/travel/d1/1711/b6/777e5b00a12ac5b5.jpg;https://img1.qunarzz.com/travel/d9/1711/9a/67f42d681049b5.jpg;https://img1.qunarzz.com/travel/d6/1711/7c/31baa04b22a692b5.jpg;https://img1.qunarzz.com/travel/d6/1711/86/7813741c9185e7b5.jpg;https://img1.qunarzz.com/travel/d1/1711/74/e2f3f5dc31c2a9b5.jpg', '4', '四平景点排名第1', '2小时 - 3小时', '2', '43.0085725505', '124.6015900851', '0', '叶赫那拉古城，位于四平市梨树县东南部，是著名民俗风景旅游点，以富有女真族特色的古城堡建筑闻名。\n叶赫部族，在满语中意为“河边的太阳”，是满族的重要发祥地之一。明末元初的叶赫族先人征服了周围的弱小部落之后，在叶赫两岸的山头上选择险要之地筑起两座城堡。尽管叶赫部于619年初被清朝太祖皇帝努尔哈赤所灭，却无法掩埋其对清朝皇室史的重要而又微妙的作用：清太祖努尔哈赤夫人孝慈高皇后那拉氏及清末慈禧太后皆出生在此。故该城享有“两代皇后的家乡”的别称。直至今日，热情好客的叶赫人仍部分保留着传统的满族文化特色，并将以独特的满族礼节迎接慕名而去的海内外游客。\n古城的建筑极有特点，每城有木、土、石三道城墙，城外有护城河环绕，登台而远眺，更觉朔风猎猎四野茫茫；内有八角时楼，古朴肃穆，巍峨雄俊，是我国北方少见的古建筑群体，极具考古及观赏价值。堡体及内部楼台等保存完整，散落着多处清朝历史中著名的遗迹，其中点将台、烽火台尤为引人注目。如今的叶赫那拉景区一分为二，分“古城文化区”和“转山湖风景游艺渡假区”，古城文化区内，有东西二城遗址、古驿站、伽蓝寺、娘娘庙、虫王庙等文化古迹可供游客参观游览；转山湖景区风光迤俪曼妙，湖面约5000亩的水域，再现了“山水共天一色”的诗意境界。', '中国吉林省四平市', '0434-3560666', NULL, '8:00-18:00', 19559, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi714372-yeheneilachengyizhi', '5', '', '5', '', '5', '3', 4, 37519, 170, 372536, NULL, 199493, NULL, 371275, 74903, 22366, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
