INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (284, '712825', '协格尔曲德寺', NULL, '西藏 > 定日 > 定日景点 > 协格尔曲德寺', '日喀则', '定日', 'https://img1.qunarzz.com/travel/poi/1803/f3/2638c6d98efe4537.jpg', '0', '定日景点排名第6', '0.5小时 - 1小时', '1', '28.6684109625', '87.1269887094', '0', '协格尔曲德寺位于定日县城内，建于1385年，距定日县珠峰宾馆7公里，位于协格尔金刚山（也称孜加山）上，依山而筑。相传，建寺之前在山上有一修行洞，邦·洛追登也大师（出生于1276年）和他儿子在洞中修练时预言，在此山上将会有个讲经院。1385年，罗达·司徒曲述仁钦修建了协格尔曲德寺。\n协格尔曲德寺建造后，不断扩大建筑规模，僧人有100多名。各种教派共存一寺，和睦相处，萨迦教派、夏鲁教派、格鲁教派各拥有7个扎仓，寺院有15个殿堂，主供佛为释迦牟尼（高9米）镀金佛像。\n1645年，在五世达赖喇嘛执政期间，该寺统一信奉格鲁教派，该寺先后任了28位堪布，后又划归为拉萨色拉寺的下属寺。曲德寺印制的藏经曾闻名后藏，该寺现保存有较珍贵的文物一百多件，其中手印经、经板、唐卡、协格尔志、绸缎、佛像、法器等文物。1985年，寺庙进行了修复，现有4个殿堂，40名僧人，主供佛手持金刚佛。当地每年都会举行一次赛马会，其中内容非常丰富，有赛马，舞蹈比赛，对歌等等。', '日喀则定日县境内', NULL, NULL, '9:30-18:00', 9781, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi712825-xiegeerqudesi', '5', '', '', '4', '1', '2', 8, 20370, 167, 95927, NULL, NULL, 41422, 14901, 46054, 11559, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (285, '701572', '官正九龙山', NULL, '山东 > 莱芜 > 莱芜景点 > 官正九龙山', '莱芜', '莱芜', NULL, '--', '莱芜景点排名第14', NULL, '0', '36.4714134123', '117.5652279293', NULL, '谚语说：\"山不在高，有仙则灵\"。固原县城南门外的九龙山山不算高，也无修成仙人之传说。但是，九龙山的历代屡建寺庙，不息祭祀，在民众心中的地位，则非同凡俗。九龙山，据乡民传说，以其山形似巨龙九条出香炉山，沿川而下主脉直奔固原城而得名。九龙山脉，是从海拔2825米的固原第二高峰香炉山向东北方径直伸出。主脉绵延25公里直至固原城西南1公里许。山向东南方向发展有黑剌沟、三十里铺、冯家庄、大马庄4道支脉；向西北方向有童家梁、老庄沟、孟家沟、柳沟4道支脉，与主脉共九道山岭，似9龙奔腾，以扇面向东北方伸出。清水河、羊坊河两水环抱，雄姿勃勃，十分壮观。登上九龙山，四季景观各有不同：春观山前果园鲜花怒放，白里透红，粉色一片；夏观清水河川，羊坊塬麦浪滚滚，鳞波腾翻；秋看马营河农户场院，丰收洋芋堆积如山；冬看粉条村银线般达到粉条，一排排挂在羊坊河两岸。寺庙建筑是九龙山美景的重要组成部分。凡名山胜迹，都伴以殿宇亭榭古朴建筑作景。九龙山很早就有庙宇建在龙鼻山头上，为乡民信士进行祭祀游览的活动场所。九龙山古有\"九仙庙山\"之称。所谓九仙者，意为多种神仙。我国道家立上仙、高仙、大仙、玄仙、天仙、真仙、神仙、灵仙、至仙为九仙，据创修九龙山子孙堂碑记载，旧有佛殿、真武殿、关帝殿、灵宫殿，日久毁坏。', '莱芜市莱城区雪野镇官正村', NULL, NULL, NULL, 15031, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi701572-guanzhengjiulongshan', '1', '5', '2', '2', '1', '4', 5, 38096, 152, 11167, 292222, 44639, 25894, 15961, 172637, 49427, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
