INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (296, '9511295', '灯塔岛', NULL, '广东 > 汕尾 > 汕尾景点 > 灯塔岛', '汕尾', '汕尾', 'https://img1.qunarzz.com/travel/d3/1702/e1/78097dd418ac2db5.jpg;https://img1.qunarzz.com/travel/d3/1702/d4/5522ece163a6c4b5.jpg;https://img1.qunarzz.com/travel/d3/1702/82/a61e5be57e29acb5.jpg;https://img1.qunarzz.com/travel/d6/1702/c5/0322ea50adfe36b5.jpg;https://img1.qunarzz.com/travel/d9/1702/f4/f1e7b82429d076b5.jpg;https://img1.qunarzz.com/travel/d1/1702/70/678725b45ad5f4b5.jpg', '5', '汕尾景点排名第38', NULL, '2', '22.6680425785', '115.5722203784', '2', '灯塔岛岛东与岛西海面的情景是迥然不同的，东边波滚浪啸，如万马奔腾；西边风平浪静，如潭可印月。礁盘生态系统良好。座落在遮浪半岛的东南端，距国际航道仅11海里，是红海湾与竭石湾的分割点，与南澳半岛一衣带水，仅隔一条0.16km宽的海峡，宛如与南澳半岛相依相伴的一颗明珠，素有“粤东麒麟角”之美誉。民间传说从前南海观音娘娘云游至此，为海上奇观所吸引，便驻足岩岛。适遇岛东一渔船满载而归，她为了测试人心，即化作一老妇向其乞鱼，那渔民非但不给，反而奚落，她便使波涛汹涌。她转又向岛西的渔船乞鱼，那渔民虽然只捕到几条鱼，却把鱼连篮奉送，她便使浪平波静。其实鸟西与南澳半岛相隔很近，半鸟有如屏障似地挡住了风浪。这动人的传说，给岩岛奇景抹上神奇的色彩。', '位于广东省汕尾市遮浪镇近郊', NULL, NULL, NULL, 3573, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi9511295-dengtadao', '3', '1', '', '2', '', '4', 5, 45848, 139, 50674, 22730, NULL, 36498, NULL, 71635, 53225, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
