INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (316, '10023756', '九龙峪', NULL, '山东 > 青州 > 青州景点 > 九龙峪', '潍坊', '青州', 'https://img1.qunarzz.com/travel/poi/1712/70/a01731fe5da3e237.jpg', '--', '青州景点排名第15', '3天', '0', '36.5728', '118.504288', '0', '九龙峪生态旅游景区位于青州市南部山区，跨弥河、王坟两镇，规划面积10平方公里。景区有龙门崮、修真宫、紫阳洞、衡王墓等18处人文、自然景观，合称“九龙十八景”。景区以生态、文化、高端为引领，主要分为三大功能区，分别为原生态山体保护区、生态旅游开发区、旅游配套区。\n原生态山体保护区，山体、植被将严格被保护，不进行任何开发建设，保持原有的自然风貌。并利用雨季造林，在景区内大面积栽植松树、侧柏等植被，提高森林覆盖率。该景区内既有丰厚的自然资源，也有龟背石、竹叶石等鬼斧神工的山体景观，还有赵匡胤、修真宫等人文历史资源，是人们回归自然、观光旅游、避暑度假的理想去处。\n生态旅游区，分为民俗文化园、生态养老区、禅修文化园三部分，注重保护生态资源，强调生态与自然、人文与环境、休闲与旅游的和谐统一，并依托依托得天独厚的地理、地貌优势，适应国情与时代发展趋势，培植可持续发展的生态养老益生新型产业。\n旅游配套区，有云鼎会所、生态停车场、养老配套设施、生态安置区等几部分，为景区的发展提供配套服务，并对景区内的村落进行拆迁安置。', '潍坊市青州市王坟镇大涧头大桥九龙峪景区', '0536-3090196', NULL, '全天开放', 16719, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10023756-jiulongyu', '', '1', '', '', '2', '3', 0, 45445, 145, NULL, 16932, NULL, NULL, 18178, 34815, 22318, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
