INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (350, '709999', '万象洞', NULL, '甘肃 > 陇南 > 陇南景点 > 万象洞', '陇南', '陇南', 'https://img1.qunarzz.com/travel/d8/1508/93/4c33e60cdef3f.jpg;https://img1.qunarzz.com/travel/d5/1508/68/fe2e8a638547b3.jpg;https://img1.qunarzz.com/travel/d8/1508/d0/7c17d56af29472.jpg;https://img1.qunarzz.com/travel/d3/1508/4d/27d5e0cdb8980.jpg;https://img1.qunarzz.com/travel/d6/1508/a9/27a29767524eea.jpg;https://img1.qunarzz.com/travel/d9/1508/d3/6528cc26c2c7ac.jpg', '4.5', '陇南景点排名第7', NULL, '8', '33.3350181311', '105.0133332499', '4', '万象洞原名仙人洞、五仙洞，相传五位仙人在此修炼而得名。万象洞开成于千万年至三亿年前，属典型的岩溶地貌。洞内深不可测，是目前我国开发的最大地下天然艺术宫殿。石乳、石笋、石柱、石幔、石花等自然景观千姿百态。已开发的11个景区，有120多个较大景观依形象排为月宫、天宫、龙宫三大洞天，步入洞中，步移景异，如入仙境。从南北朝时期至今，历代名人所留墨迹、石碑随处可见，共有一百多方，诗词题刻768首。犹以民国时期陕、甘、青检察使高一涵在洞中所题“别有洞天”和赵朴初题写的“万象洞”最引人注目。从武都县城至万象洞口有盘山公路相通。', '陇南市武都区汉王镇　', '0939-8526288', NULL, '8：00-18：00', 8722, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi709999-wanxiangdong', '3', '4', '1', '3', '4', '5', 8, 43578, 158, 86657, 70246, 13679, 42120, 85408, 363795, 91504, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
