INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (375, '10012038', '玉华宫', NULL, '陕西 > 铜川 > 铜川景点 > 玉华宫', '铜川', '铜川', 'https://img1.qunarzz.com/travel/poi/1712/86/55410f7c531ffc37.jpg', '--', '铜川景点排名第16', '3天', '0', '35.349485', '108.956805', '0', '玉华宫景区位于铜川市西北郊的玉华镇，始建于唐高祖于武德七年 （公元624年），曾经是西部唯一的皇家避暑行宫，唐高宗即位后，改玉华宫为寺庙。显庆四年（公元659年），闻名于世的玄奘法师，带门徒移居玉华寺翻译佛经，并在玉华寺住了四年，于麟德元年（公元664年）圆寂于玉华寺肃成院。如今，当年的宫殿寺庙建筑已经不复存在，因为这里群山环绕，植被茂盛，已经成为周边市民避暑度假的地方。\n玉华宫景区内树木茂盛，处处能看到飞瀑和清泉，山中还有不少潭水和溪流，水质清澈，是周边市民游山的好去处。这里一年四季景色各异，春天野花遍布山野各处；夏天这里的温度也高，浓荫的枝叶遮挡了阳光；到了秋天，满山都是红叶；每年冬季，这里都会举办冰雪节，游客可以在这里滑雪滑冰，每到一、二月最冷的时节，景区内还会有精美的冰雕供游客观赏。\n景区内有酒店和农家乐等设施，为游客提供住宿和餐饮服务。除了领略自然之美，游客还可以去景区内的玄奘纪念馆游玩，这里是几年玄奘大师的专题博物馆，馆内展出了部分玉华宫出土的文物和佛教物品。景区内的肃成院遗址，是当年唐玉华宫肃成殿遗址，玉华寺期间更名为肃成院，也是当年玄奘法师的住宿、理佛及圆寂之地。', '铜川市印台区西北郊玉华镇玉华村', '0919-7586001;0919-7586113', NULL, '9:00-17:30', 501, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10012038-yuhuagong', '', '3', '', '4', '', '3', 9, 36013, 133, NULL, 28346, NULL, 90808, NULL, 63399, 23815, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
