INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (379, '718241', '百花岭风景区', NULL, '海南 > 琼中 > 琼中景点 > 百花岭风景区', '琼中', '琼中', 'https://img1.qunarzz.com/travel/d1/1608/93/e4343a352e2607b5.jpg;https://img1.qunarzz.com/travel/d4/1608/cf/3bdd30499994f3b5.jpg;https://img1.qunarzz.com/travel/d8/1608/7a/6da752b724fd75b5.jpg;https://img1.qunarzz.com/travel/poi/201211/15/2d7652b9abe04229ddb12cfb.jpg;https://img1.qunarzz.com/travel/d2/1608/59/987907133260bbb5.jpg;https://img1.qunarzz.com/travel/d3/1608/83/d6041ea98da05eb5.jpg', '4', '琼中景点排名第1', NULL, '2', '19.010202', '109.826309', '0', '位于琼中黎族苗族自治县根营镇东南7公里处，素有“绿色宝库”的美称。主峰海拔1100多米，与东面的五指山、西北部的黎母山形成三足鼎立之势，峰峦叠嶂，巍峨蜿蜒。百花岭瀑布是花岭风景区的特级景观，是游客观赏的主要景点。落差高度达300米，巨瀑分三级跌宕泻下，其声喧哗，其色雪白，其势磅礴，蔚为壮观。站在观瀑亭中，昂首仰望，那瀑布如哈达从两颗古树之间贴绝崖峭壁飞泻而下，随风飘来的水花雾气，轻轻的吻着游客的手脸。往上攀树爬藤而上就达到第二级，有“妙药池”，据说这级瀑布的井型池中，随着飞瀑的冲击，不时腾滚出一种红色的花果，是包治百病的灵丹妙药，故而得名。最高的一级称为“金龙吐珠”，因其有一股清泉从山顶飘逸而下，激起的水雾在阳光的反射下，呈现五彩缤纷。两边的两股支流，像银白龙牙从血口伸出；再加上两壁生长的翠竹枯木，似龙爪与龙须，古人们美其名曰“金龙吐珠”。', '琼中黎族苗族自治县根营镇东南7公里', NULL, NULL, '全天', 6695, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi718241-baihualingfengjingqu', '', '', '2', '1', '4', '3', 2, 27553, 167, NULL, NULL, 15087, 12401, 188483, 45206, 20436, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
