INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (384, '10010884', '三海岩', NULL, '广西 > 灵山 > 灵山景点 > 三海岩', '钦州', '灵山', 'https://img1.qunarzz.com/travel/poi/1712/4a/5e5e1198ce900b37.jpg', '--', '灵山景点排名第2', NULL, '0', '22.420964', '109.283583', '0', '三海岩是灵山古“八景”之一,位于灵山中学校园内。过去,灵山隶属广东,因此,三海岩自古以来被誉为“粤西胜景”。\n宋治平二年(1065年),广西铃辖兼钦州知州陶弼来灵山寻幽探胜,取上古沧海“三变”的意义给其名为三海岩。三海岩外藤蔓螺瘢,榕槟盘曲、莺落鸟渡,整个岩洞像个有盖的大鼎,内有钱岩、龟岩、月岩三个洞府,岩内石钟乳遍布,幽深诡异,气候宜人,有“六月无三伏,一朝有四时”之胜。岩壁上保留有历代墨客骚人和文武官员崖刻138幅,其中抗金名将岳飞的儿子岳霖题的《过灵山述怀》序言部分保存得相当完好,这些石刻具有相当高的史料价值和艺术欣赏价值。岩前有明朝镇国将军白玉始修的足音亭,岩后有海岩亭。三海岩于1996年被定为广西第四批文物保护单位。\n它由龟岩、钱岩、月岩三个洞组成，又取沧海桑田三变之意，故名。洞外青翠明朗，幽静怡然，有“足音亭”、“海岩亭”。洞府高大宽敞，可容数千人，洞中钟乳倒垂，落泉淋沥，凉风习习，幽异诡谲，“六月无三伏，一朝有四时” 正是这里的写真。三海岩自古就被誉为“粤西胜境”，明人李符清赞曰：“游尽中原名胜地，那知仙境在乡园！”', '钦州市灵山县灵山中学校园内', NULL, NULL, NULL, 20150, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10010884-sanhaiyan', '', '', '', '', '4', '2', 2, 25630, 124, NULL, NULL, NULL, NULL, 62739, 46809, 10346, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (385, '706935', '青云山民俗游乐园', NULL, '山东 > 安丘 > 安丘景点 > 青云山民俗游乐园', '潍坊', '安丘', 'https://img1.qunarzz.com/travel/poi/1803/6f/d0e0baea3cabcc37.jpg;https://img1.qunarzz.com/travel/d4/1702/27/41ca4922f7cb8fb5.jpg;https://img1.qunarzz.com/travel/d5/1702/cd/9c8ecdcdc2d716b5.jpg;https://img1.qunarzz.com/travel/d6/1702/81/6d3ee1de26f71bb5.jpg;https://img1.qunarzz.com/travel/d4/1702/70/0784de0159eb12b5.jpg;https://img1.qunarzz.com/travel/d3/1702/79/2b2319af081b85b5.jpg', '4.3', '安丘景点排名第1', '3小时 - 4小时', '5', '36.4461867693', '119.2459063512', '0', '青云山民俗游乐园位于潍坊市南30公里，是一处突出民俗文化特色，集旅游、观光、休闲、娱乐、购物于一体的大型综合性旅游景区。\n游乐园依托青云山自然地貌，建有山水园林、民族风情、野生动物、休闲娱乐、齐鲁民俗和桃花源六大功能区。山水园林区里山水环绕，桥亭廊榭，茂林修竹，如置身江南水乡。民族风情区集西南傣、佤、怒、摩梭、哈尼五个少数民族村寨风情于一体，各寨依山傍水，尽显西南边陲美丽风光和神秘色彩。野生动物区以别具一格的园林造型，在变化万千的沟壑中圈养10多种稀有动物，体现了野生动物和自然的完美结合。\n齐鲁民俗村建有县衙署、官宦住宅、富有人家和代表齐鲁不同地区的普通民居、钟楼、戏台、生产工具陈列室等，以一个清朝村落的形式展现了齐鲁大地的风土人情、生活习俗。', '山东潍坊南30公里（安丘市区东）', '0536-4321090', 'http://www.pbqy.com/', '7:30-18:00', 4164, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi706935-qingyunshanminsuyoule', '', '3', '', '4', '', '3', 4, 37098, 131, NULL, 43955, NULL, 138318, NULL, 95834, 26732, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
