INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (387, '3864557', '巴颜喀拉山', NULL, '青海 > 玛多 > 玛多景点 > 巴颜喀拉山', '果洛', '玛多', 'https://img1.qunarzz.com/travel/poi/1803/3a/b7b7735db86c0337.jpg;https://img1.qunarzz.com/travel/d8/1511/49/75b8b40166f5f7.jpg;https://img1.qunarzz.com/travel/d7/1511/db/a975baf1e3902af7.jpg;https://img1.qunarzz.com/travel/d9/1511/30/6c2ca4efd1e6a4f7.jpg', '0', '玛多景点排名第3', NULL, '2', '34.8924914389', '96.4714219149', '0', '巴颜喀拉在蒙古语的意思是“富饶青黑色的山”。这里是长江与黄河源流区的分水岭，北麓的约古宗列渠是黄河源头所在，南麓是长江北源所在。巴颜喀拉山人烟稀少，只有藏人在此从事畜牧。最出名的应该是巴颜喀拉山圆盘了，是1938年在青海巴颜喀拉山发现的神秘石头圆盘，形似石器时代的唱片，后来被称作“太空碟”，其中心有个孔并从中间辐射出水波纹线条，考古学家发现水波纹是稠密的文字连成的螺旋线。后来有个教授破译了密码并开始解释这些“说话的凹槽”，刻下的文字涉及太空船等信息，如石盘上所记录的12,000年前在地球上着陆。', '果洛藏族自治州久治县', NULL, NULL, '全天开放', 6359, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi3864557-bayankalashan', '', '', '', '4', '4', '1', 4, 41726, 205, NULL, NULL, NULL, 72408, 128783, 10431, 5556, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
