INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (390, '8595423', '寿佛寺', NULL, '湖南 > 资兴 > 资兴景点 > 寿佛寺', '郴州', '资兴', 'https://img1.qunarzz.com/travel/poi/1507/a9/61913dd1a554df.png;https://img1.qunarzz.com/travel/d3/1411/7a/7a15e13f294bb18acdb.jpg', '4', '资兴景点排名第3', NULL, '5', '25.95465', '113.267607', '4', '寿佛寺位于美丽的东江河畔凉树湾，是东江湾景区的重要景点之一，这里背倚周源山，前临小东江，群山环抱，翠竹相拥，山水相贯，风景殊异，总体规划面积30.7公顷，建设用地面积11公顷，总投资3500万元。资兴是无量寿佛释全真的故乡。寿佛释全真，俗姓周，名宗惠，唐开元十六年出生于湖南资兴周源山，唐天宝二年到郴州开元寺出家受戒，后到浙江杭州拜道钦禅师为师，唐天宝七年随师进京晋谒了唐玄宗，唐至德元年到广西湘源县（今广西全州）开创净土院（今湘山寺，被誉为楚南第一禅林），唐咸通八年端坐圆寂，享年138岁。全真大师著有《牧牛歌》、《遗教经》《湘山百问》等著作，受到过历史上五位皇帝的敕封，宋徽宗敕封他为“寂照大师”，宋高宗加封他为“慈佑寂照妙应普惠大师”，宋宁宗、宋理宗又累次加封，清朝咸丰皇帝敕封他为“保惠无量寿佛”。由于他德懋寿高，远近都尊称他为“无量寿佛”、“寿佛老爷”,在江南地区、港澳台甚至东南亚一带享有盛名。', '资兴市其他213省道(近湘南植物园西南门)', NULL, NULL, '8:00-20:30', 18718, '四季皆宜。', NULL, NULL, NULL, 'http://travel.qunar.com/p-oi8595423-shoufosi', '5', '5', '4', '1', '', '4', 1, 46115, 83, 229487, 284207, 135721, 22026, NULL, 144972, 51648, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
