INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (398, '7468880', '满族风情园', NULL, '黑龙江 > 绥化 > 绥化景点 > 满族风情园', '绥化', '绥化', 'https://img1.qunarzz.com/travel/poi/1408/26/66b238bcf4466571ffffffffc8d65eac.jpg', '--', '绥化景点排名第8', NULL, '0', '46.8400462279', '126.4973855062', '0', '满族风情园展厅展示的内容分满族的源流、生产、生活、礼仪、信仰等六个部分，分别用文字、图片和实物等多方面展示其历史风貌。生活展厅和生产展厅不仅详细补充了主展厅的陈设，更着重展示了生产、生活情节。十二帝展厅主要展示的是清朝十二个皇帝的画像、生平简介以及清朝年间出土的一些胆瓶、玉璧、帽筒等文物。满族历史文化长廊主要展示的内容是满族兴起的故事以及重大战争场面。满族风情园的建立，对于挖掘满族文化和民族风俗、促进民族文化发展有着极其重要作用。馆藏的一件件民俗文物，展现了一幅幅纷繁的满族历史风情画卷；一个个生动神奇的文物故事，描绘出满族先民用智慧和汗水创造的多彩生活及灿烂文化。望奎满族风情园必将成为一所进行民族历史文化研究、民族团结进步教育和爱国主义教育的基地，富有黄河以北第一家诗词之县盛誉的望奎小城，满族风情园成为了一道亮丽的风景线，并在社会主义政治文明、物质文明、精神文明和社会主义建设中发挥积极作用。', '黑龙江省绥化市望奎县', NULL, NULL, '全天', 8656, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi7468880-manzufengqingyuan', '', '2', '', '4', '4', '4', 1, 32378, 67, NULL, 12228, NULL, 133624, 93610, 48874, 51302, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
