INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (411, '10010909', '返湾湖风景区', NULL, '湖北 > 潜江 > 潜江景点 > 返湾湖风景区', '潜江', '潜江', NULL, '--', '潜江景点排名第5', NULL, '0', '30.327616', '112.685733', NULL, '返湾湖旅游风景区位于潜江市中部，后湖管理区境内。是集旅游、度假、休闲、垂钓、观赏于一体的具有水乡园林特色的旅游风景区。\n返湾湖旅游风景区面积2万余亩。其中最具特色的景观是1万余亩的天然湖泊(名返湾湖)。湖泊中央有湖中园，建有避暑水寨、芙蓉岛、百鸟洲、接官厅、将军厅、返湾厅、度假村等景点。湖中园四面环水，绿树成荫，水天一色，再加上美丽动人的蒋娘娘传说，令人心旷神怡，浮想联翩。还有素享盛誉的水乡“六绝”佳肴，让你回味无穷。\n返湾湖旅游风景区水体清澈透明，无污染，是环保型绿色水源。返湾湖野生动植物种类繁多，可供游人观赏。在柳荫深处，可听水鸟悠悠轻唱，在平湖浪里，可观野花静静绽放。特别是在莲花盛开的季节，泛舟摘莲，花香扑鼻，别具江南水乡泽国的情趣。\n返湾湖旅游风景区中建有浮水观鸟亭，亭柱悬有“百鸟争喧百鸟戏，众客揽胜众客留”的楹联。几年前，因农渔业资源利用强度加大和偷猎乱捕现象严重，造成湖泊面积、生物物种数量急剧下降。为使百鸟“回家”，后湖农场及返湾湖旅游管理处对水生、陆生生物及生态环境共同组成的湿地生态系统和生物物种多样性进行了重点保护。\n返湾湖风景区地理位置位于潜江市中部，后湖管理区境内。', '潜江市中部', NULL, NULL, NULL, 17136, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10010909-fanwanhufengjingqu', '', '4', '2', '2', '5', '5', 4, 37477, 176, NULL, 84689, 29036, 28750, 128134, 109769, 92444, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
