INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (419, '707043', '象牙山', NULL, '辽宁 > 开原 > 开原景点 > 象牙山', '铁岭', '开原', 'https://img1.qunarzz.com/travel/d4/1701/c5/791b358665ea46b5.jpg;https://img1.qunarzz.com/travel/d9/1701/27/49d2a588f1463eb5.jpg;https://img1.qunarzz.com/travel/d7/1607/50/bd3c2abb13f1439a.jpg;https://img1.qunarzz.com/travel/d1/1607/a3/b6de5e0a237be19a.jpg;https://img1.qunarzz.com/travel/poi/1803/23/2eb1d6e6bff34d37.jpg', '0', '开原景点排名第1', '5小时', '2', '42.3588751906', '124.2527651231', '0', '辽北唯一火山岩地质公园。金朝时称此山为松山，亦名“向阳山”，后因音转为象牙山。\n象牙山风景区面积为99平方公里，为长白山哈达岭余脉，主峰高615.2米。象牙山天设地造许多奇观异景，有怪石突兀的羊肠凳，石脊蜿蜒而上的长城栈道，栩栩如生的天然大佛、玉壶乾坤及对吻石，更有辽北唯一的夹扁石群。山上植被丰富，物种繁多。桑、柞、松、柳等古树，榛、麻、梨、果应有尽有，享有人间花果山、世外桃源之美誉。\n景区内分东、西2条旅游线路。有天梯、天衣一缝、沉天缝、会仙棚、度仙棚、聚仙棚、飞来石、将军床、猴石、蝴蝶泉、婵娟湖等，不仅曲径通幽，怪石嶙峋，又有丰富的历史积淀，如清代风格特色的“清诗屏”，唐朝李世民为躲盖苏文追杀的藏君洞，见证历史烟火的烽火台，佛家修建的万象塔等，共计有景点40余处。一年一度的象牙山梨花节成为辽北重要的节日，吸引着大量游客前来观光旅游。全象牙山庄占地面积为一万平方米，为电视连续剧《马大帅1》、《马大帅2》、《马大帅3》的拍摄主景地，随着电视剧在全国的热播，年接待游客达50万人次。', '辽宁省铁岭开原市东南29公里处的松山堡境内', '024-73291333', NULL, '全天开放', 15998, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi707043-xiangyashan', '', '5', '4', '3', '5', '1', 2, 36508, 71, NULL, 218183, 158575, 53612, 154902, 10563, 5541, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
