INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (421, '9630462', '美榔双塔', NULL, '海南 > 澄迈 > 澄迈景点 > 美榔双塔', '澄迈', '澄迈', 'https://img1.qunarzz.com/travel/d3/1611/9b/5392c96b0629dcb5.jpg;https://img1.qunarzz.com/travel/d1/1611/be/d4fb4d9a1180d5b5.jpg;https://img1.qunarzz.com/travel/d8/1611/2a/a203069233fd55b5.jpg;https://img1.qunarzz.com/travel/d2/1611/c2/415472fd00fe87b5.jpg;https://img1.qunarzz.com/travel/d6/1611/24/849b41d4816e04b5.jpg;https://img1.qunarzz.com/travel/d7/1611/89/215f40daca578b5.jpg', '--', '澄迈景点排名第5', NULL, '0', '19.808521', '110.169347', '0', '美榔双塔系元代古塔，俗称“姐妹塔”，位于海南省澄迈县美亭乡美榔村东南面。据《正德琼台志》载，元代人陈道叙有二女，长女出嫁，次女出家为尼。他为了纪念二女而建此塔，一座为平面呈六角形长层，现存六层，仿木结构阁楼式。\n美榔双塔整体造型协调节器美观，互有变化，其刻工精巧细致，实为琼州石塔的艺术珍品。双塔四周长着茂林翠竹，溪涧叮咚，环境清幽怡静，游人身临其境地，犹如步入一方净土，大有回归大自然之感觉。美榔双塔是海南现存的为数不多的古塔之一，它不仅为研究海南古代文明发展史和建筑艺术提供直接依据，而且也具有较大的旅游观赏价值。', '海南省澄迈县美亭乡美榔村东南面', NULL, NULL, NULL, 993, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi9630462-meilangshuangta', '', '', '5', '4', '3', '4', 9, 48093, 105, NULL, NULL, 307276, 75336, 73341, 168542, 51748, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (422, '715663', '莱芜战役纪念馆', NULL, '山东 > 莱芜 > 莱芜景点 > 莱芜战役纪念馆', '莱芜', '莱芜', 'https://img1.qunarzz.com/wugc/p235/201207/30/d0762d65c6f1abfb93835fbb.jpg;https://img1.qunarzz.com/travel/poi/1803/2a/0bd85a19f9acfd37.jpg', '4.7', '莱芜景点排名第16', '1小时 - 2小时', '3', '36.223536659', '117.6981348507', '0', '莱芜战役是世界军事史上100个经典战例之一，电影《南征北战》、《红日》都是以莱芜战役为原形所拍摄。为纪念莱芜战役胜利50周年，1997年建成莱芜战役纪念馆，馆名为迟浩田题写。目前，这样规模的全景画馆世界上只有十几座，我国有4座。\n莱芜战役纪念馆由革命烈士纪念塔、展览馆和全景画馆三大主体建筑构成，成“品”字布局。革命烈士纪念塔高19米，花岗石砌成，为全国100个重点纪念碑之一；展览馆设有5个展厅，展板面积700多平方米，馆名为粟裕题写；全景画馆高17米，周长120米，地面塑形1100平方米，模拟音响、模拟自然光，设有电动旋转看台，融“声、光、电、形”于一体，采取真空幻象，严格的视觉透视和局部夸张等手法，造成视觉上的逼真，能在有限的空间里给人造成无限的暇想，站在旋转看台上能使你仿佛置身于革命炮火连天、硝烟滚滚的战火之中，给人以身临其境和惊心动魄之感，具有极强的艺术感染力和极高的艺术欣赏价值。', '莱芜市英雄路北首汶阳大街43号', '0634-8805690', 'http://zhanyi.laiwu.com/', '8:00-18:00', 2773, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi715663-laiwuzhanyijinianguan', '', '5', '5', '4', '1', '5', 8, 47895, 90, NULL, 227958, 110300, 135870, 7268, 302670, 100811, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (423, '5143089', '朗木寺', NULL, '甘肃 > 甘南 > 甘南景点 > 朗木寺', '甘南', '甘南', 'https://img1.qunarzz.com/travel/poi/1803/e1/14b57ba0ee7f1937.jpg;https://img1.qunarzz.com/travel/d1/1612/d5/da0f00178f5ccb5.jpg;https://img1.qunarzz.com/travel/d7/1612/c5/8e0468a260c070b5.jpg;https://img1.qunarzz.com/travel/d9/1612/dd/f00d4e405b40dfb5.jpg;https://img1.qunarzz.com/travel/d0/1612/2b/e39ff6685d0e1fb5.jpg;https://img1.qunarzz.com/travel/d1/1612/e7/ccbcb61e551608b5.jpg', '4.3', '甘南景点排名第11', '3天 - 6天', '8', '34.093622', '102.639936', '8', '郎木寺处地空灵，山水相依，景色十分秀美。寺前山色，形似僧帽，寺东红色砂砾岩壁高峙，寺西石峰高峻挺拔，嶙峋嵯峨。金碧辉煌的寺院建筑群和错落有致的塔板民居掩映在郁郁葱葱的古柏苍松间，还有白龙江的源头泛著波光在月夜轻歌。郎木寺经过历世活佛的创建、扩建，现有闻恩学院、续 部学院、时轮学院、医学院、印经院。寺院的佛塔主要有第一世活佛的肉身灵塔，据说该灵体的头发、指甲如新生长的一般；有以数万两黄金、白银和松耳石、珍珠等制作的历世 其他活佛的灵塔，以及70余座大小不一、制作各异的佛塔。 郎木寺辖有十座属寺和两座静修院，前后有七十任赤哇，1958年时僧人多达500余名，是安多地区闻名的大寺院之一。', '四川省阿坝藏族羌族自治州若尔盖县S313', NULL, NULL, '镇上全天开放，两座寺院开放时间8:00-17:00。', 10389, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi5143089-langmusi', '', '', '5', '1', '4', '4', 6, 45198, 74, NULL, NULL, 189673, 24102, 65621, 42146, 43115, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (424, '10095531', '楼兰酒庄', NULL, '新疆 > 鄯善 > 鄯善景点 > 楼兰酒庄', '吐鲁番', '鄯善', 'https://img1.qunarzz.com/travel/poi/1712/47/d0797fa446a14637.jpg', '5', '鄯善景点排名第4', NULL, '1', '42.9111308248', '90.3032907232', '0', '吐鲁番楼兰酒庄景区始建于1976年，占地590亩，是集葡萄种植、葡萄酒生产交易、葡萄酒文化体验、休闲度假、观光旅游、餐饮、会议、娱乐等多功能于一体的综合性葡萄酒主题庄园。\n楼兰酒庄景区地处新疆维吾尔自治区吐鲁番盆地以东鄯善县境内。公元前77年，汉昭帝立尉屠耆为王，改楼兰国为鄯善；这座举世闻名的丝绸之路要驿，将世界各地的声音、色彩、图案都吸纳于此，商贸、文化、战争和东西方文明迤逦而过，孕育出罗布泊地区最具特色、独一无二的楼兰文化。鄯善这片土地今天仍闪烁着非凡的光华，楼兰酒庄景区也因此拥有着独特的旅游资源和深厚的文化底蕴。\n2011年，楼兰酒厂进行了吐鲁番地区最古老的生土建筑结构改造，正式升级为楼兰酒庄；2013年，酒庄新建成集发酵区、储酒区、灌装区、成品库于一体的酿造中心，整个灌装线采用国际先进的葡萄酒生产工艺，以国家葡萄酒行业标准生产高品质葡萄酒；2014年，先后建成楼兰文化中心、楼兰接待中心、橡木桶酒窖、地下酒窖、白兰地车间等；2015年完成楼兰古堡的建造，其中包括楼兰葡萄酒博物馆和楼兰酒店。楼兰酒庄景区将“丝绸之路”文化和“楼兰”文明，融入到楼兰酒庄的发展当中，全面充盈和促进鄯善县旅游业的大建设、大发展。', '吐鲁番市鄯善县楼兰酒业路1号（双水磨园艺场）', NULL, NULL, '9:00-18:00', 3126, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10095531-loulanjiuzhuang', '4', '', '', '1', '3', '2', 7, 32305, 192, 150526, NULL, NULL, 9684, 27232, 13062, 12399, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
