INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (430, '703917', '腊子口', NULL, '甘肃 > 甘南 > 甘南景点 > 腊子口', '甘南', '甘南', 'https://img1.qunarzz.com/travel/d9/1709/99/830eaf5fe17328b5.jpg;https://img1.qunarzz.com/travel/d4/1703/d6/d592a49c6b74e5b5.jpg;https://img1.qunarzz.com/travel/d6/1703/3d/89b1a9d6c1470b5.jpg;https://img1.qunarzz.com/travel/d5/1709/27/d61d472c7dbae5b5.jpg;https://img1.qunarzz.com/travel/d4/1703/1f/a01c7ef12a676eb5.jpg;https://img1.qunarzz.com/travel/d2/1501/92/092f5a4713e0.jpg', '4', '甘南景点排名第5', '1小时 - 2小时', '7', '34.1144961224', '103.9027746979', '12', '腊子口在迭部县东北，是迭部通往汉族地区的门户和重要交通孔道。腊子口系藏语之转音，意为“险绝的山道峡口”。实如其名，腊子口周围群山耸列，峡口如刀劈斧削，腊子口河从峡口奔涌而出，两崖林密道隘，真是“一夫当关，万夫莫开”的险要之地。\n1935年9月，中国工农红军北上抗日，长征途经腊子口，与凭借天险、阻截红军北上的国民党鲁大昌部新编十四师进行了激烈的战斗。红军战士机智勇敢地挫败了敌人的严密封锁，一举突破天险，为红军顺利进入陕甘宁边区，实现北上抗日打开了通道。因而，这次战斗的壮举和天险腊子口一起被载入中国革命史册。\n为了纪念红军长征和缅怀具有伟大历史意义的腊子口战役，国务院决定将腊子口列为重点文物保护单位，并立有“腊子口战役纪念碑”供旅游者和行人瞻仰。纪念碑始建于一九八○年，一九九二年经省政府批准重建，杨成武将军亲自题写了《腊子口战役纪念碑》碑文。', '甘肃省甘南藏族自治州迭部县腊子口乡', NULL, NULL, '全天开放', 10324, '每年6月-8月，这时甘南的气温一般保持在15～30℃之间，凉爽宜人，景色也是最美的。', NULL, '地势极其险要，注意安全。', '2-3小时', 'http://travel.qunar.com/p-oi703917-lazikou', '3', '4', '', '2', '1', '1', 2, 32240, 61, 23658, 98451, NULL, 13161, 24621, 18731, 6205, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
