INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (434, '709529', '紫霄岩', NULL, '福建 > 莆田 > 莆田景点 > 紫霄岩', '莆田', '莆田', 'https://img1.qunarzz.com/travel/d9/1504/da/6cee4a8217958.jpg', '4', '莆田景点排名第15', NULL, '2', '25.5159558509', '119.0598020387', '0', '在市区以北约10公里处，漫山遍野奇石嶙峋，千姿百态，故有“紫霄怪石”之称。山石肖人肖物，千姿百态，绝顶有“万岁石”，尤为峻拔。此外，还有“仙人冢”、“暖日台”、“三清石”、“太府厝”、“仙巾帽”、“观音石”、“石镜屏”等石景，各具风姿，引人入胜。涉紫云溪，沿蜿蜒小道盘山而上，只见巨石对峙如门，上刻“紫云岩”三字，两旁分别刻“岩前松推千峰月，云外寺传午夜钟”的对联。穿过石门，两棵联成“人”字形的连理榕，盘根踞石，古干凌云。不远处的琉璃峡上，飞架一座小巧玲珑的天台桥，相传是宋代蔡襄所建。桥旁有一石，敲击时其声如鼓，故称“石鼓”。附近的古榕盘石而生，根须婆娑，浓荫蔽日，故称“石上松”。再上去有唐代建的迎福院、祥云殿。左后侧有一天然石洞，内有清泉一泓，不溢不涸，不旱不竭，雅称“玉壶池”。洞中有罗汉石像，洞前有一条溪涧，涧上横着一石，其形如舟，流水激石，声如鸣雷，故名“雷轰石”。溪涧中产有一种珍稀的小虾，浑身赤色，象煮熟的一样；还有一种似已剪尾的小螺，这些都是冰川前古生物的遗种。', '莆田市西天尾镇渭阳村', '0594-2892732', NULL, '7：30——17：00', 6558, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi709529-zixiaoyan', '4', '', '5', '', '1', '2', 9, 36014, 74, 68533, NULL, 118303, NULL, 17382, 23118, 10594, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
