INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (442, '713033', '世界风筝博物馆', NULL, '山东 > 潍坊 > 潍坊景点 > 世界风筝博物馆', '潍坊', '潍坊', 'https://img1.qunarzz.com/travel/d3/1504/fb/32d15c9e2d40ec.jpg;https://img1.qunarzz.com/travel/d7/1504/4e/e005ee83fa65c4.jpg;https://img1.qunarzz.com/travel/d8/1504/d0/4c279f1f34b339.jpg;https://img1.qunarzz.com/travel/d6/1504/42/5be19ecceab6c5.jpg;https://img1.qunarzz.com/travel/d6/1504/be/28c13bc4bf2465.jpg;https://img1.qunarzz.com/travel/d7/1502/2c/7fd5eedc9fbf9c.jpg', '4.2', '潍坊景点排名第2', '1小时 - 2小时', '16', '36.7100565576', '119.1210752784', '4', '潍坊风筝博物馆是我国第一座大型风筝博物馆，建筑造型选取了潍坊龙头蜈蚣风筝的特点，屋脊是一条完整的组合陶瓷巨龙，屋顶用孔雀兰琉璃瓦铺成，似蛟龙遨游长空伏而又起，设计风格在国内独树一帜,是“世界风筝都”的标志性建筑。\n风筝博物馆旨在收藏、陈列古今中外的风筝珍品及有关风筝的文物资料。该馆共设有八个展厅即：风筝的历史文化展，潍坊风筝精品展，世界精品风筝展，中国精品风筝展，风筝综合功能展，潍坊国际风筝会，民俗展厅，风筝放映厅。\n八各展厅分别介绍了风筝的历史、应用及沿革发展，潍坊风筝的流派及分类，国外风筝展，国内风筝流派，精品风筝及国际风筝会等内容。展览以翔实的风筝文物资料以及照片、文字、绘画、复制品等，全面展示了风筝的历史文化、现代文化、国际文化及延伸文化，充分体现了中华民族灿烂文化所独具的艺术魅力。\n此外，博物馆还为游客准备了风筝的扎制材料和工具，使游客能亲身体验扎制风筝的乐趣，可以拿着亲手制作的风筝到新建的“世界风筝都纪念广场”去放飞，在这里，会勾起游客对美好童年的回忆。', '潍坊市奎文区行政街66号', '0536-8251745;0536-8237313', 'http://www.wfkitemuseum.cn/', '全年开馆,春夏:8:00-18:00,秋冬:8:00-17:30', 4411, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi713033-shijiefengzhengbowuguan', '', '3', '3', '1', '2', '5', 2, 36217, 174, NULL, 49052, 20139, 21528, 38966, 80113, 91018, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
