INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (455, '722967', '龙虎山', NULL, '江西 > 鹰潭 > 鹰潭景点 > 龙虎山', '鹰潭', '鹰潭', 'https://img1.qunarzz.com/travel/d0/1409/08/cb2aa965c28dd894ffffffffc8d65eac.jpg;https://img1.qunarzz.com/travel/d1/1704/38/a77e59e7b2ae1eb5.jpg;https://img1.qunarzz.com/travel/d9/1702/cf/4f6ced1649dc1b5.jpg;https://img1.qunarzz.com/travel/d4/1702/e2/b6a74ba432e284b5.jpg;https://img1.qunarzz.com/travel/d7/1702/b0/07f29ea8de73b5b5.jpg;https://img1.qunarzz.com/travel/d6/1704/d1/908ba859f62cc6b5.jpg', '4.6', '鹰潭景点排名第1', '4小时 - 1天', '107', '28.078519', '116.995158', '32', '龙虎山位于鹰潭市郊，是道教发源地，道教创始人张道陵当年就是在此修炼得道的。龙虎山的外貌和武夷山相似，都属于典型的丹霞地貌，是世界自然遗产。乘竹筏在泸溪河上看两岸的山景感觉非常棒，而古越人的崖墓悬棺、升棺表演也是很夺人眼球的看点。\n主要景点沿着泸溪两岸由北向南分布，依次是象鼻山-仙水岩-无蚊村-正一观-上清古镇（上清宫），紧凑点可一天游玩主要景点，也可分为两天细细游览。景区是一票制（每个景点可游览一次），通常会买全程票，包含观光车和漂流（或游船）费用，比较合算。观光车招手即停，可多次乘坐。如果需要在此留宿，鹰潭市区以及景区的游客中心、仙水岩、上清镇、九曲洲附近均有住宿，但通常以游客中心附近为大本营。龙虎山景区内有地质博物馆、道教博物馆。', '江西省鹰潭市郊西南20公里处', '400-8853-766', 'http://www.longhushan.com.cn', '7:30-17:30（随季节略有调整）', 2749, NULL, NULL, '龙虎山门票龙虎山仙水岩船筏联游票价（含游船票、导游票）：139元/人次仙人城票价：30元象鼻山票价：15元上清宫票价：30元天门山门票：25元龙虎山水浒影视基地门票：30元龙虎山旅游贴士：1、龙虎山悬棺表演时间分别为13：30分和16：30分，每天只表演两场，节假日期间会加场。2、龙虎山虽然主要是竹筏联游，但是旅游的途中还有40—50分钟的步行路程，请游客在游览时尽量穿轻便的鞋子前往。3、鸬鹚捕鱼表演时间 09：48 11：48 13：48 15：48 。', NULL, 'http://travel.qunar.com/p-oi722967-longhushan', '', '4', '2', '', '5', '2', 6, 47142, 150, NULL, 110610, 29932, NULL, 112815, 38104, 13188, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
