INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (457, '704061', '中国观音故里旅游区', NULL, '四川 > 遂宁 > 遂宁景点 > 中国观音故里旅游区', '遂宁', '遂宁', 'https://img1.qunarzz.com/travel/d7/1607/18/72b20d7fd504a49a.jpg;https://img1.qunarzz.com/travel/d5/1607/24/7186f47877a0129a.jpg;https://img1.qunarzz.com/travel/d5/1607/14/1845e45a13f20d9a.jpg;https://img1.qunarzz.com/travel/d1/1607/3c/aea90634eadc7d9a.jpg;https://img1.qunarzz.com/travel/d5/1607/43/dfa1c47e053f309a.jpg;https://img1.qunarzz.com/travel/d2/1607/32/f2a2bdf434d4d49a.jpg', '4.3', '遂宁景点排名第1', NULL, '9', '30.4989109976', '105.5721382751', '4', '中国观音故里旅游区广德风景区位于遂宁城西2里处，景区以广德寺为核心景区，始建于唐朝（公元618年前后），距今已有近1400年历史。 自唐代宗永泰元年（公元765年）克幽禅师开山阐教以来，香火兴盛，高僧辈出。历受唐、宋、明三朝九帝十一次敕封，是古代朝廷最早钦定的观音道场、皇家禅林。\n寺庙依山而建，规模宏大，曾主领川、滇、黔三百余佛教寺院，建筑形仿京城故宫排列，寺内古建筑群无不彰显着它作为“皇家禅林”的尊贵与庄严。 广德寺现存殿堂楼塔亭坊二十九处，建筑总面积达二万八千余平方米，其中明清建筑共有十一处，建筑面积5632.69平方米，为全川之冠。千百年来，广德寺作为观音道场、皇家禅林，四海归崇、龙天敬仰、六和僧集，到寺庙许愿和还愿的信众络绎不绝。广德寺贵为皇家禅林，到此祈官、升学、发财的香客、信众也是纷至沓来，多有灵验，广为传颂。千百年传承下来的“广德六宝”更是世间罕见的珍贵文物和广德寺厚重历史文化积淀的最好佐证。2006年广德寺被列为全国重点文物保护单位，2007年被评为国家AAAA级景区。经过景区多年的营销和宣传，广德景区游客、信众数量不断攀升，年接待量已突破百万人次。\n广德寺现有的六件镇寺之宝，见证了传承千年的观音文化和皇家文化，它们是：圣旨牌坊、善济塔、西来玉佛、九龙碑、观音珠宝印、四国文玉印。', '四川省遂宁市广德路', '0825-2325888', NULL, '8:30-17:00', 14022, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi704061-zhongguoguanyingulilu:', '2', '4', '2', '1', '', '3', 4, 37496, 75, 49766, 55542, 47253, 16879, NULL, 28797, 27919, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
