INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (462, '709481', '米拉日巴佛阁', NULL, '甘肃 > 合作 > 合作景点 > 米拉日巴佛阁', '甘南', '合作', 'https://img1.qunarzz.com/travel/poi/201408/06/d10b730c67b5e34ac8d65eac.jpg;https://img1.qunarzz.com/wugc/p149/201207/05/b0ea47fd94fadd4193835fbb.jpg;https://img1.qunarzz.com/travel/d5/1804/57/1a13e37e38a546b5.jpg;https://img1.qunarzz.com/travel/d7/1804/ec/ba881df39d4b7ab5.jpg;https://img1.qunarzz.com/travel/d1/1804/21/dc7b54c2372469b5.jpg;https://img1.qunarzz.com/travel/d9/1804/37/7af52f3fdd701db5.jpg', '4.5', '合作景点排名第1', '1小时 - 2小时', '38', '35.0200029227', '102.9219724446', '1', '米拉日巴佛阁位于甘南州合作市，全称是“安多合作米拉日巴九层佛阁”，佛阁大院四周有佛塔1500多个，经轮130个，阁内各类佛像1700余尊，蔚为壮观；与周围的建筑物相比，它高高耸立、气势非凡。佛阁始建于清乾隆年间，毁于文革，现存建筑重建于1988年。\n米拉日巴佛阁是为纪念米拉日巴而修建的，共有九层，这是藏区两座米拉日巴佛阁之一，也是藏区唯一供奉藏传佛教各派宗师和历史名人的高层建筑。佛阁的每一层都有不同的主题和供奉对象，每一层都代表了藏传佛教的一个时代或是一个支派。内有以金刚为主的四密乘的众多佛像、菩萨、护法神等各类佛像1720尊，其中还有被神化的藏族历史文化名人，如松赞干布和文成公主等。', '甘肃省甘南藏族自治州合作市安多藏区', '0941-8231118', NULL, '8:00-18:00', 10001, NULL, NULL, '进入佛阁参观需要脱鞋。', '1-2小时', 'http://travel.qunar.com/p-oi709481-milaribafoge', '', '', '', '3', '1', '3', 0, 22514, 105, NULL, NULL, NULL, 68295, 20145, 74317, 22931, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
