INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (478, '7648081', '临高角', NULL, '海南 > 临高 > 临高景点 > 临高角', '临高', '临高', 'https://img1.qunarzz.com/travel/d7/1712/d2/7be8d1a3bb297eb5.jpg;https://img1.qunarzz.com/travel/d6/1712/95/cc659eb12b4fcab5.jpg;https://img1.qunarzz.com/travel/d1/1712/a1/671f399775203db5.jpg;https://img1.qunarzz.com/travel/d4/1712/84/bb3415234c41d0b5.jpg;https://img1.qunarzz.com/travel/d4/1712/f5/59e806f3016de5b5.jpg;https://img1.qunarzz.com/travel/d3/1712/57/74d6c715cee057b5.jpg', '4.3', '临高景点排名第1', '4小时 - 5小时', '12', '20.014146', '109.720894', '6', '临高角位于距临高县城10公里的海边上，是突出于琼州海峡上的一个岬角，岬角顶端有250米长的天然拦潮礁石堤直插大海，传说这是仙人为人们指路时留下的。1950年4月17日，中国人民解放军解放海南时用木帆船横渡琼州海峡，首先就是在临高角登陆的。 临高角岸上有千米长的海滩，沙白水清，是西海岸的优良天然泳场之一。临高角有个奇观，其沙滩的西侧风平浪静，东侧却波涛滚滚。当地还有个百年前建造的灯塔，是著名的国际航标。 除了海滩和解放军雕像，临高角公园的风景非常漂亮，还可一尝走一下战壕和迷宫。景区野营度假村已开业接待中外宾客。白日游泳，夜晚游乐，各项设施皆备。椰林中，还有颇具风情的吊脚木屋。', '海南省临高县城北部', NULL, NULL, '全天开放', 8744, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi7648081-lingaojiao', '', '', '1', '', '3', '1', 1, 42327, 88, NULL, NULL, 11678, NULL, 60134, 7464, 5657, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (479, '708187', '沙山公园', NULL, '新疆 > 鄯善 > 鄯善景点 > 沙山公园', '吐鲁番', '鄯善', 'https://img1.qunarzz.com/travel/poi/201408/08/ccc372bbd69d4a15c8d65eac.jpg;https://img1.qunarzz.com/travel/poi/1803/a7/e3edf7ce67f96137.jpg;https://img1.qunarzz.com/travel/d7/1606/c1/4bfb6099b84b33f7.jpg;https://img1.qunarzz.com/travel/d2/1606/9f/ac72eb5714ac1cf7.jpg', '5', '鄯善景点排名第2', '2小时', '3', '42.8503644857', '90.2218752795', '0', '公园分南北两部分，以沙山边缘东西向横穿的溪流分界。北部区占地500亩，东测为水上活动区、文化娱乐区、儿童活动区、花卉观赏区和葡萄园。西侧为草坪区、植物园。南部区为沙漠，有沙漠活动区、沙疗区和沙漠植物区。沙山公园南部的沙丘上，夏季炎热，冬季寒冷，昼夜温差大。每当盛夏，气温极高，表层沙温可达7℃以上，最高82.3℃。公园内已开辟沙疗区域，不少关节炎等患者前来治疗。再往南去不远，就是大沙丘所在，只见丘峰轮廓清晰，丘脊平滑似线，迎风面纱波似水，背风坡落沙如泻。站在沙丘顶端，静候大漠日出的辉煌时刻，目送夕阳西沉染尽天际的自然奇观间只觉人与自然融为一体。沙山一溪之隔的公园北部则冬夏温差不大，夏季气候凉爽湿润。由泉水汇聚而成的溪水清澈透明，潺潺流淌，溪边高大多姿的古树环绕，浓荫蔽日，洼地中泉水翻动浮沙，引人入胜，园中的人工湖碧水一潭，倒映湖心岛上的凉亭，背衬高大的沙山，景色分外迷人。沙山公园条件优越，已成为鄯善县城建重点，目前每年游人己逾万。', '新疆吐鲁番鄯善县辟展乡境内', NULL, NULL, '9:00-18:00', 4104, '六月至九月。', NULL, NULL, NULL, 'http://travel.qunar.com/p-oi708187-shashangongyuan', '', '', '', '', '5', '5', 5, 33951, 191, NULL, NULL, NULL, NULL, 147473, 196112, 82589, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (480, '10004315', '奎星楼', NULL, '安徽 > 阜阳 > 阜阳景点 > 奎星楼', '阜阳', '阜阳', 'https://img1.qunarzz.com/travel/poi/1712/3f/6700bb514dd6a037.jpg', '--', '阜阳景点排名第11', '1小时', '0', '32.9012489319', '115.8214462736', '0', '全砖结构，三层六边形，通高9.93米，叠涩出檐，起脊翘角，结顶饰铁制舞凤，六角制铜制响铃（1981年重修时改铁制铃）。凤舞而有容仪，古人以为有瑞应，故顶饰具有传统文化性质。奎星楼三层均室，但互不贯通。一层朝南开一半圆形券门，二层一门四圆形券窗，三层南门楣额为“奎壁联辉”。“奎”、“壁”、“斗”均属二十八宿星座。“奎星”是二十八宿中白虎七宿的首宿，因其“屈曲相钩，似文字之画”，所以《孝经·援神契》有“奎主文章”之说。《春秋元命苞》更认为汉字起源也与奎星有关，说苍颉“仰观奎星圜曲之势，俯察鱼文鸟羽、山川指掌，而创文字”。《历代名画记》说：“奎有芒角，下主辞章”。由于古籍中常把奎星与文字、文章联系在一起，所以历代文人都崇拜奎星，各地为之建楼奉祀，以祈祷本地文人辈出，文风昌盛。', NULL, NULL, NULL, NULL, 13786, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi10004315-kuixinglou', '', '', '', '4', '4', '5', 5, 22774, 184, NULL, NULL, NULL, 134264, 176786, 154112, 111905, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
