INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (483, '7469149', '热带飞禽世界', NULL, '海南 > 定安 > 定安景点 > 热带飞禽世界', '定安', '定安', 'https://img1.qunarzz.com/travel/d7/1602/b8/d267873ff8f52af7.jpg;https://img1.qunarzz.com/travel/d3/1602/3d/f175d733d57d42f7.jpg;https://img1.qunarzz.com/travel/d4/1602/11/d95d1d933e027f7.jpg;https://img1.qunarzz.com/travel/d8/1602/90/04b9193cef56a0f7.jpg;https://img1.qunarzz.com/travel/d0/1602/f9/1cdfb12bca7f8f7.jpg;https://img1.qunarzz.com/travel/poi/1803/64/37121db73226d637.jpg', '4.3', '定安景点排名第4', '3小时 - 4小时', '3', '19.683741', '110.360249', '2', '在这由近千种不同植物美化而成的466亩生态空间里，展示着300余种30000余只各式热带飞禽，涵盖了海南鸟类的绝大多数。这里面有世界最大的鸟――鸵鸟；有世界上第二小的小鸟――珍珠鸟；有飞得最高的鸟——天鹅；有鸟中潜水冠军——凤头潜鸭……特别值得一提的是：其中有五十余种鸟类，都属于海南独有的鸟类或亚种，一般游客是难得一见的。\n在这个鸟类王国中，除了能欣赏到各种各样千姿百态的鸟类外，多才多艺的鸟类明星们还将带给您精彩纷呈的各类飞禽表演。这其中有精彩激烈的飞禽王国奥运会、有中国首家推出的空中禽王――猛禽表演，有生猛的斗鸡、诙谐的斗画眉、沙漠中的鸵鸟骑士，还有在见龙古塔上演绎的孔雀东南飞……这里不仅是鸟的世界，还是花的海洋，近千种奇花异木点缀在园区，直插云天的乔木，婆娑起舞的花灌木，碧绿如茵的草地，一年四季，繁花似锦……好一派具有南国风情的热带风光。众多散放的鸟类，或徜徉林间，或栖于枝头，又或翱翔于蓝天白云间，无拘无束，自由自在的享受着生命的美好！\n倾听天籁鸟音，感悟祥和世界 ，请到飞禽世界来，这里有你童年的梦想；请到飞禽世界来，到这里寻找生命的真谛！', '海南省定安县塔岭开发区', '0898-66787537', NULL, '8:00-18:00', 4859, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi7469149-redaifeiqinshijie', '2', '1', '', '', '5', '3', 5, 22765, 175, 20274, 14243, NULL, NULL, 189645, 81787, 21696, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (484, '705607', '双峰山', NULL, '湖北 > 孝昌 > 孝昌景点 > 双峰山', '孝感', '孝昌', 'https://img1.qunarzz.com/travel/poi/201211/15/929ce4c194faff8eddb12cfb.jpg', '5', '孝昌景点排名第2', NULL, '1', '31.9741677605', '112.814533604', '0', '双峰山属大别山余脉，总面积62.5平方公里，主峰由两座对峙的山峰组成，海拔888米，为孝感第一峰，相传由七仙女“仙化”而成，双峰山由此得名。双峰山森林公园森林覆盖率达87%。地处亚热带季风气候区，雨量充沛，气候温和，是鄂东北景点最多、面积最大的国家森林公园。境内有峡谷探幽、双峰托日、书院听琴、回龙晨钟、凉亭看花、林海听涛、古寨烽烟、白云晓月、万兽朝圣、农家社火、达海洞天、沧海泛舟12个景区。双峰山有兵寨文化、汉孝文化、民俗文化和农耕文化四大文化。自古以来，流传有孟宗哭竹、董永与七仙女的民间故事，留下了白云古寨、双峰书院、第一泉、回龙寺等名胜古迹。白云寨古战场建寨历史1400多年，是湖北规模最大、保存最好的山地古兵寨。双峰书院是明末清初工部侍郎、画家程正揆讲学的地方；书院旁边立有清代乾隆皇帝下江南时御批的“天下第一泉”石碑；回龙寺是孝感最早的佛教寺庙，相传朱元璋曾在此避难。滑石民俗文化村还保存有蒋纬国结发之妻石静宜女士的旧居。区内群山连绵，林木葱茏，溪流环绕，钟灵毓秀，气候宜人，自古即有“仙源”之称，具有丰富而独特的自然、人文、历史和文化资源，是一片“玉笼青纱人未识”的人们旅游休闲的胜地，也是开发建设的风水宝地。', '湖北省孝感市双峰山旅游渡假区', '0712-4928005', NULL, '8:00-17:00', 7562, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi705607-shuangfengshan', '2', '', '3', '5', '5', '4', 7, 28537, 162, 47629, NULL, 53176, 81820, 105805, 180216, 50170, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
