INSERT INTO `scenic_spot`(`id`, `dest_id`, `dest_name`, `dest_en_name`, `navigation_bar`, `real_city`, `city`, `photos`, `score`, `ranking`, `recommend_playtime`, `comment_num`, `lat`, `lng`, `travel_book_num`, `summary_description`, `address`, `phone`, `website`, `opening_hours`, `tourists_num`, `tourist_season`, `traffic_guide`, `tips`, `time_reference`, `url`, `grade_2015`, `grade_2016`, `grade_2017`, `grade_2018`, `grade_2019`, `grade_2020`, `type`, `today_tourists_num`, `play_time`, `year_tourists_2015`, `year_tourists_2016`, `year_tourists_2017`, `year_tourists_2018`, `year_tourists_2019`, `year_tourists_2020`, `year_tourists_2021`, `create_time`, `update_time`, `delete_time`) VALUES (490, '706953', '巩乃斯大草原', NULL, '新疆 > 伊犁 > 伊犁景点 > 巩乃斯大草原', '伊犁', '伊犁', 'https://img1.qunarzz.com/travel/poi/201408/04/cbf961f5ca44eabac8d65eac.jpg;https://img1.qunarzz.com/travel/d8/1805/de/960ca24a729788b5.jpg;https://img1.qunarzz.com/travel/d4/1805/66/a0b25a2e256379b5.jpg;https://img1.qunarzz.com/travel/d5/1805/8a/2219f0a8fddff4b5.jpg;https://img1.qunarzz.com/travel/d2/1805/95/0042c62999d7b5.jpg;https://img1.qunarzz.com/travel/d7/1805/63/c4cec78352d23ab5.jpg', '4.8', '伊犁景点排名第25', '2小时', '7', '43.4362063238', '83.2668676074', '4', '伊犁巩乃斯，蒙语意为“太阳坡”分布在伊犁新源境内，是新疆细毛羊的故乡，也是“天马－－伊犁马”的著名产地，伊犁巩乃斯草原地势跌宕起伏，气象万千。山地草原，河谷草原并存，春夏秋冬牧场齐备，河湖泉涧，野果山花，珍奇异兽，应有尽有，放眼巩乃斯，尽是迷人好去处。 伊犁巩乃斯草原四季景色俱佳，而以春色为最，6月哈萨克牧民从“冬窝子”转场而来，使花香鸟语之中，又多了阵阵牧歌，片片牧群，盛装的少女，骠悍的骑手，为壮美的大草原增添了盎然生机。距新源县城3公里，恰合普河飞泻而成的恰合普瀑布，呼啸而下，为巩乃斯重要的景点之一。 巩乃斯草原主要指巩乃斯河系贯通的河谷山地草原，是伊犁多类型草场的典型分布区。巩乃斯草原大部分位于新源县辖区内，海拔800-2084米，巩乃斯草原地域辽阔，沟谷众多，是新疆著名的草原，它不仅是新疆细毛羊的故乡而且是伊犁天马的重要产地。巩乃斯草原降水丰富，水系发达，物种类繁多。 每年6-9月是草原的黄金季节，辽阔的草原，美丽的山岗、群群牛羊和点点毡房构成草原之夏的生活圈。草原恰似五彩织成的地毯，绿底银边花带，在蓝天映衬下尤显华丽而气势恢宏。皑皑雪峰，繁花似锦的五花草甸、苍翠的云杉林带、银色的水飘带，溪水潺潺，泉水叮咚。时而狐狸、旱獭、野猪、雪鸡等出没在森林和草丛间。每逢夏季，国内外众多游客来此体会伊犁大草原秀美风光与浓郁的民族风情。', '新疆-伊犁新源县辖区内', '0999-8028697', NULL, '全天', 10305, NULL, NULL, NULL, NULL, 'http://travel.qunar.com/p-oi706953-gongnaisidacaoyuan', '', '1', '5', '2', '', '5', 6, 29490, 197, NULL, 15624, 166580, 41401, NULL, 179795, 103488, '2011-08-04 00:00:00', '2011-08-05 00:00:00', NULL);
